INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (101, 1, '3·6·9 정기예금', '{"상품특징":"3개월마다 높은 금리로 갈아탈 수 있는 옵션 보너스 제공\n입출금과 거치식 예금의 장점만을 모은 편리하고 유용한 상품\n내집마련, 결혼, 출산 등 기쁜 날 해지 시 해당 기간별 고시 이율 적용","가입대상":"실명의 개인 또는 개인사업자","예금종류":"정기예금","가입기간":"1년제","최저가입금액":"3백만원 이상 단, 인터넷뱅킹 및 스마트폰뱅킹은 최저 1만원 이상 (원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","금리":"기간: 3개월(중도해지), 금리(연율,세전): 0.7%\n기간: 6개월(중도해지), 금리(연율,세전): 0.8%\n기간: 9개월(중도해지), 금리(연율,세전): 0.9%\n기간: 1년, 금리(연율,세전): 2.2%, 추가컬럼1: 2.2%, 추가컬럼2: 2.2%","세제혜택":"비과세종합저축 가능(전 금융기관 통합한도 범위내), 만기 후 이자는 소득세 부과","일부해지":"가입기간중 만기해지 포함 총 3회 분할인출 가능(중도해지금리 적용)\n단, 분할해지 후 예금잔액이 3백만원 이상인 경우에만 가능","부가서비스":"기쁜 날 서비스\n자녀 결혼 등 Life Event 로 목돈이 필요할 경우 중도해지 시 가입 당시의 경과기간별 고단위 플러스 확정금리형 기본고시금리 적용 지급\n대상 사유 : 내집마련, 결혼, 출산, 유학, 은행이 지정하는 거치식 상품(지수플러스정기예금, MMF 제외한 집합투자상품, 3개월 이상의 특정금전신탁상품)으로 가입 및 대출상환 (반드시 해지금액 이상의 상품 가입 및 대출 상환 시에만 적용)\n해지예약 서비스\n신청 시 제일 먼저 돌아오는 3개월 해당일에 자동 해지 후 연결계좌 입금 (연결계좌는 본인명의 요구불통장만 가능)\n해지예약 서비스를 통한 분할중도해지 불가\n※ 이 예금의 우대서비스(부가서비스 포함)의 내용 및 대상요건은 변경가능하며, 변경 시 변경사유, 변경내용 등을 변경시행일 1개월 전 1개월 동안 은행 홈페이지 또는 영업점에 고시합니다.","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<이자지급방법 변경>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 만기일시지급식\n변경 후 : 만기일시지급식 : 만기(후)해지시 이자를 지급\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n<위법계약해지권 항목 신설>\n기존 가입고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (102, 1, '고단위 플러스(금리연동형)', '{"상품특징":"이자지급 방법별 특징\n만기일시지급식 : 고객이 선택한 금리연동 주기(1개월, 3개월, 6개월, 1년)마다 복리로 셈하여 만기해지시 일괄지급됩니다.\n이자지급식 : 고객이 선택한 금리연동 주기별(1개월, 3개월, 6개월, 1년)로 해당일에 원천징수 후 지급됩니다.\n(단, 금리연동 주기에 따라 3개월, 6개월 이자지급식은 선택이 불가능할 수 있습니다.)\n연원가식 : 가입일로부터 매 1년마다 변동금리로 적용되며, 매 1년마다 계산된 이자를 원천징수후 원금에 가산합니다. 1년마다 이자를 발생시키므로, 만기에 이자소득이 집중되는 것을 막아줍니다. (1년 연동형만 가입가능)","가입대상":"제한없음","예금종류":"정기예금","가입기간":"1개월 연동형 : 1년제\n3, 6개월 연동형 : 1, 2, 3, 5년제\n1년 연동형 : 5년제(법인고객 신규 일시중단)","최저가입금액":"인터넷뱅킹, 스마트폰 뱅킹 : 1만원 이상(원단위)\n영업점 : 1천만원 이상(원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급\n이자지급식: 매월 이자를 지급\n연원가식 : 매 1년마다 원금에 가산","금리":"기간: 1개월 연동형, 금리(연율,세전): 1.7%, 추가컬럼1: 1.7%, 추가컬럼2: 1.8%, 추가컬럼3: 1.8%\n기간: 3개월 연동형, 금리(연율,세전): 1.8%, 추가컬럼1: 1.8%, 추가컬럼2: 1.9%, 추가컬럼3: 1.9%\n기간: 6개월 연동형, 금리(연율,세전): 1.9%, 추가컬럼1: 1.9%, 추가컬럼2: 2.0%, 추가컬럼3: 2.0%\n기간: 1년 연동형, 금리(연율,세전): 1.95%, 추가컬럼1: 1.95%, 추가컬럼2: 2.05%, 추가컬럼3: 2.05%","세제혜택":"개인의 경우 비과세종합저축 가능(전 금융기관 통합한도 범위내), 만기 후 이자는 소득세 부과","일부해지":"만기해지 포함 총 3회 이내 만기일시지급식만 가능(이자지급식, 연원가식은 불가)\n단, 분할해지 후 예금잔액이 1천만원 이상인 경우에만 가능(중도해지금리 적용)","유의사항":"예금주가 만기일전 예금일로부터 1연동기간이 도래하기 전에 지급청구 할 때는 예금일로부터 지급일 전일까지 기간에 대해 예금일에 영업점에 고시한 중도해지금리를 적용한다.\n예금주가 만기일전 예금일로부터 1연동기간 이상 경과한 후에 지급청구 할 때는 매 연동기간의 충족분에 대해서는 매 이율결정일의 고시이율을 적용하며, 매 연동기간 경과 잔여기간에 대해서는 직전 이율결정일부터 지급일 전일까지는 예금일에 영업점에 고시한 중도해지이율을 적용한다.\n이자지급식의 경우 중도해지 시에는 예금 계약일로부터 지급일 전날까지 기간에 대하여 계약일 당시 영업점에 게시한 중도해지금리로 셈하여 이자를 지급하며, 이미 지급한 이자는 지급할 금액에서 차감됩니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음(일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.09.24 변경)\n<원급 및 이자지급제한 변경>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n변경 후 :\n계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한(일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가\n<위법계약해지권 변경>\n기존 가입고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능\n변경 후 :\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (103, 1, '고단위 플러스(금리확정형)', '{"상품특징":"이자지급 방법별 특징\n만기일시지급식 : 만기해지시 원금과 함께 지급됩니다.\n이자지급식 : 고객의 지정에 따라 월별, 3개월별 또는 매1년마다 지급됩니다.\n수시이자지급식 : 경과기간에 대하여 고객이 지정한 날까지 이자를 지급할 수 있으며, 이자금액과 이자수령시기를 자유로이 조절하여 종합과세대응에 유리합니다.\n연원가식 : 매 1년마다 계산된 이자를 원천징수 후 원금에 가산합니다. (가입기간 1년초과 5년이내의 경우만 가능)","가입대상":"제한없음","예금종류":"정기예금","가입기간":"1개월~5년(일 단위 만기 지정 가능)","최저가입금액":"인터넷뱅킹, 스마트폰 뱅킹 : 1만원 이상(원단위)\n영업점 : 1백만원 이상(원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급\n이자지급식: 월별, 3개월별,매 1년마다 지급\n연원가식 : 매 1년마다 원금에 가산\n수시이자지급식 : 경과기간에 대하여 손님이 지정한 날까지 이자를 지급","금리":"기간: 1개월 이상~3개월 미만, 금리(연율,세전): 1.7%, 추가컬럼1: 1.7%, 추가컬럼2: 1.8%, 추가컬럼3: 1.8%\n기간: 3개월 이상~6개월 미만, 금리(연율,세전): 1.8%, 추가컬럼1: 1.8%, 추가컬럼2: 1.9%, 추가컬럼3: 1.9%\n기간: 6개월 이상~1년 미만, 금리(연율,세전): 1.9%, 추가컬럼1: 1.9%, 추가컬럼2: 2.0%, 추가컬럼3: 2.0%\n기간: 1년 이상~2년 미만, 금리(연율,세전): 1.95%, 추가컬럼1: 1.95%, 추가컬럼2: 2.05%, 추가컬럼3: 2.05%\n기간: 2년 이상~3년 미만, 금리(연율,세전): 1.95%, 추가컬럼1: 1.95%, 추가컬럼2: 2.05%, 추가컬럼3: 2.05%\n기간: 3년 이상~4년 미만, 금리(연율,세전): 1.95%, 추가컬럼1: 1.95%, 추가컬럼2: 2.05%, 추가컬럼3: 2.05%\n기간: 4년 이상~5년 미만, 금리(연율,세전): 2.05%, 추가컬럼1: 2.05%, 추가컬럼2: 2.15%, 추가컬럼3: 2.15%\n기간: 5년 이상, 금리(연율,세전): 2.05%, 추가컬럼1: 2.05%, 추가컬럼2: 2.15%, 추가컬럼3: 2.15%","세제혜택":"개인의 경우 비과세종합저축 가능(전 금융기관 통합한도 범위내), 만기 후 이자는 소득세 부과","일부해지":"만기해지 포함 총 3회 이내 만기일시지급식만 가능(이자지급식, 수시이자지급식, 연원가식은 불가)\n단, 분할해지 후 예금잔액이 1백만원 이상인 경우에만 가능(중도해지금리 적용)","갱신":"가능","유의사항":"예금주가 만기일전에 지급청구 할 때는 예금일로부터 지급일 전일까지 기간에 대해 영업점에 고시한 기간별 중도해지금리를 적용한다.\n이자지급식 및 수시이자지급식의 경우 중도해지 시에는 예금 계약일로부터 지급일 전날까지 기간에 대하여 계약일 당시 영업점에 게시한 중도해지금리로 셈하여 이자를 지급하며, 이미 지급한 이자는 지급할 금액에서 차감됩니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음(일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2023.02.13 변경)\n<우대금리(쿠폰)항목 신설>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전 :\n해당사항 없음(신설)\n변경 후 :\n금리우대 쿠폰의 우대금리는 하나원큐 내 쿠폰함 또는 상품 가입시 제공되는 쿠폰의 우대금리를 따르며, 만기해지시에 기본금리에 가산하여 지급합니다. 단, 신규 가입시에만 쿠폰 사용이 가능하며, 재예치 또는 중도해지시에는 적용되지 않습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (104, 1, '양도성예금증서(CD)', '{"상품특징":"만기일 이전이라도 양도가 가능하여 단기자금운용에 적합한 시장성 예금","가입대상":"제한없음. 단, 외국인 비거주자는 증권투자전용 비거주자 원화계정을 통해서만 가능함.","예금종류":"양도성예금증서","가입기간":"30일 이상 ~ 5년 이하 (단, 공휴일은 만기일 지정 불가)","최저가입금액":"액면금액기준 1천만원 이상","발행형식":"실물발행 : 무기명 할인식\n통장발행 : 등록발행시 교부되는 등록필증(통장)에 발행의뢰인 명의 표기","금리":"기간: 30일~89일, 금리(연율,세전): 1.75%, 추가컬럼1: 1.75%\n기간: 90일~180일, 금리(연율,세전): 1.85%, 추가컬럼1: 1.85%\n기간: 181일~364일, 금리(연율,세전): 1.95%, 추가컬럼1: 1.95%\n기간: 365일, 금리(연율,세전): 2.0%, 추가컬럼1: 2.0%\n기간: 1년초과~2년미만, 금리(연율,세전): 2.05%, 추가컬럼1: 2.05%\n기간: 2년이상~3년미만, 금리(연율,세전): 2.15%, 추가컬럼1: 2.15%\n기간: 3년이상~5년이하, 금리(연율,세전): 2.25%, 추가컬럼1: 2.25%","이자지급방법":"만기일시지급식 : 만기(후) 해지시 이자를 지급","중도해지":"불가","일부해지":"불가","세제혜택":"비과세종합저축으로 가입 불가","이용제한사항":"실물발행의 경우 예금잔액증명서 발급불가(단, 발행사실 확인서 발급 가능)","원금 및 이자지급 제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한\n등록(통장)발행의 경우 예금잔액증명서 발급 당일에는 잔액 변동 불가","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.09.24 변경)\n<위법계약해지권>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.\n변경 후 : 삭제","예금자 보호":"이 금융상품은 예금자보호법에 따라 보호되지 않습니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (105, 1, '외화고단위플러스 정기예금(금리연동형)', '{"상품특징":"이자지급방법은 만기일시지급식, 이자지급식 (예금당시 손님의 지정에 따라 매월별, 매3개월별 마다 지급)\n분할해지\n- 금리연동형 : 만기해지포함 총 3회이내 분할 해지 가능","가입대상":"제한없음","예금종류":"외화정기예금","가입기간":"6개월이상 5년이하","최저가입금액":"미화 $100 상당액 이상","예치통화":"15개국 통화 (USD, JPY, EUR, GBP, CHF, AUD, CAD, NZD, HKD, SGD, SEK, DKK, NOK,KWD,CNY)","이자지급방법":"만기일시지급식 : 만기(후) 해지 시 이자를 지급\n이자지급식 : 월별 / 3개월별 이자를 지급","금리":"금리안내\n가입일 당시 영업점 및 당행 홈페이지에 고시된 예치기간별 해당 이율 적용\n※ 금리연동형은 선택한 연동 주기의 해당일마다 변동된 이율을 적용하며 가입시 선택한 이율방식은 가입기간 중에 변경 안됨.","부가서비스":"1. 선물환 거래 가능\n2. 자동갱신서비스\n- 처음 가입한 예금과 동일조건(동일상품, 동일만기)으로 갱신거래 약정일까지 자동갱신","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락여부를 통지해야하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 : 해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (106, 1, '외화고단위플러스 정기예금(금리확정형)', '{"상품특징":"이자지급방법은 만기일시지급식, 월이자지급식, 수시이자지급식\n분할해지\n- 금리확정형 : 만기해지포함 총 3회이내 분할 해지 가능","가입대상":"제한없음","예금종류":"외화정기예금","가입기간":"7일이상 1년이내","최저가입금액":"미화 $100 상당 이상","예치통화":"15개국 통화 (USD, JPY, EUR, GBP, CHF, AUD, CAD, NZD, HKD, SGD, SEK, DKK, NOK,KWD,CNY)","이자지급방법":"만기일시지급식 : 만기(후 )해지 시 이자를 지급\n월이자지급식 : 매월 이자를 지급\n수시이자지급식 : 경과기간에 대하여 손님이 지정한 날까지 이자를 지급","금리":"금리안내\n가입일 당시 영업점 및 당행 홈페이지에 고시된 예치기간별 해당 이율 적용","예금담보대출":"예금잔액 범위내에서 가능","부가서비스":"1. 선물환 거래 가능\n2. 자동갱신서비스\n- 처음 가입한 예금과 동일조건(동일상품, 동일만기)으로 갱신거래 약정일까지 자동갱신","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락여부를 통지해야하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 : 해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (107, 1, '외화양도성예금증서(통장식)', '{"가입대상":"제한없음","예금종류":"외화양도성예금증서","가입기간":"30일 이상 365일 이내","최저가입금액":"액면금액 US $5,000상당액 이상","이자지급방법":"만기일시지급식 : 만기(후) 해지 시 이자를 지급","금리":"금리안내\n가입일 당시 고시한 외화양도성예금증서 이율의 가입기간별 해당 이율을 적용","예금담보대출":"예금잔액 범위내에서 가능","기타":"예치통화\n15개국 통화 (USD, JPY, EUR, GBP, CHF, AUD, CAD, NZD, HKD, SGD, SEK, DKK, NOK,KWD,CNY)","유의사항":"세금우대 또는 생계형저축 불가합니다.\n중도해지 및 신규취소가 불가합니다.\n신규한 지점에서만 지급이 가능합니다.\n예금잔액증명서는 발급이 불가하며, 소지인에게 발행사실확인서 발급은 가능합니다.\n이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우와 여행자수표로 인출하는 경우에는 현찰수수료 또는 여행자수표 수수료가 발생할 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락여부를 통지해야하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 : 해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 금융상품은 예금자보호법에 따라 보호되지 않습니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (108, 1, '외화정기예금', '{"가입대상":"제한없음","예금종류":"외화정기예금","가입기간":"1일 이상 24개월 이하","최저가입금액":"제한없음","예치통화":"18개 통화 (USD, EUR, JPY, GBP, CHF, CAD, AUD, NZD, HKD, SGD, SEK, DKK, NOK, KWD, CNY, RUB, AED, TRY)","이자지급방법":"만기일시지급식 : 만기(후) 해지 시 이자를 지급\n월이자지급식 : 매월 이자를 지급","금리":"금리안내\n가입일 당시 영업점 및 당행 홈페이지에 고시된 외화정기예금의 예치기간별 해당 금리 적용","기타":"발행 형식에 따라 통장식과 증서식이 있음.\n※ 인터넷에서 신규 가능","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락여부를 통지해야하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 : 해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (109, 1, '정기예금', '{"상품특징":"목돈을 일정기간 동안 예치하여 안정적인 수익을 추구하는 예금","가입대상":"제한없음","예금종류":"정기예금","가입기간":"1개월 이상 3년 이하 (연,월,일 단위), 4년제, 5년제","최저가입금액":"1만원이상","이자지급방법":"아래 두가지 방법 중 택일\n만기일시지급식 : 만기(후) 해지시 일자를 지급\n월이자지급식 : 이자를 매월 정기적으로 지급","일부해지":"만기일시지급식에 한하여 만기해지 포함 총3회 일부해지 가능 (일부해지 후 잔액은 최소가입금액 이상이어야 함)\n일부해지 시 일부해지 금액에 대하여 중도해지금리 적용","금리":"기간: 1개월, 금리(연율,세전): 1.6%, 추가컬럼1: 1.7%\n기간: 3개월, 금리(연율,세전): 1.7%, 추가컬럼1: 1.8%\n기간: 6개월, 금리(연율,세전): 1.8%, 추가컬럼1: 1.9%\n기간: 1년, 금리(연율,세전): 1.85%, 추가컬럼1: 1.95%\n기간: 2년, 금리(연율,세전): 1.85%, 추가컬럼1: 1.95%\n기간: 3년, 금리(연율,세전): 1.85%, 추가컬럼1: 1.95%\n기간: 4년, 금리(연율,세전): 1.85%, 추가컬럼1: 1.95%\n기간: 5년, 금리(연율,세전): 1.85%, 추가컬럼1: 1.95%","세제혜택":"비과세종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 만료일 이후의 이자는 과세","원금 및 이자지급 제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한 (일부해지 포함)\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한  경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면  등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<상품특징>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 : 목돈을 일정기간 동안 예치하여 안정적인 수익을 추구하는 예금\n<가입기간>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 1개월 이상 3년 이내(월, 연, 일 단위), 4년제, 5년제\n변경 후 : 1개월 이상 3년 이하(월, 연, 일 단위), 4년제, 5년제\n<이자지급방법>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 만기일시지급식, 월이자지급식 중 택일\n변경 후 : 아래 두가지 방법 중 택일\n만기일시지급식 : 만기(후) 해지시 일자를 지급\n월이자지급식 : 이자를 매월 정기적으로 지급\n<일부해지>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n만기일시지급식에 한하여 만기해지 포함 총3회 일부해지 가능(일부해지 후 잔액은 최소가입금액 이상이어야 함)\n일부해지 시 일부해지 금액에 대하여 중도해지금리 적용\n<세제혜택>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내), 만기후 이자는 소득세 부과\n변경 후 :\n비과세종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 만료일 이후의 이자는 과세\n<원금 및 이자지급 제한>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한 (일부해지 포함)\n예금잔액증명서 발급 당일에는 잔액 변동 불가\n<위법계약해지권>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전 : 신설\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한  경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1개월","금리(연율,세전)":"1.6%","추가컬럼1":"1.7%"},{"기간":"3개월","금리(연율,세전)":"1.7%","추가컬럼1":"1.8%"},{"기간":"6개월","금리(연율,세전)":"1.8%","추가컬럼1":"1.9%"},{"기간":"1년","금리(연율,세전)":"1.85%","추가컬럼1":"1.95%"},{"기간":"2년","금리(연율,세전)":"1.85%","추가컬럼1":"1.95%"},{"기간":"3년","금리(연율,세전)":"1.85%","추가컬럼1":"1.95%"},{"기간":"4년","금리(연율,세전)":"1.85%","추가컬럼1":"1.95%"},{"기간":"5년","금리(연율,세전)":"1.85%","추가컬럼1":"1.95%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (110, 1, '표지어음', '{"상품특징":"은행이 보유한 원어음(상업어음 또는 무역어음)을 근거로 발행하는 배서양도 가능한 어음","가입대상":"제한없음. 단, 외국인 비거주자는 증권투자전용 비거주자 원화계정을 통해서만 가능함.","예금종류":"표지어음","가입기간":"30일 이상 ~ 365일 이하 (단, 원어음 최장 만기일 범위내에서 판매가능)","최저가입금액":"액면금액 1천만원 이상","발행형식":"할인식 (실물발행, 통장발행)","이자지급방법":"만기일시지급식 : 만기(후) 해지시 이자를 지급","금리":null,"중도해지":"불가","일부해지":"불가","세제혜택":"비과세종합저축으로 가입 불가","이용제한 사항":"실물발행의 경우 예금잔액증명서 발급 불가\n실물발행의 경우 어음 재발행 불가","원금 및 이자지급 제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한\n등록(통장)발행의 경우 예금잔액증명서 발급 당일에는 잔액 변동 불가","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.09.24 변경)\n<상품특징>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.\n변경 후 : 삭제","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (111, 1, '하나의 정기예금', '{"상품특징":"계약기간 및 가입금액이 자유롭고 자동재예치를 통해 자금관리가 가능한 하나원큐 전용 정기예금","가입대상":"실명의 개인 또는 개인사업자","예금종류":"정기예금","가입기간":"1개월 이상 ~ 5년 이내 일단위","가입금액":"1백만원 이상","이자지급방법":"만기일시지급식 : 만기(후) 해지시 원금과 함께 지급","기본금리":"금리안내","만기자동재예치\n선택시":"만기전일까지 재예치를 신청할 수 있으며, 재예치 방식은 원금 또는 (세후)원리금 중에서 선택할 수 있습니다.\n재예치시에는 최초 계약기간 단위로 만기일(단, 휴일인 경우 익영업일)에 재예치하며, 재예치일의 하나원큐(스마트폰 뱅킹) 상품상세 페이지에 안내한 계약기간별 적용금리를 적용합니다.\n※ 원금 재예치시 세후이자는 사전에 지정한 본인의 하나은행 입출금통장에 입금됩니다.\n※ 아래의 사유 발생시 재예치가 불가합니다.\n비과세종합저축한도를 초과하거나 관련 세법 개정으로 인한 한도 변경시\n압류 등 지급제한이 있는 경우\n판매중단 등 (단, 판매중단일 이후 만기도래한 계좌에 한함)","세제혜택":"비과세종합저축으로 가입 가능 (전 금융기관 통합한도 범위 내) 단, 만기 후 이자는 소득세 부과\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있습니다.","일부해지":"만기일 이전 2회까지 가능하며, 일부해지 금액에 대해서는 중도해지금리를 적용합니다.\n법적제한이 있는 경우 일부해지 불가합니다.","원금 및\n이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음 (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있음\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락여부를 통지해야 하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1개월 이상","금리(연율,세전)":"1.8%"},{"기간":"3개월 이상","금리(연율,세전)":"1.9%"},{"기간":"6개월 이상","금리(연율,세전)":"2.0%"},{"기간":"9개월 이상","금리(연율,세전)":"2.0%"},{"기간":"12개월 이상","금리(연율,세전)":"2.05%"},{"기간":"13개월 이상","금리(연율,세전)":"2.05%"},{"기간":"18개월 이상","금리(연율,세전)":"2.05%"},{"기간":"24개월 이상","금리(연율,세전)":"2.05%"},{"기간":"36개월 이상","금리(연율,세전)":"2.05%"},{"기간":"48개월 이상","금리(연율,세전)":"2.15%"},{"기간":"60개월","금리(연율,세전)":"2.15%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (112, 1, '행복knowhow 연금예금', '{"상품특징":"안정적인 노후자금이 필요하시죠?\n매달 수령하는 원리금을 생활자금으로 이용하세요.\n수령하는 금액을 자유롭게 변경할 수 있을까 걱정되세요?\n만기수령금액은 손님의 필요에 따라 언제든지 변경 가능합니다. 돈이 많이 필요할 땐 많게, 그렇지 않을 때는 적게,이자만 필요하실 때는 이자만, 만기수령액도 ''내 맘대로'' 행복knowhow 연금예금을 설계하세요.\n※ 만기희망잔액은 수시로 변경가능하며, 이 경우 변경한 다음달부터 원리금이 재산정됩니다.","가입대상":"실명의 개인 또는 개인사업자","예금종류":"정기예금","가입기간":"최장 31년까지 가능\n거치기간 : 1년(선택사항)\n원리금지급기간 : 1년~30년(연단위 지정)","최저가입금액":"1백만원 이상(원단위)","이자지급방법":"거치기간 : 만기일시지급식 (이자계산 일단위\n원리금 지급기간 : 원리금균등지급방식 (완전 원리금균등 및 부분 원리금균등, 이자계산 월단위)\n※ 부분 원리금균등 지급의 경우 \"가입원금- 만기희망잔액\" 에 대하여 동일한 세전 원리금 지급","금리":"기간: 1년, 금리(연율,세전): 2.2%, 추가컬럼1: 2.2%\n기간: 2년, 금리(연율,세전): 2.3%, 추가컬럼1: 2.3%\n기간: 3년, 금리(연율,세전): 2.3%, 추가컬럼1: 2.3%","세제혜택":"비과세종합저축 가능(전 금융기관 통합한도 범위내), 만기 후 이자는 소득세 부과","예금담보대출":"만기희망잔액 범위내에서 가능 (만기희망잔액 변경 가능)","일부해지":"거치기간 동안에만 총3회까지 가능 (최소가입금액 1백만원 이상유지, 분할해지시 중도해지금리 적용)","유의사항":"원리금 균등지급으로 원금 감소에 따라 세금 우대 및 생계형 한도도 자동 감액\n법적 제한이 있는 경우 원리금분할지급 중지\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n<위법계약해지권 항목 신설>\n기존 가입고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년","금리(연율,세전)":"2.2%","추가컬럼1":"2.2%"},{"기간":"2년","금리(연율,세전)":"2.3%","추가컬럼1":"2.3%"},{"기간":"3년","금리(연율,세전)":"2.3%","추가컬럼1":"2.3%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (113, 1, '1년 연동형 정기예금', '{"가입대상":"개인, 개인사업자 또는 법인","예금종류":"정기예금","가입기간":"최장 15년까지 연단위로 지정","최저가입금액":"1만원 이상 (원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급\n1년 이자지급식: 매 1년마다 지급","금리":null,"세제혜택":"개인의 경우 비과세종합저축 가능(전 금융기관 통합한도 범위내), 만기 후 이자는 소득세 부과","예금담보대출":"예금잔액 범위내에서 가능","일부해지":"불가","유의사항":"만기일 전에 해지할 경우 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n1년 단위로 일반정기예금 1년제 기본금리에 연동하는 변동금리 상품입니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<이자지급방법 변경>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 만기일시지급식, 1년 이자지급식\n변경 후 :\n만기일시지급식 : 만기(후)해지시 이자를 지급\n1년 이자지급식 : 매1년마다 지급\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n<위법계약해지권 항목 신설>\n기존 가입고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (114, 2, '(내맘) 적금', '{"상품특징":"저축금액, 만기일, 자동이체 구간까지 내 맘대로 디자인하는 DIY적금","상품명 지정":"예금주명 또는 별칭을 상품명에 지정가능","가입대상":"실명의 개인 또는 개인사업자","가입기간":"6개월 이상 ~ 60개월 이하 (월 또는 일단위 지정)\n※ 정액적립식은 월단위만 가능","가입금액":"매월 1천원 이상 ~ 1천만원 이하","적립한도":"ㆍ자유적립식 : 매월 1원 이상 ~ 1,000만원 이하 (원단위)\nㆍ정액적립식 : 매월 1천원 이상 ~ 1,000만원 이하(원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식, 정액적립식","금리":"금리안내\n기준일자 : 2025-08-10\n내맘 적금\n기간 금리(연율,세전)\n정액적립식 자유적립식\n6개월이상~7개월미만 2.0% 1.5%\n7개월이상~13개월미만 2.1% 1.5%\n13개월이상~25개월미만 2.2% 1.6%\n25개월이상~37개월미만 2.3% 1.7%\n37개월이상~60개월이하 2.4% 1.8%","우대금리":"연 0.50% (2019.04.15 기준,세전)\n이 예금 가입후(또는 재예치후)만기월 전월까지 하나은행의 입출금이 자유로운 예금을 통하여 계약기간의 ½이상\n월부금 자동이체실적을 충족한 경우 연 0.5%의 우대금리를 만기해지 시 제공 (단, 월별 1회 납입횟수만 인정)\n만기수취이자 예시(최고금리/정액적립식 기준)\n- 월 납입액 10만원/계약기간 60개월/연4.15% = 이자 632,875원(세전)\n※ 위 사항은 예시이며 납입액, 계약기간, 적용금리 등은 세부사항에 따라 변동될 수 있습니다.","우대금리(쿠폰)":"금리우대쿠폰을 발급받을 경우 기본금리에 가산하여 우대금리를 지급합니다. 금리우대쿠폰의 금리는 하나원큐 내 쿠폰함 또는 상품 가입시 제공되는 쿠폰의 우대금리를 따릅니다. 단, 신규 가입시에만 쿠폰 사용이 가능하며, 재예치 또는 중도해지시에는 적용되지 않습니다.","만기자동재예치\n선택시 (총3회)":"이 예금을 자유적립식으로 가입하고 자동재예치를 선택한 경우, 만기일에 최초 계약기간 단위로 총 3회 자동 재예치되며, 재예치시에는 최소 가입금액 1천원을 제외한 세후 원리금 전액이 사전에 지정한 본인명의 입출금계좌로 입금됩니다.\n재예치를 희망하지 않는 경우에는 온라인 및 영업점 창구를 통해 변경/해제 신청이 가능하며, 신청 후 도래하는\n재예치 거래시 적용됩니다.\n만기일이 휴일인 경우에는 익영업일에 재예치, 해당일이 없는 경우에는 그 달의 마지막 영업일에 재예치하며, 재예치일을\n새로운 계약일로 간주합니다.\n단, 압류 등 지급제한이 있는 경우에는 재예치 되지 않습니다.","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내), 단, 만기후이자는 소득세 부과","일부해지":"만기일 이전 총 2회 입금건별 균등계산방식에 따라 일부해지가 가능하며, 정액적립식은 일부해지 후 월저축금이 변경됩니다.\n(단, 일부해지 후 예금 잔액은 1천원 이상이어야 하고, 일부해지 금액에 대해서는 중도해지금리를 적용함)","유의사항":"만기일 전에 중도해지 시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n계좌별칭은 계좌조회>계좌관리>계좌별칭변경에서 등록 및 변경 가능합니다.\n우대금리는 만기 해지 시 제공하며, 일부 해지 및 중도해지 시에는 제공하지 않습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음(일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있음.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.08.31 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<적립한도>\n변경 전\n해당사항 없음(신설)\n변경 후\n자유적립식 : 매월 1원 이상 ~ 1,000만원 이하 (원단위)\n정액적립식 : 매월 1천원 이상 ~ 1,000만원 이하(원단위)","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (115, 2, '(아이) 꿈하나 적금', '{"상품특징":"아이의 출생, 입학 등 특별한 해에 특별금리를 제공하는 적금!","가입대상":"만 18세 이하 실명의 개인 (1인 1계좌)","가입기간":"1년","가입금액":"1천원 이상 150만원 이하 (원단위)","적립한도":"1천원 이상 150만원 이하 (분기한도)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":null,"우대금리":"최대 연0.8% (2021.09.01기준, 세전)\n아래 항목을 충족한 경우, 최대 연0.8%의 우대금리를 제공\n①가입우대 : 이 예금을 최초 가입한 경우 연 0.2%\n(해지후 재가입하는 경우 제공하지 않음)\n② 청약우대 : 가입일(또는 재예치일)에 주택청약종합저축을 보유한 경우 연 0.4%\n③ 자동이체우대 : 계약기간중 (주)하나은행 입출금통장을 통하여 6회이상 월 자동이체로 적립한 경우 연 0.2%\n④ 카드우대 : 이 예금 가입후(또는 재예치 후)3개월이 되는 달의 말일까지 (주)하나은행 본인명의 입출금통장에서\n하나(현대)카드사의 체크카드 결제실적을 보유한 경우 연0.2%\n⑤ 아동수당우대 : 가입후(또는 재예치후) 만기전전월말 기준, ㈜하나은행 계좌로 아동수당법에서 정한 아동수당을 1회이상\n수령한 경우 연 0.2%\n(단, 보호자계좌로 수령시 수급계좌 정보를 만기(또는 재예치)전일까지 ㈜하나은행에 제공한 경우에 한함)","HAPPY YEAR 특별금리\n(2021.09.01)":"① 아래 조건 충족시 특별금리 제공. 단,다항에 해당할 경우 다항의 특별금리만(연1.0%) 적용되며 (가항,나항 중복적용불가),가항 및 나항의 특별금리는 동일계약기간별 최대 연0.3% 적용\n가. 이 예금 가입고객이 출생후 1년 이내, 만7세, 만13세, 만16세가 되는 해당 계약기간별 1년간 연0.3%\n나. 이 예금 가입고객이 동일기관, 학교 등 10인 이상 단체 가입 시 해당 가입년도 연0.3% (동일 영업점에서 확인하고 신규하는 경우, 인터넷뱅킹/스마트폰뱅킹/콜센터 신규불가)\n다. 예금주가 만 3세 이하이고 대리인이 「(아이) 꿈하나 적금」을 신규 하는 시점에 「(태아) 사랑 하나 적금」을 보유하고 있거나 보유한 적이 있는 경우, 가입 후 1년간 연1.0% (단, 최초 1회에 한함)\n② 희망대학입학 축하금리 : 만기 전 1년간 연2.0% 우대 (만 14세까지 희망대학 등록 가능)\n※우대금리 및 특별금리는 만기해지시 최종 적용되며, 중도해지시는 적용되지 않습니다.\n출시 이벤트 금리 제공 : 최대 연 0.3%\n- 제공대상 : ‘18.5.2 ~ 5.31까지 가입된 이 적금 계좌에 한하여 적용\n(단, HAPPY YEAR 특별금리 기 적용 계좌 제외)","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내), 만기후이자는 소득세 부과","일부해지":"재예치계좌에 한하여 재예치 원리금 범위내 계약기간 중 2회까지 가능하며, 재예치일에 기본금리가 적용됩니다.\n(일부해지 후 예금잔액은 1만원이상)","유의사항":"만기일전에 중도해지 시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n청약우대는 가입일 (또는 재예치일) 이전 또는 당일에 보유시 적용됩니다.\n동일기관, 학교 등 단체신규 10인 이상의 금리는 동일 영업점에서 확인하고 신규하는 경우 적용됩니다.\n만 19세까지만 만기 자동재예치되며 희망대학 입학 축하금리는 만14세까지만 등록가능하며, 입학통지서 등 증빙자료를 KEB하나은행 영업점으로 제출하셔야 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.09.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 미적용\n<HAPPY YEAR 특별금리 항목 신설>\n변경 전 : 이 예금의 가입고객이 아래 항목을 충족하는 경우, 동일 계약기간별 최대 연0.3%의 특별금리를 만기해지 시에 지급한다.\n가. 이 예금 가입고객이 출생후 1년 이내, 만7세, 만13세, 만16세가 되는 해당 계약기간별 1년간 연0.3%\n나. 이 예금 가입고객이 동일기관, 학교 등 10인 이상 단체 가입 시 해당 가입년도 연0.3% (동일 영업점에서 확인하고 신규하는 경우, 인터넷뱅킹/스마트폰뱅킹/콜센터 신규불가)\n변경 후 : 이 예금의 가입고객이 아래 항목을 충족하는 경우, 아래 3가지 항목 중 다목에 해당할 경우 다목의 특별금리만 적용된다.(가목, 나목 중복 적용 불가) 단, 다목의 조건에 해당하지 않을 경우 가목과 나목의 특별금리는 동일 계약기간별 최대 연0.3%를 만기해지 시에 지급한다\n가. 이 예금 가입고객이 출생후 1년 이내, 만7세, 만13세, 만16세가 되는 해당 계약기간별 1년간 연0.3%\n나. 이 예금 가입고객이 동일기관, 학교 등 10인 이상 단체 가입 시 해당 가입년도 연0.3% (동일 영업점에서 확인하고 신규하는 경우, 인터넷뱅킹/스마트폰뱅킹/콜센터 신규불가)\n다. 예금주가 만 3세 이하이고 대리인이 「(아이) 꿈하나 적금」을 신규 하는 시점에 「(태아) 사랑 하나 적금」을 보유하고 있거나 보유한 적이 있는 경우, 가입 후 1년간 연1.0% (단, 최초 1회에 한함)\n상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<이자지급방법 변경>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 만기일시지급식\n변경 후 : 만기일시지급식 : 만기(후)해지시 이자를 지급\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n<위법계약해지권 항목 신설>\n기존 가입고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (116, 2, '(K리그) 우승 적금', '{"상품특징":"축구를 응원하는 마음을 담아, 응원팀 우승 및 축덕카드 사용 실적으로 우대금리를 제공하는 하나원큐 전용 상품","가입대상":"만 14세 이상 실명의 개인 및 개인사업자 (1인 1계좌)","판매기간":"2025.02.13(목) ~ 2025.09.30(화) [시즌제 운영]","만기일":"2026.01.11(일) [만기일 고정]","가입기간":"가입자별 상이 [가입일 ~ 2026.01.11(일)]\n※ 만기일 고정 상품으로 상품가입일에 따라 가입자별 계약기간이 상이함","가입금액":"1만원 이상 ~ 50만원 이하 (원 단위)","적립한도":"매월 1만원 이상 ~ 50만원 이하 (원 단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":"금리안내\n※ 만기일 고정 상품으로 가입일에 따라 가입자별 가입기간이 상이","우대금리":"최대 연 5.00% (2025.02.13 기준, 세전)\n아래 우대항목을 충족하는 경우, 최대 연 5.00%의 우대금리를 만기해지시 제공합니다. (단, 중도해지시 미적용)\n우대항목 내용\n축덕카드 사용(주1)\n(연 1.00%) 이 예금 가입일로부터 가입연도말까지 하나 축덕카드(신용/체크)사용 실적을 10회 이상 보유한 경우\n성공한 덕후(응원팀 우승(주2))\n(연 1.00%) 이 예금 가입시 선택한 나의 응원팀이 가입연도 해당 시즌 최종 우승(주2)한 경우\n너DO?나DO! (친구초대)(주3)\n(최대 연 2.00%) 나의 초대코드로 친구가 같이 가입하여 My팀을 구성한 팀원 모두에게 우대금리 제공\n팀원수 우대금리\n11명 완성 연 2.00%\n6~10명 연 1.00%\n2~5명 연 0.50%\n하나원큐 축구Play 참여\n(연 1.00%) 이 예금 가입일로부터 가입연도말까지 「하나원큐 축구Play(주4)」콘텐츠 참여횟수에 따라 제공되는 총 11개 아이템을 모두 모은 경우\n(주1) 카드사용실적 : 카드 사용(승인) 시점 본인 명의 하나은행 입출금통장이 카드 결제계좌로 등록된 경우에 한함\n(주2) 응원팀 우승 : 가입시 선택한 응원팀은 변경 불가하며, 응원팀 소속리그 우승 포함\n(주3) 친구초대 : 동일한 초대코드를 이용해 가입한 모두에게 우대금리 적용(동일 초대코드는 해지 후 재사용 불가/팀원수별 우대금리는 중복적용 불가)\n(주4) 하나원큐 축구Play : 하나원큐 앱 내 축구 콘텐츠 제공 서비스로 콘텐츠 종류 및 아이템은 달라질수 있음(콘텐츠 예시 : 퀴즈HANA,응원HANA, 승리HANA, 아이템 예시 : 엠블럼 퍼즐조각)\n만기수취이자 예시(최고금리 기준)\n월 납입액 50만원/계약기간 6개월/ 연7.00% = 이자 61,250원(세전)\n※ 위 사항은 예시이며, 가입금액, 계약기간, 적용금리 등은 세부사항에 따라 변동 될 수 있습니다.\n※ 만기일 고정 상품으로 가입자별 계약기간 상이, 가입기간 6개월 기준 예시","일부해지":"만기일 이전 2회까지 가능하며, 일부해지 금액에 대해서는 가입일에 고시한 중도해지금리를 적용합니다.\n(일부해지 후 예금 잔액은 1만원 이상 유지)\n법적제한이 있는 경우 일부해지 불가합니다.","세제혜택":"비과세종합저축으로 가입 가능합니다. (전 금융기관 통합한도 범위 내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됩니다.","유의사항":"가입 시 선택한 응원팀에 따라 상품명이 변경됩니다.(예시 : 응원팀 대전 선택→대전 우승 적금)\n매년 시즌제 상품 운영을 위해 만기일이 고정된 적립식 상품입니다.\n우대금리 적용기준에 대한 유의사항을 자세히 읽어보시기 바랍니다.\n※ 금융상품에 관한 계약을 체결하기전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및\n이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다. (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가합니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.02.13 변경)\n<판매기간 변경>\n■ 기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n2024.06.11(화) ~ 2024.09.30(월) [시즌제 운영]\n변경 후\n2025.02.13(목) ~ 2025.09.30(화) [시즌제 운영]","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (117, 2, '급여하나 월복리 적금', '{"상품특징":"급여 하나로 OK! 월복리로 이자에 이자가 OK!","가입대상":"실명의 개인 또는 개인사업자 (1인 1계좌)\n(연금하나 월복리 적금 또는 주거래하나 월복리 적금과 중복 가입 불가)","가입기간":"1년, 2년, 3년","가입금액":"1만원 이상 300만원 이하 (원단위)\n(단, 청년응원 특별금리 등록시 1만원이상 150만원 이하)","적립한도":"1만원 이상 300만원 이하 (분기한도, 원단위)\n(단, 청년응원 특별금리 적용기간은 분기당 1만원이상 150만원 이하)","이자지급방법":"만기일시지급식(월복리식) : 만기(후) 해지시 이자를 지급\n(단, 중도해지금리 및 만기후금리는 단리로 계산)","적립방법":"자유적립식","금리":"기간: 1년, 금리(연율,세전): 2.75%\n기간: 2년, 금리(연율,세전): 2.8%\n기간: 3년, 금리(연율,세전): 2.85%","우대금리":"최대 연1.00% (2021.02.01 기준, 세전)\n아래 항목을 충족한 경우, 최대 연1.00%의 우대금리를 제공합니다. (단, 중도해지시 미제공)\n우대항목 내용\n급여하나 우대\n(연0.90%) 이 예금 만기 전전월말 기준, 계약기간의 1/2 이상 본인명의 하나은행 입출금통장을 통해\n급여 입금실적 (주) 보유한 경우 ( 월 1회 인정 )\n온라인 재예치 우대\n(연0.10%) 이 예금을 온라인채널(인터넷뱅킹/스마트폰뱅킹)을 통해 가입 또는 만기재예치하는 경우\n㈜급여입금 인정기준\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원\n이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등\n비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","우대금리(쿠폰)":"금리우대쿠폰을 발급받을 경우 기본금리에 가산하여 우대금리를 지급합니다. 금리우대쿠폰의 금리는 하나원큐 내 쿠폰함 또는 상품 가입시 제공되는 쿠폰의 우대금리를 따릅니다. 단, 신규 가입시에만 쿠폰 사용이 가능하며, 재예치 또는 중도해지시에는 적용되지 않습니다.","청년응원 특별금리":"연 1.3%\n※ 가입일 기준 만 35세 이하만 가능\n이 예금을 1년제로 가입하고, 아래 항목을 모두 충족하는 경우 만기해지시 제공\n만기 전전월말 기준 6개월 이상 본인명의 하나은행 입출금통장을 통해,\n급여 입금실적(주) 보유한 경우(월 1회 인정)\n하나카드 결제실적(신용/체크)을 월 30만원 이상 보유한 경우\n(하나머니* 충전방식 제외)\n*하나머니: 하나금융그룹의 통합 멤버십 포인트\n※ 고객별 1회에 한해 제공되는 특별금리로, 중도해지 및 재예치 기간은 미적용","만기자동재예치 선택시(총 3회)":"이 예금은 자동재예치를 선택한 경우, 만기일에 최초 계약기간 단위로 총 3회 자동재예치되며, 재예치 후 최소 가입금액 1만원을 제외한 나머지 세후 원리금은 사전에 지정한 본인의 입출금통장에 입금됩니다.\n재예치 계약기간을 다르게 희망하거나 재예치를 희망하지 않는 경우에는 영업점 창구를 통해 변경/해제 신청이 가능하며, 신청 후 도래하는 재예치거래시 적용됩니다.\n만기일이 휴일인 경우에는 익영업일에 재예치, 해당일이 없는 경우에는 그 달의 마지막 영업일에 재예치하며, 재예치일을\n새로운 계약일로 간주합니다.\n단, 압류 등 지급제한이 있는 경우에는 재예치 되지 않습니다.","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내) 단, 만기후이자는 소득세 부과","특별중도해지":"영업점 창구를 통해 아래 사유별 증빙서류를 제시하고 이 예금을 중도해지하는 경우, 이 예금 가입일(또는 재예치일)의 계약기간별 기본금리를 적용합니다.(우대금리는 미적용)\n※ 해지 사유 : 본인 퇴직, 창업, 결혼, 주택구입","일부해지":"예금 가입후(또는 재예치후) 2회까지 가능하며, 일부해지 금액에 대해서는 중도해지 금리를 적용합니다. (일부해지 후 예금잔액은 1만원이상)","유의사항":"만기일전에 중도해지 시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n이 예금은 만기일 전일까지 기간에 대하여 월복리로 이자를 계산하여 지급하며, 중도해지금리(일부해지 포함) 및 만기후금리는 단리로 계산하여 지급합니다.\n자동재예치 등록계좌는 만기일에 계좌별 최대 3회까지 자동재예치되므로, 재예치횟수 3회가 초과되는 경우에는 더 이상 재예치되지 않습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음(일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있음.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<급여인정기준>\n변경 전 :\n타인으로부터 건당 50만원 이상 급여를 의미하는 적요(급여, 월급, 연봉, 봉급, 상여, 성과, 보너스, SALARY, PAY, BONUS 등)를 포함하여 입금되거나 또는 사전 지정한 급여일 전후 1영업일 내에 입금되는 자금\n변경 후 :\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원\n이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등\n비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년","금리(연율,세전)":"2.75%"},{"기간":"2년","금리(연율,세전)":"2.8%"},{"기간":"3년","금리(연율,세전)":"2.85%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (118, 2, '내집마련 더블업(Double-Up)적금', '{"상품특징":"주택청약종합저축을 신규 가입한 날, 단 하루 가입할 수 있는 특별한 적금 !\n적금 만기시 주택청약종합저축을 보유하고 있다면 금리가 두~배 되는 적금!","가입대상":"이 예금 가입일에 하나은행의 주택청약종합저축을 신규 가입한 실명의 개인 또는 개인사업자 (1인 1계좌)\n※ 거래순서 : 주택청약종합저축 신규 -> 내집마련 더블업 적금 신규 (동일자 거래에 한 함)","가입기간":"1년, 2년","가입금액":"5만원 이상 20만원 이하 (원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"정액적립식\n매월 적립일에 5만원 이상 20만원 한도 (원 단위)","금리":null,"우대금리":"이 예금 만기 시점에 본인명의 당행 주택청약종합저축을 보유한 경우\n각 계약기간 기본금리에 해당하는 우대금리를 만기시 제공 (중도해지시에는 미제공)\n※ 기본금리 변동시 우대금리도 동일하게 변동됩니다.","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내), 만기후이자는 소득세 부과","특별중도해지":"본인명의 당행의 주택청약종합저축에 납입 목적으로 이 예금을 중도해지 시, 가입일의 계약기간별 기본금리를 적용합니다. (단, 영업점 창구를 통한 예금의 해지 및 주택청약 종합저축 납입거래에 한 함)","일부해지":"불가","유의사항":"만기일전에 중도해지 시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n우대금리는 만기해지시에 적용되며, 중도해지시에는 적용되지 않습니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2024.05.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 미적용\n<우대금리 내용변경>\n변경 전\n우대금리 1년제 연 2.50%, 2년제 연 1.75%(2023.04.29 기준, 세전)\n적금만기 시점에 본인명의 ㈜하나은행의 주택청약종합저축 보유 시, 가입일의 계약기간별 기본금리에 해당하는 만기축하우대금리를 제공합니다 (중도해지시에는 미적용)\n※기본금리 변동시 우대금리도 동일하게 변동됩니다.\n변경 후\n이 예금 만기 시점에 본인명의 당행 주택청약종합저축을 보유한 경우\n각 계약기간 기본금리에 해당하는 우대금리를 만기시 제공 (중도해지시에는 미제공)\n※ 기본금리 변동시 우대금리도 동일하게 변동됩니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.\n※ \n[주택청약종합저축] 이 금융상품은 예금자보호법에 따라 보호되지 않으나, 주택기금의 조성 재원으로 정부가\n관리하고 있습니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (119, 2, '달달 하나 적금', '{"상품특징":"하나원큐 전용 상품","가입대상":"만 14세 이상 실명의 개인 및 개인사업자 (1인 1계좌)","판매기간":"2025.07.23(수)~ 2025.12.31(수) [3만좌 한정]","가입기간":"1년","가입금액":"1만원 이상 ~ 30만원 이하 (원 단위)","적립한도":"매월 1만원 이상 ~ 30만원 이하 (원 단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":null,"우대금리":"최대 연 5.00% (2025.07.23기준, 세전)\n아래 우대항목을 충족하는 경우, 최대 연 5.00%의 우대금리를 만기해지시 제공합니다. (단, 중도해지시 미적용)\n우대항목 내용\n급여이체 우대\n(연 1.00%) 이 예금 가입 후 만기 전전월말 기준, 가입일로부터 본인명의 하나은행 입출금 통장을 통해, 6회 이상 급여(주1)입금 실적 보유한 경우 (월1회 인정)\n하나카드 결제\n(연 0.50%) 이 예금 가입 후 만기 전전월말 기준, 가입일로부터 본인명의 하나은행 입출금 통장을 통해, 6회 이상 하나카드 결제 실적을 보유한 경우 (월1회 인정, 하나머니(주2) 충전방식 제외)\n이벤트 특별금리\n(최고 연 3.50%) ① 첫거래 감사 (연 1.50%) : 이 예금 가입 일로부터 직전 1년간 적금 또는 예금을 미보유한 경우 (단, 주택청약종합저축 예외)\n② 달달 하나 금리(최고 연 2.00%) : 급여입금 실적 보유시,이 예금 가입익월부터 만기일 전월까지 매월 1회 이벤트 참여를 통해 달달하나금리를 획득한 경우(주3)\n(주1) ① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인 거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n(주2) 하나머니 : 하나금융그룹의 통합 멤버십 포인트\n(주3) 달달 하나 금리란, 하나원큐 內 『달달하나적금 혜택받기』에서 매월 1회 이벤트 참여를 통해 랜덤으로 제공하는 이벤트 특별금리로 만기시 합산하여 제공됩니다.\n이벤트 참여 대상 및 상세내용은 하나원큐 등을 통해 확인 가능합니다.","만기수취이자\n예시\n(최고금리기준)":"월 납입액 30만원/계약기간 12개월/ 연7.00%\n= 이자 136,500원(세전)\n※ 위 사항은 예시이며, 가입금액, 계약기간, 적용금리 등은 세부사항에 따라 변동 될 수 있습니다.","일부해지":"만기일 이전 2회까지 가능하며, 일부해지 금액에 대해서는 가입일에 고시한 중도해지금리를 적용합니다. (일부해지 후 예금 잔액은 1만원 이상 유지)\n법적제한이 있는 경우 일부해지 불가합니다.","세제혜택":"비과세종합저축으로 가입 가능합니다. (전 금융기관 통합한도 범위 내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됩니다.","유의사항":"이 예금은 양도 및 상속에 의한 명의변경이 불가합니다. 단, 상속에 의한 해지는 가능합니다.\n이벤트 특별금리 內 달달하나금리의 ‘급여입금 실적’ 인정기간은 전월 1일부터 말일로 합니다.\n달달 하나 금리는 이벤트 기간내 최초 가입시에만 제공되며, 해당 계좌 해지 후 재가입시 제공되지 않습니다.\n하나원큐 內 「달달 하나 적금」 이벤트 유의사항을 자세히 읽어보시기 바랍니다.\n※ 금융상품에 관한 계약을 체결하기전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다. (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가합니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (120, 2, '대전하나 축구사랑 적금', '{"상품특징":"대전하나시티즌 승리를 기원하는 마음으로 적금 만기이자를 대전하나시티즌에 후원하는 상품","가입대상":"만기이자(전부 또는 일부)를 대전하나시티즌 후원 계좌로 이체 동의한 실명의 개인 및 개인사업자 (1인 2계좌)","가입기간":"1년","가입금액":"1만원 이상 ~ 30만원 이하 (원 단위)","적립한도":"매월 1만원 이상 ~ 30만원 이하 (원 단위)","이자지급방법":"만기일시지급식 : 만기(후)해지 시 이자를 지급","적립방법":"자유적립식","만기해지\n및 후원방식":"이 예금은 만기해지 시 세후이자에 대해 아래의 후원방식 중 예금주가 선택한 방식으로 대전하나시티즌 후원 계좌에 이체하고 차액은 사전에 등록된 예금주 계좌에 입금합니다.\n이자 전액\n이자의 50%에 해당하는 금액\n이자의 10%에 해당하는 금액","금리":"기간: 1년, 금리(연율,세전): 1.8%","우대금리":"최대 연 2.50% (2024.03.08 기준, 세전)\n아래 우대항목을 충족하는 경우, 최대 연 2.50%의 우대금리를 만기해지 시 제공합니다. (단, 중도해지 시 미적용)\n우대항목 우대금리 내용\n하나카드 결제실적 연 1.50% 이 예금을 가입 후 만기 전전월 기준 6개월 이상 본인명의 하나은행 입출금통장을 통해 하나카드 결제실적(신용/체크)을 보유한 경우\n마케팅 동의 연 0.50% 이 예금을 가입 전 하나은행 상품, 서비스 마케팅 등의 항목 모두를 동의한 경우\n하나원큐 Push푸시알림 동의 연 0.50% 이 예금을 가입 전 하나원큐 마케팅 Push알림을 동의한 경우","일부해지":"만기일 이전 2회까지 분할해지 가능하며, 일부해지 금액에 대해서는 가입일에 고시한 중도해지 금리를 적용합니다. (일부해지 후 예금잔액은 1만원 이상 유지)\n법적 제한이 있는 경우 일부 해지 불가합니다.","세제혜택":"비과세종합저축 가입 가능합니다. (전 금융기관 통합한도 범위 내) 단, 만기 후 이자는 소득세 부과됩니다.\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됩니다.","유의사항":"적금 만기 시 이자의 전부 또는 일부를 대전하나시티즌 후원계좌(예금주 : 재단법인 하나금융축구단)로 자동 입금되는 상품으로 중도해지 시에는 후원되지 않습니다.\n후원 방법은 만기 전일까지 변경 가능합니다.\n※ 금융상품에 관한 계약을 체결하기전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한 (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2024.03.08 변경) ■ 기존 가입고객에 대한 적용 여부 : 적용 (단, 우대금리는 신규가입일 당시 영업점 및 하나은행 홈페이지에 게시한 우대금리 적용)\n<가입대상>\n만기이자(전부 또는 일부)를 대전하나시티즌 후원 계좌로 이체 동의한 실명의 개인 및 개인사업자 (1인 2계좌)\n<우대금리>\n아래 항목을 충족하는 경우, 최대 연 2.50% 의 우대금리를 만기해지시 제공합니다.\n(단, 중도해지시 미적용)\n우대항목 우대금리 내용\n하나카드\n결제실적 연 1.50% 이 예금을 가입 후 만기 전전월 기준 6개월 이상 본인명의 하나은행 입출금통장을 통해 하나카드 결제실적(신용/체크)을 보유한 경우\n마케팅 동의 연 0.50% 이 예금을 가입 전 하나은행 상품, 서비스 마케팅 등의 항목 모두를 동의한 경우\n하나원큐 Push(푸시) 알림 동의 연 0.50% 이 예금을 가입 전 하나원큐 마케팅 Push알림을 동의한 경우","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년","금리(연율,세전)":"1.8%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (121, 2, '도전365 적금', '{"상품특징":"하나머니 앱 연동 자신의 걸음수에 따라 우대금리 혜택을 받는 적금\n[걸음수 측정]\n안드로이드 : 삼성헬스 지원이 가능한 기기에서 사용가능\n아이폰 : iOS 8.0 이상의 기기에서 사용가능\n(단, 아이폰 5C 이하 기기는 사용불가)","가입대상":"만 14세 이상 실명의 개인 및 개인사업자 (1인 1계좌)","가입기간":"1년","가입금액":"1천원 이상 20만원 이하 (원단위)","적립한도":"월 1천원 이상 20만원 이하","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":null,"우대금리":"최대 연 2.50% (2023.08.21 기준, 세전)\n아래 우대항목을 모두 충족하는 경우, 최대 연 2.50%의 우대금리를 만기해지시 제공합니다. (단, 중도해지시 미적용)\n우대항목 내용\n건강서비스 우대\n(최대 연2.00%)\n하나머니 앱내 ‘내 걸음수 확인하기’ 버튼을 누른 하나머니 회원이 적금 신규일부터 11개월이 되는 일자까지, 아래 항목을 충족하는 경우\n\n건강서비스우대항목 상세 상세 우대금리\n걸음수 데이터 10만보 이상 ~ 200만보 미만 연 1.00%\n걸음수 데이터 200만보 이상 ~ 365만보 미만 연 1.50%\n걸음수 데이터 365만보 이상 연 2.00%\n연령 우대\n(연 0.40%) 이 예금의 가입자가 가입일 기준 만 65세 이상인 경우\n자동이체 우대\n(연 0.10%) 당행 요구불 통장에서 자동이체를 통해 이 적금 통장에 매월 10만원 이상으로 3회차 이상 납입한 경우","분할해지":"최종 해지분 포함 3회까지 분할해지 가능하며, 중도해지금리를 적용함\n(분할해지 후 잔액은 이 상품의 최소 가입금액 이상이어야 함)","세제혜택":"비과세종합저축 가능 (전 금융기관 통합한도 범위내)\n※ 만기후이자는 소득세 부과","유의사항":"건강서비스는 걸음수 데이터를 의미하며, 안드로이드(삼성헬스 앱), 아이폰(건강 앱)의 걸음데이터를 사용합니다.\n안드로이드 : 삼성헬스 지원이 가능한 기기에서 사용가능\n아이폰 : iOS 8.0 이상의 기기에서 사용가능\n(단, 아이폰 5C 이하 기기는 사용불가)\n적금 가입 후 하나머니 앱 > 내 걸음수 확인하기 버튼을 클릭하여 걸음수 기록을 하는 경우에 한하여 걸음수 데이터 누적 및 우대금리 혜택을 받으실 수 있습니다.\n건강서비스(걸음수)는 하나머니 앱을 통해서만 확인할 수 있습니다.\n(하나원큐 및 인터넷뱅킹 확인불가)\n걸음수는 하나머니 앱이 설치된 휴대폰에서 측정된 걸음수를 기준으로 하며, 휴대폰 외 기타 주변기기(스마트워치 등)를 통해 걸음수가 중복 측정될 경우 걸음수가 더 높은 기기의 걸음수가 반영됩니다.\n걸음수 측정 종료일(신규일부터 11개월 되는 일자) 이후, 만기일이전까지 반드시 본인의 누적 걸음수를 하나머니 앱 로그인 후 확인하셔야 걸음수 데이터에 대한 건강서비스 우대금리가 적용됩니다.\n본인 명의의 휴대폰을 보유한 손님에 한하여 건강서비스 우대에 따른 금리혜택을 제공합니다.\n하나머니 앱을 통하여 본인의 걸음수 데이터를 상시 확인하시기 바랍니다.\n가입자의 사정에 의해, 휴대폰을 교체하거나 데이터가 초기화가 되는 경우 걸음수 데이터가 복구되지 않으므로, 반드시 데이터 백업(저장) 후 사용하셔야 합니다.\n만기일 전에 중도해지시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n우대금리는 만기해지시에 적용되며, 중도해지(또는 분할해지)시에는 적용되지 않습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음(일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있음.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2023.08.21 변경)\n<우대금리 변경>\n■ 기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n① 건강서비스 우대 최대 연1.50%\n하나머니 앱내 ‘내 걸음수 확인하기’ 버튼을 누른 하나머니 회원이 신규일로부터 11개월 되는 일자까지 아래 우대항목을 충족하는 경우 해당 우대금리를 제공합니다.\n※ 우대금리 요건\n걸음수 데이터 200만보 이상~300만보 미만: 연 0.70%\n걸음수 데이터 300만보 이상~350만보 미만: 연 1.10%\n걸음수 데이터 350만보 이상: 연 1.50%\n② 고령자 우대(연0.10%)\n가입일 기준 만 65세이상인 경우\n③ 자동이체 등록우대(연0.10%)\n당행 요구불 통장에서 자동이체를 통해 이적금 통장에 매월 10만원 이상으로 3회차 이상 납입한 경우\n변경 후\n① 건강서비스 우대 최대 연2.00%\n하나머니 앱내 ‘내 걸음수 확인하기’ 버튼을 누른 하나머니 회원이 신규일로부터 11개월 되는 일자까지 아래 우대항목을 충족하는 경우 해당 우대금리를 제공합니다.\n※ 우대금리 요건\n걸음수 데이터 10만보 이상 ~ 200만보 미만: 연 1.00%\n걸음수 데이터 200만보 이상 ~ 365만보 미만: 연 1.50%\n걸음수 데이터 365만보 이상: 연 2.00%\n② 연령 우대(연0.40%)\n가입일 기준 만 65세이상인 경우\n③ 자동이체 우대(연0.10%)\n당행 요구불 통장에서 자동이체를 통해 이적금 통장에 매월 10만원 이상으로 3회차 이상 납입한 경우\n<유의사항 내용 추가>\n■ 기존 가입고객에 대한 적용 여부 : 적용\n변경 전\n적금 가입 후 하나머니 앱 > 내 걸음수 확인하기 버튼을 클릭하여 걸음수 기록을 하는 경우에 한하여 걸음수 데이터 누적 및 우대금리 혜택을 받으실 수 있습니다.\n건강서비스(걸음수)는 하나머니 앱을 통해서만 확인할 수 있습니다. (하나원큐 및 인터넷뱅킹 확인불가)\n걸음수 측정 종료일(신규일부터 11개월 되는 일자) 이후, 만기일이전까지 반드시 본인의 누적 걸음수를 하나머니 앱 로그인 후 확인하셔야 걸음수 데이터에 대한 건강서비스 우대금리가 적용됩니다.\n변경 후\n적금 가입 후 하나머니 앱 > 내 걸음수 확인하기 버튼을 클릭하여 걸음수 기록을 하는 경우에 한하여 걸음수 데이터 누적 및 우대금리 혜택을 받으실 수 있습니다.\n건강서비스(걸음수)는 하나머니 앱을 통해서만 확인할 수 있습니다. (하나원큐 및 인터넷뱅킹 확인불가)\n걸음수는 하나머니 앱이 설치된 휴대폰에서 측정된 걸음수를 기준으로 하며, 휴대폰 외 기타 주변기기(스마트워치 등)를 통해 걸음수가 중복 측정될 경우 걸음수가 더 높은 기기의 걸음수가 반영됩니다.\n걸음수 측정 종료일(신규일부터 11개월 되는 일자) 이후, 만기일이전까지 반드시 본인의 누적 걸음수를 하나머니 앱 로그인 후 확인하셔야 걸음수 데이터에 대한 건강서비스 우대금리가 적용됩니다.\n<위법계약해지권 문구 변경>\n■ 기존 가입고객에 대한 적용 여부 : 적용\n변경 전\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능\n변경 후\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (122, 2, '부자씨 적금', '{"상품특징":"하나원큐 로그인 횟수 & 하나 합 서비스 등록에 따라 우대금리 받고, 적금 모아서 예금으로~ 예금이 모여서 목돈을 제공하는 상품","가입대상":"실명의 개인 및 개인사업자 (1인 1계좌)","가입기간":"1년","가입금액":"1원 이상 ~ 50만원 이하 (원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":"기간: 1년, 금리(연율,세전): 2.3%","우대금리":"최대 연 2.00% (2023.03.13 기준, 세전)\n아래 우대항목을 충족하는 경우, 최대 연 2.00%의 우대금리를 만기해지시 제공합니다. (단, 중도해지시 미적용)\n우대항목 내용\n하나원큐 로그인\n(연 1.20%) 이 예금 가입일(재예치일)로 부터 만기전일까지 매월1회 이상 하나원큐 로그인을 한 경우, 로그인 해당 월마다 연 0.10% 적용 후 합산하여 제공\n※ 예시) 계약기간 중 하나원큐 로그인을 3월, 7월, 8월, 10월,11월,12월만 한 경우\n▶▶▶▶▶우대금리 연 0.60% 적용\n『하나 합 서비스』\n가입 및 기관 연결\n(연 0.60%) 이 예금 가입일(재예치일)기준 익월말까지 『하나 합 서비스』 가입 및 하나은행외 기관을 1개 이상 연결하고 만기전전월말에 유지한 경우\n마케팅동의\n(연 0.10%) 이 예금 가입(또는 재예치)전 하나은행 상품, 서비스 마케팅 동의 항목 모두를 동의한 경우\n하나원큐 Push 알림동의\n(연 0.10%) 이 예금 가입(또는 재예치)전 하나원큐 마케팅Push알림을 동의한 경우\n※ 신규가입시 금리우대쿠폰을 적용한 경우, 쿠폰의 우대금리를 추가로 제공합니다. (단, 중도해지시 미적용)","재예치":"만기전일까지 재예치를 신청할 수 있으며, 재예치방식은 (세후)원리금 방식입니다.\n기등록된 자동이체는 재예치 기간에도 유효하게 적용됩니다.\n재예치시에는 1년 단위로 만기일(단, 휴일인 경우 익영업일)에 재예치하며, 재예치된 (세후)원리금은 재예치일의 ‘하나의 정기예금’ 1년제 적용금리가, 재예치이후 적립금은 재예치일의 ‘부자씨 적금’의 기본금리와 우대금리가 적용됩니다.\n※ 아래의 사유 발생시 재예치가 불가합니다.\n비과세종합저축 한도를 초과 하거나 관련 세법 개정으로 인한 한도 변경시\n압류 등 지급제한이 있는 경우\n판매중단 등(단, 판매중단일이후 만기 도래한 계좌 에 한함)\n\n※ ‘하나의 정기예금’ 적용금리는 영업점 및 인터넷홈페이지, 하나원큐(스마트폰뱅킹)에 고시합니다.","세제혜택":"비과세종합저축으로 가능(전 금융기관 통합한도 범위 내) 단, 만기 후 이자는 소득세 부과됩니다.\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됩니다.","일부해지":"재예치된 (세후)원리금에 한해 만기일이전 2회까지 가능하며, 일부해지 금액에 대해서는 중도해지금리를 적용합니다.\n일부해지후 예금잔액은 계약기간내 적립금 이상을 유지하여야 합니다.\n법적제한이 있는 경우 일부해지 불가합니다.","유의사항":"재예치시에는 1년 단위로 만기일(단, 휴일인 경우 익영업일)에 재예치 하며, 재예치된 (세후)원리금은 재예치일의 ‘하나의 정기예금‘ 1년제 적용금리가, 재예치이후 적립금은 재예치일의 ‘부자씨 적금’의 기본금리와 우대금리 가 적용됩니다.\n(※‘하나의 정기예금_1년제’ 적용금리는 영업점 및 인터넷 홈페이지 하나원큐(스마트폰뱅킹)에 고시합니다.)\n일부해지는 재예치된 (세후)원리금에 한해 만기일 이전 2회까지 가능하며, 일부해지후 예금잔액은 계약기간내 적립금 이상을 유지하여야 합니다.\n※ 금융상품에 관한 계약을 체결하기전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다. (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가합니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년","금리(연율,세전)":"2.3%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (123, 2, '상호부금', '{"상품특징":"정액적립방식 또는 자유적립방식을 선택하여 적립하고 만기에 목돈을 마련하는 상품","가입대상":"제한없음","예금종류":"상호부금","가입기간":"6개월 이상 ~ 5년 이하(일단위)","가입금액":"1) 정액적립식 : 5천원이상(원단위)\n2) 자유적립식 : 1천원이상(원단위)","적립금액":"1) 정액적립식 : 5천원이상(원단위)\n2) 자유적립식 : 1회 1천원이상 ~ 1천만원 이하(원단위)\n단, 계약기간 2/3 경과 후 나머지 1/3 기간에는 전 기간 적립한 잔액의 1/2이내 입금 가능","이자지급방법":"만기일시지급식 : 만기(후) 해지시 이자를 지급","금리":"기간: 6개월 이상, 금리(연율,세전): 1.65%, 추가컬럼1: 1.65%, 추가컬럼2: 1.65%, 추가컬럼3: 1.65%\n기간: 1년 이상, 금리(연율,세전): 1.75%, 추가컬럼1: 1.75%, 추가컬럼2: 1.75%, 추가컬럼3: 1.75%\n기간: 2년 이상, 금리(연율,세전): 1.85%, 추가컬럼1: 1.85%, 추가컬럼2: 1.85%, 추가컬럼3: 1.85%\n기간: 3년 이상, 금리(연율,세전): 1.95%, 추가컬럼1: 1.95%, 추가컬럼2: 1.95%, 추가컬럼3: 1.95%\n기간: 4년 이상, 금리(연율,세전): 1.95%, 추가컬럼1: 1.95%, 추가컬럼2: 1.95%, 추가컬럼3: 1.95%\n기간: 5년, 금리(연율,세전): 2.05%, 추가컬럼1: 2.05%, 추가컬럼2: 2.05%, 추가컬럼3: 2.05%","세제혜택":"비과세종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 만료일 이후의 이자는 과세","원금 및 이자지급 제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한 (일부해지 포함)\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한  경우, 계약체결일로부터 5년이내 범위에서 위반사실을 안 날로부터 1년이내에 서면  등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","일부해지":"만기해지 포함 총3회 일부해지 가능(일부해지 후 잔액은 최소가입금액 이상이어야 함)\n일부해지 시 일부해지 금액에 대하여 중도해지금리 적용\n일부해지 후 월저축금이 변경됨 (일부해지 후 월저축금 : 최초 월저축금 – (분할해지금액/납입횟수)","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<상품특징>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 : 정액적립방식 또는 자유적립방식을 선택하여 적립하고 만기에 목돈을 마련하는 상품\n<가입대상>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 : 제한없음\n<가입기간>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 6개월 이상 ~ 60개월 이하(일단위)\n변경 후 : 6개월 이상 ~ 5년 이하(일단위)\n<최저가입금액>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 최저가입금액\n1) 정기적립식 5,000원 이상\n2) 자유적립식 1,000원 이상\n변경 후 : 가입금액\n1) 정액적립식 :5천원 이상(원단위)\n2) 자유적립식 : 1회 1천원 이상 ~ 1천만원 이하(원단위)\n단,계약기간 2/3 경과 후 나머지 1/3 기간에는 전 기간 적립한 잔액의 1/2이내 입금 가능\n<이자지급방법>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 만기일시지급식\n변경 후 : 만기일시지급식 : 만기(후)해지시 이자를 지급\n<일부해지>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n만기해지 포함 총3회 일부해지 가능(일부해지 후 잔액은 최소가입금액 이상이어야 함)\n일부해지 시 일부해지 금액에 대하여 중도해지금리 적용\n일부해지 후 월저축금이 변경됨 (일부해지 후 월저축금 : 최초 월저축금 – 분할해지금액/납입횟수)\n<세제혜택>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 개인의 경우 비과세종합저축 가능 9전 금융기관 통합한도 범위내), 만기 후 이자는 소득세 부과\n변경 후 :\n비과세종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 만료일 이후의 이자는 과세\n<유의사항>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 만기일 전에 중도해지시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n변경 후 : 삭제\n<원금 및 이자지급 제한>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한 (일부해지 포함)\n예금잔액증명서 발급 당일에는 잔액 변동 불가\n<위법계약해지권>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전 : 신설\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한  경우, 계약체결일로부터 5년이내 범위에서 위반사실을 안 날로부터 1년이내에 서면  등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.\n※ [주택청약종합저축] 이 금융상품은 예금자보호법에 따라 예금보험공사가 보호하지 않습니다.\n그러나, 주택도시기금의 조성재원으로 정부가 별도로 관리(지급보증) 하고 있습니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"6개월 이상","금리(연율,세전)":"1.65%","추가컬럼1":"1.65%","추가컬럼2":"1.65%","추가컬럼3":"1.65%"},{"기간":"1년 이상","금리(연율,세전)":"1.75%","추가컬럼1":"1.75%","추가컬럼2":"1.75%","추가컬럼3":"1.75%"},{"기간":"2년 이상","금리(연율,세전)":"1.85%","추가컬럼1":"1.85%","추가컬럼2":"1.85%","추가컬럼3":"1.85%"},{"기간":"3년 이상","금리(연율,세전)":"1.95%","추가컬럼1":"1.95%","추가컬럼2":"1.95%","추가컬럼3":"1.95%"},{"기간":"4년 이상","금리(연율,세전)":"1.95%","추가컬럼1":"1.95%","추가컬럼2":"1.95%","추가컬럼3":"1.95%"},{"기간":"5년","금리(연율,세전)":"2.05%","추가컬럼1":"2.05%","추가컬럼2":"2.05%","추가컬럼3":"2.05%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (124, 2, '손님케어 적금', '{"상품특징":"하나은행 고객센터를 통하여 가입하는 손님케어센터 전용 적금","가입대상":"실명의 개인 또는 개인사업자 (1인 1계좌)","가입기간":"1년","가입금액":"매월 1천원 이상 ~ 20만원 이하 (원단위)","이자지급방법":"만기일시지급식","금리":"기간: 1년, 금리(연율,세전): 3.1%","우대금리":"최대 연 1.40% (2022.05.27 기준, 세전)\n이 예금의 가입자가 아래 항목을 충족하는 경우 최대 연 1.40%의 우대금리를 만기에 제공합니다. (단, 중도해지(일부해지) 시 미제공)\n우대항목 내용 우대금리\n손님케어 신규 손님 이 예금을 고객센터를 통해 신규한 경우 연 0.20%\n마케팅동의 손님 이 예금 신규 전 또는 신규 후 익월말일까지 하나은행 상품·서비스 마케팅 동의를 한 경우 연 0.50%\n자동이체 손님 하나은행 입출금이 자유로운 예금에서 자동이체를 통하여 이 예금으로 불입된 월 납입금액 합계가 5만원 이상으로 6개월 이상 납입한 경우 연 0.50%\n소중한 손님 ① 정기예금 또는 적금 해지 손님 : 이 예금 신규시 당일 해지된 하나은행 원화 거치식 예금 또는 적립식 예금 보유 손님 연 0.20% 최대\n연 0.20%\n② 대출 이용 손님 : 이 예금 신규시 하나은행 원화 적금을 미보유한 대출 손님 연 0.20% (청약 제외)\n③ 적금 첫거래 손님 : 이 예금 신규시 하나은행 원화 거치식 예금과 적립식 예금을 미보유한 손님 연 0.20% (청약 제외)\n④ 생일축하 손님 : 실명번호 기준 생일 월에 이 예금을 신규한 손님 연 0.20%\n⑤ 『하나 합』 서비스㈜ 가입손님 : 이 예금 신규 후 익월말일기준, 『하나 합』 서비스를 등록한 손님 연 0.20%\n\n㈜ 『하나 합』 서비스 : 여러 기관에 흩어진 내 자산·지출 정보를 한눈에 조회하고 관리할 수 있는 마이데이터 기반 하나은행 자산 관리 서비스를 말하며, 만19세 이상부터 가입 가능합니다.\n※ 우대금리는 만기해지시에 적용하며, 중도(분할)해지시에는 적용하지 않습니다.","세제혜택":"비과세 종합저축으로 가입 가능(전금융기관 통합한도 범위 내) 단 만기 후 이자는 소득세 부과됨","예금담보대출":"예금잔액 범위 내에서 가능","일부해지":"만기일 이전 총 2회까지 가능하며, 일부해지 금액에 대해서는 중도해지금리가 적용됩니다.\n(단, 일부해지 후 예금잔액은 1천원 이상이어야 함)","유의사항":"이 적금 가입은 하나은행 고객센터에서만 가능합니다.\n마케팅 동의 우대금리는 마케팅 미동의 손님일 경우 가입 후 익월말까지 동의하셔야 우대금리 적용이 가능합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및\n이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음.(일부해지 포함)\n※민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있음.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.05.27 변경)\n■ 기존 가입고객에 대한 적용 여부 : 미적용\n<‘소중한 손님‘ 우대금리 항목의 우대요건추가>\n변경 전\n정기예금 또는 적금 해지 손님 연 0.20%\n대출이용손님 연 0.20%\n적금 첫거래 손님 연 0.20%\n생일축하 손님 연 0.20%\n변경 후\n정기예금 또는 적금 해지 손님 연 0.20%\n대출이용손님 연 0.20%\n적금 첫거래 손님 연 0.20%\n생일축하 손님 연 0.20%\n『하나 합』 서비스㈜ 가입손님 : 이 예금 신규 후 익월말일기준, 『하나 합』 서비스를 등록한 손님 연0.20%\n\n㈜ 『하나 합』 서비스 : 여러 기관에 흩어진 내 자산·지출 정보를 한눈에 조회하고 관리할 수 있는 마이데이터 기반 하나은행 자산 관리 서비스를 말하며, 만19세 이상부터 가입 가능합니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (125, 2, '하나 아이키움 적금', '{"상품특징":"아동 양육을 위한 수당(영아[부모급여],아동,양육수당) 수급자 및 임산부 대상 우대 금리를 제공하고, 다자녀 가구 대상 특별 우대금리를 제공하는 상품","판매기간":"2023.04.25(화) ~ 2025.12.31(수) [5만좌 한정]\n※ 한도 소진 시 판매중단","가입대상":"실명의 개인 및 개인사업자 (1인 1계좌)","가입기간":"1년","가입금액":"1만원 이상 ~ 30만원 이하 (원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":"기간: 1년, 금리(연율,세전): 2.0%","우대금리":"최대 연 4.00% (2025.06.30 기준, 세전)\n아래 우대항목을 충족하는 경우, 최대 연 4.00%의 우대금리를 만기해지시 제공합니다. (단, 중도해지시 미적용)\n우대항목 내용\n양육수당 수급\n또는\n임산부\n(연 2.00%) 양육수당 수급 이 예금 가입후 만기 전전월말 기준, 본인명의 하나은행 입출금통장으로 양육을 위한 수당 (정부 및 지자체가 제공하는 부모급여, 영아수당, 아동수당, 양육수당)을 6회 이상 입금 받은 경우\n(단, 아동명의로 수당 입금시(주1) 부모 중 1인에 한해 우대금리 제공)\n임산부 이 예금 가입후 만기 전영업일까지 임산부임을 확인할 수 있는 서류(주2)를 하나은행 영업점으로 제출한 경우\n아이 미래 지킴 서약\n(연 1.00%) 이 예금 가입월의 말일까지 하나원큐에서 “아이 미래 지킴”을 서약한 경우\n주택청약종합저축\n(연 0.60%) 당행 주택청약종합저축\n신규 또는 보유시 이 예금 가입일로부터 3개월이 되는 달의 말일기준 당행 ‘주택청약종합저축’을 신규 또는 보유하고 만기 시점까지 유지한 경우\n타행 주택청약종합저축\n보유시 이 예금 가입일까지 ‘하나원큐(스마트폰뱅킹)’를 통하여 오픈뱅킹서비스를 가입하고, 주택청약종합저축을 등록·조회한 경우(주3)\n하나 합 서비스\n(연 0.30%) 이 예금 가입일 기준 익월말까지 『하나 합 서비스』 가입 및 하나은행외 기관을 1개이상 연결하고 만기전전월말에 유지한 경우\n마케팅동의\n(연 0.10%) 이 예금 가입전 하나은행 상품,서비스 마케팅 동의 항목 모두를 동의한 경우\n(주1) 아동명의 하나은행 입출금계좌로 수령시, 수급계좌와 자녀임을 확인할 수 있는 서류를 만기 전영업일까지 하나은행 영업점에 제출하여 등록(부모 중 1인만 등록 가능, 서류 : 발급일로부터 3개월이내의 가족관계증명서, 주민등록등본 등)\n(주2) 임산부임을 확인할 수 있는 서류 : 산부인과 등 의료기관에서 발급받은 임신사실확인서\n(주3) 오픈뱅킹서비스 등록·조회 방법 : 하나원큐(스마트폰뱅킹)로그인 → 오픈뱅킹서비스 신규가입 → 기관추가 및 해당상품 등록 → 하나원큐 내 ‘전계좌조회_오픈뱅킹’ 을 통해 정상조회시 완료\n만기수취이자 예시(최고금리/정액적립식 기준)\n- 월 납입액 30만원/계약기간 12개월/연8.00% = 이자 156,000원(세전)\n※ 위 사항은 예시이며 납입액, 계약기간, 적용금리 등은 세부사항에 따라 변동될 수 있습니다.","다자녀 가구 특별우대금리":"최대 연 2.00% (2025.06.30 기준, 세전)\n우대금리 자녀수 내 용\n연 1.00% 2명 이 예금의 가입후 만기 전영업일까지 다자녀 【미성년(만 19세 미만) 자녀만 해당】 가구임이 확인 되는 서류(주4)를 하나은행 영업점으로 제출한 경우\n연 2.00% 3명 이상\n(주4) 미성년 자녀 확인 서류 : 다자녀 가구임을 확인할 수 있는 가족관계증명서, 주민등록등본 등","일부해지":"만기일 이전 2회까지 분할해지 가능하며, 일부해지 금액에 대해서는 가입일에 고시한 중도해지 금리를 적용합니다. (일부해지 후 예금잔액은 1만원 이상 유지)\n법적제한이 있는 경우 일부해지 불가합니다.","세제혜택":"비과세종합저축 가입 가능합니다. (전 금융기관 통합한도 범위 내) 단, 만기 후 이자는 소득세 부과됩니다.\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됩니다.","유의사항":"서류 확인을 통해 (특별)우대금리 적용 대상 여부 확인이 필요한 경우에는 가까운 하나은행 영업점을 방문하여 해당서류를 제출하여야 합니다. (아동 명의로 계좌로 수당 수급을 확인하는 경우, 임산부임을 확인하는 경우, 다자녀 가구임을 확인하는 경우)\n“하나 합 서비스”는 여러 기관에 흩어진 내 자산·지출 정보를 한눈에 조회하고 관리할 수 있는 마이데이터 기반 하나은행 자산관리 서비스입니다.\n※ 금융상품에 관한 계약을 체결하기전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다. (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가합니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.06.30 변경)\n<판매기간 변경>\n■ 기존 가입고객에 대한 적용 여부 : 적용\n변경 전\n2023.04.25(화) ~ 2025.06.30(월) [5만좌 한정]\n※ 한도 소진 시 판매중단\n변경 후\n2023.04.25(화) ~ 2025.12.31(수) [5만좌 한정]\n※ 한도 소진 시 판매중단","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년","금리(연율,세전)":"2.0%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (126, 2, '하나 중소기업재직자 우대저축', '{"상품특징":"중소벤처기업부 『중소기업재직자 우대저축공제 사업』 에 참여하는 재직자가 자산을 형성할 수 있도록 적립금 납입시 ‘기업지원금’을 지원하는 적립식 상품","가입대상":"중소기업재직자 우대저축공제 약관에 따라 중소벤처기업진흥공단에서 상품 가입을 승인한 실명의 개인(은행별 1인 1계좌)","가입기간":"5년","가입금액":"10만원 이상 ~ 50만원 이하 (만원 단위), 단, 중소벤처기업진흥공단에서 승인한 가입금액으로 함","적립한도":"10만원 이상 ~ 50만원 이하 (만원 단위), 단, 가입금액과 동일한 금액으로 월1회에 한해 적립 가능함","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":"기간: 36개월, 금리(연율,세전): 3.0%\n기간: 60개월, 금리(연율,세전): 2.5%","우대금리":"최대 연 2.00% (2024.10.24 기준, 세전)\n아래 우대항목을 충족하는 경우, 최대 연 2.00%의 우대금리를 만기해지시 제공합니다. (단, 중도해지 시 미적용)\n우대항목 내용\n급여 이체\n(연 1.40%) 이 예금 가입 후 만기 전전월말 기준, 계약기간의 ½이상 본인명의 하나은행 입출금 통장을 통해 급여 입금(주1) 실적을 보유한 경우 (월1회 인정)\n카드 결제\n(연 0.50%) 이 예금 가입 후 만기 전전월말 기준, 계약기간의 1/2이상 본인명의 하나은행 입출금통장을 통해 하나카드 결제실적을 보유한 경우 (*하나머니 충전방식 제외)\n마케팅 동의\n(연 0.10%) 이 예금 가입 전 하나은행 상품,서비스 마케팅 동의 항목 모두를 동의한 경우\n(주1) 급여 인정 기준\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n*하나머니 : 하나금융그룹의 통합 멤버십 포인트","만기수취이자 예시":"월 납입액 50만원/ 계약기간 60개월/ 연 5.00% = 이자 3,812,500원(세전) (최고금리/정액적립식 기준)\n※ 위 사항은 예시이며 납입액, 계약기간, 적용금리 등은 세부사항에 따라 변동될 수 있습니다.","수수료우대\n서비스":"수수료우대 항목 우대내용\n당행 자동화기기를 통한 현금인출 및 타행이체 수수료 무제한 면제\n인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행이체 수수료\n납부자자동이체(타행자동이체) 수수료\n타행 자동화기기 현금인출 수수료\n(CD 공동망 참여 금융회사로 타행 점내 설치된 자동화 기기) 월10회 면제\n창구 타행이체 수수료","환율우대\n서비스":"당발 송금 및 환전시 환율(spread)우대 우대내용\n영업점에서 USD 환전시(외화지폐) 90%우대\n영업점에서 JPY/EUR 환전시(외화지폐) 80%우대\n영업점 및 비대면채널(인터넷뱅킹,스마트폰뱅킹)에서 USD/JPY/EUR 당발 송금시 50%우대","일부해지":"불가","세제혜택":"비과세종합저축으로 가입 가능합니다. (전 금융기관 통합한도 범위 내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됩니다.","기업지원금":"중소기업 재직자의 장기재직 촉진 및 자산형성을 지원하기 위해, 중소기업이 『중소기업재직자 우대저축공제』에 적립하는 공제금으로 만기해지시 지급\n단, 중도해지시 사유에 따라 중소벤처기업진흥공단의 심사를 통해 기업지원금이 지급될 수 있음\n기업지원금은 이 예금 신규시 지정한 공제금 수령계좌로 입금\n※ 기업지원금의 지급에 관한 사항은 중소벤처기업진흥공단의 『중소기업재직자 우대저축 공제약관』 에서 정하는 바에 따름","유의사항":"상품가입전 중소벤처기업진흥공단을 통해 가입승인 여부를 확인합니다.\n이 적금 가입시 중소벤처기업진흥공단 및 재직기업이 가입자에게 지급하는 기업지원금의 지급조건, 지급액, 지급시기 및 방법 등에 동의한 것으로 봅니다.\n이 예금은 ‘하나은행’과 ‘중소벤처기업진흥공단’의 업무협약에 의해 운영되는 상품으로, 향후 업무협약 및 관련법 등의 변경에 따라 상품내용이 변경될 수 있습니다.\n※ 금융상품에 관한 계약을 체결하기전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및\n이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가합니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경) ■ 기존 가입고객에 대한 적용 여부 : 적용\n<급여인정조건>\n변경 전:\n(주1) 급여입금 인정 기준: 타인으로부터 건당 50만원 이상 입금시, 급여를 의미하는 적요 (급여,월급,연봉,봉급,상여,성과,보너스, salary, bonus 등)를 포함하여 입금되거나 또는 사전 지정한 급여일 전후 1영업일 내에 입금되는 자금\n변경 후:\n(주1) 급여 인정 기준\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"36개월","금리(연율,세전)":"3.0%"},{"기간":"60개월","금리(연율,세전)":"2.5%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (127, 2, '하나 타이밍 적금', '{"상품특징":"지금은 저축할 타이밍!\n게임하듯 재밌게 타이밍 버튼을 터치하여 저축시 우대금리가 제공 되는 상품","가입대상":"실명의 개인 또는 개인사업자 (1인 1계좌)","가입기간":"1개월이상 6개월이하 (일 또는 월단위 지정)","가입금액":"1천원 이상 50만원 이하(원단위)","납입한도":"월 최대 65만원(10원이상, 원단위)\n기본 한도 최대 50만원 + 타아밍 버튼 입금 한도 최대 15만원","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","금리":"기간: 1개월~6개월, 금리(연율,세전): 2.7%","우대금리":"최대 연 1.00% (2021.10.07 기준, 세전)\n예금 가입 후(또는 재예치 후) 매 계약기간별 아래 항목을 충족하는 경우, 최대 연1.00%의 우대금리를 추가로 제공합니다.\n단, 중도해지 시에는 적용하지 않습니다.\n우대항목 내용\n타이밍 버튼\n입금 우대\n(최대 연 0.80%) 이 예금 만기일 전일까지 타이밍 버튼을 통해 입금한 횟수에 따라 우대금리 제공\n- 5회~9회: 0.10%\n- 10회~14회: 0.20%\n- 15회~19회: 0.30%\n- 20회~24회: 0.40%\n- 25회~29회: 0.50%\n- 30회~34회: 0.60%\n- 35회~39회: 0.70%\n- 40회 이상: 0.80%\n※ 타이밍 버튼 입금은 1일 최대 30회까지만 가능하며 입금횟수는 만기전일까지 집계하여 우대금리 제공(타이밍 버튼 입금을 제외한 자동이체, 영업점/인터넷/스마트폰뱅킹 등으로 입금한 금액은 타이밍 버튼 입금 횟수에 포함되지 않음)\n친구추천 우대\n또는\n재예치 우대\n(연 0.10%)\n※ 중복적용불가 이 예금 가입시 부여되는 추천번호를 친구가 이 예금 상품 가입시 등록하면 본인 및 가입한 친구의 예금에 각각 제공. 단, 최초 가입 계약기간에 한함(재예치 제외)\n이 예금을 만기 재예치시 제공\n자동이체 우대\n(연 0.10%) 하나은행 입출금통장에서 1회 이상 자동이체로 납입한 경우 제공","우대금리(쿠폰)":"금리우대쿠폰을 발급받을 경우 기본금리에 가산하여 우대금리를 지급합니다. 금리우대쿠폰의 금리는 하나원큐 내 쿠폰함 또는 상품 가입시 제공되는 쿠폰의 우대금리를 따릅니다. 단, 신규 가입시에만 쿠폰사용이 가능하며, 재예치 또는 중도해지시에는 적용되지 않습니다.","타이밍 버튼 저축\n알림 PUSH":"타이밍 버튼 저축 알림 PUSH는 고객이 저축을 습관화 하고 타이밍 버튼을 통한 적립을 통해 우대금리를 받을 수 있도록 신청 고객에게 매일 1회 하나원큐 스마트폰 뱅킹 앱을 통해 저축할 시간 알림을 PUSH로 보내는 서비스입니다.\n타이밍 버튼 월 적립 한도 또는 일 적립 횟수가 이미 충족된 날에는 저축 알림 PUSH가 제공되지 않습니다.","만기자동재예치\n선택시(총 5회)":"이 예금 가입시 자동재예치를 선택한 경우, 만기일에 최초 계약기간단위로 최대 5회 자동재예치되며, 자동재예치시에는 최소 가입금액 1천원을 제외한 나머지 세후원리금은 사전에 지정한 본인명의 입출금통장으로 이체됩니다.\n만기일이 휴일인 경우에는 익영업일에 재예치하고, 해당일이 없는 경우에는 그 달의 마지막 영업일에 재예치하며, 재예치일을 새로운 계약일로 간주합니다.\n압류 등 지급제한이 있거나 만기일 전에 고객이 자동재예치를 해제하는 등 기타 재예치를 할 수 없는 경우에는 재예치 대상에서 제외됩니다.","세제혜택":"비과세종합저축 가능(전 금융기관 통합한도 범위 내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세","유의사항":"만기일 전에 중도해지 시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n우대금리는 만기 해지 시 제공하며, 일부 해지 및 중도해지 시에는 제공하지 않습니다.\n※금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및\n이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음 (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있음\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2023.04.07 변경) ■ 기존 가입고객에 대한 적용 여부 : 미적용\n가입기간\n변경 전\n가입기간: 6개월\n변경 후\n가입기간: 1개월이상 6개월이하\n(일 또는 월단위 지정)\n\n■ 기존 가입고객에 대한 적용 여부 : 미적용\n가입금액\n변경 전\n1천원 이상 20만원 이내(원단위)\n변경 후\n1천원 이상 50만원 이내(원단위)\n\n■ 기존 가입고객에 대한 적용 여부 : 미적용\n납입한도\n변경 전\n월 최대35만원(10원이상, 원단위)\n기본 한도 최대 20만원 + 타이밍 버튼 입금한도 최대 15만원\n변경 후\n월 최대65만원(10원이상, 원단위)\n기본 한도 최대 50만원 + 타이밍 버튼 입금한도 최대 15만원\n\n■ 기존 가입고객에 대한 적용 여부 : 미적용\n만기 후 금리\n변경 전\n1개월 이내: 지급당시 해당기간별 기본금리의 1/2\n1개월 초과: 지급당시 해당기간별 기본금리의 1/4\n변경 후\n1개월 이내: 지급당시 정기적금 6개월 기본금리의 1/2\n1개월 초과: 지급당시 정기적금 6개월 기본금리의 1/4","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1개월~6개월","금리(연율,세전)":"2.7%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (128, 2, '하나더소호 가맹점 적금', '{"상품특징":"카드 가맹점 대금 입금에 따라 우대금리를 제공하는 개인사업자 전용 적금","가입대상":"만 14세 이상 실명의 개인사업자 (1인 1계좌)","판매기간":"2025.03.04(화)~ 2025.12.31(수) [3만좌 한정]","가입기간":"1년","가입금액":"1만원 이상 ~ 30만원 이하 (원 단위)","적립한도":"매월 1만원 이상 ~ 30만원 이하 (원 단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":"금리안내\n※ 만기일 고정 상품으로 가입일에 따라 가입자별 가입기간이 상이","우대금리":"최대 연 6.00% (2025.03.04기준, 세전)\n아래 우대항목을 충족하는 경우, 최대 연 6.00%의 우대금리를 만기해지시 제공합니다. (단, 중도해지시 미적용)\n우대항목 내용\n가맹점 입금실적\n보유(연 2.0%) 만기 전전월말 기준, 가입월부터 본인명의 하나은행 입출금통장을 통해\n가맹점 매출대금(카드사) 입금실적을 6개월이상 보유한 경우 (가맹점 매출대금의 월 1회 이상 입금시 인정)\n가맹점 대금입금\n카드사 개수\n(최고 연 4.0%) 만기 전전월말 기준, 가입월부터 본인명의 하나은행 입출금통장을 통해 가맹점 매출대금(카드사) 입금실적 개수별로 우대금리 제공\n(다만, 동일 카드사의 복수 입금은 1개로 인정)\n연 1.0% 가맹점 매출대금 입금 카드사 3~6개\n연 4.0% 가맹점 매출대금 입금 카드사 7~9개\n* 카드가맹점 매출대금 입금 인정 기준: 9대 카드사(하나, 삼성, BC, 현대, 농협, KB, 신한, 롯데, 우리)에서 가맹점 대표자 명의 계좌 앞 직접 입금되는 매출대금","만기수취이자\n예시\n(최고금리기준)":"월 납입액 30만원/계약기간 12개월/ 연8.00%\n= 이자 156,000원(세전)\n※ 위 사항은 예시이며, 가입금액, 계약기간, 적용금리 등은 세부사항에 따라 변동 될 수 있습니다.","특별중도해지":"사업 관련 등의 사유로 자금이 필요하여 중도해지시, 신규가입시점의 기본금리를 적용합니다. (영업점만 가능, 증빙서류 제출필수)\n※ 대상 사유\n① 사업장 구입비, 임차보증금 지급용도 (부동산 매매/임대차 계약서)\n② 부가세/소득세 등 사업관련 세금납부 (세금청구서)\n※ 단, 부동산계약서 잔금납부일/세금납부기한이 해지요청일부터 적금 만기일 이내일 경우.","일부해지":"만기일 이전 2회까지 가능하며, 일부해지 금액에 대해서는 가입일에 고시한 중도해지금리를 적용합니다. (일부해지 후 예금 잔액은 1만원 이상 유지)\n법적제한이 있는 경우 일부해지 불가합니다.","세제혜택":"비과세종합저축으로 가입 가능합니다. (전 금융기관 통합한도 범위 내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됩니다.","유의사항":"이 예금은 신규계좌 수 3만좌 제한에 따라, 판매가 중단될 수 있습니다.\n이 예금은 양도 및 상속에 의한 명의변경이 불가합니다. 단, 상속에 의한 해지는 가능합니다.\n사장님 응원서비스로 인한 중도해지는 하나은행 영업점을 방문하여 해당서류를 제출하여야 합니다.\n※ 금융상품에 관한 계약을 체결하기전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다. (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가합니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\" (본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년","금리(연율,세전)":"2.0%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (129, 2, '함께하는 사랑 적금', '{"가입대상":"실명의 개인(개인사업자 포함) 또는 법인(1인 1계좌)","예금종류":"적립식 예금","가입기간":"12개월 ~ 36개월(월단위 만기 지정)","최저가입금액":"개인 - 1만원 이상 1백만원 이하, 1개월 적립한도 1백만원\n법인 - 1만원 이상 1천만원 이하, 1개월 적립한도 1천만원","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"정액적립식\n매월 적립일에 1회적립\n자유적립식\n1회 1만원 이상 자유적립","금리":"분류: -, 금리(연율,세전): 0.1%","우대금리":"최대 연 0.9% 우대 가능\n※ 우대이자율은 개인(개인사업자포함) 고객에게만 제공하며, 만기 해지 시에만 제공합니다.\n거래실적 우대금리\n가입 후 3개월 내 급여, 4대연금(국민/공무원/군인/사학), 가맹점대금(BC/하나)중 한가지 이상 하나은행 입출금계좌로 이체 시 연0.2%\n가입 후 3개월 내 아파트관리비 또는 하나카드사와 하나은행 제휴카드사의 신용(체크)카드를 합하여 월10만원 이상 하나은행 입출금계좌에서 결제 시 연0.2%\n하나은행 처음거래 또는 장기고객 우대\n하나은행 처음 거래 시 또는 10년이상 거래 시 연0.3%\n※ 처음거래 기준은 가입일 현재 하나은행 예금 잔액이 0원인 경우, 10년 이상 거래 기준은 고객 최초등록일로부터 10년 이상 경과\n사회적배려자 및 나눔실천 우대\n기초생활수급자, 근로장려금수급자, 소년소녀가장, 한부모가족지원보호대상자, 다문화가족, 탈북자 증빙서류 또는 (가입일 이후) 봉사활동확인증/기부금영수증/기부금자동이체신청서 중 1개 확인 시 연0.2%","세제혜택":"개인의 경우 비과세종합저축 가능(전 금융기관 통합한도 범위내), 만기 후 이자는 소득세 부과","예금담보대출":"예금잔액 범위내에서 가능","일부해지":"불가","부가서비스":"후원금 제공\n은행은 협약이 유효한 년도까지 매년 12월말 기준으로 2회이상 불입한 계좌 연 평균잔액의 0.1%를 은행이 자체 출연하여 지방자치단체(또는 지방자치단체가 지정한 단체)에 연1회 후원금 제공\n※후원금은 하나은행과 상호협력협약을 체결한 지방자치단체별로 ''5백좌 이상''인 경우에만 제공됩니다.","유의사항":"만기일 전에 중도해지 시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<이자지급방법 변경>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 만기일시지급식\n변경 후 : 만기일시지급식 : 만기(후)해지시 이자를 지급\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n<위법계약해지권 항목 신설>\n기존 가입고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (130, 2, '행복나눔 적금', '{"가입대상":"실명의 개인(개인사업자 포함) 또는 법인","예금종류":"상호부금","가입기간":"12개월 ~ 36개월(월단위 만기 지정)","최저가입금액":"1만원 이상 (원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"정액적립식\n매월 적립일에 1회 적립, 1만원이상 1천만원 이하\n자유적립식\n1회 최저납입금액 1만원, 1개월 납입한도 1백만원","금리":null,"우대금리":"최대 연 0.3% 우대 가능(개인에게만 제공)\nKEB하나은행 입출금통장에서 이 예금으로 월1만원 이상 자동이체 시 연0.1%\n봉사 활동 인증서 제시 또는 2인 이상 동시 가입 시 연 0.1%\n가입일 이후 만기일 이전에 공익/기부 단체 , 기관 등에 일정금액을 기부 시 연 0.1%\n(해당기관 발급 기부금 영수증 또는 KEB하나은행에서 입금한 경우는 무통장입금증 제시)","세제혜택":"개인의 경우 비과세종합저축 가능(전 금융기관 통합한도 범위내), 만기 후 이자는 소득세 부과","예금담보대출":"예금잔액 범위내에서 가능","일부해지":"불가","부가서비스":"지정계좌 이체서비스 : 적금의 만기일에 자동으로 해지되어 지정 입출금 통장으로 입금되며,입출금통장에서는 일정 금액을 고객이 지정한 계좌로 이체하여 드립니다.\n부기명 인자서비스 : 고객이 지정하는 단체명을 통장에 인자하여 드립니다.(단체의 사업자등록번호 필수)","기타":"후원금 지급\n은행은 이 예금의 가입자가 특정단체 또는 기관을 지정하여 등록하고, 2회 이상 적립한 경우 매년 12월말 기준으로 해당계좌들의 12개월간 연 평균잔액의 0.1%를 은행이 자체 출연하여 해당 단체 또는 기관에 연1회 후원금을 제공합니다.\n단, 단체 또는 기관별 등록된 계좌수가 (500좌) 미만인 경우에는 지급하지 않으며, 익년으로 이월하지 않습니다.","유의사항":"※ 만기일 전에 해지할 경우 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<이자지급방법 변경>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 만기일시지급식\n변경 후 : 만기일시지급식 : 만기(후)해지시 이자를 지급\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n<위법계약해지권 항목 신설>\n기존 가입고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (131, 2, '펫사랑 적금', '{"상품특징":"펫코노미 시대(반려인 1,500만명) 반려동물을 위한 목돈마련 저축상품","가입대상":"실명의 개인 또는 개인사업자 (1인 1계좌)","예금종류":"적립식예금(정액적립식)","가입기간":"1년","가입금액":"월 10만원 ~ 50만원 (원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","금리":"기간: 1년, 금리(연율,세전): 2.3%","우대금리":"최대 연0.5%(세전) 제공\n① 펫사랑 서약 우대 : 연 0.1%\n- 예금 가입시(또는 가입월 말일까지) 온라인채널(인터넷/스마트폰 뱅킹)에서 반려동물을 위한 「온라인 펫사랑 서약서」 를 작성한 경우\n② 하나카드 우대 : 연 0.2%\n- 예금 가입일로부터 만기 전전월말 기준 본인명의 하나은행 입출금통장을 통해 10만원 이상 하나카드(신용/체크) 결제 실적 보유한 경우\n③ 마케팅 동의 우대 : 연 0.2%\n- 가입 전 하나은행 상품·서비스 마케팅 동의 항목 모두 동의한 경우\n· 만기수취이자 예시(최고금리 기준)\n- 월 납입액 50만원/계약기간 12개월/연2.80% = 이자 91,000원(세전)\n※ 위 사항은 예시이며 납입액, 계약기간, 적용금리 등은 세부사항에 따라 변동될 수 있습니다.","세제혜택":"개인의 경우 비과세종합저축 가능(전 금융기관 통합한도 범위내), 만기 후 이자는 과세","예금담보대출":"가능","특별중도해지":"가능. 의료비 지출 증빙서류를 영업점에 제출하시면 가입시 기본금리가 적용됩니다.","일부해지":"만기일 이전 총 2회까지 가능하며, 일부해지 금액에 대해서는 중도해지 금리가 적용됩니다. (단, 일부해지 후 월적립액은 최소 10만원 이상이어야 함)","부가서비스":"연계.제휴서비스 내용 : 반려동물 배상책임보험서비스 무료가입\n① 보험가입대상\n이 예금 가입시 반려동물 배상책임보험서비스를 신청하고 개인(신용)정보의 제3자 제공에 동의한 고객으로 필수정보(동물종류(개/고양이), 품종, 반려동물 이름, 성별, 나이, 몸무게, 반려동물사진)를 제출한 고객\n② 보험서비스 내용\n담보 담보내용 보험가입금액\n(1사고당) 자기부담금\n(1사고당) 종류 비고\n반려동물\n배상책임\n(대인,\n대동물) 피보험자가 대한민국 내에서 보험기간 중에 발생된 보험증권에 기재된 반려동물의 행위에 기인하는 우연한 사고로 인하여 타인의 신체장해 및 타인 소유의 반려동물에 손해를 입혀 발생하는 법률상의 배상책임 5백만원 3만원 개\n(맹견 등\n제외)\n또는\n고양이 1인\n1마리\n한(限)\n가. 보험상품 : DB손해보험 주식회사의 프로미 반려동물배상책임보험Ⅱ\n나. 보장기간\n- 보험개시일은 적금 신규 다음날로 하며, 보험개시일로부터 1년까지 적용됩니다. 단, 보장기간은 적금 계약기간을 초과할 수 없으며, 적금을 중도해지 하는 경우 보험은 해당일 24:00에 해지됩니다.\n- 필수정보 중 ''반려동물 사진''은 DB손해보험 주식회사 앞으로 반드시 제출하셔야 보험가입이 완료됩니다.\n보험가입동물의 정확한 정보수집이 안 되었을 경우 보험사고 발생시 보험금 지급이 불가할 수 있습니다.\n필수정보 제출처 제출시기 제출방법(채널)\n동물종류(개/고양이),\n품종, 반려동물 이름,\n성별, 나이, 몸무게 하나은행 예금 상품 가입시 영업점, 하나원큐\n반려동물 사진 DB 손해보험 주식회사 예금상품 가입시\n(또는 가입월 말일까지) 카카오톡 플러스\n※ 예금상품 가입 시 ‘반려동물 배상책임보험서비스 선택여부(신청함, 신청안함) 및 필수정보＇를 정확히 기재하시기 바랍니다. (신규일 이후 선택/수정 불가)\n※ 정확한 보험가입을 위해 반려동물 사진 송부시 보험회사 요청정보(예금주명, 생년월일 등)를 제출하기시 바랍니다.\n다. 위 보험서비스 제공과 관련된 보장내용은 해당 보험약관에서 정하는 바에 따릅니다.\n- 보험약관은 ‘DB손해보험 주식회사 홈페이지(www.idbins.com)>공시실>상품공시실>상품목록 및 기초서류> 프로미 반려동물배상책임보험Ⅱ 검색’에서 확인하실 수 있습니다.\n라. 아래의 반려동물은 이 보험의 가입 대상이 아니며, 보상하지 아니합니다.\n- 판매점, 브리더 등이 매매(賣買)를 목적으로 사육·관리하는 반려동물\n- 경찰견, 구조견, 군견, 사냥개 등 특수한 목적의 개(犬)(단, 맹도견, 청도견 등 장애인 안내견은 제외)\n- 투견, 경주견 등 흥행을 목적으로 사육·관리하는 개(犬)\n- 유기동물 보호시설 등에서 사육·관리하는 반려동물\n마. 맹견 등의 경우 보험 가입 불가\n바. 맹견 등의 종류 : 아이리시 울프 하운드, 알래스칸 말라무트, 그레이트 피레니즈, 시베리안 허스키, 저먼 셰퍼드,\n세인트 버나드, 티베티안 마스티프, 도베르만, 도사견, 뉴 펀들랜드, 버니즈 마운틴 도그, 비어디드 콜리, 불 테리어, 불독, 프렌치 불독, 불 마스티프, 마스티 프, 미니어처 불테리어, 나폴리탄 마스티프, 믹스견(45kg 이상), 이 외 동물보호법 시행규칙 제1조의3(맹견의 범위)에 따라 목줄과 입마개를 하여야 하는 맹견\n사. 외국인인 경우, 유효한 외국인등록번호 소지자에 한합니다.\n③ 유의사항\n- 보험담보위험, 보험금 지급 등 보험가입서비스 제공과 관련된 사항은 해당 보험약관에서 정하는 바에 따르며, 보험관련 상담은 보험가입 다음달 21일 이후 DB손해보험 주식회사 고객상담센터(일반 및 보상문의 1899-6762)에서 문의 가능합니다.\n- 명의변경으로 인한 예금주 변경 시 보험가입서비스가 제공되지 않습니다.\n- 보험사에서 정하는 반려동물 배상책임보험의 목적물은 개와 고양이에 한합니다.\n연계.제휴서비스 이행책임 : 하나은행은 연계.제휴서비스 등을 부당하게 축소하거나 변경하지 않으며, 불가피하게 축소하거나 변경하는 경우 그에 상응하는 다른 연계.제휴서비스를 제공\n연계.제휴서비스 제공기간 : 보험개시일은 적금 신규 다음날로 하며, 보험개시일로부터 1년까지 적용.\n※ 단, 보장기간은 예금 계약기간을 초과할 수 없으며, 적금을 중도해지 하는 경우 보험서비스는 해당일 24:00에 제공 중단됩니다.\n연계.제휴서비스 변경내용 사전 통지방법 : 다음중 2개이상의 방법으로 6개월 전부터 매월 고지\n- 서면 교부, 우편 또는 전자우편, 전화 또는 팩스, 휴대전화 메시지 또는 이에 준하는 전자적 의사표시\n※단, 그에 상응하는 다른 연계.제휴서비스를 제공하거나 금융회사의 휴업.파산.경영상의 위기 등에 따른 불가피한 경우 제외\n연계.제휴서비스 제공요건 : 이 적금 가입시 반려동물 배상책임보험서비스를 신청하고 개인(신용)정보의 제3자 제공에 동의한 고객으로 필수정보 (동물종류(개/고양이), 품종, 반려동물 이름, 성별,나이, 몸무게, 반려동물사진) 를 제출한 고객","유의사항":"만기일 전에 중도해지시 약정금리보다 낮은 중도해지금리가 적용됩니다.\n일부해지 후 월 납입액은 최초 납입액과 달라지므로 유의하시기 바랍니다.\n→일부해지 후 월 납입액 : 최초 월 납입액 – (일부해지금액/납입횟수)\n적금의 중도해지시 반려동물 배상책임보험서비스 무료가입 보장기간도 종료됩니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음 (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있음\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2023.11.30 변경)\n<부가서비스 변경>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경전\n가. 보험상품 : 프로미 반려동물배상책임보험\n변경후\n가. 보험상품 : 프로미 반려동물배상책임보험 Ⅱ","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년","금리(연율,세전)":"2.3%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (132, 2, '평생 군인 적금', '{"상품특징":"군 복무중인 직업군인 전용 적금","가입대상":"군 복무 중인 직업군인*으로 실명의 개인 (1인 1계좌)\n*장교, 준사관, 부사관 및 육·해·공군 사관생도, 간호사관생도, 학군장교후보생(ROTC),\n학사장교후보생, 육군 3사관생도, 기타 장교 후보생, 준사관·부사관후보생, 군무원 (※현역병 등 의무복무병 제외)","가입기간":"1년","가입금액":"매월 10원 이상 ~ 30만원 이하","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":"기간: 1년, 금리(연율,세전): 1.25%","우대금리":"최대 연 4.0% (2019.10.11 기준, 세전)\n예금 가입 후 아래 항목을 충족하는 경우, 최대 연4.0%의 우대금리를 만기에 제공\n우대항목 내용\n군급여 우대 (연2.0%) 이 예금 만기 전전월말 기준, 6개월 이상 본인명의 ㈜하나은행 입출금계좌로\n국군재정관리단을 통한 급여이체를 충족한 경우\n카드 우대 (연0.5%) 이 예금 만기 전전월말 기준, 6개월 이상 본인명의 ㈜하나은행 계좌에서 하나카드(신용,체크) 결제대금을 월 30만원이상 보유한 경우\n청약 우대 (연1.0%) 이 예금 가입일로부터 다음달 말일까지 본인명의 주택청약종합저축을 ㈜하나은행에서 신규로 가입한 경우\n자동이체 우대 (연0.3%) ㈜하나은행 계좌에서 자동이체로 이 예금에 6개월 이상 납입한 경우\n마케팅동의 우대 (연0.2%) 이 예금 가입전 ㈜하나은행 상품·서비스 마케팅동의 항목 모두를 동의한 경우","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내), 단, 만기후이자는 소득세 부과","일부해지":"만기일 이전 2회까지 가능하며, 일부해지 금액에 대해서는 중도해지금리를 적용함\n(일부해지 후 잔액 1천원 이상)","유의사항":"만기일 전에 중도해지 시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n신규시점에 가입자격확인서류를 제출하여야 하며, 영업점에서만 가입이 가능합니다.\n우대금리는 만기해지시 제공하며, 중도해지시에는 제공하지 않습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<이자지급방법 변경>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 만기일시지급식\n변경 후 : 만기일시지급식 : 만기(후)해지시 이자를 지급\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n<위법계약해지권 항목 신설>\n기존 가입고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년","금리(연율,세전)":"1.25%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (133, 2, '하나 미소드림 적금', '{"상품특징":"미소금융대출 및 채무조정 성실상환자 대상으로 해지 시 서민금융진흥원에서 가입자에게 별도지원금을 지급하는 적금","가입대상":"서민금융진흥원 자산형성상품 참여추천을 받은 실명의 개인(1인1계좌)","예금종류":"정기적금","가입기간":"1년 이상 5년 이하 (연단위)","가입금액":"매월 1만원 이상 20만원 이하 (원단위)","이자지급방법":"만기일시지급식","적립방법":"자유적립식","금리":"기간: 1년, 금리(연율,세전): 3.6%\n기간: 2년, 금리(연율,세전): 3.8%\n기간: 3년 이상, 금리(연율,세전): 4.0%","우대금리":"연 1.0%\n이 예금 만기해지시 만기해지 축하 금리 제공","세제혜택":"비과세종합저축 가능 (전 금융기관 통합한도 범위 내) 단, 만기 후 이자는 소득세 부과","예금담보대출":"예금잔액 범위내에서 가능","일부해지":"전액 중도해지만 가능","양수도":"양도불가 (단, 상속은 가능)","유의사항":"만기일 전에 해지할 경우 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n서민금융진흥원 이자 지원을 받기 위해서는 가입 후 해당 서민금융진흥원을 방문하여 약정하셔야 하며 약정시 이 예금 가입통장 사본을 지참하여야 합니다.\n서민금융진흥원에서 지원하는 이자는 적금 해지 후 예금이자계산서를 지참하여 서민금융진흥원에 방문하여야 수령 가능합니다.\n(서민금융통합콜센터 : 1397)\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및\n이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음 (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2023.03.31 변경)\n<가입금액 및 적립한도>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전 :\n1만원 이상 10만원 이하\n변경 후 :\n1만원 이상 20만원 이하\n<우대금리>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전 :\n해당사항 없음 (신설)\n변경 후 :\n만기해지시 연 1.0% 우대금리 제공","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년","금리(연율,세전)":"3.6%"},{"기간":"2년","금리(연율,세전)":"3.8%"},{"기간":"3년 이상","금리(연율,세전)":"4.0%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (134, 2, '연금하나 월복리 적금', '{"상품특징":"연금 하나로 OK! 월복리로 이자에 이자가 OK!","가입대상":"실명의 개인 또는 개인사업자 (1인 1계좌)\n(급여하나 월복리 적금 또는 주거래하나 월복리 적금과 중복 가입 불가)","가입기간":"1년, 2년, 3년","가입금액":"1만원 이상 300만원 이하 (원단위)","적립한도":"1만원 이상 300만원 이하 (분기한도, 원단위)","이자지급방법":"만기일시지급식(월복리식) : 만기(후) 해지시 이자를 지급\n(단, 중도해지금리 및 만기후금리는 단리로 계산)","적립방법":"자유적립식","금리":"기간: 1년, 금리(연율,세전): 2.95%\n기간: 2년, 금리(연율,세전): 3.0%\n기간: 3년, 금리(연율,세전): 3.1%","우대금리":"최대 연1.00% (2021.02.01 기준, 세전)\n아래 항목을 충족한 경우, 최대 연1.00%의 우대금리를 제공합니다. (단, 중도해지시 미제공)\n우대항목 내용\n연금하나 우대\n(연0.90%) 이 예금 만기 전전월말 기준, 계약기간의 1/2 이상 본인명의 하나은행 입출금통장을 통해\n연금 입금실적 (주) 보유한 경우 (월 1회 이체만 인정)\n온라인 재예치 우대\n(최대 연0.10%) 이 예금을 온라인채널(인터넷뱅킹/스마트폰뱅킹)을 통해 가입 또는 만기재예치하는 경우\n㈜연금실적 인정기준 : 4대 공적연금(국민, 공무원, 군인, 사학), 기초(노령)연금, 보훈연금, 산업재해보상보험급여, 하나은행에서 가입한 개인연금(주택연금, 연금신탁, 연금펀드), 기타연금(사전 등록한 연금 수령일 전후 1영업일내에 대량이체 방식으로 건당 20만원 이상 입금되고 적요에 ‘연금’ 또는 보험사명이 기재된 자금)","우대금리(쿠폰)":"금리우대쿠폰을 발급받을 경우 기본금리에 가산하여 우대금리를 지급합니다. 금리우대쿠폰의 우대금리는 하나원큐 내 쿠폰함 또는 상품 가입시 제공되는 쿠폰의 우대금리를 따릅니다. 단, 신규 가입시에만 쿠폰 사용이 가능하며, 재예치 또는 중도해지시에는 적용되지 않습니다.","만기자동재예치 선택시(총 3회)":"이 예금은 자동재예치를 선택한 경우, 만기일에 최초 계약기간 단위로 총 3회 자동재예치되며, 재예치 후 최소가입금액 1만원을 제외한 나머지 세후 원리금은 사전에 지정한 본인의 입출금통장에 입금됩니다.\n재예치 계약기간을 다르게 희망하거나 재예치를 희망하지 않는 경우에는 영업점 창구를 통해 변경/해제 신청이 가능하며, 신청 후 도래하는 재예치거래시 적용됩니다.\n만기일이 휴일인 경우에는 익영업일에 재예치, 해당일이 없는 경우에는 그 달의 마지막 영업일에 재예치하며, 재예치일을\n새로운 계약일로 간주합니다.\n단, 압류 등 지급제한이 있는 경우에는 재예치 되지 않습니다.","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내)\n단, 만기후이자는 소득세 부과","특별중도해지":"영업점 창구를 통해 아래 사유별 증빙서류를 제시하고 이 예금을 중도해지하는 경우, 이 예금 가입일(또는 재예치일)의 계약기간별 기본금리를 적용합니다.(우대금리는 미적용)\n※ 해지 사유 : 병원비(본인,배우자), 자녀 결혼, 자녀 주택구입","일부해지":"예금 가입후(또는 재예치후) 2회까지 가능하며, 일부해지 금액에 대해서는 중도해지 금리를 적용합니다.\n(일부해지 후 예금잔액은 1만원 이상)","유의사항":"만기일전에 중도해지 시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n이 예금은 만기일 전일까지 기간에 대하여 월복리로 이자를 계산하여 지급하며, 중도해지금리(일부해지 포함) 및 만기후금리는 단리로 계산하여 지급합니다.\n자동재예치 등록계좌는 만기일에 계좌별 최대 3회까지 자동재예치되므로, 재예치횟수 3회가 초과되는 경우에는 더 이상 재예치되지 않습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.09.16 변경)\n■ 기존 가입고객에 대한 적용 여부 : 미적용\n<연금실적 인정기준 변경>\n변경 전\n㈜연금실적 인정기준 : 4대 공적연금(국민,공무원,군인,사학), 기초(노령)연금, 보훈연금, 산업재해 보상보험 보험급여, 하나은행에서 가입한 개인연금(주택연금,연금신탁,연금보험,연금펀드), 기타연금(사전 지정한 연급수급 지정일 전후 1영업일에 건당 20만원이상 대량이체방식으로 입금되고 ‘연금’ ‘보험사명’ 등의 적요 포함 자금)\n변경 후\n㈜연금실적 인정기준 : 4대 공적연금(국민, 공무원, 군인, 사학), 기초(노령)연금, 보훈연금, 산업재해보상보험급여, 하나은행에서 가입한 개인연금(주택연금, 연금신탁, 연금펀드), 기타연금(사전 등록한 연금 수령일 전후 1영업일내에 대량이체 방식으로 건당 20만원 이상 입금되고 적요에 ‘연금’ 또는 보험사명이 기재된 자금)\n<우대금리(쿠폰)항목 신설>\n변경 전\n해당사항 없음(신설)\n변경 후\n금리우대쿠폰을 발급받을 경우 기본금리에 가산하여 우대금리를 지급합니다. 금리우대쿠폰의 금리는 하나원큐 내 쿠폰함 또는 상품 가입시 제공되는 쿠폰의 우대금리를 따릅니다. 단, 신규 가입시에만 쿠폰 사용이 가능하며, 재예치 또는 중도해지시에는 적용되지 않습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년","금리(연율,세전)":"2.95%"},{"기간":"2년","금리(연율,세전)":"3.0%"},{"기간":"3년","금리(연율,세전)":"3.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (135, 2, '주거래하나 월복리 적금', '{"상품특징":"공과금, 카드대금 이체 등 각종 주거래 건수에 따라 우대금리가 제공되는 월복리 적금!","가입대상":"실명의 개인 또는 개인사업자 (1인 1계좌)\n(급여하나 월복리 적금 또는 연금하나 월복리 적금과 중복 가입 불가)","가입기간":"1년, 2년, 3년","가입금액":"1만원 이상 300만원 이하 (원단위)","적립한도":"1만원 이상 300만원 이하 (분기한도, 원단위)","이자지급방법":"만기일시지급식(월복리식) : 만기(후) 해지시 이자를 지급\n(단, 중도해지금리 및 만기후금리는 단리로 계산)","적립방법":"자유적립식","금리":"기간: 1년제, 금리(연율,세전): 2.75%\n기간: 2년제, 금리(연율,세전): 2.8%\n기간: 3년제, 금리(연율,세전): 2.85%","우대금리":"최대 연1.00% (2021.02.01 기준, 세전)\n아래 항목을 충족한 경우, 최대 연1.00%의 우대금리를 제공합니다. (단, 중도해지시 미제공)\n우대항목 내용\n주거래하나 우대\n(연0.50%) 이 예금 만기 전전월말 기준, 본인명의 하나은행 입출금통장을 통해 예금 계약기간의 1/2이상 이체된 주거래실적 (주) 1종 보유한 경우\n주거래플러스 우대\n(연0.90%) 이 예금 만기 전전월말 기준, 본인명의 하나은행 입출금통장을 통해 예금 계약기간의 1/2이상 이체된 주거래실적 (주) 2종 이상 보유한 경우\n온라인 재예치 우대\n(최대 연0.10%) 이 적금을 온라인 채널(인터넷뱅킹/스마트폰뱅킹)을 통해 가입 또는 만기재예치 하는 경우\n(주) 주거래실적 인정종목 : 급여, 연금, 하나카드/현대카드 결제(하나머니* 충전방식 제외), 아파트관리비 출금, 공과금 출금, 하나카드/BC카드 가맹점대금 입금, 본인명의 당행 주택청약저축 납입 (월 1회 인정)\n*하나머니: 하나금융그룹의 통합 멤버십 포인트\n※ 유의사항 : 주거래하나 우대와 주거래플러스 우대적용시, 주거래실적 종목별 각각 계약기간의 1/2이상 이체유무를 판단하여 제공합니다.\n※ 급여입금 인정기준 ①\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n※ 연금입금 인정기준 : 4대 공적연금(국민, 공무원, 군인, 사학), 기초(노령)연금, 보훈연금, 산업재해보상보험 보험급여, 하나은행에서 가입한 개인연금(주택연금, 연금신탁, 연금보험, 연금펀드), 기타연금(사전지정한 연금수급일 전후 1영업일에 건당 20만원이상 대량이체방식으로 입금되고 ''연금''''보험사명'' 등의 적요 포함 자금)","우대금리(쿠폰)":"금리우대쿠폰을 발급받을 경우 기본금리에 가산하여 우대금리를 지급합니다. 금리우대쿠폰의 금리는 하나원큐 내 쿠폰함 또는 상품 가입시 제공되는 쿠폰의 우대금리를 따릅니다. 단, 신규 가입시에만 쿠폰 사용이 가능하며, 재예치 또는 중도해지시에는 적용되지 않습니다.","만기자동재예치 선택시(총 3회)":"이 예금은 자동재예치를 선택한 경우, 만기일에 최초 계약기간 단위로 총 3회 자동재예치되며, 재예치 후 최소가입금액 1만원을 제외한 나머지 세후 원리금은 사전에 지정한 본인의 입출금통장에 입금됩니다.\n재예치 계약기간을 다르게 희망하거나 재예치를 희망하지 않는 경우에는 영업점 창구를 통해 변경/해제 신청이 가능하며, 신청 후 도래하는 재예치거래시 적용됩니다.\n만기일이 휴일인 경우에는 익영업일에 재예치, 해당일이 없는 경우에는 그 달의 마지막 영업일에 재예치하며, 재예치일을\n새로운 계약일로 간주합니다.\n단, 압류 등 지급제한이 있는 경우에는 재예치 되지 않습니다.","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내)\n단, 만기후이자는 소득세 부과","특별중도해지":"영업점 창구를 통해 아래 사유별 증빙서류를 제시하고 이 예금을 중도해지하는 경우, 이 예금 가입일(또는 재예치일)의 계약기간별 기본금리를 적용합니다.(우대금리는 미적용)\n※ 해지 사유 : 본인 결혼, 출산, 주택구입, 자녀학자금","일부해지":"예금 가입후(또는 재예치후) 2회까지 가능하며, 일부해지 금액에 대해서는 중도해지 금리를 적용합니다.\n(일부해지 후 예금잔액은 1만원 이상)","유의사항":"만기일전에 중도해지 시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n이 예금은 만기일 전일까지 기간에 대하여 월복리로 이자를 계산하여 지급하며, 중도해지금리(일부해지 포함) 및 만기후금리는 단리로 계산하여 지급합니다.\n자동재예치 등록계좌는 만기일에 계좌별 최대 3회까지 자동재예치되므로, 재예치횟수 3회가 초과되는 경우에는 더 이상 재예치되지 않습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음(일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있음.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<급여인정기준>\n변경 전\n타인으로부터 건당 50만원 이상 급여를 의미하는 적요(급여, 월급, 연봉, 봉급, 상여, 성과, 보너스, SALARY, PAY, BONUS 등)를 포함하여 입금되거나 또는 사전 지정한 급여일 전후 1영업일 내에 입금되는 경우\n변경 후\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1년제","금리(연율,세전)":"2.75%"},{"기간":"2년제","금리(연율,세전)":"2.8%"},{"기간":"3년제","금리(연율,세전)":"2.85%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (136, 2, '자유적금', '{"상품특징":"계약기간 동안 수시로 적립할 수 있는 자유적립식 상품","가입대상":"제한없음","예금종류":"적립식 예금 (자유적립식)","가입기간":"6개월 이상 60개월 이내(월단위)","최저가입금액":"1천원 이상 1억원 이하 (원단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식\n가입자 1인당 매월 1억원 한도 내에서 원단위로 자유적립\n(단, 계약기간 2/3 경과 후 나머지 1/3기간에는 경과 전 기 적립한 잔액의 1/2을 초과하여 입금할 수 없음)","금리":"기간: 6개월 이상, 금리(연율,세전): 1.9%\n기간: 1년 이상, 금리(연율,세전): 2.0%\n기간: 2년 이상, 금리(연율,세전): 2.1%\n기간: 3년 이상, 금리(연율,세전): 2.2%\n기간: 5년 이상, 금리(연율,세전): 2.3%","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내), 만기후이자는 소득세 부과","일부해지":"만기일 이전 2회 가능 (중도해지금리 적용 및 최저 1천원 이상 유지)","유의사항":"만기일 전에 중도해지 시 약정한 금리보다 낮은 중도해지금리가 적용됩니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<이자지급방법 변경>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 만기일시지급식\n변경 후 : 만기일시지급식 : 만기(후)해지시 이자를 지급\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n<위법계약해지권 항목 신설>\n기존 가입고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"6개월 이상","금리(연율,세전)":"1.9%"},{"기간":"1년 이상","금리(연율,세전)":"2.0%"},{"기간":"2년 이상","금리(연율,세전)":"2.1%"},{"기간":"3년 이상","금리(연율,세전)":"2.2%"},{"기간":"5년 이상","금리(연율,세전)":"2.3%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (137, 2, '트래블로그 여행 적금', '{"상품특징":"트래블로그 카드 사용 실적으로 우대금리를 제공하는 하나원큐(비대면)전용 상품","가입대상":"만 14세 이상 실명의 개인 및 개인사업자 (1인 1계좌)","가입기간":"3개월 이상 ~ 12개월 이하 (일 또는 월단위 지정)","가입금액":"1만원 이상 ~ 50만원 이하 (원 단위)","적립한도":"1만원 이상 ~ 50만원 이하 (원 단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":"기간: 3개월이상~6개월미만, 금리(연율,세전): 1.8%\n기간: 6개월이상~12개월이하, 금리(연율,세전): 2.4%","우대금리":"최대 연 2.00% (2023.11.01 기준, 세전)\n아래 우대항목을 충족하는 경우, 최대 연 2.00%의 우대금리를 만기해지시 제공합니다. (단, 중도해지시 미적용)\n우대항목 내용\n트래블로그 카드\n사용실적\n(최대 연 1.80%) 이 예금 가입일로부터 만기 전전일 기준, 하나은행 입출금통장으로 결제(충전)계좌를 등록하고, 트래블로그(신용 및 체크) 카드 사용(승인)실적(주1)을 10회 이상 보유한 경우\n※ (주1)트래블로그 카드 승인시점에 결제(충전)계좌가 하나은행으로 등록된 건만 인정\n【예시】 하나은행 결제계좌 등록(변경)전, 미용실에서 사용(승인)한 경우 미인정\n마케팅동의\n(연 0.10%) 이 예금 가입전 하나은행 상품,서비스 마케팅 동의 항목 모두를 동의한 경우\n하나원큐 Push 알림동의\n(연 0.10%) 이 예금 가입 전 하나원큐 마케팅Push알림을 동의한 경우\n만기수취이자 예시(최고금리 기준)\n- 월 납입액 50만원/계약기간 12개월/연5.0% = 이자 162,500원(세전)\n※ 위 사항은 예시이며 납입액, 계약기간, 적용금리 등은 세부사항에 따라 변동될 수 있습니다.","일부해지":"만기일 이전 2회까지 가능하며, 일부해지 금액에 대해서는 가입일에 고시한 중도해지금리를 적용합니다. (일부해지 후 예금 잔액은 1만원 이상 유지)\n법적제한이 있는 경우 일부해지 불가합니다.","세제혜택":"비과세종합저축으로 가입 가능합니다. (전 금융기관 통합한도 범위 내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됩니다.","유의사항":"이 예금의 우대항목중 『트래블로그 카드 사용실적』의 경우, 이 예금 가입일로부터 만기 전전일까지 트래블로그(신용 및 체크)카드 승인시점에 결제(충전)계좌가 하나은행으로 등록되어 있고, 사용(승인)실적이 10회 이상 보유한 경우에 우대금리를 제공합니다.\n【예시】 하나은행 결제계좌 등록(변경)전, 카드가맹점에서 사용(승인)한 경우 실적 미인정\n하나은행 결제계좌 등록(변경)후, 카드가맹점에서 사용(승인)한 경우 실적 인정\n※ 금융상품에 관한 계약을 체결하기전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다. (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가합니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"3개월이상~6개월미만","금리(연율,세전)":"1.8%"},{"기간":"6개월이상~12개월이하","금리(연율,세전)":"2.4%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (138, 2, 'HIFI PLUS외화적립예금', '{"상품특징":"외화정기예금과 같은 수익성 보장, 요구불예금과 유사한 유동성 보장\n적립일/적립횟수/납입금액 제한없음","가입대상":"제한없음","예금종류":"정기예금","가입기간":"1개월 이상 24개월 이내","최저가입금액":"제한없음","이자지급방법":"만기일시지급식 : 만기(후) 해지 시 이자를 지급","금리":"금리안내\n가입일 당시 영업점 및 당행 홈페이지에 고시된 외화정기예금 이율\n※ 납입금별로 입금일부터 만기일 전날까지의 기간에 대해 입금일자에 고시된 예치기간 해당 이율 적용","중도해지금리":"분할인출 외의 사유로 만기일 전에 지급 요청 시 다음의 중도해지 이율을 적용\n기간 중도해지 금리\n7일 미만 무이자\n1개월 미만 약정이율의 1/10\n3개월 미만 약정이율의 3/10\n6개월 미만 약정이율의 4/10\n12개월 미만 약정이율의 6/10\n12개월 이상 약정이율의 8/10","만기후 금리":"납입원금의 최종잔액에 대해 납입금 별로 만기 후 경과일수에 입금일 당시 약정이율의 3/10 적용","예치통화":"15개국 통화 (USD, JPY, EUR, GBP, CHF, AUD, CAD, NZD, HKD, SGD, SEK, DKK, NOK,KWD,CNY)","부가서비스":"우대항목 우대내용 우대율\n최근3개월 평잔\n미화5만불 이상 해외로 송금수수료 50%\n최근3개월 평잔\n미화1만불 이상 해외로 송금수수료\n* 전신료는 감면 없음 30%","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락여부를 통지해야하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 : 해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (139, 2, '스마트팝콘 외화적립예금', '{"상품특징":"스마트폰 뱅킹으로만 가입 가능\n납입금액, 적립횟수 및 적립일자에 제한없이 불입\n계약기간 중 만기일 전까지 5회 분할인출","가입대상":"개인에 한하며 스마트폰뱅킹으로만 가입 가능","예금종류":"외화정기예금","가입기간":"3개월 이상 12개월 이내","최저가입금액":"제한없음","예치통화":"15개국 통화 (USD, JPY, EUR, GBP, CHF, AUD, CAD, NZD, HKD, SGD, SEK, DKK, NOK,KWD,CNY)","이자지급방법":"만기일시지급식 : 만기(후 )해지 시 이자를 지급","금리":"금리안내\n가입일 당시 영업점 및 당행 홈페이지에 고시된 외화정기예금 금리\n※ 납입금별로 입금일부터 만기일 전날까지의 기간에 대해 입금일자에 고시된 예치기간 해당 금리 적용","중도해지금리":"분할인출 외의 사유로 만기일 전에 지급 요청 시 다음의 중도해지 이율을 적용\n기간 중도해지 금리\n7일 미만 무이자\n1개월 미만 약정이율의 1/10\n3개월 미만 약정이율의 3/10\n6개월 미만 약정이율의 4/10\n12개월 미만 약정이율의 6/10","만기후 금리":"납입원금의 최종잔액에 대해 납입금 별로 만기 후 경과일수에 입금일 당시 약정이율의 3/10 적용","부가서비스":"납입금별로 연 0.1%의 스마트폰전용계좌 우대이율을 추가로 적용하며, 1개월 미만의 납입금에 대하여는 우대이율 미적용\n가입기간 중 본인명의로 다음의 외환거래가 발생하는 경우에는 각 거래종류별 우대이율을 합산하여 최대 연 0.2%의 우대이율을 추가로 적용하며, 이 예금을 만기까지 보유하는 경우에만 적용\n우대대상 외환거래 우대이율\n영업점창구에서 USD500 상당액 이상 해외송금한 경우 연 0.05%p\n영업점창구에서 USD200 상당액 이상 현찰환전한 경우 연 0.05%p\n인터넷뱅킹/스마트폰뱅킹으로 USD500 상당액 이상 해외송금한 경우 연 0.10%p\n인터넷뱅킹/스마트폰뱅킹으로 USD200 상당액 이상 현찰환전한 경우 연 0.10%p","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락여부를 통지해야하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 : 해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (140, 2, '일달러 외화적금', '{"상품특징":"소액으로 쉽고 간편하게 모으고 분할인출이 만기 전 5회까지 가능한 미달러 전용 외화적금","가입대상":"개인 (국민인 거주자로 1인 1계좌만 가능)","예금종류":"자유적립 외화적금","가입기간":"6개월(재예치 가능)","가입금액":"미화 1달러 이상(월 최대 납입한도 미화 10,000불)","예치통화":"미국 달러화 (USD)","이자지급방법":"만기일시지급식 : 만기(후) 해지 및 재예치 시 이자를 지급","적립방법":"자유적립식 (적립횟수, 적립일자 제한없음)\n원화대가, 외화현찰 거래만 가능\n외화동전의 거래는 적립 및 출금거래 모두 불가\n※ 제한사항 상세 내용은 상품설명서 참조","금리":"금리안내\n가입일 또는 재예치일 당시 영업점 및 당행 홈페이지(www.kebhana.com)에 고시된 자유적립외화적금 이율\n분할인출하는 경우: 납입금별로 입금일부터 분할인출일 전날까지의 기간에 대해 가입일자에 고시된 예치기간 해당 이율 적용\n만기시점: 납입금별로 입금일부터 만기일 전날까지의 기간에 대해 가입일자에 고시된 가입기간 해당 이율 적용","우대금리":"이벤트 우대금리\n이 적금 가입 고객이 아래의 우대 항목을 충족할 경우 연 0.50% 의 우대이율을 만기 해지 시 추가 제공\n(분할인출, 중도해지, 만기 후 이율에는 미제공)\n이벤트 기간 2022.09.03~2025.08.31\n이벤트\n우대금리\n(연환산 이율%, 세전) 연 0.50%\n우대항목 충족시\n고시이율 + 우대이율 (연 0.50%)\n우대 항목 이벤트 기간 내\n본 상품 신규 가입 및 만기(6개월)유지 동시 충족 시\n(재예치 건 제외)","재예치 선택시":"이 적금의 신규개설 시 또는 만기일 이전에 손님이 적금의 재예치를 신청한 때에는 만기일(휴일인 경우 익영업일)에 납입원금은 전과 동일한 기간으로 자동 재예치 처리되며, 이때 발생한 이자는 재예치시점의 전신환매입율로 환전 되어 사전에 등록된 본인명의 입출금이 자유로운 원화예금으로 입금됨. 단, 압류 등 지급제한이 있는 경우에는 재예치 되지 않음.","분할인출":"만기 전 5회까지 분할인출 가능(선입선출)\n분할인출 후 잔액은 미화 1 달러 이상 유지해야함\n분할인출 시 납입금별로 입금일부터 인출일 전날까지의 기간에 대해 가입일 당시 영업점과 인터넷 홈페이지에 고시한 예치기간별 해당이율 적용\n법적제한이 있는 경우 일부해지 불가.","중도해지금리":"분할인출 외의 사유로 만기일 전에 지급 요청 시 다음의 중도해지 이율을 적용\n기간 중도해지 금리\n7일 미만 예치 시 무이자\n7일 이상 1개월 미만 예치 시 약정이율의 1/10\n3개월 미만 예치 시 약정이율의 3/10\n6개월 미만 예치 시 약정이율의 4/10","만기후 금리":"납입원금의 최종 잔액에 대해 만기 후 경과일수에 가입일 당시 약정이율의 3/10을 적용","우대서비스":"현찰수수료 면제\n이 적금의 최초 신규일로부터 1개월 이상 경과한 이후에 원화로 외화를 매입하여 적립한 금액을 이 적금에서 외화현찰로\n인출하는 경우 이 적금의 보유기간 동안 외화현찰수수료를 전액 면제함.\n이 적금을 재예치한 경우 재예치일부터 위 우대서비스를 적용 함.\n환율우대\n이 적금에 가입한 손님이 원화로 외화를 매입하여 이 적금에 적립하는 경우 40% 환율(Spread) 우대서비스 제공.\n환율(Spread)우대 이벤트 (~2025.08.31)\n전자금융으로 외화매매시 80% 환율(Spread)우대 적용","유의사항":"이 적금을 분할인출의 경우를 제외하고, 만기 전 계좌를 중도해지 할 경우 적용이율보다 낮은 중도해지이율이 적용됨.\n이 적금의 입금 및 해지 시 원화로 입·출금하는 경우 적용하는 환율은 입출금 당시 영업점 및 인터넷홈페이지에 고시된\n전신환 매매율(전신으로 송금하거나 송금 받을 때 적용하는 환율)이 적용됨.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n기타 유의사항\n이 적금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화로 입출금하는 경우에는 현찰수수료가 부과될 수 있음\n외화 동전(cent)은 입·출금이 불가\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락여부를 통지해야하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 :해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (141, 2, '자녀사랑외화로유학적금', '{"상품특징":"하단의 요건 중 1가지에 해당되는 경우 우대금리 (0.2%) 제공 (JPY제외)\n초,중,고등학생 명의의 계좌 개설시\n예금주가 계약기간 중 당행에서 유학경비 송금시\n자유적립 (납입금액, 적립횟수, 적립일자에 제한 없이 불입)\n단, 만기 전 1개월간 납입금 합계는 그 이전에 불입한 월평균 적립액의 2배를 초과하지 못함.","가입대상":"개인에 한함","예금종류":"외화적금","가입기간":"6개월이상 12개월 이내","최저가입금액":"제한없음","예치통화":"8개국 통화 (USD, EUR, JPY, GBP, CHF, CAD, AUD, NZD)","이자지급방법":"만기일시지급식 : 만기(후) 해지 시 이자를 지급","금리":"금리안내\n가입일 당시 영업점 및 당행 홈페이지에 고시된 자유적립외화적금 금리\n※ 납입금별로 입금일부터 만기일 전날까지의 기간에 대해 가입일자에 고시된 가입기간 해당 금리 적용","중도해지금리":"분할인출 외의 사유로 만기일 전에 지급 요청 시 다음의 중도해지 이율을 적용\n기간 중도해지 금리\n7일 미만 무이자\n1개월 미만 약정이율의 1/10\n3개월 미만 약정이율의 3/10\n6개월 미만 약정이율의 4/10\n6개월 이상 약정이율의 6/10","만기후 금리":"납입원금의 최종 잔액에 대해 만기 후 경과일수에 가입일 당시 약정이율의 3/10을 적용","부가서비스":"외환수수료 및 환율우대서비스\n최근3개월 평잔이 미화 5만불 이상\n- 해외로 송금수수료 : 50% 우대 (전신료는 감면 없음)\n- 유학경비 송금시 환율우대 : 주요통화(USD, EUR, JPY) 40%, 기타통화 20%\n최근3개월 평잔이 미화 1만불 이상\n- 해외로 송금수수료 : 30% 우대 (전신료는 감면 없음)\n- 유학경비 송금시 환율우대 : 주요통화(USD, EUR, JPY) 40%, 기타통화 20%\n납입금 자동이체시 환율우대 : 주요통화(USD, EUR, JPY) 40%, 기타통화 20%","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락여부를 통지해야하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 : 해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (142, 2, '더 와이드(The Wide)외화적금', '{"상품특징":"상품 가입 기간 동안 조건 충족 시 우대금리를 최고 연 0.3% 지급\n원화를 외화로 환전하여 이 적금에 적립 시 환율(Spread)우대 제공\n가입일로부터 6개월 경과 후 분할 인출 하거나 해지하여 영업점에서 해외송금 할 경우 송금수수료 30% 감면","가입대상":"개인","예금종류":"외화적금","가입기간":"6개월 이상 24개월 이내","최저가입금액":"제한없음","예치통화":"8개국 통화 (USD, EUR, JPY, GBP, CHF, CAD, AUD, NZD)","이자지급방법":"만기일시지급식 : 만기(후) 해지 시 이자를 지급","금리":"금리안내\n가입일 당시 영업점 및 당행 홈페이지에 고시된 자유적립외화적금 금리\n※ 납입금별로 입금일부터 만기일 전날까지의 기간에 대해 가입일자에 고시된 가입기간 해당 금리 적용","중도해지금리":"분할인출 외의 사유로 만기일 전에 지급요청시 다음의 중도해지이율을 적용합니다.\n기간 중도해지 금리\n7일 미만 무이자\n1개월 미만 약정이율의 1/10\n3개월 미만 약정이율의 3/10\n6개월 미만 약정이율의 4/10\n12개월 미만 약정이율의 6/10\n12개월 이상 약정이율의 8/10","만기후 금리":"납입원금의 최종 잔액에 대해 만기 후 경과일수에 가입일 당시 약정이율의 3/10을 적용","부가서비스":"환율우대\n- 원화로 외화를 매입하여 이 적금에 입금하는 경우 40% 우대 (USD, EUR, JPY이외 통화는 20%)\n해외로 외화송금 수수료 우대\n- 동 적금 자금으로 영업점을 통해 해외송금하는 경우 30% 감면\n  (이 적금 가입일로부터 6개월 경과 후 인출 또는 해지하는 경우에 한함, 전신료는 감면 없음)\n우대금리\n- 가입기간 중 해외여행을 증빙하는 서류(여행사 해외 여행계약서, 여행관련 제비용 영수증 및 기타 해외여행이 인정되는\n  서류)를 제출하는 경우 연 0.10%p의 우대이율을 제공함\n- 동 적금적립을 위해 자동이체를 신청한 경우 연 0.10%p의 우대이율을 제공함\n- 가입기간 중 본인명의로 다음의 외환거래가 발생하는 경우 각각 연 0.10%p의 우대이율을 제공함\n우대대상 외환거래\nUSD500 상당액 이상 해외송금한 경우\nUSD200 상당액 이상 현찰환전한 경우\n유학생송금 지정거래가 등록된 경우\n*추가이율은 최대 0.30%p를 적용하며 동 적금을 만기까지 보유하는 경우에만 적용합니다.","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락여부를 통지해야하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 : 해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (143, 3, '(지역장려금) 하나통장', '{"상품특징":"하나은행과 협약한 지자체의 지역장려금(결혼, 저출생, 청년지원 정책) 입금 실적에 따라 혜택을 제공하는\n지역장려금 전용통장","가입대상":"해당 지자체에서 가입대상자로 확인한 만14세 이상 만40세 이하 실명의 개인 및 개인사업자\n(지자체의 각 사업별로 1인 1계좌 가입 가능)","예금종류":"저축예금","상품명변경":"가입시 지역장려금(지원금)에 따라 상품명이 변경됨","전환여부":"전환불가(이 예금에서 다른 예금으로 전환 또는 다른 예금에서 이 예금으로의 전환 불가)\n단, 이 예금 상품가입 후, 만42세 이후 첫번째 도래하는 결산일에 [주거래 하나통장]으로 자동전환됨.\n[주거래 하나통장]을 보유하고 있는 경우에는[저축예금]으로 자동전환 됨.\n전환된 이후에는 전환된 상품의 우대서비스 및 혜택만 제공","이자지급 및\n이자지급방법":"이자계산기간동안 매일의 최종잔액에 고시금리를 적용한 일별이자를 합산하여 지급\n이자계산기간 : 최초 예금일(또는 원가일)부터 원가일(또는 지급일) 전날까지의 기간\n이자결산일 : 매월 세번째 금요일\n이자지급(원가)일 : 이자결산일의 익영업일","금리":"금액: 5천만원 미만, 금리(연율,세전): 0.1%\n금액: 5천만원 이상, 금리(연율,세전): 0.1%","우대금리":"최대 연 2.40%\n아래 항목을 충족한 경우, 매일의 최종 잔액 중 2백만원 이하의 금액에 대해 최대 연 2.40% 우대금리를 제공\n우대항목 내용\n급여이체\n(연 1.40%) 전월에 『(지역장려금) 하나통장』으로 급여 (주1)가 입금 된 경우\n지역장려금 우대금리\n(연 1.00%) 이 통장으로 당행과 협약한 지자체의 지역장려금(지원금)이 입금 확인된 경우\n6개월 간 제공(주2) 함.\n※ (주2) 장려금이 최초 입금된 시점으로부터 6개월간 적용\n(주1) 급여이체 인정기준:\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","수수료 우대":"※『(지역장려금) 하나통장』으로 급여가 입금된 경우 ,충족 월의 다음달 아래의 수수료 항목 무제한 우대서비스 제공\n(단, 이통장을 통한 거래에 한해 제공)\n수수료 항목 우대내용\n당행 자동화기기를 통한 현금인출 및 타행이체 수수료 무제한 면제\n※ 이 통장을 통한 거래에서\n발생하는 수수료에 한함\n인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행이체 수수료\n납부자자동이체 (타행자동이체)수수료\n타행 자동화기기*를 통한 현금인출 수수료 면제\n영업점 창구를 통한 타행이체 수수료 면제\n*CD기 공동망 참여 금융회사로 타행 점내 설치된 자동화기기에 한해 서비스가 제공되며, 은행 외부에 설치된 CD기(또는 ATM기) 및 한컴/효성/나이스 등 VAN사 CD기(또는 ATM기)에서 출금하는 경우 수수료 면제가 제공되지 않음.","세제혜택":"비과세 종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됨","유의사항":"이 예금은 양도 및 상속에 의한 명의변경이 불가합니다. 단, 상속에 의한 해지는 가능합니다.\n우대금리의 제공 조건 및 이자율은 시장금리 변동에 따라 변경 가능하며, 변경 시 재 고시합니다.\n이 예금 가입을 위해서는 각 지자체에서 부여한 사업별 승인 번호가 필요합니다.\n상품가입 후 만42세 이후 첫번째 도래하는 결산일에 [주거래 하나통장]으로 자동 전환됩니다.단, [주거래 하나통장]을 보유하고 있는 경우에는 [저축예금]으로 자동전환 됩니다.전환된 이후에는 전환된 상품의 우대서비스 및 혜택만 제공합니다.\n가입시 지자체의 장려금(지원금)에 따라 상품명이 변경됩니다.\n(예시 : 대전 , 결혼장려금 ➔ 대전두리 하나통장)\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다.\n※민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가합니다.\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용 계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있습니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<우대금리 급여이체 실적조건>\n변경 전\n급여이체 실적조건: 건당 50만원 이상 입금시, 급여적요(급여, 월급, 봉급, 상여, SALARY, BONUS 등)를 포함하여 입금 되거나 사전등록한 급여지정일 전후 1영업일 내 자금이 입금되는 월의 다음달에 제공합니다.(단 타인으로부터 이 통장에 입금된 건에 한함)\n변경 후\n급여이체 실적조건:\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"5천만원 미만","금리(연율,세전)":"0.1%"},{"금액":"5천만원 이상","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (144, 3, '‘네이버페이 머니 하나 통장 서비스’ 해지시 『하나 제휴 통장』으로 상품명 변경', '{"상품특징":"제휴사의 특정서비스 등록 시 우대금리 및 서비스를 제공하는 통장","가입대상":"만 17세 이상의 실명의 개인 또는 개인사업자 (1인 1계좌)","예금종류":"저축예금","가입방법":"네이버파이낸셜 주식회사의 웹 또는 앱페이지에 연계된 『하나은행 비대면계좌개설서비스』를 통해서만 가입이 가능하며,\n【네이버파이낸셜 제휴서비스/ [네이버페이 머니(하나)통장] 서비스】(이하 ‘네이버페이 머니 하나 통장 서비스’라 한다) 등록시\n전용 상품으로 이용가능","전환여부":"다른 예금에서 이 예금으로 전환 또는 이 예금에서 다른 예금으로 전환 불가","금리":null,"우대금리":"1. ‘네이버페이 머니 하나 통장 서비스’ 이용시, 매일의 최종 잔액 중 2백만원이하 금액에 대해서 아래의 기간 동안 우대금리 및 이벤트 특별금리를 제공합니다.\n2. 2023.9.4이후 신규한 계좌에 한해, 해지 후 재가입 시 우대금리 및 이벤트 특별금리를 제공하지 않습니다. 단 최초 가입일로부터 1개월 이내 재가입한 경우에는 제공합니다.\n요건 우대금리\n네이버페이 머니 하나 통장\n서비스 이용시 가입일(통장 개설일)로부터 1년 이내 연 2.90%\n가입(통장개설) 후 1년 초과 5년 이내 연 0.40%\n이벤트 특별금리\n【2024.03.01이내 가입한 경우】 가입일로부터 1년이내 연 1.00%\n[적용 예시] \n2023.09.05 ‘네이버페이 머니 하나 통장’ 가입시\n기본금리(연 0.10%)+우대금리(연2.90%)+이벤트 특별금리(연 1.00%)= 연 4.00% 제공","수수료 우대":"이 통장을 통한 거래시 아래의 수수료 우대 서비스 제공\n수수료항목 우대내용\n당행 자동화기기를 통한 현금인출 및 타행이체 수수료 무제한 면제\n※ 이 통장을 통한 거래에서\n발생하는 수수료에 한함\n인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행이체 수수료\n납부자자동이체 (타행자동이체)수수료","연계·제휴\n서비스":"연계·제휴 서비스 내용 : ‘네이버페이 머니 하나 통장 서비스‘ 이용시, 제휴사가 제공합니다.\n(단, ‘네이버페이 머니 하나 통장 서비스‘ 해지시 해당서비스를 제공하지 않습니다.)\n제휴사명 서비스명칭 제휴서비스 내용\n네이버파이낸셜\n주식회사(주1) 【네이버파이낸셜 제휴서비스 /\n[네이버페이 머니 (하나) 통장] 서비스】 ① 페이머니(주2) 를 이 예금으로 자동 입금\n② 네이버페이 서비스 페이지 내에서 이 예금의 잔액 및 거래 내역 조회\n③ 네이버페이 결제 연동\n(주1) \n네이버파이낸셜 주식회사는 간편결제, 간편송금 등의 금융플랫폼 서비스(네이버페이 서비스) 사업을 영위하며,\n네이버페이 서비스를 통해 이 예금 서비스를 사용할 수 있도록 합니다.\n(주2) \n페이머니 : 네이버페이 회원이 직접 유상결제 또는 다른 네이버페이 회원으로부터 송금받은 송금잔고를 페이머니로\n전환하는 등의 다양한 방법으로 충전하여 네이버페이 사용처에서 사용 할 수 있는 전자적 지급수단\n연계·제휴 서비스 이행책임 :\n하나은행은 연계·제휴서비스 등을 부당하게 축소하거나 변경하지 않으며, 불가피하게 축소하거나 변경하는 경우 그에 상응하는 다른 연계·제휴서비스를 제공합니다.\n연계·제휴 서비스 제공기간 :\n‘네이버페이 머니 하나 통장 서비스’를 등록하여 ‘네이버페이 머니 하나 통장’으로 이용시에만 제공합니다. (단, 제휴사와의 계약기간내에 한함)\n연계·제휴 서비스 변경내용 사전 통지방법 :\n서면, 우편, 팩스, 전화, E-mail, LMS등의 방법 중 2개 이상의 방법으로 축소·변경 6개월전부터 매월 고지합니다. (단, 그에 상응하는 다른 연계·제휴서비스를 제공하거나 금융회사의 휴업·파산·경영상의 위기 또는 연계·제휴서비스등을 제공하는 자의 일방적인 제공 중단 등 6개월 전부터 고지 하기 어려운 불가피한 사유가 있는 경우에는 해당 상황이 발생하는 즉시 고지하는 경우 제외됩니다.)\n연계·제휴 서비스 제공요건 :\n제휴사 앞 금융거래정보 이용·제공 동의를 하고 ‘네이버페이 머니 하나 통장 서비스''를 이용시 제공합니다.","혁신금융서비스\n승인에\n관한사항":"‘네이버페이 머니 하나 통장 서비스’는 금융혁신지원특별법에 따라 혁신금융사업자로 선정된 은행이 시험 운영하여 제공하는 혁신 금융 서비스로 동 특별법이 정하는 다음과 같은 사유가 발생하는 경우 서비스 운영이 종료될 수 있습니다.\n1. 지정기간이 만료되는 경우(지정만료일 : 2024. 11.02)\n2. 금융위원회가 혁신금융서비스 지정을 취소하거나, 중지명령(변경결정)을 하는 경우\n3. 은행이 금융위원회에 혁신금융서비스 지정철회를 요청하여 지정취소가 되는 경우\n4. 합병 등 조직변경으로 혁신금융서비스 지정의 효력이 상실되는 경우\n네이버페이 머니 하나 통장 서비스’의 운영이 종료되더라도 은행은 기존에 체결된 계약에 따른 의무를 이행 해야합니다. 다만, 이행이 불가능한 경우에는 이용자의 동의를 얻어 계약을 조기에 종료할 수 있으며, 서비스 중단으로 이용자의 손해가 발생한 경우 이를 배상할 책임이 있습니다.\n네이버페이 머니 하나 통장 서비스’는 혁신금융서비스로 선정되어 시험운영하는 서비스이며, 그로 인해 예상하지 못한 위험이 발생할 수 있습니다. 이용자에게 통장개설 전 ‘혁신금융서비스 고지사항’을 안내하고, 이용자는 동 내용에 대해 동의 후 서비스를 이용할 수 있습니다. 다만, 해당 조치는 회사가 이용자에 대하 부담하는 법률적 책임에 영향을 미치지 않습니다.\n은행은 혁신금융서비스의 제공 및 중단 등으로 인하여 이용자에게 손해가 발생한 경우 고의 또는 과실이 없음을 입증하지 못하는 한 이를 배상할 책임이 있으며, 은행의 책임과 관련하여 금융혁신지원특별법, 전자금융거래법, 은행법 등 다른 법령에 이용자에게 유리하게 적용될 수 있는 규정이 있는 경우에는 그 법령이 우선 적용됩니다.","세제혜택":"비과세 종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됨","유의사항":"우대금리의 제공은 ‘네이버페이 머니 하나 통장 서비스’ 이용시에만 적용됩니다.\n2023.9.4 이후 신규한 계좌에 한해, 해지 후 재가입한 경우 우대금리 및 이벤트 특별금리를 제공하지 않습니다.\n단, 최초 가입일로부터 1개월이내 재가입한 경우에는 제공합니다.\n우대서비스 내용 및 대상 요건은 변경 가능하며, 변경 시 재고시 합니다.\n본 상품은 금융위원회 혁신금융서비스 지정에 관한 내용을 반영하여 신규계좌수를 150만좌로 제한합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동이 불가합니다.\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용 계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있습니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","민원처리 및\n분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 영업점, 고객센터(1599-1111,0,1), 고객의 소리(080-933-1111), 또는 인터넷 홈페이지(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2023.09.04)\n기존 가입고객에 대한 적용 여부 : 적용\n<네이버페이 머니 하나 통장 서비스 제공 변경 및 추가>\n변경 전\n‘네이버페이 머니 하나 통장 서비스’ 이용시에만 우대금리를 제공\n(단, ‘네이버페이 머니 하나 통장 서비스’ 해지시에는 우대금리를 미제공)\n가입일(통장 개설일)로부터 1년 이내 우대금리 연 2.90% + 이벤트 특별금리(주1) 연 1.00% = 연 3.90% 제공\n(주1) 이벤트 특별금리 : 출시일로 부터 6개월 이내 상품을 가입시 추가 제공\n- [적용 예시] 기본금리(연 0.10%) + 우대금리(연2.90%) + 이벤트 특별금리(연 1.00%) = 연 4.00% 제공\n변경 후\n1.‘네이버페이 머니 하나 통장 서비스’ 이용시, 매일의 최종 잔액 중 2백만원이하 금액에 대해서 아래의 기간 동안 우대금리 및 이벤트 특별금리를 제공합니다.\n2. 2023.9.4이후 신규한 계좌에 한해, 해지 후 재가입 시 우대금리 및 이벤트 특별금리를 제공하지 않습니다. 단 최초 가입일로부터 1개월 이내 재가입한 경우에는 제공합니다.\n이벤트 특별금리 【2024.03.01이내 가입한 경우 】 가입일로부터 1년이내 연 1.00% 제공\n[적용 예시] 2023.09.05 ‘네이버페이머니하나통장’ 가입시 기본금리(연 0.10%)+우대금리(연2.90%)+이벤트 특별금리(연 1.00%)= 연 4.00% 제공\n<유의사항>\n변경 전\n우대금리의 제공은 ‘네이버페이 머니 하나 통장 서비스’ 이용시에만 적용됩니다.\n우대서비스 내용 및 대상 요건은 변경 가능하며, 변경 시 재고시 합니다.\n‘네이버페이 머니 하나 통장서비스＇는 금융혁신지원특별법에서 정하는 혁신금융서비스 지정에 관한 내용을 반영 합니다.\n본 상품은 금융위원회 혁신금융서비스 지정에 관한 내용을 반영하여 신규계좌수를 50만좌로 제한합니다.\n변경 후\n우대금리의 제공은 ‘네이버페이 머니 하나 통장 서비스’ 이용시에만 적용됩니다.\n2023.9.4 이후 신규한 계좌에 한해, 해지 후 재가입한 경우 우대금리 및 이벤트 특별금리를 제공하지 않습니다.\n단, 최초 가입일로부터 1개월이내 재가입한 경우에는 제공합니다.\n우대서비스 내용 및 대상 요건은 변경 가능하며, 변경 시 재고시 합니다.\n본 상품은 금융위원회 혁신금융서비스 지정에 관한 내용을 반영하여 신규계좌수를 150만좌로 제한합니다.\n상품내용 변경에\n관한 사항\n(2023.08.21)\n기존 가입고객에 대한 적용 여부 : 적용\n<우대금리 내용 변경>\n변경 전\n네이버페이 머니 하나 통장 서비스 이용시 매일의 최종잔액 중 1백만원 이하 금액에 대해 아래의 기간 동안 우대금리를 적용\n변경 후\n네이버페이 머니 하나 통장 서비스 이용시 매일의 최종잔액 중 2백만원 이하 금액에 대해 아래의 기간 동안 우대금리를 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (145, 3, '건설하나로 통장', '{"상품특징":"건설근로자를 위한 금융혜택 제공(건설근로자공제회 협약, 기관영업부)","가입대상":"다음 각 호에서 정하는 실명의 개인 또는 개인사업자 (1인 1계좌)\n1. 건설근로자공제회에서 발급한 건설근로자공제회 회원임을 확인 할 수 있는 서류 제시자 (서류 예시 : 퇴직공제금적립내역서)\n2. 건설업 기초안전보건교육이수증 소지자\n3. 건설업 취업인정증 소지자\n4. E-9 비자 소지한 건설업 종사 외국인\n5. 건설근로자공제회에서 발급한 기능향상지원사업 훈련 수강 확인증 소지자\n※ 각 가입대상별 자격확인 증빙서류 제출 후 가입 가능","예금종류":"저축예금","금리":null,"우대금리":"아래 필수요건 1가지 이상을 충족하는 경우, 매일의 최종 잔액 중 1백만원까지 기본금리에 연 0.9%의 우대금리를 추가제공 (2019.3.16 현재,세전)\n① 전월말 기준 이 예금의 월 평균잔액 50만원 유지시\n② 전월말 기준 이 예금에서 하나은행 적금상품으로 매월 자동이체 실적이 있는 경우\n③ 전월말 기준 이 예금으로 급여 (주1) 이체 시\n\n   (주1) 급여 인정 기준 :\n   ① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n   (ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n   ② 회사에 의한 ‘급여코드’로 입금된 금액이 월 50 만원 이상인 경우\n   ③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n   ※ 본인 거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n   ※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n※ 단, 원가일이 속한 달의 전월말 또는 해지일 전월말을 기준으로 최근 3개월동안 상기 필수요건을 2개월이상 유지하는\n경우에 제공\n※ 1백만원 초과 금액은 우대금리 미제공\n※ 신규 가입일로부터 1년이 되는 날까지 충족여부에 관계없이 우대금리를 제공","수수료 우대":"아래 필수요건 1가지 충족시 해당월 수수료 우대서비스 I 월 10회 제공\n아래 필수요건 1가지 충족 후 추가요건 1가지 충족 시 수수료 우대서비스 II 월 10회 제공\n※ 필수요건\n- 전월말 기준 이 예금의 월 평균잔액 50만원 유지 시\n- 전월말 기준 이 예금에서 하나은행 적금상품으로 매월 자동이체 실적이 있는 경우\n- 전월말 기준 이 예금으로 예금 가입자가 지정한 날에 타인으로부터 50만원 이상 급여이체시\n※ 추가요건\n- 전월말 기준 이 예금에서 하나카드(신용/체크) 결제 시\n- 전월말 기준 이 예금에서 각종 공과금 2건 이상 자동이체 시\n- 전월말 기준 이 예금에서 아파트 관리비 자동이체 시\n※ 신규 가입일로부터 1년이 되는 날까지는 충족여부에 관계없이 수수료 우대서비스 I 월 10회 면제\n※ 신규가입월 및 가입월 다음월까지는 충족여부에 관계없이 수수료 우대서비스 II 월 10회 면제\n\n[수수료 우대서비스 I (월 10회)]\n- 하나은행 자동화기기를 통한 마감 후 현금인출 시 발생하는 수수료 면제\n- 하나은행 자동화기기, 폰뱅킹(ARS), 인터넷뱅킹, 모바일뱅킹을 통한 타행 이체 거래 시 발생하는 수수료 면제\n[수수료 우대서비스 II (월 10회)]\n- 타행 자동화기기㈜ 를 통한 현금 인출 거래\n㈜ CD기 공동망 참여 금융회사로 타행 점내 설치된 자동화기기에 한해 서비스 제공. 은행 외부 설치된 CD기 및 한컴/효성/나이스 등 VAN사 CD기 출금하는 경우 수수료 우대 없음","세제혜택":"전 금융사 통합한도 내에서 비과세 저축으로 가입가능","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음\n※ 민사집행법에 따라 최저생계비 이하 등 압류 금지채권에 해당하는 경우에는 법원에 압류 금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있음\n예금잔액증명서 발급 당일 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<우대금리 항목 내용 수정 및 급여 인정 기준 추가>\n변경 전 :\n③ 전월말 기준 이 예금으로 예금 가입자가 지정한 날에 타인으로부터 50만원이상 급여이체시\n변경 후 :\n③ 전월말 기준 이 예금으로 급여 (주1) 이체 시\n\n(주1) 급여 인정 기준 :\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50 만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인 거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (146, 3, '급여하나 통장', '{"상품특징":"직장인을 위한 수수료우대 혜택과 만35세이하 청년직장인에게 특별 우대금리가 제공되는 월급통장","가입대상":"실명의 개인 또는 개인사업자 (1인 1계좌)","예금종류":"저축예금","전환여부":"저축예금에 한하여 동 상품으로 전환가능\n(단, 기존 수수료면제통장에서 전환 시 수수료면제횟수는 전환월은 기존상품의 잔여횟수를 적용하고, 다음달부터는 동 상품의\n실적에 따라 수수료면제횟수 적용)","금리":"금액: 5천만원 미만, 금리(연율,세전): 0.1%\n금액: 5천만원 이상, 금리(연율,세전): 0.1%","우대금리":"청년직장인 우대금리\n만35세이하 급여이체 직장인 대상 1백만원까지 연1.40% 추가제공 (2019.3.16 현재,세전)\n제공대상 : 만35세 이하 고객으로, 매 결산월(3,6,9,12월)의 전월말일 또는 해지일 전월말일 기준, 과거 3개월동안 2개월이상 이 통장에 급여(주1)가 입금되는 경우\n제공내용 : 해당 이자계산기간내 매일의 최종잔액 중 1백만원까지 기본금리에 우대금리 연1.40% 추가제공\n(단, 1백만원 초과금액은 우대금리 미제공)\n제공기간 : 해당 고객이 만35세 초과후 첫번째 도래되는 결산시까지 제공\n※ 유의사항\n다른 통장에서 이 통장으로 전환하는 경우에는, 전환일부터 이 통장의 금리를 적용함","수수료 우대":"아래의 제공조건을 충족하는 경우, 충족월 다음달에 수수료우대서비스를 제공합니다.\n(이 통장을 통한 거래에 한하여 제공)\n구분 제공 조건 (주1) 제공 내용 (주2)\n급여하나 우대 이 통장에 급여 입금 수수료우대 I (무제한)\n+ 수수료우대 II (월5회)\n급여플러스 우대 이 통장에 급여 입금\n+ 주거래이체실적 1건이상 수수료우대 I (무제한)\n+ 수수료우대 II (월10회)\n하나머니 우대 하나금융그룹의 하나머니 회원 수수료우대 I (월10회)\n(주1) 제공조건 인정 기준\n급여 입금\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원\n이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등\n비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n주거래 이체실적 하나은행에서 가입한 본인명의 연금상품 (연금펀드,연금신탁,연금보험) 이체, 아파트관리비 출금, 하나카드/현대카드 결제 (하나머니** 충전방식 제외), 하나카드/BC카드 가맹점대금 입금, 공과금 출금(타행카드 결제대금 제외), 본인명의 당행 주택청약종합저축 납입\n**하나머니: 하나금융그룹의 통합 멤버십 포인트\n(주2) 우대수수료 내용\n수수료 우대 Ⅰ 수수료 우대 Ⅱ\n당행 자동화기기를 통한 마감 후 현금인출 수수료 면제\n당행 자동화기기를 통한 타행이체 수수료 면제\n인터넷뱅킹, 폰뱅킹(ARS), 스마트폰뱅킹을 통한 타행이체 수수료 면제\n납부자자동이체 및 타행자동이체 수수료 면제\n타행 자동화기기*를 통한 현금 인출 수수료 면제\n창구 타행이체 수수료 면제\n* CD기 공동망 참여 금융회사로 타행 점내 설치된 자동화기기에 한해 서비스 제공. 은행 외부 설치된 CD기 및 한컴/효성/나이스 등 VAN사 CD기 출금하는 경우 수수료우대 없음\n※ 이 통장을 신규 가입후 다음달 말일까지는 제공조건 충족여부와 관계없이 수수료우대 I 을 총합 월 10회 제공합니다.","직장인 응원 서비스":"직장인 응원서비스를 신청하는 경우, 수수료우대 I (무제한) 및 수수료우대 II (총합 월5회) 를 신청일로부터 6개월이 종료하는 달까지 제공합니다. (이 통장을 통한 거래에 한 하여 제공)\n신청대상 : 휴직(육아/인병), 퇴직한 고객이 사유 발생월 전월 기준으로 과거 3개월동안 2개월이상 이 통장에 급여입금실적\n(주1)이 있는 경우\n신청방법 : 휴직/퇴직후 3개월이내 영업점 창구를 통해 사유별 증빙서류 제출\n(증빙서류(예시) : 육아휴직 또는 인병휴직 확인서, 퇴직증명서 등 확인 가능한 서류)","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내)","유의사항":"“청년 직장인 우대금리”는 급여입금실적을 충족하는 만35세이하 고객에 한하여 제공됩니다.\n수수료우대서비스는 이 통장에 의한 거래에만 적용되며, 매월 요건 충족여부를 확인하여 다음월에 수수료우대 서비스를 제공합니다.\n우대금리, 우대서비스 내용 및 대상 요건은 변경 가능하며, 변경 시 재고시 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<급여인정기준>\n변경 전 :\n타인으로부터 건당 50만원 이상 입금 (급여적요 포함 입금 또는 급여지정일 전후 1영업일내 입금)\n(급여 적요: 급여, 월급, 봉급, 연봉, 급료, 상여, 성과, 보너스, 월보수, SALARY, PAY, BONUS 등)\n변경 후 :\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원\n이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등\n비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"5천만원 미만","금리(연율,세전)":"0.1%"},{"금액":"5천만원 이상","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (147, 3, '달달 하나 통장', '{"가입대상":"만 14세 이상 실명의 개인 및 개인사업자(1인1계좌)","예금종류":"저축예금","가입방법":"전환불가(이 예금에서 다른 예금으로 전환 또는 다른 예금에서 이 예금으로의 전환 불가)","이자계산 및\n이자지급방법":"이자계산기간동안 매일의 최종잔액에 고시금리를 적용한 일별이자를 합산하여 지급\n이자계산기간 : 최초 예금일(또는 원가일)부터 원가일(또는 지급일) 전날까지의 기간\n이자결산일 : 매월 세번째 금요일\n이자지급(원가)일 : 이자결산일의 익영업일","금리":"금액: 5천만원 미만, 금리(연율,세전): 0.1%\n금액: 5천만원 이상, 금리(연율,세전): 0.1%","우대금리":"최대 연 1.90%\n아래 항목을 충족한 경우, 2백만원까지 최대 연 1.90% 우대금리 제공 (2025.04.01 현재, 세전)\n우대항목 내용\n급여이체\n(연 1.90%) 급여이체 실적 (주1) 충족시 제공 (이 통장을 통한 입금 시)\n(주1) 급여이체 실적조건:\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","수수료 우대":"이 통장으로 우대금리 요건(급여이체 실적)을 충족하는 경우, 충족월의 다음달에 아래의 수수료 우대혜택 제공\n수수료 항목 우대내용\n당행 자동화기기를 통한 현금인출 및 타행이체 수수료 무제한 면제\n※ 이 통장을 통한 거래에서\n발생하는 수수료에 한함\n인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행이체 수수료\n납부자자동이체(타행자동이체)수수료\n타행 자동화기기*를 통한 현금인출 수수료 면제\n영업점 창구를 통한 타행이체 수수료 면제","세제혜택":"비과세 종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됨","유의사항":"이 예금은 양도 및 상속에 의한 명의변경이 불가합니다. 단, 상속에 의한 해지는 가능합니다.\n우대금리의 제공 조건 및 이자율은 시장금리 변동에 따라 변경 가능하며, 변경 시 재고시합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다.\n※민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가합니다.\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용 계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있습니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<우대금리 급여이체 실적조건>\n변경 전\n급여이체 실적조건: 건당 50만원 이상 입금시, 급여적요(급여, 월급, 봉급, 상여, SALARY, BONUS 등)를 포함하여 입금 되거나 사전등록한 급여지정일 전후 1영업일 내 자금이 입금되는 월의 다음달에 제공합니다.(단 타인으로부터 이 통장에 입금된 건에 한함)\n변경 후\n급여이체 실적조건:\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"5천만원 미만","금리(연율,세전)":"0.1%"},{"금액":"5천만원 이상","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (148, 3, '당근머니 하나 통장', '{"상품특징":"‘당근페이 서비스’ 이용 실적에 따라 우대금리 및 제휴 서비스를 제공하는 상품","가입대상":"만 17세 이상 실명의 개인 및 개인사업자 (1인 1계좌)","예금종류":"저축예금","가입방법":"「하나은행 비대면 계좌개설 서비스」를 통해서만 가입 가능\n※ ‘당근’ (주1) 앱(App.) 또는 웹 페이지 內 연동\n(주1) 당근: 중고거래 기반의 지역 생활 커뮤니티","상품간전환":"불가 (단, 제휴 계약 종료 시 종료일 이후 최초 도래하는 결산일에 ''저축예금'' 으로 전환)","이자계산 및\n이자지급방법":"이자계산기간동안 매일의 최종 잔액에 대해 고시금리를 적용한 일별 이자를 합산하여 지급\n이자계산기간: 최초 예금일(또는 원가일)부터 원가일(또는 지급일)전날까지의 기간\n이자지급(원가)일: 매월 세번째 금요일의 다음 영업일","금리":"금액: 5천만원 미만, 금리(연율,세전): 0.1%\n금액: 5천만원 이상, 금리(연율,세전): 0.1%","우대금리":"「 당근머니 하나 통장」에 입금된 당근머니 (주2)로 당근페이 서비스 (주3)를 이용한 경우, 아래의 조건 충족 시 매일의 최종 잔액 중 3백만원 이하의 금액에 대해 우대금리를 제공\n※ 단, ㈜당근페이와의 제휴 계약이 종료된 경우, 우대금리를 제공하지 않습니다.\n기준일자 : 2025-03-24\n제공 조건 우대금리(세전)\n전월 ‘당근페이 서비스’ 이용 실적이 한번 있는 경우 연 1.9%\n전월 ‘당근페이 서비스’ 이용 실적이 두번 이상 있는 경우 연 2.9%\n(주2) 당근머니: 당근페이 서비스에 가입한 회원이 직접 유상충전하거나 다른 회원으로부터 수취하여 당근페이 사용처에서 사용할 수 있는 전자적지급수단\n(주3) 당근페이 서비스: 당근 거래를 위한 당근페이의 간편 결제 및 간편 송금 등의 서비스","수수료 우대":"이 통장을 통한 거래 시 아래의 수수료 우대 서비스 제공\n수수료우대 우대내용\n당행 자동화기기를 통한 현금인출 및 타행이체 수수료 무제한 면제\n인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행이체 수수료\n납부자자동이체(타행자동이체)수수료","연계·제휴\n서비스":"연계·제휴 서비스 내용: 「당근머니 하나 통장」을 당근페이 (하나)제휴 서비스의 연결 계좌로 등록한 경우 제휴사가 제공\n제휴사명 서비스 명칭 제휴서비스 내용\n당근페이㈜ (주4) 당근페이 (하나)제휴 서비스 ① 당근머니를 이 예금으로 자동 입금\n② 당근페이 서비스 페이지 내에서 이 예금의 잔액 및 거래내역 조회\n③ 당근페이 결제 연동\n(주4) \n당근페이㈜: ‘당근’거래를 위한 결제, 송금 서비스 등을 제공하는 회사\n연계·제휴 서비스 이행 책임: 하나은행은 연계·제휴 서비스 등을 부당하게 축소하거나 변경하지 않으며, 불가피하게 축소하거나 변경하는 경우 그에 상응하는 다른 연계· 제휴 서비스를 제공\n연계·제휴 서비스 제공기간: 「당근머니 하나 통장」을 ‘당근페이 (하나)제휴 서비스’에 등록하여 이용 시에만 제공 (단, 제휴사와의 계약기간 내에 한하며, 선불 충전을 위한 계좌 연결 시에는 제공하지 않음)\n연계· 제휴 서비스 변경내용 사전 통지 방법: 서면, 우편, 팩스, 전화, E-mail, LMS 등의 방법 중 2개 이상의 방법으로 축소·변경 6개월 전부터 매월 고지 (단, 그에 상응하는 다른 연계· 제휴 서비스를 제공하거나 금융회사의 휴업·파산· 경영상의 위기 또는 연계· 제휴 서비스 등을 제공하는 자의 일방적인 제공 중단 등 6개월 전부터 고지하기 어려운 불가피한 사유가 있는 경우 제외)\n연계·제휴 서비스 제공 요건: 제휴사 앞 금융거래정보 이용·제공 동의를 하고 ‘당근페이 (하나)제휴 서비스’를 이용 시 제공","혁신금융 서비스\n승인에\n관한 사항":"혁신금융 서비스 승인에 대한 내용 : 본 상품은 금융위원회 혁심금융서비스 지정에 관한 내용을 반영하여 신규 계좌 수를 57만좌로 제한합니다.\n“당근머니 하나 통장” 서비스는 금융혁신지원 특별법에 따라 혁신금융사업자로 선정된 은행이 시험운영하여 제공하는 혁신금융서비스로 동 특별법이 정하는 다음과 같은 사유가 발생하는 경우 서비스 운영이 종료될 수 있습니다.\n① 지정 기간이 만료되는 경우 (서비스 시행 후 2년 ‘2027.03.23’)\n② 금융위원회가 혁신금융서비스 지정을 취소하거나, 중지명령(변경결정)을 하는 경우\n③ 은행이 금융위원회에 혁신금융서비스 지정 철회를 요청하여 지정 취소가 되는 경우\n④ 합병 등 조직변경으로 혁신금융서비스 지정의 효력이 상실되는 경우\n제 1항에 따라 ‘당근머니 하나 통장 서비스’의 운영이 종료되더라도 은행은 기존에 체결된 계약에 따른 의무를 이행 해야합니다. 다만, 이행이 불가능한 경우에는 이용자의 동의를 얻어 계약을 조기에 종료할 수 있으며, 서비스 중단으로 이용자의 손해가 발생하 경우 이를 배상할 책임이 있습니다.\n‘당근머니하나통장 서비스’는 혁신금융서비스로 선정되어 시험운영하는 서비스이며, 그로 인해 예상하지 못한 위험이 발생할 수 있습니다. 이용자에게 통장개설 전 ‘혁신금융 서비스 고지사항’을 안내하고, 이용자는 동 내용에 대해 동의 후 서비스를 이용할 수 있습니다. 다만, 해당 조치는 회사가 이용자에 대해 부담하는 법률적 책임에 영향을 미치지 않습니다.\n㈜ 하나은행은 혁신금융서비스의 제공 및 중단 등으로 인하여 이용자에게 손해가 발생한 경우 고의 또는 과실이 없음을 입증하지 못하는 한 이를 배상할 책임이 있으며, 은행의 책임과 관련하여 금융혁신지원특별법, 전자금융거래법, 은행법 등 다른 법령에 이용자에게 유리하게 적용 될 수 있는 규정이 있는 경유에는 그 법령이 우선 적용됩니다.","세제혜택":"비과세 종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됨","유의사항":"㈜당근페이와의 제휴 계약이 종료된 경우, 우대금리를 제공하지 않으며, 종료일 이후 최초 도래하는 결산일에 ‘저축예금’으로 전환됨\n우대금리의 제공조건 및 이자율은 시장금리 변동에 따라 바뀔 수 있으며, 변경 시 재고시 함\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다.\n※민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동이 불가합니다.\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’ 에서 정의한 피해의심거래계좌 및 사기이용 계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있습니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","민원처리 및\n분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 영업점, 고객센터(1599-1111,0,1), 고객의 소리(080-933-1111), 또는 인터넷 홈페이지(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 “5천만원까지”(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"5천만원 미만","금리(연율,세전)":"0.1%"},{"금액":"5천만원 이상","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (149, 3, '보통예금', '{"상품특징":"가입대상 및 예치한도 제한이 없는 수시 입출금 에금\n기존 보통예금을 e-플러스 통장으로 전환하실 수 있습니다.    \n(단, 법인은 전환불가하며 e-플러스 통장으로 전환하실 경우 기존 통장은 사용하실 수 없습니다.)","가입대상":"제한없음","예금종류":"보통예금","가입기간":"제한없음","가입금액":"제한없음","이자지급방법":"이자 계산기간 동안 매일의 최종잔액에 기본금리를 적용한 일별 이자를 합산하여 지급\n(6월, 12월의 제3금요일에 결산하여 결산일 익일 지급)","금리":"금액: -, 금리(연율,세전): 0.1%","세제혜택":"비과세종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있음","원금 및 이자지급 제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급 제한\n예금잔액증명서 발급 당일에는 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.09.24 변경)\n<상품특징>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.\n변경 후 : 삭제","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"-","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (150, 3, '사업자 주거래 우대통장', '{"가입대상":"실명의 개인사업자 및 고유(납세)번호를 부여 받은 임의단체(1인 1계좌)\n※ 고유(납세)번호를 부여 받은 임의단체는 보통예금으로만 가입 가능","예금종류":"기업자유예금,보통예금,저축예금,MMDA","이자지급방법":"이자계산기간동안 매일의 최종잔액에 고시금리를 적용한 일별이자를 합산하여 지급합니다.\n이자계산기간 이자결산일 이자지급일(원가일)\n예금일(또는 원가일)부터 원가일(또는 지급일)\n전날까지의 기간 매년 3,6,9,12월의 제3금요일\n(보통예금은 매년 6,12월의 제3금요일) 결산일 익일","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음.","전환여부":"가능 (단, 기존 수수료면제통장에서 전환 시 수수료면제횟수는 해당월은 기존상품의 수수료면제 잔여횟수를 적용하고, 다음달부터는 동 상품의 실적에 따라 수수료면제횟수 적용)","금리":"분류: -, 금리(연율,세전): 저축예금:0.1%,보통예금:0.1%,기업자유예금:0.1%,MMDA:금액별 고시금리","수수료 우대":"※ 수수료 우대서비스 내용\n수수료 우대서비스 I (면제 횟수 제한 없음) 수수료 우대서비스Ⅱ (총합 월10회 면제)\n당행 자동화기기 통한 마감 후 현금인출 거래\n당행 자동화기기를 통한 타행이체 거래\n전자금융(인터넷, 텔레뱅킹, 스마트폰뱅킹)을 통한 타행이체 거래\n납부자자동이체(타행자동이체) 거래\n창구에서 타행이체 거래\n타행 자동화기기를 통한 현금인출 거래\n(은행점포내 자동화기기 또는 은행이 직접 관리하는 점외 자동화기기에 한함)\n\n※ 수수료 우대 요건\n전월에 이 예금으로 아래 요건 중 1가지 이상 충족하는 경우(수수료 우대서비스 Ⅰ, Ⅱ 제공)\n- 하나카드 또는 BC카드 가맹점 매출대금 입금\n- 4대사회보험료(국민연금, 건강보험, 고용보험, 산재보험) 모두 자동이체\n전월에 이 예금으로 (가), (나) 모두 충족하는 경우(수수료 우대서비스 Ⅰ만 제공)\n(가) 월평잔 50만원 유지\n(나) 공과금 2건 이상 자동이체\n전월에 이 예금으로 (가), (나)를 모두 충족하면서 (다), (라) 요건 중 1가지 이상 충족하는 경우(수수료 우대서비스 Ⅰ, Ⅱ 제공)\n(다) 아파트관리비 자동이체\n(라) 하나카드사의 신용/체크카드 결제","세제혜택":"비과세종합저축 가능(전 금융기관 통합한도 범위 내)","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","유의사항":"최초 가입일부터 다음월까지 수수료 우대요건에 상관없이 수수료 우대서비스 Ⅰ, Ⅱ 제공\n가입일 다음월부터 매월 수수료우대요건 충족여부를 확인한 후 다음달 1일부터 말일까지 제공\n전월 수수료 우대서비스 제공 요건을 충족하지 못하는 경우에는 별도의 통보없이 해당월은 수수료 우대서비스를 제공하지 않음\n이 통장을 통한 거래에 한해 수수료 우대서비스 제공","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.12.01 변경)\n■기존 가입고객에 대한 적용 여부 : 적용\n<이자지급방법>\n변경 전 : 예금과목의 이자지급방법에 따름\n변경 후 : 이자계산기간동안 매일의 최종잔액에 고시금리를 적용한 일별이자를 합산하여 지급합니다.\n이자계산기간 이자결산일 이자지급일(원가일)\n예금일(또는 원가일)부터\n원가일(또는 지급일)\n전날까지의 기간 매년 3,6,9,12월의\n제3금요일\n(보통예금은 매년 6,12월의 제3금요일) 결산일 익일\n<위법계약해지권>\n변경 전 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제 47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담 없이 계약해지가 가능합니다.\n변경 후 : 삭제\n<자료열람요구권>\n변경 전 : 신설\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 “5천만원까지“\n(본 은행의 여타 보호 상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["분류","금리(연율,세전)"],"데이터":[{"분류":"-","금리(연율,세전)":"저축예금:0.1%,보통예금:0.1%,기업자유예금:0.1%,MMDA:금액별 고시금리"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (151, 3, '수퍼플러스', '{"상품특징":"목돈을 1개월 미만의 기간으로 운용하실 때에도 적당한 상품입니다.","가입대상":"제한없음","예금종류":"저축예금, 기업자유예금","가입기간":"1일 이상 (1일 이상 예치시 이자 지급)","최저가입금액":"제한없음","이자지급방법":"이자원가식(3,6,9,12月) 또는 이자지급식(매인출시) 선택가능","금리":null,"세제혜택":"개인의 경우 비과세종합저축 가능(전 금융기관 통합한도 범위내)","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음\n<위법계약해지권 항목 신설>\n기존 가입 고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (152, 3, '영하나플러스 통장', '{"상품특징":"다양한 수수료 우대서비스를 제공하는 YOUTH고객 전용통장","가입대상":"만 30세 이하 실명의 개인 및 개인사업자 (1인 1계좌)","예금종류":"저축예금","전환여부":"저축예금에 한하여 동 상품으로 전환가능\n(단, 기존 수수료면제통장에서 전환 시 수수료면제횟수는 해당월은 기존 상품의 잔여횟수를 적용하고,\n다음달부터는 동 상품의 실적에 따라 수수료면제혜택 제공)\n상품가입(전환) 후, 만 35세가 되는 시점의 익월 첫 영업일에 [주거래하나통장]으로 자동 전환됨.\n(단, [주거래하나통장]을 보유하고 있는 경우에는 [저축예금]으로 자동전환 됨. 전환이후에는 전환된 상품의\n우대서비스 및 혜택만 제공)","금리":"분류: -, 금리(연율,세전): 0.1%","수수료 우대":"아래의 서비스 제공 조건 중 1가지 이상을 해당월에 충족한 경우, 충족월 다음달(익월 1일부터 말일까지)에 수수료\n우대서비스를 제공\n서비스 제공 조건\n본인명의 하나카드(신용/체크)의 대금을 이 통장에서 결제 시\n타인으로부터 월 건당 10만원 이상 입금 시\n수수료 우대\n당행 자동화기기를 통한 현금인출 수수료 무제한 면제\n당행 자동화기기를 통한 타행이체 수수료 무제한 면제\n인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행 이체 수수료 무제한 면제\n납부자자동이체 수수료 무제한 면제\n타행 자동화기기를 통한 현금 인출 수수료 월5회 면제\n※ 이 통장을 신규 후 다음달 말일까지는 제공조건 충족여부와 관계없이 수수료 우대서비스 제공\n※ 이 통장을 통한 거래에서 발생하는 수수료에 한하여 제공됨","세제혜택":"비과세종합저축 가능 (전 금융기관 통합한도 범위 내)","유의사항":"수수료 우대서비스는 이 통장에 의한 거래에만 적용되며, 매월 요건 충족여부를 확인하여 다음월에 수수료\n우대서비스를 제공합니다.\n우대서비스 내용은 은행의 사정에 의해 변경될 수 있습니다. 동 내용 변경 시 변경사유, 변경내용 등 내용을\n영업점 및 당행 인터넷 홈페이지에 1개월간 게시합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음\n<위법계약해지권 항목 신설>\n기존 가입 고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["분류","금리(연율,세전)"],"데이터":[{"분류":"-","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (153, 3, '저축예금', '{"상품특징":"개인(개인사업자 포함)이 가입할 수 있는 수시 입출금 예금\n기존 저축예금을 e-플러스 통장으로 전환하실 수 있습니다.     \n(단, 법인은 전환불가하며 e-플러스 통장으로 전환하실 경우 기존 통장은 사용하실 수 없습니다.)","가입대상":"실명의 개인(개인사업자 포함)","예금종류":"저축예금","가입기간":"제한없음","가입금액":"제한없음","이자지급방법":"이자 계산기간 동안 매일의 최종잔액에 기본금리를 적용한 일별 이자를 합산하여 지급\n( 3월, 6월, 9월, 12월의 제3금요일에 결산하여 결산일 익일 지급)","금리":"금액: 5천만원 미만, 금리(연율,세전): 0.1%\n금액: 5천만원 이상, 금리(연율,세전): 0.1%","세제혜택":"비과세종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있음","원금 및 이자지급 제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급 제한\n예금잔액증명서 발급 당일에는 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.09.24 변경)\n<상품특징>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.\n변경 후 : 삭제","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"5천만원 미만","금리(연율,세전)":"0.1%"},{"금액":"5천만원 이상","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (154, 3, '주거래하나 통장', '{"상품특징":"급여(연금) 실적이 없어도, 카드대금, 공과금 실적, 청약통장 저축 등으로 수수료를 아낄 수 있는 통장","가입대상":"실명의 개인 또는 개인사업자 (1인 1계좌)","예금종류":"저축예금","전환여부":"저축예금에 한하여 동 상품으로 전환가능\n(단, 기존 수수료면제통장에서 전환 시 수수료면제횟수는 전환월은 기존상품의 잔여횟수를 적용하고, 다음달부터는 동 상품의\n실적에 따라 수수료면제횟수 적용)","금리":"분류: -, 금리(연율,세전): 0.1%","수수료 우대":"아래의 제공조건을 충족하는 경우 충족월 다음달에 수수료우대서비스를 제공합니다. (이 통장을 통한 거래에 한하여 제공)\n구분 제공 조건 (주1) 제공 내용 (주2)\n주거래하나 우대 이 통장에 주거래이체실적 1가지 항목 충족 수수료우대Ⅰ(무제한)\n주거래플러스 우대 이 통장에 주거래이체실적 2가지 항목 이상 충족 수수료우대Ⅰ(무제한)\n+ 수수료우대Ⅱ(월 10회)\n하나머니 우대 하나금융그룹의 하나머니 회원 수수료우대Ⅰ(월 10회)\n(주1)제공조건 : 주거래이체항목의 충족 갯수에 따라 주거래하나 우대 또는 주거래플러스 우대서비스 제공\n주거래이체 항목 실적 인정 기준\n급여 입금\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원\n이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등\n비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n연금 입금 4대 공적연금(국민, 공무원, 군인, 사학), 기초(노령)연금, 보훈연금, 산재보험보험급여, 하나은행에서 가입한 개인연금(주택연금, 연금신탁, 연금보험, 연금펀드), 기타연금(고객 지정 연금수급일 전후 1영업일에 대량이체방식으로 건당 20만원이상 입금되고 적요에 ''연금'' 또는 ''보험사명''이 기재된 연금)\n카드 가맹점대금 입금 하나카드/BC카드 가맹점 대금\n아파트관리비 출금 자동이체방식으로 이체되는 아파트관리비\n카드 결제대금 출금 하나카드/현대카드의 결제대금으로 월 5만원 이상\n(신용·체크 합산, 하나머니** 충전방식 제외)\n**하나머니: 하나금융그룹의 통합 멤버십 포인트\n주택청약종합저축 납입 하나은행의 본인명의 주택청약종합저축에 납입\n공과금 2건 출금 도시가스요금, 전기료, 통신요금, 보험료 등 지로/CMS/펌뱅킹으로 이체\n(단, 타행카드 결제대금 제외)\n(주2)우대수수료 내용\n수수료 우대Ⅰ 수수료 우대Ⅱ\n당행 자동화기기를 통한 마감 후 현금인출 수수료 면제\n당행 자동화기기를 통한 타행이체 수수료 면제\n인터넷뱅킹, 폰뱅킹(ARS), 스마트폰뱅킹을 통한 타행이체 수수료 면제\n납부자자동이체 및 타행자동이체 수수료 면제\n타행 자동화기기*를 통한 현금 인출 수수료 면제\n창구 타행이체 수수료 면제\n*CD기 공동망 참여 금융회사로 타행 점내 설치된 자동화기기에 한해 서비스 제공. 은행 외부 설치된 CD기 및 한컴/효성/나이스 등 VAN사 CD기 출금하는 경우 수수료 우대 없음\n※이 통장을 신규 가입후 다음달 말일까지는 제공조건 충족여부와 관계없이 수수료우대Ⅰ을 총합 월 10회 제공합니다.","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내)","유의사항":"수수료우대서비스는 이 통장에 의한 거래에만 적용되며, 매월 요건 충족여부를 확인하여 다음월에 수수료우대 서비스를 제공합니다.\n우대서비스 내용 및 대상 요건은 변경 가능하며, 변경 시 재고시 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<급여인정기준>\n변경 전 :\n타인으로부터 건별 50만원 이상 급여적요* 포함 입금 또는 급여지정일 전후 1영업일 내에 입금\n(*급여적요: 급여, 월급, 봉급, 연봉, 급료,상여, 성과, 보너스, SALARY,,BONUS 등)\n변경 후 :\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원\n이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등\n비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["분류","금리(연율,세전)"],"데이터":[{"분류":"-","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (155, 3, '증권저축계좌(제휴증권사 국내/해외주식)', '{"상품특징":"증권저축계좌란\n은행이 증권사와 업무협약을 통해 은행창구에서 개설하는 증권계좌로 은행의 요구불계좌와 증권계좌를 연결한\n복합금융상품을 말함\n증권사의 증권계좌를 은행에서 개설대행함으로써 은행 요구불계좌의 편리한 지급결제 기능과 증권사의 증권서비스\n(주식매매 등), CMA의 고금리운용 등을 연계하는 계좌\n신규처리의 흐름\n은행의 요구불통장(모계좌)신규 -> 증권사의 증권계좌 신규 -> 현금IC카드(요구불통장) 및 제휴증권카드 발급\n제휴증권카드 발급수수료: 건당 2,000원\n제휴증권카드 발급가능 증권사: 한국투자증권(전계좌), 하나증권(현물이체계좌, 빅팟CMA)에 한하여 발급가능","가입대상":"실명의 개인(국민인 거주자에 한함)\n- 다만, 미성년자 또는 대리인에 의한 개설은 증권사와의 개별계약서에 따름\n빅팟통장은 1인1계좌만 가능(2023.10.27 신규가입 중단)\n국내선물옵션계좌, 해외선물옵션계좌,F/X마진계좌의 개설은 본인만 가능하며, 대리인에 의한 계좌개설 및\n미성년자에 대한 계좌개설은 할 수 없음","은행계좌 계정과목":"증권저축계좌와 연계 가능한 은행의 계정과목\n- 보통예금, 저축예금, MMDA (하나빅팟CMA(2023.10.27 신규가입 중단)는 기업자유예금도 포함)\n기존계좌에 대한 증권계좌 신규개설\n- 신규계좌 및 이미 개설된 요구불계좌에 대해서도 증권사의 증권계좌를 개설 할 수 있음","가입상품의 종류":"국내현물, 해외현물, 국내선물옵션, 해외F/X, 수익증권, 하나빅팟CMA(2023.10.27 신규가입 중단)\n단, 미래에셋증권, 신한투자증권은 국내현물계좌에서 수익증권 매매가 가능함","업무방법과 이용방법":"구분 연계방식(은행 예수금방식) 이체방식(증권사 예수금방식)\n업무방법 증권사의 증권계좌와 연결된 은행\n요구불통장의 자금을 증권계좌로 별도의\n이체조작함이 없이 주식 등의 매매 증권계좌와 연결된 은행 요구불계좌의\n자금을 증권계좌로 이체한 후 주식등의\n매매거래가 가능한 방식\n계좌구성 은행 요구불통장 + 증권사의 증권계좌 (1) 가상계좌 이체방식\n은행 요구불통장 + 가상계좌\n+ 증권사의 증권계좌\n(2) 일반 이체방식\n은행 요구불통장 + 증권사의\n증권계좌\n입출금 방법 고객자금이 은행 요구불통장에 있으므로\n입출금방식이 요구불통장과 동일하며,\n연결된 증권계좌로는 입금 불가 은행의 요구불통장에 있는 자금을\n증권계좌로 이체하거나, 증권계좌에 있는\n자금을 은행 요구불통장으로 이체\n이용방법\n증권계좌 신규 당일부터 증권거래가 가능함.\n계약의 해지는 영업점에서 해지함.\n주식매매거래 방법\n해당 증권사의 HTS,MTS,홈페이지 이용\n공무주청약, 유상청약 등의 업무처리는 증권사별 정책 및 사정에 따라 자격이 조정될 수\n있으므로 해당 증권사(콜센터)로 문의 후 처리","유의사항":"하나원큐(1Q)에서 비대면으로 증권저축계좌 개설시 거래조건\n1. 개설가능한 증권사는 하나증권, KB증권, 현대차증권, 미래에셋증권이며 이체방식으로만 개설가능\n2. 가입대상은 만 19세 이상 실명의 개인(국민인 거주자)에 한함\n3. 증권계좌 개설 가능 시간\n하나증권 00:15 ~ 23:44\nKB증권 01:00 ~ 23:30\n현대차증권 02:00 ~ 23:30\n미래에셋증권 00:15 ~ 23:30\n증권사의 증권계좌와 연계된 은행 요구불통장은 양도 및 명의변경 할 수 없음\n다만, 예금주 사망, 개명에 의한 명의변경은 할 수 있음\n증권저축계좌로 등록이 안되는 경우\n1. 요구불계좌에 주의사고등록이 되어있는 경우\n2. 한도대출약정이 있는 요구불계좌에 연계방식 증권계좌를 연결하는 경우","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2023.10.27 변경)\n■ 기존 가입고객에 대한 적용 여부 : 미적용\n<가입대상>\n변경 전\n실명의 개인(국민인 거주자에 한함)\n다만, 미성년자 또는 대리인에 의한 개설은 증권사와의 개별계약서에 따름 빅팟통장은 1인1계좌만 가능 국내선물옵션계좌, 해외선물옵션계좌,F/X마진계좌의 개설은 본인만 가능하며, 대리인에 의한 계좌개설 및 미성년자에 대한 계좌개설은 할 수 없음\n변경 후\n실명의 개인(국민인 거주자에 한함)\n다만, 미성년자 또는 대리인에 의한 개설은 증권사와의 개별계약서에 따름 빅팟통장은 1인1계좌만가능( 2023.10.27 신규가입중단) 국내선물옵션계좌, 해외선물옵션계좌,F/X마진계좌의 개설은 본인만 가능하며, 대리인에 의한 계좌개설 및 미성년자에 대한 계좌개설은 할 수 없음\n<은행계좌 계정과목>\n변경 전\n증권저축계좌와 연계 가능한 은행의 계정과목\n보통예금, 저축예금, MMDA ,(하나빅팟CMA는 기업자유예금도 포함) 기존계좌에 대한 증권계좌 신규개설\n신규계좌 및 이미 개설된 요구불계좌에 대해서도 증권사의 증권계좌를\n개설 할 수 있음\n변경 후\n증권저축계좌와 연계 가능한 은행의 계정과목\n보통예금, 저축예금, MMDA ,(하나빅팟CMA( 2023.10.27 신규가입중단)는\n기업자유예금도 포함)\n기존계좌에 대한 증권계좌 신규개설\n신규계좌 및 이미 개설된 요구불계좌에 대해서도 증권사의 증권계좌를\n개설 할 수 있음\n<가입상품의 종류>\n변경 전\n국내현물, 해외현물, 국내선물옵션, 해외F/X, 수익증권, 하나빅팟CMA 단,\n미래에셋증권, 신한투자증권은 국내현물계좌에서 수익증권 매매가 가능함\n변경 후\n국내현물, 해외현물, 국내선물옵션, 해외F/X, 수익증권,\n하나빅팟CMA(2023.10.27 신규가입중단) 단, 미래에셋증권, 신한투자증권은\n국내현물계좌에서 수익증권 매매가 가능함","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (156, 3, '청년내일저축계좌', '{"상품특징":"보건복지부 『자산형성지원 통장사업(운영지침)』에서 정한 대상자가 자산을 형성할 수 있도록 적립금 납입시 정부지원금을 지원하는 적립식 상품","가입대상":"“운영지침”에 따라 지방자치단체가 은행에 가입 승인을 통지한 실명의 개인","가입기간":"3년(군입대특례자의 경우 5년)","가입금액":"매월 10만원 이상 ~ 50만원 이하 (만원 단위)","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"자유적립식","금리":"기준일자 : 2022-07-08\n청년내일저축계좌\n기간 금리(연율,세전)\n3년 2.00%","우대금리":"최대 연 3.00% (2025.07.01 기준, 세전)\n아래의 우대항목을 충족한 경우, 최대 연 3.00%의 우대금리를 만기해지시 제공합니다. (단, 중도해지시 미제공)\n우대항목 내용\n급여 및 주거래이체\n(연 1.20%) 이 예금가입후 만기 전전월말 기준, 본인명의 하나은행 입출금 통장을 통해 12회차 이상 급여입금 (주1) (또는 가맹점대금 입금) 및 주거래 이체 (주2) 실적을 같은 월에 모두 보유한 경우 (월1회 인정)\n※ 1회차 인정요건 : 급여입금 (또는 가맹점대금 입금) + 주거래이체\n\n※ 예시\n(’22년 4월) 급여 입금 + 아파트 관리비 출금: 1회차 인정\n(’22년 5월) 가맹점대금 입금 + 신용(체크)카드 결제대금 출금 : 1회차 인정\n(’22년 7월) 급여 미입금 + 공과금 출금 : 1회차 미인정\n주택청약종합저축\n(연 1.00%) 아래의 ① 또는 ②에 해당하는 경우 우대금리 제공\n\n① 당행 주택청약종합저축 신규 또는 보유 손님\n: 이 예금 가입일로부터 3개월이 되는 달의 말일기준 당행 ‘주택청약종합저축’을 신규 또는 보유하고 만기 시점까지 유지한 경우\n\n② 타행 주택청약종합저축 보유 확인 손님\n: 이 예금 가입일까지 ‘하나원큐(스마트폰뱅킹)’를 통하여 오픈뱅킹서비스를 가입하고, 주택청약종합저축을 등록·조회한 경우\n\n※ 오픈뱅킹서비스 등록·조회방법 : 하나원큐(스마트폰뱅킹)로그인→오픈뱅킹서비스 신규가입→기관추가 및 해당상품 등록→하나원큐내 ‘전계좌조회_오픈뱅킹’ 을 통해 정상조회시 완료\n마케팅동의\n(연 0.50%) 이 예금 가입전 하나은행 상품·서비스 마케팅 동의 항목 모두를 동의한 경우\n하나 합 서비스 (주3)\n(연 0.30%) 이 예금 가입일 기준 익월말까지 『하나 합 서비스』 가입 및 하나은행외 기관을 1개이상 연결하고 만기전전월말에 유지한 경우\n(주1) 급여이체 인정기준\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n(주2) 주거래이체 인정기준 : 하나카드 결제(신용/체크), 아파트관리비 자동이체 출금, 공과금 자동이체 출금\n(주3) 하나 합 서비스 : 여러 기관에 흩어진 내 자산·지출 정보를 한눈에 조회하고 관리할 수 있는 마이데이터 기반 하나은행자산관리 서비스를 말하며, 기관이라 함은 신용정보법상의 이용자의 전송요구에 따라 개인신용정보를 제공하는 은행, 카드사, 보험사, 증권사 등을 의미합니다. 하나합서비스는 하나원큐(스마트폰뱅킹)에서 가입 및 기관연결이 가능합니다.","정부지원금":"자산형성지원을 위한 정부 지원금으로 근로소득장려금 및 정책대상별 추가지원금 등이 있으며, 지원내용(지원조건, 지원금액, 지급방법 등)은 보건복지부 『자산형성지원 통장사업(운영지침)』에서 정하는 바에 따릅니다.\n이 예금 신규시 지정한 지원금 수령계좌로 입금됩니다.","적립중지":"실직, 본인 및 부양가족의 질병·사고 등 부득이한 사유가 있는 경우 지방자치단체에 적립 중지를 신청할 수 있으며, 지방자치단체가 은행에 적립중지를 승인 통지한 경우 적립금은 입금제한등록 됩니다.\n적립 중지 기간 중 정부지원금 미지원 됩니다.","수수료 우대":"당행의 입출금통장에서 이 예금으로 매월 자동이체를 통하여 입금하는 경우, 익월 1일부터 말일까지 아래의 수수료우대서비스를 제공합니다.\n1. 당행자동화기기, 인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행이체수수료 (월 10회 면제)\n2. 당행자동화기기를 통한 현금인출 수수료 (월 10회 면제)\n※ 이 예금의 신규 가입후 다음달 말일까지는 자동이체 등록여부와 관계없이 수수료 우대서비스 제공  \n※ 이 예금으로 자동이체 출금 계좌 등록한 입출금통장을 통한 거래에서 발생하는 수수료에 한하여 제공","세제혜택":"비과세종합저축 가입 가능 (전 금융기관 통합한도 범위 내) 단, 만기 후 이자는 소득세 부과\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됨","유의사항":"정부지원금에 관한 세부내용(지원조건, 지원금액, 지급방법 등)은 보건복지부 『자산형성지원 통장사업 (운영지침)』에서 정하는 바에 따릅니다.\n정부지원금은 신규시 지정한 지원금 수령계좌로 입금됩니다.\n계좌의 해지는 지방자치단체 (‘운영지침’에서 명시한 기관)등에 사전 신청 후, 은행 앞 해지 승인이 통지된 경우에만 가능 합니다.\n적립 한도는 당월 적립 인정기간 內에 최소 10만원이상 최대 50만원이내입니다.\n【당월 (적립월) 인정기간 : 전월 23일(휴일인 경우 익영업일)~당월22일(휴일인 경우 익영업일)】\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및\n이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한 (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용\n변경에 관한 사항\n(2025.07.01 변경) ■ 기존 가입고객에 대한 적용 여부 : 적용\n<급여인정기준>\n변경 전\n타인으로부터 건당 50만원 이상 입금 (급여적요 포함 입금 또는 급여지정일 전후 1영업일내 입금)\n(급여 적요: 급여, 월급, 봉급, 연봉, 급료, 상여, 성과, 보너스, 월보수, SALARY, PAY, BONUS 등)\n변경 후\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"3년","금리(연율,세전)":"2.0%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (157, 3, '청년 주택드림 청약통장', '{"상품특징":"가입자격 및 무주택 조건 등을 충족하는 경우 최대 4.5% 이율 제공\n이자소득 500만원까지, 연간불입액 600만원 한도로 비과세혜택 적용\n무주택세대주인 근로소득자라면 최대 120만원 소득공제","가입대상":"만 19세 ~ 만 34세 이하의 무주택 거주자로서 다음 1), 2) 중 어느 하나에 해당하는 자\n1) 연소득 5천만원 이하 신고소득(근로,사업,기타) 이 있는 자\n2) 직전 과세기간(직전 과세기간의 소득서류가 확정되지 않은 기간에 가입하는 경우 전전년도 포함)\n비과세 소득만 있는 자 중 다음 중 어느 하나에 해당하는 자\n① 현역병, 상근예비역, 의무경찰, 해양의무경찰, 의무소방원, 사회복무요원, 대체복무요원 등(이하 “현역병 등”이라 한다)\n② 현역병 등으로서 복무를 마친 자. 단, 직전 과세기간 중 복무한 기간이 1일 이상 반드시 포함되어 있어야 함","준비서류":"실명확인증표, 소득증빙서류\n※ 가입연령 차감을 위한 병역 이행 확인 서류\n- 병적증명서(현역병 등, 현역병 등으로서 복무를 마친 자)\n- 군경력 증명서(현역 간부(장교/부사관 등))\n※ 재직기간 및 재직여부는 별도 확인하지 않음\n※ 가입 시 소득, 무주택 관련 서류의 사실관계에 대한 고객의 확인서를 징구함\n[소득유형별 신규시 제출서류]\n구분 내용\n기본 서류 ∙ 소득확인증명서 (청년우대형주택청약종합저축 가입 및 과세특례 신청용)\n대체 서류 근로 소득자 상반기 가입시 직전년도에 최초로 소득이 발생하고, 그 소득이 확정되지 않아 소득확인증명서 발급 불가한 경우 당해연도에 최초로 소득이 발생하여 신고소득서류가 없고 근로기간 1년 미만 근로소득자인 경우\n∙ 근로소득원천징수영수증 ∙ 급여명세표\n기타 소득자 상반기 가입시 직전년도에 최초로 소득이 발생하고, 그 소득이 확정되지 않아 소득확인증명서 발급 불가한 경우\n∙ 기타소득원천징수영수증\n현역병 등 및 전역자 병적증명서 및 ''신고사실없음 사실증명''\n[가입시 소득증빙 서류 제출 기준]\n순번 소득발생 시점 가입시점 가입 시 소득증빙 서류\n전전년도 직전년도 당해년도 당해년도\n1 О О О 또는 X 상반기 전전년도 소득확인증명서\n2 О О О 또는 X 하반기 직전년도 소득확인증명서\n3 X О О 또는 X 상반기 직전년도 원천징수영수증\n4 X О О 또는 X 하반기 직전년도 소득확인증명서\n5 X X О 상반기 당해년도 급여명세표\n6 X X О 하반기 당해년도 급여명세표\n7 О X X 상반기 전전년도 소득확인증명서\n※ \n급여명세표의 경우 근로소득원천징수부, 임금대장, 갑근세원천징수확인서, 그 외 소정 양식의 출력물로서 회사의 직인이\n찍힌 것은 인정한다.\n※ \n직전과세기간의 신고소득이 없고 소득증빙기간이 1년 미만인 근로소득이며 소득기간이 표시된 경우에는\n(총 수입금액 ÷ 소득발생 개월 수) × 12로 연환산하고, 기간의 표시가 없는 경우에는 총액으로 한다.\n※ \n현역병 등은 병적증명서를 소속 부대에서 발급한 군복무 확인서로, 사관생도는 병적증명서를 사관학교 재학증명서로\n갈음할 수 있다.","납입금액":"매월 정해진 날짜 신규일에 해당하는 날에 2만원부터 100만원 이하의 금액을 자유롭게 납입\n※ \n단, 잔액이 1,500만원 미만인 경우 1,500만원까지 일시예치 가능\n잔액 1,500만원 이상인 경우 100만원 이내에서 자유적립\n※ \n「청년희망적금」, 「청년도약계좌」, 「장병내일준비적금」, 「디딤씨앗통장」, 「청년내일저축계좌」 만기수령금을 「청년 주택드림 청약통장」에 일시납 하는 경우, 만기수령금을 포함한 월 납입금 총액이 1,500만원을 초과하더라도 5,000만원 이내에서 일시납 가능(영업점에서만 가능)","저축기간":"가입일로부터 해지일까지\n※ 단, 주택청약 당첨 이후 납입하는 경우에는 납입회차, 납입금액, 납입기간 인정되지 않음","금리":"금리안내\n※ 변동금리로서 정부 고시에 의하여 변경될 수 있으며, 이율이 변경되는 경우 변경일 기준으로 변경 후 이율 적용\n○ 「청년 주택드림 청약통장」 이율 한도 및 적용기간\n가입기간이 신규가입일로부터 2년 이상인 경우(단, 청약당첨으로 인한 해지인 경우 2년 미경과라도 적용)에 한하여 적용\n납입원금 5천만원 한도 내(단, 전환신규한 경우 전환원금 제외)에서 가입 후 10년 내에 납입한 원금에 대한 이율은 무주택 기간에 한하여 최대 4.5%의 이율을 적용함.\n※ 단, 가입 당시 주택을 소유하고 있었던 경우에는 가입 기간 전체에 대하여 주택을 소유하고 있었던 것으로 보며, 가입 기간 중 주택을 취득한 경우 가입일로부터 주택 취득일이 속한 연도의 직전년도 말일까지를 무주택 기간으로 봄","비과세":"비과세 한도\n이자소득의 합계액 500만원 한도, 연간 납입액 총600만원 한도\n비과세 요건 (대상자)\n가입 당시 무주택세대의 세대주 또는 그 세대주의 배우자로서 다음 중 어느 하나에 해당 될 것\n1. 직전 과세기간의 총급여액이 3천6백만원 이하인 근로소득자\n2. 직전 과세기간의 종합소득금액이 2천 6백만원 이하인 자\n단, 직전 과세기간의 소득을 확인하기 어려운 경우 전전 과세기간의 소득으로 판단\n가입기간이 2년 이상 일것\n※ 단, 조특법상 특별해지사유에 해당시 가입기간 2년 이내라도 비과세 가능\n비과세 신청시 제출서류\n무주택 확인서 (영업점 비치)\n소득확인증명서(청년우대형주택청약종합저축 가입 및 과세특례 신청용)\n비과세 신청자용 각서 및 정보제공동의서(영업점 비치) * 본인 및 세대분리된 배우자(해당 시)가 작성\n가입일 현재 세대주 또는 세대주의 배우자임을 입증할 수 있는 ‘주민등록등본’ (최근 3개월 이내 발급분)\n* 비과세 신청자가 세대주의 배우자로서 세대분리된 경우는 비과세 신청자의 배우자(세대주) 기준으로 발급\n가족관계증명서(세대분리된 배우자가 있는 경우에 한함)\n병적증명서(현역병 등 또는 전역자 경우에 한함)\n※ 위 서류는 가입시 제출함이 원칙이나, 부득이 제출하지 못한 경우 가입후 2년이내(해지전)제출하여야 합니다.\n※ 비과세종합저축으로 가입 불가\n※ 조세특례제한법의 개정(2021.1.1시행)에 따라 계좌의 가입일이 속한 과세기간의 직전 3개 과세기간 중 1회 이상 소득세법 제14조 제3항 제6호에 따른 소득의 합계액이 종합과세기준금액을 초과시 일반과세로 전환되거나 비과세 소득에 대하여 추징될 수 있습니다.","주택청약 당첨시 일부인출":"기존 주택청약종합저축을 보유한 고객이 가입조건을 갖춘 경우 전환 가능\n※ 기존 주택청약종합저축의 해지당일 전환신규만 가능하며, 기존 계좌의 이자는 별도 지급하고, 원금은 신규 통장에 전액 예치되어야 함. 전환원금 입금 분은 우대금리 적용 및 납입인정횟수에서 제외됨\n※ 기존 주택청약종합저축의 납입인정회차, 납입금액, 가입기간은 기존 주택청약종합저축에서 연속하여 인정되며, 선납 및 미납회차는 인정되지 않으며 소멸함\n※ 약정납입일은 전환신규일로 변경됨\n※「청년우대형 주택청약종합저축」가입자는 모두 「 청년 주택드림 청약통장」으로 전환됨","청약통장 순위자격":"가입자가 주택청약에 당첨된 경우, 1회에 한하여 청약 당첨 주택의 계약금 납부목적으로 일부 금액 인출 가능 (영업점 방문 필요)","소득공제":"청년 주택드림 청약통장에 가입하기 위해 기존 주택청약종합저축을 해지한 경우, 해지 계좌의 청약순위 자격을 인정","유의사항":"연간 납입액(최고 300만원)의 40% (최대 공제한도 120만원)\n대상 : 과세기간 총 급여액이 7천만원 이하 근로자인 무주택세대의 세대주 또는 그 세대주의 배우자\n※ 전환신규 한 경우 기존 주택청약종합저축 포함 당해 납입분만 소득공제 대상\n※ 소득공제는 소득공제신청서(무주택확인서)를 제출한 과세연도의 납입금액부터 인정 됨\n※ 주택청약종합저축 가입일로부터 5년 이내 해지(사망, 해외이주, 당첨 해지 등 제외) 또는 전용면적 85㎡ 초과 주택에 당첨으로 인한 해지 시 소득공제 받은 세액 전액이 추징 됨","상품내용\n변경에 관한\n사항":"국민주택에 청약하는 경우 납입회차별 입금액이 25만원 이상일 경우 최대 25만원까지만 인정되며 그 이상의 금액은 예치금으로 처리됩니다. (단, 약정납입일이 2024,10.31 이전인 회차에 대해서는 최대 10만원까지 인정)\n해지 시 주택소유 및 취득시기 확인을 위해 해지일 기준 최근3개월내 발급한 직전년도까지의 ‘지방세 세목별 과세증명서’ (전국단위) 를 제출하여야 합니다. 미제출 시 우대금리 적용과 비과세 혜택을 받을 수 없습니다.\n상기 내용은 2024년 11월 1일 기준으로 작성되었으며, 추후 정부시책, 기금운용계획에 따라 변경될 수 있습니다.\n이 금융상품은 예금자보호법에 따라 보호되지 않으나, 주택도시기금의 조성재원으로 정부가 관리하고 있습니다.\n주택청약종합저축의 모든 업무내용은 법령인 『주택공급에 관한 규칙』 이 우선적으로 적용됩니다. 자세한 사항은 상품설명서를 참조하시거나 영업점 직원에게 문의하시기 바랍니다\n소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.\n가입대상은 국민인 개인 또는 외국인 거주자이며, 가입금액은 2만원이상 1500만원 이하(원단위) 입니다.\n이자지급 방식은 만기일시지급식으로 만기해지시 이자를 지급합니다.\n납입기간은 가입일로부터 입주자로 선정된 날까지 이며, 매월 정해진 날짜에 2만원 이상 100만원이하의 금액을 자유롭게 납입 가능합니다.\n적용이율은 변동금리로서 국토교통부 고시에 의하여 변경될 수 있으며 금리 변경시 각 납부회차별 변경일 기준으로 기존 가입고객을 포함하여 변경 후 금리로 적용합니다.\n계좌에 압류,가압류,질권설정등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n민사집행법에 따라 최저생계비이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권범위 변경신청 등을 통해 압류를 취소할 수 있습니다.","예금자 보호":"상품내용 변경에\n관한 사항\n(2025.07.31 변경)\n■ 기존 가입 고객에 대한 적용여부 : 적용\n<일시납 허용 상품 추가>\n변경 전 : 「청년희망적금」, 「청년도약계좌」, 「장병내일준비적금」\n변경 후 : 「청년희망적금」, 「청년도약계좌」, 「장병내일준비적금」, 「디딤씨앗통장」, 「청년내일저축계좌」\n<무주택 판단 기준 제정>\n가입자격 판단 및 이자율 산정 시 적용","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (158, 3, '쿠팡 셀러 통장', '{"상품특징":"제휴사의 특정서비스 등록 및 필수요건 충족시 우대 서비스를 제공하는 통장","가입대상":"쿠팡페이㈜의 연계·제휴서비스를 이용하는 법인 및 개인사업자(1인 1계좌만 가능)","예금과목":"기업자유예금","가입방법":"쿠팡페이㈜의 웹 또는 앱페이지에 연계된 『하나은행 비대면계좌개설서비스』를 통해서 가입\n(단, 『하나은행 비대면계좌개설서비스』를 통한 가입이 제한되는 경우 영업점에서 가입 가능)","전환여부":"불가 (다른 예금에서 이 예금으로 전환 또는 이 예금에서 다른 예금으로 전환은 불가)","금리":"금액: 5천만원 미만, 금리(연율,세전): 0.1%\n금액: 5천만원 이상, 금리(연율,세전): 0.1%","우대서비스":"이 예금을 통해 필수요건 충족시 우대서비스 제공\n【필수요건】 쿠팡페이 셀러월렛 서비스를 이용하는 경우\n【우대 서비스 내용】\n당행 자동화기기를 통한 현금인출 수수료 무제한 면제\n당행 자동화기기를 통한 타행이체 수수료 무제한 면제\n인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS), 기업인터넷뱅킹, 기업스마트폰뱅킹을 통한 타행이체 수수료 무제한 면제\n납부자자동이체(타행자동이체) 수수료 무제한 면제\n국외 당발송금 송금수수료 월5회 면제\n창구에서 타행이체 수수료 월 10회 면제\n비대면 채널을 통한 외국통화 입/지급 거래시 USD 80%, JPY 및 EUR 50% 환율(Spread)우대\n(단, 해외 당/타발송금거래, 타행 당/타발송금거래, 본인명의 당행 계좌간 거래에 한함)\n※쿠팡페이 셀러월렛 서비스를 이용하지 않는 경우(서비스 가입 해지 등) 이 통장으로 *e커머스사 일반정산금(매출대금) 입금시(직전 3개월중 **2개월 이상) 우대서비스 제공\n*e커머스사 : 쿠팡, G마켓, 옥션, 11번가, 인터파크(OM/MD), 네이버(쇼핑, 스마트스토어), SSG닷컴, 카카오(카카오톡쇼핑하기, 카카오톡선물하기, 카카오스타일), 롯데ON, 티몬, 위메프(위메프, 위메프 2.0)\n**2개월 이상은 매월 연속충족 조건이 아님\n※최초 가입일부터 익익월까지 필수요건에 상관없이 우대서비스 제공\n※가입일 익익월 경과후 직전3개월간 필수요건 충족여부 확인하여 당월 우대서비스 제공\n※필수요건 미충족하는 경우 별도 통보 없이 해당월은 수수료 우대서비스를 제공하지 않음\n※이 통장을 통해 발생하는 거래에 한하여 우대서비스 제공","연계·제휴 서비스":"구   분 내                    용\n연계·제휴\n서비스 내용\n【쿠팡페이 셀러월렛 서비스 주요 내용】\n쿠팡셀러의 정산예정금 조회\n쿠팡페이 셀러월렛 서비스 내에서 이 예금의 잔액 및 거래내역 조회, 이체 서비스 제공\n전용 체크카드 신청(하나카드)\n선정산 서비스 : 정산대금 입금주기 도래 전 셀러가 전용 체크카드를 사용시, 쿠팡페이㈜의 정산예정금을 쿠팡페이㈜가 셀러계좌에 입금(자동이체) 함으로써 체크카드가 결제되는 빠른 정산 서비스\n기타 셀러 편의, 정보 제공\n연계·제휴\n서비스\n이행책임\n하나은행과 쿠팡페이㈜는 연계∙제휴서비스 등을 부당하게 축소하거나 변경하지 않으며(*선정산 서비스는 쿠팡페이 셀러월렛 서비스 이용조건에 의하여 쿠팡페이가 제공하며, 쿠팡페이㈜에 의한 이용조건의 합리적인 구체화 또는 적용은 축소변경이 아닌 것으로 봅니다) 불가피하게 축소하거나 변경하는 경우 그에 상응하는 다른 연계·제휴서비스를 제공합니다. 단, 그에 상응하는 다른 연계·제휴서비스를 제공하거나 휴업·파산·경영상의 위기 등에 따른 불가피한 경우에는 연계·제휴서비스를 축소하거나 변경할 수 있습니다.\n*합리적인 구체화 또는 적용사항은 쿠팡페이 웹 또는 앱페이지에 고시\n연계·제휴\n서비스\n제공기간\n‘쿠팡페이 셀러월렛 서비스’를 등록하여 ‘쿠팡 셀러 통장’으로 이용시에만 제공합니다.\n(단, 제휴사와 은행의 계약기간 또는 혁신금융서비스 승인 유효기간(연장기간 포함) 내에 한함)\n연계·제휴\n서비스\n변경내용 사전\n통지방법\n서면, 우편, 팩스, 전화, E-mail, LMS등의 방법 중 2개 이상의 방법으로 축소·변경 6개월전부터 매월 고지합니다. 단, 휴업·파산·경영상의 위기 또는 연계·제휴서비스를 제공하는 자의 일방적인 중단 등 6개월 전부터 고지하기 어려운 불가피한 사유가 있는 경우에는 해당 상황이 발생하는 즉시 고시할 수 있습니다.\n연계·제휴\n서비스\n제공요건\n‘쿠팡페이 셀러월렛 서비스’ 이용시\n제휴사앞 개인(신용)정보 수집·이용 및 제공 동의를 한 경우","혁신금융서비스 승인에 관한사항":"구   분\n''쿠팡페이 셀러월렛 서비스''는 금융혁신지원특별법에 따라 혁신금융사업자로 선정된 하나은행과 쿠팡페이㈜가 시험운영하여 제공하는 혁신금융 서비스로 동 특별법이 정하는 다음과 같은 사유가 발생하는 경우 서비스 운영이 종료 될 수 있습니다.\n지정기간이 만료되는 경우\n금융위원회가 혁신금융서비스 지정을 취소하거나, 중지명령(변경결정)을 하는 경우\n하나은행과 쿠팡페이㈜가 금융위원회에 혁신금융서비스 지정철회를 요청하여 지정취소가 되는 경우\n합병 등 조직변경으로 혁신금융서비스 지정의 효력이 상실되는 경우\n''쿠팡페이 셀러월렛 서비스''는 혁신금융서비스로 선정되어 시험운영하는 서비스이며, 그로 인해 예상하지 못한 위험이 발생할 수 있습니다. 이용자에게 통장개설 전 혁신금융서비스 고지사항을 안내하고, 이용자는 동 내용에 대해 동의 후 서비스를 이용할 수 있습니다. 다만, 해당조치는 하나은행과 쿠팡페이㈜가 이용자에 대해 부담하는 법률적 책임에 영향을 미치지 않습니다.\n하나은행과 쿠팡페이㈜는 혁신금융서비스의 제공 및 중단 등으로 인하여 이용자에게 손해가 발생한 경우 고의 또는 과실이 없음을 입증하지 못하는 한 이를 배상할 책임이 있으며, 하나은행과 쿠팡페이㈜가 책임과 관련하여 금융혁신 지원특별법, 전자금융거래법, 은행법 등 다른 법령에 이용자에게 유리하게 적용될 수 있는 규정이 있는 경우에는 그 법령이 우선 적용됩니다.","세제혜택":"비과세 종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됨","유의사항":"우대서비스 제공은 ‘필수요건 충족시’ 에만 적용됩니다.\n우대서비스 내용 및 대상 요건은 변경 가능하며, 변경 시 재고시합니다.\n이 통장가입절차 및 ''셀러월렛 서비스''는 금융혁신지원특별법에서 정하는 혁신금융서비스 지정에 관한 내용을 반영합니다.\n이 예금은 하나은행 기업플랫폼사업부에서 개발한 금융상품입니다. 상품문의는 영업점 또는 고객센터(1599-1111,0,1)로 문의하시기 바랍니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있습니다\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가합니다.\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용 계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있습니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 영업점, 고객센터(1599-1111,0,1), 고객의 소리(080-933-1111), 또는 인터넷 홈페이지(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호 상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"5천만원 미만","금리(연율,세전)":"0.1%"},{"금액":"5천만원 이상","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (159, 3, '하나 공무원연금 평생안심통장', '{"상품특징":"공무원연금 수급자의 기본적인 연금수급권리를 보호하기 위한 압류방지 전용통장","가입대상":"실명의 개인 또는 개인사업자로 공무원연금을 수령하는 고객","예금종류":"저축예금","이자지급방법":"※ 저축예금의 결산방법을 따름","금리":"금액: 공무원연금 수령 전용통장, 금리(연율,세전): 0.5%","수수료 우대":"이 예금으로 매월 말일자 기준으로 당월에 공무원연금 급여가 입금된 경우, 익월 1일부터 말일까지 1개월간 이 예금을 통한 아래의 거래에 대하여 우대서비스를 제공합니다.\n① 당행 자동화기기를 통한 마감 후 현금인출 및 타행 이체수수료 면제\n② 폰뱅킹(ARS), 인터넷뱅킹, 스마트폰뱅킹을 통한 타행 이체수수료 면제\n③ 납부자 자동이체(타행자동이체 포함) 수수료 면제\n④ 환전, 송금 시 환율 스프레드 최고 50% 우대\n입금제한\n① 이 예금으로의 입금은 공무원연금 수령액으로 공무원연금법 제39조 제2항, 민사집행법 시행령 제2조(이하 “관련 법령”이라함)에서 정하는 공무원연금 수급자 보호금액(이하 “보호금액”이라함)까지만 입금이 가능합니다. 단, 통장의 잔액은 금액에 제한없이 유지가 가능하며 입금 제한에 영향을 미치지 않습니다.\n② 매월 입금되는 공무원연금 급여액이 제1항의 보호금액을 초과하는 경우에는 보호금액까지만 이 예금에 입금되며, 초과한 금액은 입금이 제한되지 않는 별도의 통장으로 수령해야 합니다.\n③ 관련 법령에서 정하는 보호금액이 변경될 경우 입금이 가능한 금액도 이에 맞추어 변경됩니다.\n거래제한\n① 이 예금은 관련 법령에 따라 입금된 예금과 이에 관한 채권을 압류할 수 없고, 압류에 의한 지급제한이 원칙적으로 되지 않습니다.\n② 이 예금은 은행이 상계할 수 없습니다.\n③ 이 예금은 양도 및 담보제공 할 수 없습니다.\n④ 이 예금은 통장대출(한도대출)의 대출계좌(기본계좌)로 사용할 수 없습니다.\n⑤ 이 예금을 신용카드 결제계좌나 공과금 등 대금납부 목적의 계좌로 지정한 경우에, 결제금액 또는 납부금액이 부족하거나 결제취소 또는 납부취소 등이 발생한 때에는 제7조의 입금제한(입금은 공무원연금 급여에 한한다.)에 따라 예금주가 직접 해당기관에 결제금액 또는 납부금액을 납부하거나(또는 해당기관이 지정한 계좌로 송금) 예금주가 해당기관으로부터 직접 수령하는 방법으로 처리하여야 합니다.","세제혜택":"전 금융사 통합한도 내에서 비과세 저축으로 가입가능","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<위법계약해지권 항목 신설>\n기존 가입 고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (160, 3, '하나 군인연금 평생안심통장', '{"상품특징":"군인연금 수급자의 기본적인 연금수급권리를 보호하기 위한 압류방지통장","가입대상":"실명의 개인 또는 개인사업자","예금종류":"저축예금","이자지급방법":"저축예금의 결산방법을 따름","금리":"분류: -, 금리(연율,세전): 0.1%","수수료 우대":"이 통장으로 매월 말일자 기준으로 당월에 군인연금 급여가 입금된 경우, 익월 1일부터 말일까지 제공\n① 당행 자동화기기를 통한 마감 후 현금인출 및 타행 이체수수료 면제\n② 폰뱅킹(ARS), 인터넷뱅킹, 스마트폰뱅킹을 통한 타행 이체수수료 면제\n③ 납부자 자동이체(타행 자동이체 포함)수수료 면제\n입금제한\n① 이 예금으로의 입금은 군인연금 수령액으로 군인연금법 제7조, 민사집행법 시행령 제2조(이하 “관련 법령”이라함)에서 정하는 군인연금 수급자 보호금액(이하 “보호금액”이라함)까지만 입금이 가능합니다.\n단, 통장의 잔액은 금액에 제한없이 유지가 가능하며 입금 제한에 영향을 미치지 않습니다.\n② 매월 입금되는 군인연금 급여액이 보호금액을 초과하는 경우에는 보호금액까지만 이 통장으로 입금되며, 초과한 금액은\n입금이 제한되지 않는 별도의 통장으로 수령해야 합니다.\n③ 관련 법령에서 정하는 보호금액이 변경될 경우 입금이 가능한 금액도 이에 맞추어 변경됩니다.\n거래제한\n① 이 예금은 관련 법령에 따라 입금된 예금과 이에 관한 채권을 압류할 수 없고, 압류에 의한 지급제한이 원칙적으로\n되지 않습니다.\n② 이 예금은 은행이 상계할 수 없습니다.\n③ 이 예금은 양도 및 담보제공 할 수 없습니다.\n④ 이 예금은 통장대출(한도대출)의 대출계좌(기본계좌)로 사용할 수 없습니다.\n⑤ 이 예금을 신용카드 결제계좌나 공과금 등 대금납부 목적의 계좌로 지정한 경우에, 결제금액 또는 납부금액이 부족하거나\n결제취소 또는 납부취소 등이 발생한 때에는 입금제한(입금은 군인연금 급여에 한한다.)에 따라 예금주가 직접 해당기관에\n결제금액 또는 납부금액을 납부하거나(또는 해당기관이 지정한 계좌로 송금) 예금주가 해당기관으로부터 직접 수령하는\n방법으로 처리하여야 합니다.\n⑥ 다른 예금에서 이 예금으로 상품전환 또는 이 예금에서 다른 예금으로 상품전환은 불가능합니다.","세제혜택":"전 금융사 통합한도 내에서 비과세 저축으로 가입가능","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<위법계약해지권 항목 신설>\n기존 가입 고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["분류","금리(연율,세전)"],"데이터":[{"분류":"-","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (161, 3, '하나 국민연금 안심(安心)통장', '{"가입대상":"실명의 개인 또는 개인사업자로 국민연금을 수령하는 고객","예금종류":"저축예금","금리":"금액: 국민연금 수령 전용통장, 금리(연율,세전): 0.5%","수수료 우대":"구분 항목 내용\n당월(매월 말일 기준)에 이 통장으로 국민연금 급여가 입금되고, 이 통장을 통하여 거래가 이루어지는 경우 당행 자동화기기 마감후 현금인출 면제\n(익월 1일부터 말일 까지\n1개월간 혜택 제공)\n당행 자동화기기 타행이체\n당행 폰뱅킹(ARS) 타행이체\n당행 인터넷뱅킹 타행이체\n당행 스마트폰뱅킹 타행이체\n납부자 자동이체\n(타행자동이체 포함)\n환율우대 환전, 송금 시 환율\n스프레드 최고 50% 우대","세제혜택":"비과세 종합저축 가능(전 금융기관 통합한도 범위 내)","기타":"입금제한\n이 예금은 국민연금 수령액 중 국민연금법 제54조의 2, 제58조, 동법 시행령 제44조, 민사집행법 시행령 제2조(이하 “관련 법령”이라 함)에서 정하는 압류금지금액(이하 “보호금액”이라 함)까지만 입금이 가능함.(단, 통장의 잔액은 금액에 제한없이 유지가 가능하며, 입금 제한에 영향을 미치지 않음)\n국민연금 급여액이 제1항의 보호금액을 초과하는 경우에는 보호금액까지만 이 통장으로 입금되며, 보호금액을 초과하는 금액은 입금이 제한되지 않는 별도의 통장으로 수령해야 함.(관련 법령에서 정하는 보호금액이 변경될 경우 입금이 가능한 금액도 이에 맞추어 변경됨)\n거래제한\n이 예금은 관련 법령에 따라 법원의 압류명령에 의한 지급제한이 원칙적으로 되지 않음.\n이 예금은 은행이 상계할 수 없음.\n이 예금은 양도 및 담보로 제공할 수 없음.\n이 예금은 통장대출(한도대출)의 대출계좌(기본계좌)로 사용할 수 없음.\n이 예금을 신용카드 결제계좌나 공과금 등 대금납부 목적의 계좌로 지정한 경우에, 결제금액 또는 납부금액이 부족하거나 결제취소 또는 납부취소 등이 발생한 때에는 입금제한(입금은 국민연금 급여에 한함)에 따라 예금주가 직접 해당기관에 결제금액 또는 납부금액을 납부하거나 직접 수령하는 방법으로 처리하여야 함.","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<위법계약해지권 항목 신설>\n기존 가입 고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (162, 3, '하나 빌리언달러 통장', '{"상품특징":"하나원큐 기업(스마트폰뱅킹)에서 통장 개설, 계좌 관리, FX, 해외송금까지 가능한 수출입기업 전용 다통화외화예금\n당발송금, 타발송금 수수료 월 1회면제\n신용장 통지 수수료, L/G 발급 수수료 면제","가입대상":"법인 및 개인사업자","예금종류":"외화 입출금 통장","가입금액":"제한없음","이자지급방법":"이 예금의 이자지급은 외화보통예금의 이자지급 방법을 따르며, 이자율은 해당 통화별 외화보통예금 고시 금리를 따릅니다. 외화보통예금 이자지급방법은 매년 3,6,9,12월 셋째주 금요일(단, 공휴일인 경우 직전 영업일)에 셈하여 익일(이하 ‘원가일’이라 한다)에 원금에 더합니다.\n이 예금의 이자는 최초 예금일 또는 원가일부터 원가일 또는 지급일 전날까지의 기간 동안 매일의 최종잔액에 평균하여 은행이 영업점 및 인터넷 홈페이지에 고시한 통화별 외화보통예금 이율을 적용하여 계산합니다.","금리":"금리안내\n가입당시 영업점 또는 홈페이지에 고시된 금리 적용","예치통화":"아래의 28개국 통화 중 최대 10개까지 예치 가능합니다.\n예치 가능통화: USD, EUR, JPY, GBP, CHF, CAD, AUD, NZD, HKD, SGD, DKK, NOK, SEK, THB, IDR, SAR, AED, KWD, BHD, RUB, ZAR, HUF, PLN, MXN, CNY, TRY, CZK, RON","우대서비스":"① 국외 당발송금 수수료 월 1회 면제\n② 국외 타발송금 수수료 월 1회 면제\n③ 수출신용장 통지 수수료 면제\n④ 수입화물선취보증서 발급 수수료 면제\n국외 당발, 타발 송금수수료는 송금 원금을 하나 빌리언달러통장으로 거래 시 적용됩니다.\n수출신용장 통지 수수료와 수입화물선취보증서 발급 수수료는 횟수 제한없이 면제 됩니다.","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가입류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음(일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있음\n예금잔액증명서 발급 당일 잔액 변동 불가\n통장이 ‘전기통신금융가시 피해 방지 및 피해금 환급에 관한 특별법＇에서 정의한 피해의심거래 계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.02.07 변경)\n<예치통화>\n기존가입고객 적용여부 : 적용\n변경 전 : 27개 통화\n변경 후 : 28개 통화(기존 통화에 루마니아 레우 추가)","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (163, 3, '하나 주택연금 지킴이 통장', '{"상품특징":"주택담보 노후연금 수급자의 연금수급권리를 보호하기 위한 압류방지 전용통장","가입대상":"실명의 개인 또는 개인사업자로 주택담보 노후연금㈜ (이하 “주택연금”이라 함) 을 수령하고, “주택연금 전용계좌 이용대상 확인서＂를 제출하는 손님\n* ㈜ 하나은행의 주택담보 노후연금대출(주택연금 역모기지론) 에 의하여 입금되는 월지급금","예금종류":"저축예금","이자지급방법":"저축예금의 결산방법을 따름","금리":"금액: 5천만원 미만, 금리(연율,세전): 0.1%\n금액: 5천만원 이상, 금리(연율,세전): 0.1%","수수료 우대":"이 통장으로 매월 말일자 기준으로 당월에 주택연금이 입금된 경우, 익월 1일부터 말일까지 제공\n① 당행 자동화기기를 통한 마감 후 현금인출 및 타행이체 수수료 면제\n② 인터넷뱅킹, 폰뱅킹(ARS), 스마트폰뱅킹을 통한 타행이체 수수료 면제\n③ 납부자 자동이체(타행 자동이체 포함) 수수료 면제\n입금제한\n① 이 예금으로의 입금은 주택연금으로 한국주택금융공사법 제43조의 13, 민사집행법 제195조, 동법 시행령 제2조 (이하“관련 법령”이라함)에서 정하는 주택연금 수급자 보호금액 (이하 “보호금액＂이라 함) 까지만 입금이 가능합니다.\n주택연금 입금금액이 보호금액을 초과하는 경우에는 보호금액까지만 이 통장으로 입금되며, 초과한 금액은 입금이 제한되지 않는 별도의 통장으로 수령해야 합니다.\n단, 통장의 잔액은 금액에 제한없이 유지가 가능하며 입금 제한에 영향을 미치지 않습니다.\n② 관련법령에서 정하는 보호금액이 변경될 경우 입금이 가능한 금액도 이에 맞추어 변경됩니다.\n거래제한\n① 이 예금은 관련 법령에 따라 입금된 예금과 이에 관한 채권을 압류할 수 없고, 압류에 의한 지급제한이 원칙적으로 되지 않습니다.\n② 이 예금은 은행이 상계할 수 없습니다.\n③ 이 예금은 양도 및 담보제공 할 수 없습니다.\n④ 이 예금은 통장대출의 기본계좌 (마이너스 사용한도를 설정하는 계좌) 로 사용할 수 없습니다.\n⑤ 이 예금을 신용카드 결제계좌나 공과금 등 대금납부 목적의 계좌로 지정한 경우에, 결제금액 또는 납부금액이 부족하거나 결제취소 또는 납부취소 등이 발생한 때에는 입금제한(입금은 주택연금에 한함) 에 따라 예금주가 직접 해당기관에 결제금액 또는 납부금액을 납부하거나 (또는 해당기관이 지정한 계좌로 송금) 예금주가 해당기관으로부터 직접 수령하는 방법으로 처리하여야 합니다.\n⑥ 다른 예금에서 이 예금으로 상품전환 또는 이 예금에서 다른 예금으로 상품전환은 불가능합니다.\n⑦ 예금잔액증명서 발급 당일의 잔액 변동은 불가합니다.","세제혜택":"전 금융기관 통합한도 범위내에서 비과세 종합저축으로 가입가능합니다.","유의사항":"- 이 예금으로의 입금은 주택연금 수급자 보호금액 이하인 손님에 한하여 가능하며,주택연금 입금금액이 주택연금 수급자 보호금액을 초과하는 경우에는 입금이 제한되지 않는 별도의 통장으로 수령해야 합니다.\n- 주택연금 전용계좌 이용대상 확인서, 주택연금 보증상담 및 신청, 월지급금액산정 등 관련내용은 한국주택금융공사로 문의하여 주시기 바랍니다\n(관련문의 : 한국주택금융공사 TEL 1688-8114)\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.12.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<입금제한 내용 변경>\n변경 전 :\n① 이 예금으로의 입금은 주택연금으로 한국주택금융공사법 제43조의 13, 민사집행법 제195조, 동법 시행령 제2조 (이하“관련 법령”이라함)에서 정하는 주택연금 수급자 보호금액 (이하 “보호금액＂이라 함) 까지만 입금이 가능합니다.\n주택연금 입금금액이 보호금액을 초과하는 경우에는 이 예금으로의 입금은 불가능하며, 입금이 제한되지 않는 별도의 통장으로 수령해야 합니다. 단, 통장의 잔액은 금액에 제한없이 유지가 가능하며 입금 제한에 영향을 미치지 않습니다.\n변경 후 :\n① 이 예금으로의 입금은 주택연금으로 한국주택금융공사법 제43조의 13, 민사집행법 제195조, 동법 시행령 제2조 (이하“관련 법령”이라함)에서 정하는 주택연금 수급자 보호금액 (이하 “보호금액＂이라 함) 까지만 입금이 가능합니다.\n주택연금 입금금액이 보호금액을 초과하는 경우에는 보호금액까지만 이 통장으로 입금되며, 초과한 금액은 입금이 제한되지 않는 별도의 통장으로 수령해야 합니다.\n단, 통장의 잔액은 금액에 제한없이 유지가 가능하며 입금 제한에 영향을 미치지 않습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"5천만원 미만","금리(연율,세전)":"0.1%"},{"금액":"5천만원 이상","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (164, 3, '하나 플러스 통장', '{"가입대상":"인터넷뱅킹에 가입한 개인고객","예금종류":"저축예금,보통예금","가입방법":"① 영업점에서 예금주의 요청에 의해 신규 가입 및 전환\n② 비대면 계좌개설 서비스를 통한 신규 가입\n③ 예금주가 인터넷뱅킹, 스마트폰뱅킹, 고객센터를 이용하여 전환","금리":"분류: -, 금리(연율,세전): 저축예금:0.1%,보통예금:0.1%","우대금리":"우대이율 연0.1%\n(단, 은행거래 실적에 따라서 우대금리를 제공하는 입출금이 자유로운 통장을 하나 플러스 통장으로 전환 시 하나 플러스 통장의 우대금리는 제공되지 않고 최초 상품의 우대금리 기준으로 적용됩니다.)","수수료 우대":"제공서비스 내용\n수수료 면제 면제 대상 수수료\n전자금융(인터넷/스마트폰뱅킹, 폰뱅킹) 타행이체수수료\n당행 자동화기기 수수료\n납부자 자동이체 수수료\n현금IC카드 발급 수수료\n※ 수수료면제 적용은 하나 플러스 통장으로 거래 시 적용됩니다.\n※ 납부자 이체 및 예약이체 수수료 면제는 하나 플러스 통장으로 전환 후 다음 영업 일부터 적용됩니다.","부가서비스":"제공서비스 내용 유의사항\n현금 리펀드 온라인 쇼핑몰에서 하나 플러스 통장으로 결제 시 현금 리펀드 제공\n하나 플러스통장 을 출금계좌로 하고,\n‘실시간계좌이체’를 통해 결제할 경우,\n결제금액의 0.5%를 해당계좌로 실시간 리펀드\n결제금액이 최소 5천원 이상인 경우에 적용\n건당 최고 리펀드 금액 : 750원 (취소포함 월30회 / 실명번호 기준)\n실시간계좌이체란?\n인터넷쇼핑몰의 결제수단에는 신용카드, 실시간계좌이체, 무통장입금, 휴대폰결제 등이 있으며, 이 중‘실시간계좌이체’는 결제페이지에 입력한 구매자의 계좌에서 구매대금을 실시간으로 출금하여 결제를 완료하는 서비스임(공인인증서 기반)\n(주의 : 사후에 계좌로 송금하는 방식인 \"무통장 입금\"과 다름)\n리펀드 기간, 비율, 최고금액, 횟수 등은 은행의 사정에 따라 변경가능하며, 변경시행일 전 1개월 동안 은행 홈페이지 또는 영업점에 고시합니다.\n환율우대 환전송금 연동 거래 시 환율 spread 30% 우대\n가계부 발송\n월 1회 이메일로 동 상품의 입금 및 지급내역 송부\n홈페이지에서만 신청 가능","기타":"거래방법\n인터넷뱅킹(스마트폰뱅킹 포함), 폰뱅킹 및 자동화기기를 이용한 거래에 한합니다.\n통장발급\n이 예금은 통장을 발급하지 않으며, 전환된 계좌는 이전의 통장을 사용할 수 없습니다.\n예금주가 이 예금의 통장발급을 요청할 경우 은행은 요청일 당시 고시된 수수료를 부과 후 통장을 발급합니다.","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다..\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2021.09.24 변경)\n<원금 및 이자지급제한 변경>\n변경 전 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n변경 후 :\n계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음(일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있음\n예금잔액증명서 발급 당일에는 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음\n※ 기존 가입 고객에 대한 적용 여부 : 적용\n<위법계약해지권 변경>\n변경 전 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.\n변경 후 : 삭제\n※ 기존 가입 고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["분류","금리(연율,세전)"],"데이터":[{"분류":"-","금리(연율,세전)":"저축예금:0.1%,보통예금:0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (165, 3, '하나 행복지킴이 통장', '{"가입대상":"실명의 개인 또는 개인사업자로 다음 각각에 해당하는 손님을 대상으로 함.\n1. 「국민기초생활보장법」에서 정하는 기초생활수급자\n2. 「기초연금법」에서 정하는 기초연금 수급자\n3. 「장애인연금법」에서 정하는 장애인연금 수급자\n4. 「긴급복지지원법」에서 정하는 긴급지원금 등의 수급자\n5. 「장애인복지법」에서 정하는 장애수당, 장애아동수당 수급자\n6. 「한부모가족지원법」에서 정하는 복지급여 수급자\n7. 「아동수당법」에서 정하는 아동수당, 영아수당 수급자\n8. 「중소기업협동조합법」에서 정하는 노란우산공제금 수급자\n9. 「아동복지법」에서 정하는 자립정착금, 자립수당 수급자\n10. 「국민건강보험법」에서 정하는 요양비 등의 수급자\n11. 「노인장기요양보험법」에서 정하는 특별현금급여비 수급자\n12. 「재난적의료비 지원에 관한 법률」에서 정하는 지원금액 수급자\n13. 「의료급여법」에서 정하는 요양비 등 수급자\n14. 「영유아보육법」에서 정하는 양육수당 수급자\n15. 「고용보험법」에서 정하는 실업급여 수급자\n16. 「구직자 취업촉진 및 생활안정지원에 관한 법률」에서 정하는 구직촉진수당 수급자\n17. 「산업재해보상보험법」에서 정하는 산재보험급여 수급자\n18. 「임금채권보장법」에서 정하는 대지급금 수급자\n19. 「건설근로자의 고용개선 등에 관한 법률」에서 정하는 퇴직공제금 수급자\n20. 「청소년복지 지원법」에서 정하는 시설 퇴소청소년 자립지원수당 수급자","예금종류":"저축예금","이자지급방법":"이자계산기간동안 매일의 최종 잔액에 고시금리를 적용한 일별이자를 합산하여 지급\n이자계산기간 이자결산일 이자지급일(원가일)\n예금일(또는 원가일)부터\n원가일(또는 지급일) 전날까지의 기간 매년 3,6,9,12월의 제3금요일 결산일 익일","금리":"금액: 5천만원 미만, 금리(연율,세전): 0.1%\n금액: 5천만원 이상, 금리(연율,세전): 0.1%","우대금리":"아래에 해당되는 경우 연 2.0%의 우대금리 제공\n만 6세 이하 가입자 중 이 예금 원가일(또는 해지일)이 속한 달의 전월 또는 전전월에 “아동수당” 또는 “장애(아동) 수당”의\n수급실적이 1회 이상 있는 경우\n※ 2018년 9월 이후 입금되는 수급실적에 한합니다.\n※ 만 6세가 종료되는 달의 말일이 포함된 결산시까지 적용 합니다.\n※ e-플러스통장 운영관련 우대금리는 2018년9월 원가일 전일까지 제공합니다.","수수료 우대":"구분 항목 내용\n이 통장으로\n거래시 당행 자동화기기 마감후 현금인출 면제\n당행자동화기기 타행이체\n당행 폰뱅킹(ARS), 인터넷뱅킹,\n스마트폰뱅킹 타행이체\n납부자 자동이체\n(타행자동이체 포함)\n 자기앞수표 발행수수료","세제혜택":"비과세 종합저축 가능(전 금융기관 통합한도 범위내), 만기 후 이자는 소득세 부과","부가서비스":"별칭 및 부기명 인자 서비스(예시: 000 행복지킴이통장), 단 글자수 제한 있음","기타":"입금제한\n이 예금으로의 입금은 법령에 따라 가입대상자에게 지급되는 압류금지 수급금 등에 한하며, 그 이외에는 입금할 수 없습니다.\n거래제한\n이 예금은 관련 법령에 따라 법원의 압류명령에 의한 지급제한이 원칙적으로 되지 않습니다.\n이 예금은 은행이 상계할 수 없습니다.\n이 예금은 양도 및 담보로 제공할 수 없습니다.\n이 예금은 통장대출(한도대출)의 대출계좌(기본계좌)로 사용할 수 없습니다.\n이 예금을 신용카드 결제계좌나 공과금 등 대금납부 목적의 계좌로 지정한 경우에, 결제금액 또는 납부금액이 부족하거나 결제취소 또는 납부취소 등이 발생한 때에는 입금제한(입금은 압류금지 수급금 등에 한함)에 따라 예금주가 직접 해당기관에 결제금액 또는 납부금액을 납부하거나 직접 수령하는 방법으로 처리하여야 합니다.","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다.\n만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.05.23 변경)\n<가입대상 추가>\n■ 기존 가입고객에 대한 적용 여부: 적용\n변경 전: 1~19\n변경 후: 항목추가\n20. 「청소년복지 지원법」에서 정하는 시설 퇴소청소년 자립지원수당 수급자","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"5천만원 미만","금리(연율,세전)":"0.1%"},{"금액":"5천만원 이상","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (166, 3, '하나 해외주식전용 통장', '{"상품특징":"하나증권을 통해 미국주식 매매거래 가능","가입대상":"개인 및 개인사업자\n(국민인 거주자로 1인 1계좌만 가능)","예금종류":"외화 입출금 통장","가입기간":"제한없음","가입금액":"잔액기준 최대 미화 100,000달러","이자지급방법":"이 예금의 이자지급은 외화보통예금의 이자지급 방법을 따르며, 이자율은 해당 통화별 외화보통예금 고시 금리를 따릅니다. 외화보통예금 이자지급방법은 매년 3,6,9,12월 셋째주 금요일(단, 공휴일인 경우 직전 영업일)에 셈하여 익일(이하 ‘원가일’이라 한다)에 원금에 더합니다.\n이 예금의 이자는 최초 예금일 또는 원가일부터 원가일 또는 지급일 전날까지의 기간 동안 매일의 최종잔액에 평균하여 은행이 영업점 및 인터넷 홈페이지에 고시한 통화별 외화보통예금 이율을 적용하여 계산합니다.","금리":"금리안내\n가입당시 영업점 또는 홈페이지에 고시된 금리 적용","예치가능":"USD","거래제한":"예금은 하나증권에서 해외 주식투자를 위한 전용 예금으로 국내 외화이체, 해외송금 및 외화출금이 제한되오니 거래 시 주의하시기 바랍니다.\n이 예금의 출금은 하나증권 연계 해외주식거래 매매자금의 출금 또는 원화로의 출금만 가능합니다.","증권계좌 이용안내":"※ 연계방식 : 증권사와 은행간 실시간 잔액 정보의 공유로 증권계좌로 외화이체 거래 불필요. 은행계좌 잔액 범위 내, 증권사앱에서 해외주식 거래를 함.\n[하나증권]\n1. 계좌 신규 가능 시간 : 00:10~23:35(토/일 및 휴일 가능)\n2. 계좌신규 가능 고객 :\n영업점 신규 : 개인 및 개인사업자(국민인 거주자)\n비대면 신규 : 만 19세 이상 개인 및 개인사업자(국민인 거주자)\n3. 해외주식 거래 위한 절차\n1) 하나 해외주식전용 통장 및 하나증권 계좌 동시개설(하나 해외주식전용 통장 보유 고객은 하나증권 계좌 개설 및 연결)\n2) 하나증권 원큐스탁앱에서 온라인ID등록 및 해외주식 거래전 등록 (온라인ID등록은 하나증권 신규 고객에 한함)","우대서비스":"이 통장을 통한 원화대가 입∙출금 거래시 50% 환율(Spread) 우대 제공\n이벤트 적용환율 (~2025.12.31)\n이벤트 기간동안 이 예금에 원화대가로 입·출금 할 경우 아래의 환율을 적용합니다.\n구 분 적용 환율\n입금 거래시 매매마진율 100% 우대 환율*\n출금 거래시 80% 환율(Spread) 우대 환율\n*매매마진율 100% 우대 환율 : 매매기준율에 은행의 자금조달 비용을 가산한 환율\n※ [예시] 거래 시간대별 매매마진율 100% 우대 환율* 적용시 예상 적용환율(매매기준율 : 1,400원/1$)\n거래시간 예상 적용환율\n영업일 9시-15시 30분 1,400.2원\n그 외 시간 1,400.5원~1,401.0원\n*거래시간대별 매매마진율 100% 우대 환율은 금융시장 상황에 따라서 변경될 수 있습니다.","유의사항":"이율은 변동될 수 있으며, 변동될 경우 변동일로부터 변경된 이율을 적용합니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n이 예금은 하나증권에서 해외 주식투자를 위한 전용 예금으로 국내 외화이체 ∙ 해외송금 및 외화 출금이 제한됩니다.\n① 입금의 제한: 이 예금은 잔액기준 미달러화 (USD) 10만불까지 입금이 가능합니다.\n② 출금의 제한: 이 예금의 출금은 하나증권 연계 해외주식거래 매매자금의 출금 또는 원화로의 출금만 가능합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기 이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (167, 3, '하나더넥스트 연금 통장', '{"상품특징":"연금 입금시 우대금리와 수수료혜택을 제공하는 연금손님 전용 통장","가입대상":"실명의 개인 또는 개인사업자 (1인 1계좌)","예금종류":"저축예금","전환여부":"저축예금에 한하여 동 상품으로 전환가능\n(단, 기존 수수료면제통장에서 전환 시 수수료면제횟수는 전환월은 기존상품의 잔여횟수를 적용하고, 다음달부터는 동 상품의\n실적에 따라 수수료면제횟수 적용)","금리":"금액: 5천만원 미만, 금리(연율,세전): 0.1%\n금액: 5천만원 이상, 금리(연율,세전): 0.1%","우대금리":"연금 입금 손님 대상 1백만원까지 연 1.90% 추가제공\n제공대상 : 매 결산월(3,6,9,12월)의 전월말일 또는 해지일 전월말일 기준, 과거 3개월동안 2개월이상 이 통장에 연금(주1)이\n입금되는 경우\n제공내용 : 해당 이자계산기간내 매일의 최종잔액 중 1백만원까지 기본금리에 우대금리 연 1.90% 추가제공\n(단, 1백만원 초과금액은 우대금리 미제공)\n※ 유의사항\n다른통장에서 이 통장으로 전환하는 경우에는 전환일부터 이 통장의 금리를 적용함","수수료 우대":"아래의 제공조건을 충족하는 경우 충족월 다음달에 수수료우대서비스를 제공합니다.\n(이 통장을 통한 거래에 한 하여 제공)\n구분 제공 조건(주1) 제공 내용(주2)\n하나더넥스트\n연금 우대 이 통장에 연금 입금 수수료우대 I (무제한)\n+ 수수료우대 II (월10회)\n하나머니 우대 하나금융그룹의 하나머니 회원 수수료우대 I (월10회)\n(주1) 제공조건 인정 기준\n연금 입금\n4대 공적연금(국민, 공무원, 군인, 사학), 기초(노령)연금, 보훈연금, 산업재해보상보험급여, 하나은행에서 가입한 개인연금(주택연금, 연금신탁, 연금펀드)\n기타연금 : 사전 등록한 연금 수령일 전후 1영업일내에 대량이체 방식으로 건당 20만원 이상 입금되고 적요에 ‘연금’ 또는 ‘보험사명’이 기재된 자금\n(주2) 우대수수료 내용\n수수료 우대 Ⅰ 수수료 우대 Ⅱ\n당행 자동화기기를 통한 마감 후 현금인출 수수료 면제\n당행 자동화기기를 통한 타행이체 수수료 면제\n인터넷뱅킹, 폰뱅킹(ARS), 스마트폰뱅킹을 통한 타행이체 수수료 면제\n납부자자동이체 및 타행자동이체 수수료 면제\n타행 자동화기기*를 통한 현금 인출 수수료 면제\n창구 타행이체 수수료 면제\n* CD기 공동망 참여 금융회사로 타행 점내 설치된 자동화기기에 한해 서비스 제공. 은행 외부 설치된 CD기 및 한컴/효성/나이스 등 VAN사 CD기 출금하는 경우 수수료우대 없음\n※ 이 통장을 신규 가입후 다음달 말일까지는 제공조건 충족여부와 관계없이 수수료우대 I 을 총합 월 10회 제공합니다.","세제혜택":"개인의 경우 비과세종합저축 가능 (전 금융기관 통합한도 범위내)","유의사항":"수수료우대서비스는 이 통장에 의한 거래에만 적용되며, 매월 요건 충족여부를 확인하여 다음월에 수수료우대 서비스를\n제공합니다.\n우대금리, 우대서비스 내용 및 대상 요건은 변경 가능하며, 변경 시 재고시 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.03.14 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<상품명 변경>\n변경 전 : 연금하나 통장\n변경 후: 하나더넥스트 연금 통장\n<수수료우대서비스 명칭 변경>\n변경 전 : 연금하나 우대\n변경 후: 하나더넥스트 연금 우대","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"5천만원 미만","금리(연율,세전)":"0.1%"},{"금액":"5천만원 이상","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (168, 3, '하도급협력대금 통장', '{"상품특징":"하도급 관리시스템을 통한 자금의 관리를 통해 하도급사의 대금을 안정적으로 지급할 수 있는 상품","가입대상":"법인 및 사업자등록증을 소지한 개인사업자","예금종류":"기업자유예금","금리":null,"기타사항":"이 예금은 하도급시스템 종류에 따라 분류되며, 대상 및 이용목적에 따른 특정계좌(주1)와 세트(전부 또는 일부)하여 구성됩니다.\n(주1) 특정계좌: 【하도급협력대금 일반통장】,\n【하도급협력대금통장_고정계좌】,\n【하도급협력대금 통장_선금관리계좌】,\n【하도급협력대금통장_노무비계좌】 로 구분","세제혜택":"세금우대/생계형 불가, 양도 및 담보제공 불가, 자동이체 등록 불가, 예금의 전환 불가","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음\n<위법계약해지권 항목 신설>\n기존 가입 고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (169, 3, '함께하는 사랑 통장', '{"가입대상":"실명의 개인(개인사업자 포함) 또는 법인(1인 1계좌)","예금종류":"저축예금(개인), 기업자유예금(법인)","전환여부":"저축예금, 기업자유예금에 한정하여 기존 통장에서 본 상품으로의 전환 가능\n※ 기존 수수료면제통장에서 전환 시 수수료면제횟수는 당월은 기존상품의 수수료면제 잔여횟수를 적용하고, 다음달부터는 동 상품의 실적에 따라 수수료면제횟수 적용","금리":"분류: -, 금리(연율,세전): 0.1%","수수료 우대":"※ 수수료우대는 개인(개인사업자 포함) 고객에게만 제공합니다.\n수수료 우대서비스 및 우대요건\n‘함께하는 사랑 적금’ 보유 시\n급여, 연금(국민/공무원/군인/사학/기초/보훈/산재/기타연금 등), 가맹점대금(BC/하나) 중 1가지 이상 이 예금으로 이체 시\n아파트관리비 또는 하나카드와 하나은행 제휴 카드사의 신용(체크)카드를 합하여 월 10만원 이상 이 예금에서 결제 시\n이 예금에서 공과금 2건 이상 자동이체 시\n수수료 우대서비스 내용\n신규가입 시 다음월 말일까지 수수료 우대서비스 무제한 제공\n가입 후 다음월부터 우대요건 충족여부 체크하여, 충족 다음월부터 실적에 따라 우대서비스 제공\n우대요건 1건 충족 시 : 수수료 우대서비스I 무제한 면제\n(단, 우대요건 중 ‘함께하는 사랑 적금’ 보유 1개만 충족 시 우대서비스I 총합 월 10회만 제공됨)\n우대요건 2건 이상 충족 시 : 수수료 우대서비스I 무제한 + 수수료 우대서비스II 총합 월 10회 면제\n수수료 우대 대상거래\n(수수료 면제) 우대서비스Ⅰ (수수료 면제) 우대서비스Ⅱ\n이 예금에서 당행 자동화기기를 통한 마감 후 현금인출 거래\n이 예금에서 당행 자동화기기, 인터넷뱅킹, 폰뱅킹 (ARS), 스마트폰뱅킹을 통한 타행이체 거래\n이 예금에서 납부자이체(타행이체 포함) 거래\n이 예금의 통장 재발행 거래\n창구에서 타행이체 거래\n폰뱅킹(상담원)을 통한 당타행이체 거래\n타행 자동화기기를 통한 현금 인출 거래\n(타행은 CD기 공동망 참여 금융회사로 타행 점내 설치된 자동화기기에 한해 서비스 제공.\n은행 외부 설치된 CD기 및 한컴/효성/나이스 등 VAN사 CD기 출금하는 경우 수수료 우대 없음)","세제혜택":"개인의 경우 비과세종합저축 가능(전 금융기관 통합한도 범위내)","부가서비스":"후원금 제공\n은행은 협약이 유효한 년도까지 매년 12월말 기준으로 연 평균잔액의 0.1%를 은행이 자체 출연하여 지방자치단체(또는 지방자치단체가 지정한 단체)에 연1회 후원금 제공\n※ 후원금은 하나은행과 상호협력협약을 제결한 지방자치단체로 각 지방자치단체별로 ‘5백좌 이상’인 경우에만 제공됩니다.","유의사항":"※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음\n<위법계약해지권 항목 신설>\n기존 가입 고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["분류","금리(연율,세전)"],"데이터":[{"분류":"-","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (170, 3, '행복나눔 통장', '{"상품특징":"단체에 소속된 회원의 거래실적에 따라 지정 단체에 후원금을 드립니다.","가입대상":"실명의 개인(개인사업자 포함) 또는 법인(1인 1계좌)","예금종류":"보통예금, 저축예금, MMDA, 기업자유예금","전환여부":"가능 (단, 보통예금,저축예금,MMDA,기업자유예금 가능)","금리":"분류: -, 금리(연율,세전): 저축예금:0.1%,보통예금:0.1%,MMDA(개인):기간별,금액별 고시금리","수수료 우대":"수수료구분\n자동화기기 마감 후 현금인출\n타행이체\n폰뱅킹(ARS) 타행이체\n인터넷뱅킹 타행이체\n모바일뱅킹 타행이체\n납부자 자동이체 및 타행 자동이체\n수수료 우대서비스 대상 요건\n행복나눔 적금 가입 및 유지시\n당행에서 신용대출 10백만원 이상 유지시(한도대출인 경우 동 상품을 한도계좌로, 일반대출인 경우동 상품을 이자납입계좌로 지정해야 하며, 한도는 이자납입일을 기준으로 체크함)\n하나카드사와 당행 제휴카드사※의 신용(체크)카드를 합하여 월10만원 이상 이 예금에서 결제 시\n※ 2016.6.7 현재 당행 제휴카드사 : 현대카드\n은행 적립식예금과 적립식펀드를 합하여 월 10만원 이상 이 예금에서 자동이체 시\n동 상품의 월 평잔 100만원 이상 유지시\n신규(전환) 후 익월말까지 초기수수료 월 10회 수수료 면제\n신규(전환) 후 익월초부터 말까지 실적을 체크하여 익익월 수수료 면제\n(우대요건 1건 충족 시 익월 10회, 2건 이상 충족 시 익월 20회, 3건 이상 충족 시 익월100회 면제 가능)","세제혜택":"개인의 경우 비과세종합저축 가능(전 금융기관 통합한도 범위내)","부가서비스":"부기명 인자서비스 : 고객이 지정하는 단체명을 통장에 인자(단체 사업자등록번호 필수)","기타":"후원금 지급\n신규 가입시 단체를 등록한 계좌들의 연평잔 0.1%를 은행이 자체 출연하여 등록된 단체에 매년 12월말 기준으로 연 1회 후원금을 지급합니다. 단, 단체별 등록된 계좌수가 500좌 미만인 경우에는 지급하지 않으며, 익년으로 이월하지 않습니다.","유의사항":"1인 1계좌만 가능합니다.(수수료면제 통장과 중복가입 가능)\n우대서비스는 은행의 사정에 따라 변경 또는 중단 될 수 있으며, 변경 시 1개월 전에 고시합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<원금 및 이자지급제한 항목 신설>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 :\n계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음\n<위법계약해지권 항목 신설>\n기존 가입 고객에 대한 적용 여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 : 해당사항 없음 (신설)\n변경 후 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["분류","금리(연율,세전)"],"데이터":[{"분류":"-","금리(연율,세전)":"저축예금:0.1%,보통예금:0.1%,MMDA(개인):기간별,금액별 고시금리"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (171, 3, 'Easy-One Pack(이지원 팩) 통장', '{"상품특징":"외국인 전용 입출금통장\n카드실적이나 급여이체 및 적금이체 실적에 따라 각종 금융수수료 면제","가입대상":"실명의 외국인 개인 또는 외국인 개인사업자","예금종류":"저축예금","금리":"분류: -, 금리(연율,세전): 0.1%","수수료 우대":"이 통장을 가입한 손님이 아래의 우대서비스 1, 2별 제공조건에 해당하는 경우, 각각의 우대서비스를 제공합니다.\n우대서비스 1\n제공조건 : 이 통장에 하나카드의 신용/체크카드 결제실적이 있는 경우\n서비스내용(이 통장을 통한 거래에 한해 제공)\n- 전자금융(인터넷뱅킹, 텔레뱅킹, 모바일뱅킹, 스마트폰뱅킹)을 통한 다른은행 이체수수료 면제\n- 하나은행 CD/ATM을 이용한 출금수수료 면제\n제공방법 : “전월에 제공조건 충족” 시 당월에 “우대서비스 1” 제공함\n우대서비스 2\n제공조건: 이 통장으로 급여 (주1) 이체 실적이 있는 경우 또는 본인 명의 “Easy-One Pack 적금”에 전월 30만원 이상 입금 실적 보유시\n\n(주1) 급여 인정 기준 :\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드’로 입금된 금액이 월 50 만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인 거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n서비스내용 (이 통장을 통한 거래에 한해 제공)\n- 우대서비스 1 에서 제공되는 수수료 면제\n- 다른 은행 CD/ATM을 이용한 하나은행 계좌의 출금수수료 면제 (월 10회)\n(단, 은행 점포내 자동화기기 또는 은행이 직접 관리하는 점외 자동화기기에 한함)\n제공방법 : “전월 또는 전전월에 급여 입금실적이 있거나” 또는 전월에 본인명의 “Easy-One Pack 적금에\n30만원이상 입금실적 보유시” 당월에 “우대서비스 2” 제공함\n※ 최초 예금 가입 후 익월말까지는 제공조건 충족 여부와 관계없이 “우대서비스 1” 및 “우대서비스 2” 를 제공합니다.","세제혜택":"비과세 종합저축 가능(전 금융기관 통합한도 범위내)","유의사항":"급여이체실적 인정은 급여인정기준에 따라 입금되는 경우에 한 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<우대 서비스2 내용 변경>\n변경 전 :\n① 제공조건 : 이 통장으로 급여이체 실적(주) 있는 경우 또는 본인 명의 “Esay-One Pcak적금”에 전월 30만원 이상 입금 실적 보유시\n(주) 급여이체실적 : 타인으로부터 건당 50만원이상 입금\n(급여 의미 적요 포함 입금 또는 급여지정일 전후 1영업일내 입금)\n※ 급여 의미 적요 : 급여, 월급, 봉급, 연봉, 급료, 상여, 성과, 보너스, 월보수, SALARY, PAY, BONUS 등\n변경 후 :\n① 제공조건: 이 통장으로 급여 (주1) 이체 실적이 있는 경우 또는 본인 명의 “Easy-One Pack 적금”에 전월 30만원 이상 입금 실적 보유시\n\n(주1) 급여 인정 기준 :\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50 만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인 거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (172, 3, '글로벌페이 전용통장', '{"상품특징":"· 체크카드한도는 있지만, 전용통장 입금한도 없음.\n· 글로벌페이 원화통장으로 입금가능하며 입금 즉시 전신환 매도율 적용되어 글로벌페이외화통장으로 입금\n· 국내 ATM에서 출금 불가\n· 글로벌페이외화통장으로 입금불가","가입대상":"1. 만 14세 이상 국민인 거주자\n2. 비대면 계좌 개설시 만 17세 이상 국민인 거주자 (단 동일 상품 기가입자는 추가 개설 불가)","예금종류":"입출금이 자유로운 통장","건별 입금가능액":"10,000원 이상","입금가능통화":"글로벌페이 원화통장을 통한 원화 입금만 가능","이자지급방법":"글로벌페이 원화통장 : 매년 6월, 12월 셋째주 금요일에 실시하여 익일에 원금에 가산\n글로벌페이 외화통장 : 매년 3월, 6월, 9월, 12월의 셋째주 금요일에 실시하여 익일에 원금에 가산","금리":"금리안내\n외화보통예금 / 보통예금 금리","예치통화":"원화","계약의 해지":"· 영업점에서 해지 가능\n· 글로벌페이 원화통장과 연결된 글로벌페이외화통장을 모두 해지 해야함 (글로벌페이원화통장 혹은 외화통장만 해지 불가)","부가서비스":"영업시간외에도 글로벌페이 원화통장 입금시 글로벌페이 외화통장으로 외화자동입금 처리","용도":"글로벌페이카드의 전용 결제계좌로 이용 하나 글로벌 페이 체크카드 자세히보기","연계·제휴서비스 이행책임":"하나은행은 연계·제휴서비스 등을 부당하게 축소하거나 변경하지 않으며, 불가피하게 축소하거나 변경하는 경우 그에 상응하는 다른 연계·제휴서비스를 제공","연계·제휴서비스 제공기간":"상품 보유기간 내 연계·제휴서비스 제공","연계·제휴서비스 변경내용 사전 통지방법":"서면, 우편, 전자우편, 전화, 팩스, 휴대전화 메시지 또는 이에 준하는 전자적 의사표시 중 2개 이상의 방법으로 6개월 전부터 매월 고지 (단, 그에 상응하는 다른 연계·제휴서비스를 제공하거나 금융회사의 휴업·파산·경영상의 위에 등에 따른 불가피한 경우 제외)","연계·제휴서비스 제공요건":"글로벌페이 원화 및 외화통장 신규 후 글로벌페이체크카드를 발급 받은 고객","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음\n※ 민사집행법에 따라 최저생계비 이하 등 압류 금지채권에 해당하는 경우에는 법원에 압류 금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있음\n예금잔액증명서 발급 당일 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 : 해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (173, 3, '기업자유예금', '{"상품특징":"기업의 일시여유자금 운용에 적합한 수시 입출금 예금","가입대상":"법인 및 개인사업자","예금종류":"기업자유예금","가입기간":"제한없음","가입금액":"제한없음","이자지급방법":"이자 계산기간 동안 매일의 최종잔액에 기본금리를 적용한 일별 이자를 합산하여 지급\n(3월, 6월, 9월, 12월의 제3금요일에 결산하여 결산일 익일 지급)","금리":"금액: -, 금리(연율,세전): 0.1%","세제혜택":"비과세종합저축으로 가입 가능 (전 금융기관 통합한도 범위내)\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있음","원금 및 이자지급 제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급 제한\n예금잔액증명서 발급 당일에는 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2021.09.24 변경)\n<상품특징>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 : 설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.\n변경 후 : 삭제","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금액","금리(연율,세전)"],"데이터":[{"금액":"-","금리(연율,세전)":"0.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (174, 3, '외화다통화 예금', '{"상품특징":"하나의 대표계좌번호로만 거래가 가능\n통장이월 시 잔액이 있는 통화에 대해서만 통장에 인자","가입대상":"실명의 개인 또는 법인","예금종류":"외화 입출금통장","최저가입금액":"제한없음","이자지급방법":"① 이 예금의 이자지급은 외화보통예금의 이자지급 방법을 따르며 이자율은 해당통화별 외화보통예금 고시금리를 따릅니다.\n② 이 예금의 이자 계산은 최초 예금일 또는 지난 원가일부터 원가일 또는 지급일 전날까지의 기간으로 매일 최종잔액을 평균하여 영업점 및 인터넷 홈페이지에 고시된 통화별 이율을 적용합니다.","금리":"금리안내\n가입당시 기본상품별 영업점 또는 홈페이지에 고시된 금리 적용","예치통화":"아래의 예치가능한 통화 중 최대 10개까지 대표계좌번호로 거래 가능\n예치가능 통화 종류 : USD, EUR, JPY, GBP, CHF, CAD, AUD, NZD, HKD, SGD, DKK, NOK, SEK, THB, IDR, SAR, AED, KWD, BHD, RUB, ZAR, HUF, PLN, MXN, CNY, TRY, CZK, RON (28개국 통화)","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법‘ 에서 정의한 피해의심거래계좌 및사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","위법계약해지권":"금융회사가 금융소비자 보호에 관한 법률상 판매원칙 위반 시 일정 기간 이내에 금융소비자가 계약 해지를 요구할 수 있습니다. 금융회사는 10일 이내에 수락 여부를 통지해야 하며, 거절 시에는 거절사유와 함께 통지하여야 합니다.\n금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.02.07 변경)\n<예치통화>\n기존가입고객 적용여부 : 적용\n변경 전 : 27개 통화\n변경 후 : 28개 통화(기존 통화에 루마니아 레우 추가)","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (175, 3, '외화보통예금', '{"상품특징":"기업의 단기운전자금 운용에 적합한 수시 입출금 예금입니다.","가입대상":"제한없음(통화별 계좌개설 가능)","예금종류":"입출금이 자유로운 예금","가입기간":"제한없음","최저가입금액":"제한없음","이자지급방법":"매년 3월, 6월, 9월 및 12월의 셋째주 금요일에 셈하여 기준일의 익일에 원금에 가산","금리":"금액: -, 금리(연율,세전): 0.1%","예치가능통화":"28개국 통화 중 택1(USD, EUR, JPY, GBP, CHF, CAD, AUD, NZD, HKD, SGD, DKK, NOK, SEK, THB, IDR, SAR, AED, KWD, BHD, RUB, ZAR, HUF, PLN, MXN, CNY, TRY, CZK, RON)","세제혜택":"비과세대상 아님","유의사항":"이율은 변동될 수 있으며, 변동될 경우 변동일로부터 변경된 이율을 적용합니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음\n※ 민사집행법에 따라 최저생계비 이하 등 압류 금지채권에 해당하는 경우에는 법원에 압류 금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있음\n예금잔액증명서 발급 당일 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.02.07 변경)\n<예치통화>\n기존가입고객 적용여부 : 적용\n변경 전 : 27개 통화\n변경 후 : 28개 통화(기존 통화에 루마니아 레우 추가)","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (176, 3, '외화수퍼플러스', '{"상품특징":"1개월 미만의 여유자금을 단 하루만 맡겨도 높은 이자를 지급하는 고수익예금","가입대상":"제한없음(통화별 계좌개설 가능)","예금종류":"입출금이 자유로운 통장","가입기간":"제한없음","최저가입금액":"제한없음","이자지급방법":"이자지급식 : 예금 지급 시 원금과 이자를 지급함\n이자원가식 : 매년 3, 6, 9, 12월 제 3금요일에 셈하여 익일에 원금에 더함","금리":"금리안내\n가입당시 영업점 또는 홈페이지에 고시된 금리 적용\n※고시금리 적용기준\n예치잔액(법인, USD환산) 예치잔액(개인, USD환산) 고시금리\n1만불 미만 1만불 미만 무이자\n1만불 이상 ~ 50만불 미만 1만불 이상 ~ 10만불 미만 외화보통예금 고시금리\n50만불 이상 10만불 이상 외화보통예금 고시금리와 7일미만\n외화정기예금 고시금리 중 높은 금리","예치가능통화":"16개국 통화 중 택1 (USD, JPY, EUR, GBP, CHF, CAD, AUD, NZD, HKD, SGD, SEK, DKK, NOK, KWD, CNY, TRY)","세제혜택":"비과세대상 아님","유의사항":"이율은 변동될 수 있으며, 변동될 경우 변동일로부터 변경된 이율을 적용합니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음\n※ 민사집행법에 따라 최저생계비 이하 등 압류 금지채권에 해당하는 경우에는 법원에 압류 금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있음\n예금잔액증명서 발급 당일 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2022.01.01 변경)\n<자료열람요구권 항목 신설>\n기존가입고객 적용여부 : 적용\n변경 전 : 해당사항 없음(신설)\n변경 후 :\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (177, 3, '외화당좌예금', '{"상품특징":"기업의 단기운전자금 운용에 적합한 수시 입출금 예금입니다.","가입대상":"법인 및 개인사업자(1인 1계좌)","예금종류":"입출금이 자유로운 예금","가입기간":"제한없음","최저가입금액":"제한없음","이자지급방법":"없음","금리":"없음","예치가능통화":"28개국 통화 중 택 1(USD, EUR, JPY, GBP, CHF, CAD, AUD, NZD, HKD, SGD, DKK, NOK, SEK, THB, IDR, SAR, AED, KWD, BHD, RUB, ZAR, HUF, PLN, MXN, CNY, TRY, CZK, RON)","세제혜택":"비과세대상 아님","유의사항":"이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한될 수 있음\n※ 민사집행법에 따라 최저생계비 이하 등 압류 금지채권에 해당하는 경우에는 법원에 압류 금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있음\n예금잔액증명서 발급 당일 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.02.07 변경)\n<예치통화>\n기존가입고객 적용여부 : 적용\n변경 전 : 27개 통화\n변경 후 : 28개 통화(기존 통화에 루마니아 레우 추가)","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (178, 3, '하나 밀리언달러 통장', '{"상품특징":"은행과 제휴된 증권회사를 통해 해외주식 매매거래 가능\n외화로 바로 결제 가능한 외화카드 결제계좌\n해외여행 및 유학준비를 하나의 통장으로 관리\n여러 통화를 거래할 수 있는 외화 입출금 통장","가입대상":"개인(개인사업자 포함)","예금종류":"외화 입출금 통장","가입기간":"제한없음","가입금액":"제한없음","이자지급방법":"이 예금의 이자지급은 외화보통예금의 이자지급 방법을 따르며, 이자율은 해당 통화별 외화보통예금 고시 금리를 따릅니다. 외화보통예금 이자지급방법은 매년 3,6,9,12월 셋째주 금요일\n(단, 공휴일인 경우 직전 영업일)에 셈하여 익일(이하 ‘원가일’이라 한다)에 원금에 더합니다.\n이 예금의 이자는 최초 예금일 또는 원가일부터 원가일 또는 지급일 전날까지의 기간 동안 매일의 최종잔액에 평균하여 은행이 영업점 및 인터넷 홈페이지에 고시한 통화별 외화보통예금 이율을 적용하여 계산합니다.","금리":"금리안내\n가입당시 영업점 또는 홈페이지에 고시된 금리 적용","예치가능":"아래의 28개국 통화 중 최대 10개까지 예치 가능합니다.\n예치 가능통화: USD, EUR, JPY, GBP, CHF, CAD, AUD, NZD, HKD, SGD, DKK, NOK, SEK, THB, IDR, SAR, AED, KWD, BHD, RUB, ZAR, HUF, PLN, MXN, CNY, TRY, CZK, RON","증권계좌 이용안내":"※ 연계방식 : 증권사와 은행간 실시간 잔액 정보의 공유로 증권계좌로 외화이체 거래 불필요. 은행계좌 잔액 범위 내, 증권사앱에서 해외주식 거래를 함.\n[삼성증권]\n1. 계좌 신규 가능 시간 : 00:30~23:30(토/일 및 휴일 가능)\n2. 계좌 신규 가능 고객 : 만 19세 이상 내국인(외국인 제외)\n3. 해외주식 거래 위한 절차\n1) 하나 밀리언달러 통장 및 삼성증권 계좌 동시 개설\n  (하나 밀리언달러 통장 보유 고객은 삼성증권 계좌 개설 및 연결)\n2) 삼성증권 mPOP>고객센터>거래준비>ID/ID비밀번호 등록\n  (온라인 ID 등록은 삼성증권 신규 고객에 한함)\n3) 삼성증권 mPOP에서 개설한 계좌의 해외주식거래 이용신청(거래약정)\n4. 증권거래 가능한 시장 : 미국 시장\n[하나증권]\n1. 계좌 신규 가능 시간 : 00:10~23:35(토/일 및 휴일 가능)\n2. 계좌 신규 가능 고객 : 만 19세 이상 내국인(외국인 제외)\n3. 해외주식 거래 위한 절차\n1) 하나 밀리언달러 통장 및 하나증권 계좌 동시개설\n  (하나 밀리언달러 통장 보유 고객은 하나증권 계좌 개설 및 연결)\n2) 하나증권 원큐스탁앱에서 온라인 ID등록 및 해외주식 거래전 등록\n  (온라인 ID등록은 하나증권 신규 고객에 한함)\n[유안타증권]\n1. 계좌 신규 가능 시간: 00:10 ~ 23:35(토/일 및 휴일가능)\n2. 계좌 신규 가능 고객: 만 19세 이상 내국인(외국인 제외)\n3. 해외주식 거래 위한 절차\n1) 하나 밀리언달러 통장 및 유안타증권 계좌 동시 개설\n  (하나 밀리언달러 통장 보유 고객은 유안타증권 계좌개설 및 연결)\n2) 유안타증권 티레이더M > 로그인 > ID등록\n  (온라인 ID등록은 유안타증권 신규 고객에 한함)\n3) 유안타증권 티레이더M에서 개설한 계좌의 해외주식 거래 이용신청\n  (거래약정)\n4. 증권거래 가능한 시장 : 미국시장, 일본시장\n※ 이체방식 : 해외주식 거래를 위해 증권계좌로 외화이체 후, 증권사 앱에서 해외주식 거래를 함.\n[한국투자증권]\n1. 계좌 신규 가능 시간 : 00:10~23:30 (토/일 및 휴일 가능)\n2. 계좌 신규 가능 고객 : 만 19세 이상 내국인(외국인 제외)\n3. 해외주식 거래를 위한 절차\n1) 하나 밀리언달러 통장 및 한국투자증권 계좌 동시 개설 (하나 밀리언 달러 통장 보유 고객은 한국투자증권 계좌 개설 및 연결)\n2) 한국투자증권앱에서 ID등록 및 이용약관 동의 절차 필수, (해외주식 서비스 신청 포함)\n3) 증권사앱 주문 시 자금 이체 방법 :\n증권사앱(자산/뱅킹 > 이체 > 은행제휴계좌이체(외화))에서 미국달러 이체 입금 후 거래 시작\n4. 증권회사로 이체 가능한 외화 : 미국달러($)\n5. 증권거래 가능한 시장 : 미국 시장 등\n(증권사앱을 통한 국내,해외 시장 등 추가 투자 가능)","유의사항":"이율은 변동될 수 있으며, 변동될 경우 변동일로부터 변경된 이율을 적용합니다.\n예금의 입출금시에 적용하는 환율은 은행이 영업점에 고시한 거래시점의 대고객 전신환매매율로 합니다.\n이 예금은 원화로 인출하는 경우 환율변동으로 인하여 이익 또는 손실이 발생할 수 있으며, 외화현찰로 입출금하는 경우 현찰수수료가 부과될 수 있습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있습니다.\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동 불가\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’에서 정의한 피해의심거래계좌 및 사기 이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있음","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.02.07 변경)\n<예치통화>\n기존가입고객 적용여부 : 적용\n변경 전 : 27개 통화\n변경 후 : 28개 통화(기존 통화에 루마니아 레우 추가)","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (179, 3, '하나부가가치세매입자납부전용계좌(금거래계좌,스크랩등거래계좌,면세점송객용역거래계좌)', '{"상품특징":"조세특례제한법 제106조의 4(금 관련 제품에 대한 부가가치세 매입자 납부특례)에 따라 금사업자 간 금 관련 제품을 거래할 때와 제106조의 9(스크랩등에 대한 부가가치세 매입자 납부특례)에 따라 스크랩등사업자 간 스크랩등을 거래할 때, 제106조의 11(면세점 송객용역에 대한 부가가치세 매입자 납부특례)에 따라 관광사업자가 다른 관광사업자 또는 면세점사업자와 면세점송객용역을 거래할 때 매매대금의 결제와 부가가치세 납부의 편의를 제공하는 입출금이 자유로운 통장입니다.","가입대상":"실명의 개인사업자 또는 법인 (복수 계좌 개설 가능)\n※ 조세특례제한법 제106조의 4와 9와 11에서 정하는 금사업자, 스크랩등사업자, 관광사업자 및 면세점사업자에 한함.\n※ 거래은행 지정제로 사업자는 동일 품목에 대하여 한 곳의 금융회사에서 가입이 가능함 (복수은행거래불가)","상품종류":"“금거래계좌”, “스크랩등거래계좌”, “면세점송객용역거래계좌”","예금종류":"보통예금, 기업자유예금","가입기간":"제한없음","가입금액":"제한없음","전환여부":"가능","금리":"금리안내 보통예금 금리안내 기업자유예금","이자지급방법":"이자계산기간 동안 매일의 최종 잔액에 기본금리를 적용한 일별 이자를 합산하여 지급\n이자계산기간 예금일(또는 원가일)부터 원가일(또는 지급일)전날까지의 기간\n이자결산일\n기업자유예금\n3월,6월,9월,12월의 제3금요일에 결산하여 결산일 익일 지급\n보통예금\n6월,12월의 제3금요일에 결산하여 결산일 익일 지급\n이자지급일(원가일) 결산일 익일","수수료 우대":"• 수수료 우대 서비스 제공 대상 요건\n1. 전용계좌를 통한 부가가치세 매입자 납부 서비스 관련한 사업자 간 결제 거래에 한하여 수수료를 횟수 제한없이 면제\n① 영업점 창구를 통한 당ㆍ타행 이체 수수료\n② 기업 인터넷뱅킹 및 기업스마트폰뱅킹을 통한 당ㆍ타행 이체 수수료\n③ 입ㆍ출금 거래내역 문자통지서비스 수수료\n2. 전용계좌에서 당행 본인 명의 연결 등록된 계좌(연결계좌)로 이체 거래 시 이체 수수료를 면제","매매대금의 흐름":"① 매입자는 “금 관련 제품”, “스크랩등”, “면세점 송객용역”을 매매할 경우 본인의 전용계좌로 매매에 필요한 금액을 사전에 입금하여야 하며, 거래 가능한 이용매체를 통하여 결제할 때 매입자의 전용계좌에서 공급가액과 부가가치세가 출금되어 공급가액은 매출자의 전용계좌로 입금되고, 부가가치세액은 ‘부가가치세 관리 계정’으로 입금됩니다.\n② 조세특례제한법 및 동법 시행령과 동법 시행규칙에 의거 사업자에게 정산ㆍ환급세액이 발생한 경우 동 세액은 해당 사업자명의 전용계좌로 입금됩니다.\n③ 전용계좌로 입금된 공급가액과 정산ㆍ환급세액은 별도 신청한 당행의 본인명의 연결계좌가 있는 경우, 해당 계좌로 자동으로 이체됩니다.\n④ 매매 대금 등의 거래한도는 현행 전자금융공동망업무 규약에 따릅니다.\n⑤ 전용계좌를 모두 해지하는 경우 기존에 납부한 매입세액 등은 부가가치세 환급ㆍ정산이 불가능하게 되며, 당행 부가가치세관리계정에 관리 중인 부가가치세는 분기 익월 25일에 국고로 귀속됩니다.","민원처리절차 및 분쟁조정":"본 상품에 대한 문의사항 또는 민원상담이 필요한 경우 영업점, 고객센터, 홈페이지를 통해 문의해 주시기 바랍니다.\n고객상담센터 1599-1111, 인터넷 홈페이지 www.hanabank.com\n본 상품에 대하여 당 은행과 분쟁이 있는 경우 금융소비자보호법에 따라 금융감독원 민원상담센터 또는 e-금융민원센터에 분쟁조정을 신청할 수 있습니다.\n금융감독원 민원상담 센터 국번없이1332, e-금융민원센터 https://www.fcsc.kr","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한될 수 있습니다.\n예금잔액증명서 발급 당일 잔액 변동이 불가합니다.\n통장이 ‘전기통신금융사기 피해 방지 및 피해금 환급에 관한 특별법’ 에서 정의한 피해의심 거래계좌 및 사기 이용계좌로 이용될 경우 이체, 송금지연, 지급정지 등의 금융거래 제한조치를 할 수 있습니다.","계약 해지 방법":"영업점을 통해 해지 가능합니다.","해지 시 불이익":"전용계좌를 모두 해지하는 경우 기존에 납부한 매입세액 등은 부가가치세 환급·정산이 불가능하게 되며, 당행 부가가치세 관리계정에 관리중인 부가가치세는 분기 익월 25일에 국고로 귀속됩니다.","세제혜택":"세금우대저축 : 불가\n비과세종합저축 : 가능 (전 금융기관 통합한도 범위 내)\n※ 관련세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있음","양도 및 담보제공":"불가","거래중지":"예금잔액 및 무거래기간에 따라 거래중지계좌로 분리되어 거래가 제한될 수 있습니다.","휴면예금 및 출연":"이자지급을 포함한 최종거래일 이후 5년 이내에 계좌를 해지하지 않을 경우, 예금거래기본약관 제9조의 2 휴면예금 및 출연에 따라 서민금융진흥원 국번없이 1397으로 출연될 수 있습니다.\n휴면예금은 휴면예금 통합조회서비스 www.sleepmoney.or.kr를 통해 조회 가능합니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n- 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료,내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익 침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료 열람을 제한하거나 거절할 수 있습니다.","유의사항":"이 예금의 가입자는 “부가가치세 매입자 납부 서비스”를 신청합니다.\n이 예금은 사업자등록증 보유한 금사업자, 스크랩등사업자, 관광사업자 및 면세점사업자에 한하여 가입이 가능합니다.\n“금 관련 제품”을 공급ㆍ수입하거나 공급받으려는 “금사업자”는 “금거래계좌”를 개설하여야 하고, “스크랩등”을 공급ㆍ수입하거나 공급받으려는 “스크랩등사업자”는 “스크랩등거래계좌”를 개설하여야 하고, “면세점송객용역”을 공급하거나 공급받으려는 “관광사업자” 및 “면세점사업자”는 “면세점송객용역거래계좌”를 개설하여야 함\n원활한 결제서비스 이용을 위하여 기업 인터넷뱅킹 및 기업 스마트폰뱅킹서비스 가입을 권장합니다.\n부가가치세 매입자 납부제도의 참여은행 및 국세청, 금융결제원 앞으로 계좌번호, 고객명, 부가가치세 납부 및 환급내역 등의 동 계좌와 관련된 정보가 제공됩니다.\n수수료 및 기타 우대서비스의 내용과 대상은 변경 가능하며, 변경 시 변경사유, 변경내용 등을 변경 시행일 1개월 전 1개월 동안 은행 홈페이지 및 영업점에 고시합니다.","연계·제휴서비스의 내용":"부가가치세 매입자 납부서비스는 조세특례제한법 및 동법 시행령, 동법 시행규칙, 부가세매입자납부 공동업무 규약에 의거하여 금융결제원과 국세청을 통해 “부가가치세 매입자 납부제도” 참여은행 간 매매대금의 결제와 부가가치세 납부 편의를 제공하는 서비스입니다.","연계·제휴서비스의 이행 책임":"하나은행은 부가가치세 매입자 납부서비스를 부당하게 축소하거나 변경하지 않으며, 불가피하게 축소하거나 변경하는 경우 그에 상응하는 다른 서비스를 제공합니다.","연계·제휴서비스의 제공 기간":"전용계좌 등록 시부터 전용계좌 등록 해지 시까지 제공합니다.\n(이미 납부한 매입세액의 환급·정산은 사업자 명의의 모든 전용계좌 해지 시 불가)","연계·제휴서비스의 변경·종료에 대한 사전통지":"서면, 우편, 전자우편, 전화, 팩스, 휴대전화, 문자메시지 또는 이에 준하는 전자적 의사표시 중 2개 이상의 방법으로 축소·변경 6개월 전부터 매월 고지합니다.\n(단, 그에 상응하는 다른 서비스를 제공하거나 금융회사의 휴업·파산·경영 상의 위기 등에 따른 불가피한 경우 제외)","연계·제휴서비스의 제공 조건":"“부가가치세 매입자 납부서비스” 신청\n원활한 결제서비스 제공을 위해 기업(인터넷/스마트폰)뱅킹 가입을 권유합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.6.17 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<상품특징>\n변경전 : 조세특례제한법 제106조의 4(금 관련 제품에 대한 부가가치세 매입자 납부특례)에 따라 “금사업자”간 “금관련 제품”을 거래할 때 또는 제106조의 9(스크랩등에 대한 부가가치세 매입자 납부특례)에 따라 “스크랩등 사업자”간 “스크랩등”을 거래할 때 매매대금의 결제와 부가가치세 납부의 편의를 제공하는 입출금이 자유로운 통장입니다.\n변경후 : 조세특례제한법 제106조의 4(금 관련 제품에 대한 부가가치세 매입자 납부특례)에 따라 금사업자 간 금 관련 제품을 거래할 때와 제106조의 9(스크랩등에 대한 부가가치세 매입자 납부특례)에 따라 스크랩등사업자 간 스크랩등을 거래할 때, 제106조의 11(면세점 송객용역에 대한 부가가치세 매입자 납부특례)에 따라 관광사업자가 다른 관광사업자 또는 면세점사업자와 면세점송객용역을 거래할 때 매매대금의 결제와 부가가치세 납부의 편의를 제공하는 입출금이 자유로운 통장입니다.\n<가입대상>\n변경전 : 실명의 개인사업자 또는 법인 (복수 계좌 개설 가능)\n※ 조세특례제한법 제106조의 4 및 제106조의 9에서 정하는 “금 사업자” 및 “스크랩등 사업자”에 한함. 사업자는 한 곳의 금융회사에서만 가입이 가능함 (복수은행 거래불가)\n변경후 : 실명의 개인사업자 또는 법인 (복수계좌개설가능)\n※ 조세특례제한법 제106조의 4와 9와 11에서 정하는 금사업자, 스크랩등사업자, 관광사업자 및 면세점사업자에 한함.\n※ 거래은행 지정제로 사업자는 동일 품목에 대하여 한 곳의 금융회사에서 가입이 가능함 (복수은행거래불가)\n<상품종류>\n변경전 : “금거래계좌”, “스크랩등거래계좌”\n변경후 : “금거래계좌”, “스크랩등거래계좌”, “면세점송객용역거래계좌”\n<매매대금의 흐름도>\n변경전 : ① 매입자는 “금 관련 제품”, “스크랩등”, “면세점 송객용역”을 매매할 경우 본인의 전용계좌로 매매에 필요한 금액을 사전에 입금하여야 하며, 거래 가능한 이용매체를 통하여 결제할 때 매입자의 전용계좌에서 공급가액과 부가가치세가 출금되어 공급가액은 매출자의 전용계좌로 입금되고, 부가가치세액은 ‘부가가치세 관리 계정’으로 입금됩니다.\n②~⑤ 생략\n변경후 : ① 매입자는 “금 관련 제품” 또는 “스크랩등”을 매매할 경우 본인의 전용계좌로 매매에 필요한 금액을 사전에 입금하여야 하며, 거래 가능한 이용 매체를 통하여 결제할 경우 매입자의 전용계좌에서 공급가액과 부가가치세가 출금되어 공급가액은 매출자의 전용계좌로 입금되고, 부가가치세액은 ‘부가가치세 매입자 납부계정’으로 입금됩니다.\n②~⑤ 생략\n<유의사항>\n변경전 :\n- 이 예금의 가입자는 “부가가치세 매입자 납부 서비스”를 신청합니다.\n- 이 예금은 금(또는 스크랩등)사업자를 대상으로 사업자등록증 보유고객에 한하여 신규가입 가능합니다.\n- 원활한 결제 서비스 사용을 위해 기업 전자금융서비스(기업 인터넷뱅킹 및 스마트폰뱅킹) 가입을 권장합니다.\n- 동 계좌의 관련정보 (계좌번호, 고객명, 부가가치세 납부 및 환급 내역 등)는 “부가가치세 매입자 납부제도” 참여은행 및 국세청, 금융결제원 앞으로 제공됩니다.\n- 매입자는 “금 관련제품” 또는 “스크랩등”을 매매할 경우 본인의 전용계좌로 매매에 필요한 금액을 사전에 입금하여야 하며, 거래 가능한 이용 매체를 통하여 결제할 경우 매입자의 전용계좌에서 공급가액과 부가가치세가 출금되어 공급가액은 매출자의 전용계좌로 입금되고, 부가가치세액은 매출자 거래은행의 ‘부가가치세 매입자 납부계정’으로 입금됩니다.\n- 사업자에게 정산·환급세액이 발생한 경우 동 세액은 해당 사업자명의 전용계좌로 입금됩니다. (별도 신청한 당행의 본인명의 연결계좌가 있는 경우, 해당계좌로 자동이체)\n- 전용계좌를 모두 해지하는 경우 기존에 납부한 매입세액 등은 부가가치세 환급·정산이 불가능하게 되며, 당행 부가가치세관리계정에 관리 중인 부가가치세는 분기 익월 25일에 국고로 귀속됩니다.\n변경후\n- 이 예금의 가입자는 “부가가치세 매입자 납부 서비스”를 신청합니다.\n- 이 예금은 사업자등록증 보유한 금사업자, 스크랩등사업자, 관광사업자 및 면세점사업자에 한하여 가입이 가능합니다. “금 관련 제품”을 공급·수입하거나 공급받으려는 “금사업자”는 “금거래계좌”를 개설하여야 하고, “스크랩등”을 공급·수입하거나 공급받으려는 “스크랩등사업자”는 “스크랩등거래계좌”를 개설하여야 하고, “면세점송객용역”을 공급하거나 공급받으려는 “관광사업자” 및 “면세점사업자”는 “면세점송객용역거래계좌”를 개설하여야 함\n- 원활한 결제서비스 이용을 위하여 기업 인터넷뱅킹 및 기업 스마트폰뱅킹서비스 가입을 권장합니다.\n- 부가가치세 매입자 납부제도의 참여은행 및 국세청, 금융결제원 앞으로 계좌번호, 고객명, 부가가치세 납부 및 환급내역 등의 동 계좌와 관련된 정보가 제공됩니다.\n- 수수료 및 기타 우대서비스의 내용과 대상은 변경 가능하며, 변경 시 변경사유, 변경내용 등을 변경 시행일 1개월전 1개월 동안 은행 홈페이지 및 영업점에 고시합니다.","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (180, 3, '주택청약종합저축', '{"가입대상":"국민인 개인 (국내에 거주하는 재외동포 포함) 또는 외국인거주자 (유주택자,미성년자 가능)\n※ 전 금융기관에 걸쳐 주택청약종합저축, 청약예금, 청약부금, 청약저축 중 1인 1계좌만 가입 가능","준비서류":"실명확인증표 (원본지참)\n※ 주민등록증(재외국민 포함) / 운전면허증 / 외국국적 동포 국내거소신고증 / 외국인등록증","납입금액":"매월 정해진 날짜에 (신규일 해당일) 2만원이상 50만원 이하의 금액을 자유롭게 납입\n※ 잔액이 1,500만원 미만인 경우 1,500만원까지 일시예치 가능, 잔액이 1,500만원 이상인 경우 납입회차별 50만원 이내에서 자유적립","저축기간":"가입한 날로부터 입주자로 선정된 날까지\n※ 단, 분양전환 되지 않는 임대주택에 당첨된 경우는 제외","금리":"기간: 1개월 이내, 금리(연율,세전): 0.0%\n기간: 1개월초과 1년미만, 금리(연율,세전): 2.3%\n기간: 1년이상 2년 미만, 금리(연율,세전): 2.8%\n기간: 2년이상, 금리(연율,세전): 3.1%","소득공제":"연말정산 소득공제\n근로소득이 있는 거주자로서 총 급여액이 7천만원 이하이며 주택을 소유하지 않은 세대의 세대주 또는 그 세대주의 배우자가 해당 과세기간에 납입한 금액(무주택확인서를 제출한 과세기간 이후에 납입한 금액으로 연간 300만원 한도)의 40%에 상당하는 금액(최대 120만원)을 소득공제","전환신규":"기존 가입한 「청약저축」, 「청약예금」, 「청약부금」을 「주택청약종합저축」으로 전환가능","기타":"청약순위 발생 자격\n국민주택에 청약하려는 경우\n1순위\n① 수도권 (인천,경기 등) 건설 주택 : 가입 후 1년 경과 + 12회차 인정\n(승인에 따라 24개월 및 24회까지 연장 가능)\n② 수도권 외 건설 주택 : 가입 후 6개월 경과 + 6회차 인정\n③ 투기과열지구·청약과열지역 건설 주택 : 가입 후 2년 경과 + 24회차 인정\n※ 단, 시도지사의 승인에 따라 변경 될 수 있음\n※ 약정납입일이 도래하여 납입이 인정된 회차에 한하여 납입인정회차로 인정, 납입인정금액은 회차별 입금금액 중 최대 25만원까지만 인정(단, 약정납입일이 2024.10.31 이전인 회차에 대해서는 최대 10만원까지 인정)\n※ 월 납입금을 지연납입시 납입인정이 지연되어 순위발생이 늦어질 수 있으며, 선납하신 경우는 해당 월의 해당 약정납입일이 도래하여야 납입회차와 금액이 인정.\n※ 미성년자 가입 후 가입일로부터 성년에 이르기까지 납입횟수가 60회차 초과면 60회 납입한 것으로 (금액이 큰 순서대로)인정, 납입인정금액은 납입인정회차의 해당 납입인정금액의 총액\n2순위 : 1순위에 해당되지 않는 분(주택청약통장 보유자)\n※ 2017. 11. 24일부터 모든 지역 2순위 청약시 청약통장 보유시에만 접수가능\n민영주택에 청약하려는 경우\n1순위\n① 수도권 (인천,경기 등) 건설 주택 : 가입 후 1년 경과 + 예치기준금액 충족\n(승인에 따라 24개월 및 24회까지 연장 가능)\n② 수도권 외 건설 주택 : 가입 후 6개월 경과 + 예치기준금액 충족\n③ 투기과열지구·청약과열지역 건설 주택 : 가입 후 2년 경과 + 예치기준금액 충족\n※ 단, 시도지사의 승인에 따라 변경 될 수 있음\n※ 미성년자 가입 후 가입일로부터 성년에 이르기까지 가입기간이 5년 이상이면 5년, 5년 미만이면 해당 기간 적용, 가입일로부터 적립된 총액을 예치금으로 인정\n2순위 : 1순위에 해당되지 않는 분(주택청약통장 보유자)\n※ 2017. 11. 24일부터 모든 지역 2순위 청약시 청약통장 보유시에만 접수가능\n명의변경\n가입자가 사망한 경우에 한하여 그 상속인 명의로 변경할 수 있음\n단, 당첨된 계좌(분양 전환되지 않은 임대주택은 제외)는 명의변경할 수 없으나 가입자가 사망한 경우에는 명의변경 후 즉시 해지처리함\n원리금 지급\n가입일로 부터 해지일까지 저축기간에 따라 다르게 적용하여 해지할 때에 일시에 지급함.\n※ 변동금리로서 정부의 고시에 의하여 변경 될 수 있으며, 금리변경시 변경일 기준으로 기존 가입손님을 포함하여 변경 후 금리로 적용\n(단위:만원)\n거주지역별 민영주택 청약 예치기준 금액\n구분 서울, 부산 기타 광역시 시 · 군지역\n85㎡ 이하 300 250 200\n102㎡ 이하 600 400 300\n135㎡ 이하 1,000 700 400\n모든면적 1,500 1,000 500","유의사항":"당첨된 주택청약통장은 계약체결여부와 관계없이 재사용 할 수 없습니다.\n※ 단, 분양전환되지 않는 임대주택에 당첨된 경우에는 재사용 할 수 있습니다.\n성년에 이르기 전 가입자 유의사항\n국민주택 청약 시\n미성년자가 가입 후 가입일로부터 성년에 이르기 전까지 납입횟수가 60회 초과하면 60회 납입한 것으로 인정 (납입금액이 많은 순으로, 회차별 최대 25만원까지 인정)\n민영주택 청약 시\n미성년자가 가입 후 가입일로부터 성년에 이르기 전까지 가입기간이 5년 초과하면 5년만 인정\n(가입일로부터 적립된 총액을 예치금으로 인정)\n※ 단, ‘23.12.31 이전 납입분은 종전과 같이 최대 2년(24회차)이 적용되고, ‘24.1.1 이후 납입분은 ‘23.12.31 이전 납입분과 합산하여 최대 5년(60회차)이 적용됩니다.\n전환신규시 유의사항\n청약저축 및 청약예·부금을 주택청약종합저축으로 전환신규한 경우 순위 인정 기준\n국민주택 청약 시 : 청약저축에서 전환된 경우 기존 납입 인정된 횟수와 금액은 인정(단, 기존 주택청약종합저축의 선납·미납회차는 인정되지 않으며 소멸함) 되며, 청약예·부금에서 전환된 경우 전환원금을 제외한 신규 납입금부터 납입횟수와 금액을 인정\n민영주택 청약 시 : 청약저축에서 전환된 경우 전환 후 신규납입분부터 인정(전환원금은 예치금으로 인정)되며 청약예·부금에서 전환된 경우 기존 가입기간 그대로 인정\n※ 전환 완료 익일 이후 입주자모집공고부터 청약접수 가능합니다. 전환 후에는 신규일 이전의 모집공고에 대해서는 청약신청이 불가합니다.\n※ 청약당첨 등 제한사항이 있는 경우 전환신규 대상에서 제외됩니다.\n※ 주택청약종합저축으로 전환은 영업점 및 하나원큐 채널에서만 가능합니다. (인터넷뱅킹 전환 불가)\n※ 다른 은행간 전환하는 경우, 기존 「청약예금」또는 「청약부금」을 가입한 은행에서 해지(전환해지)가 선행되어야 하며, 해지 후 20영업일 이내에 전환신규가 완료되어야 합니다. 하나은행 전환해지는 영업점방문이 필요하며, 청약저축은 다른은행간 전환이 불가합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.31 변경)\n■ 기존 가입 고객에 대한 적용여부 : 적용\n<전환신규 가능일 연장>\n청약예부금 및 청약저축을 주택청약종합저축으로 전환할 수 있는 기한을 1년 연장.\n(’25.09.30 → ‘26.09.30)","예금자 보호":"이 금융상품은 예금자보호법에 따라 보호되지 않으나, 주택도시기금의 조성재원으로 정부가 관리하고 있습니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"1개월 이내","금리(연율,세전)":"0.0%"},{"기간":"1개월초과 1년미만","금리(연율,세전)":"2.3%"},{"기간":"1년이상 2년 미만","금리(연율,세전)":"2.8%"},{"기간":"2년이상","금리(연율,세전)":"3.1%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (181, 3, '희망저축계좌I', '{"상품특징":"보건복지부 『자산형성지원 통장사업 (운영지침)』에서 정한 대상자가 자산을 형성할 수 있도록\n적립금 납입시 정부지원금을 지원하는 적립식 상품","가입대상":"\"운영지침\"에 따라 지방자치단체가 은행에 가입 승인을 통지한 실명의 개인","예금종류":"자유적립식","가입기간":"3년","가입금액":"10만원 이상 ~ 50만원 이하(만원 단위)","적립한도":"당월 인정기간 內 최소 10만원이상~ 최대 50만원이하 (당월 인정기간 內 최초 입금금액은 10만원이상, 만원 단위)\n【당월 (적립월) 인정기간 : 전월 23일(휴일인 경우 익영업일)~당월22일(휴일인 경우 익영업일)】\n단, 자동이체는 최소 10만원이상 최대 50만원 범위내 만원 단위로 등록 가능","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","정부지원금":"자산형성지원을 위한 정부 지원금으로 근로소득장려금 및 정책대상별 추가지원금 등이 있으며, 지원내용(지원조건, 지원금액, 지급방법 등)은 보건복지부 『자산형성지원 통장사업 (운영지침)』에서 정하는 바에 따릅니다.\n이 예금 신규시 지정한 지원금 수령계좌로 입금됩니다.","적립중지":"실직, 본인 및 부양가족의 질병·사고 등 부득이한 사유가 있는 경우 지방자치단체에 적립 중지를 신청할 수 있으며, 지방자치단체가 은행에 적립중지를 승인 통지한 경우 적립금은 입금제한등록 됩니다.\n적립 중지 기간 중 정부지원금 미지원 됩니다.","금리":"기간: 3년, 금리(연율,세전): 2.0%","우대금리":"아래의 우대항목을 충족한 경우 최대 연 2.50%의 우대금리를 만기해지시 제공합니다. (단, 중도해지시 미제공)\n우대항목 내용\n급여 및 주거래이체 우대\n(연 1.00%) 이 예금 가입후 만기 전전월말 기준, 본인명의 하나은행 입출금 통장을 통해 12회차 이상 급여입금 (주1) (또는 가맹점대금 입금) 및 주거래 이체 (주2) 실적을 같은 월에 모두 보유한 경우 (월1회 인정)\n※ 1회차 인정요건 : 급여입금 (또는 가맹점대금 입금) + 주거래이체\n\n예시\n(’22년 4월) 급여 입금 + 아파트 관리비 출금 : 1회차 인정\n(’22년 5월) 가맹점대금 입금 + 신용(체크)카드 결제대금 출금 : 1회차 인정\n(’22년 7월) 급여 미입금 + 공과금 출금 : 1회차 미인정\n주택청약종합저축 우대\n(연 1.00%) 아래의 ① 또는 ② 에 해당하는 경우 우대금리 제공\n\n① 당행 주택청약종합저축 신규 또는 보유 손님\n이 예금 가입일로부터 3개월이 되는 달의 말일기준 당행 ‘주택청약종합저축’ 을 신규 또는 보유하고 만기 시점까지 유지한 경우\n\n② 타행 주택청약종합저축 보유 확인 손님\n이 예금 가입일까지 ‘하나원큐(스마트폰뱅킹)’를 통하여 오픈뱅킹서비스를 가입하고, 주택청약종합저축을 등록·조회한 경우\n\n※ 오픈뱅킹서비스 등록·조회방법 : 하나원큐(스마트폰뱅킹)로그인 → 오픈뱅킹서비스 신규가입 → 기관추가 및 해당상품 등록 → 하나원큐내 ‘전계좌조회_오픈뱅킹’ 을 통해 정상조회시 완료\n마케팅동의 우대\n(연 0.50%) 이 예금 가입 전 하나은행 상품,서비스 마케팅 동의 항목 모두를 동의한 경우\n(주1) 급여 인정 기준\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n(주2) 주거래이체 인정기준 : 하나카드 결제(신용/체크), 아파트관리비 자동이체 출금, 공과금 자동이체 출금","수수료 우대":"당행의 입출금통장에서 이 예금으로 매월 자동이체를 통하여 입금하는 경우, 익월 1일부터 말일까지 아래의 수수료우대서비스를 제공합니다.\n1. 당행자동화기기, 인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행이체수수료 (월 10회 면제)\n2. 당행자동화기기를 통한 현금인출 수수료 (월 10회 면제)\n※ 이 예금의 신규 가입후 다음달 말일까지는 자동이체 등록여부와 관계없이 수수료 우대서비스 제공\n※ 이 예금으로 자동이체 출금 계좌 등록한 입출금통장을 통한 거래에서 발생하는 수수료에 한하여 제공","세제혜택":"비과세종합저축 가입 가능 (전 금융기관 통합한도 범위 내) 단, 만기 후 이자는 소득세 부과\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됨","유의사항":"정부지원금에 관한 세부내용(지원조건, 지원금액, 지급방법 등)은 보건복지부 『자산형성지원 통장사업 (운영지침)』에서 정하는 바에 따릅니다.\n정부지원금은 신규시 지정한 지원금 수령계좌로 입금됩니다.\n계좌의 해지는 지방자치단체 (‘운영지침’에서 명시한 기관)등에 사전 신청 후, 은행 앞 해지 승인이 통지된 경우에만 가능 합니다.\n적립 한도는 당월 적립 인정기간 內에 최소 10만원이상 최대 50만원이내입니다.\n【당월 (적립월) 인정기간 : 전월 23일(휴일인 경우 익영업일)~당월22일(휴일인 경우 익영업일)】\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및\n이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한 (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경) ■ 기존 가입고객에 대한 적용 여부 : 적용\n<급여인정조건>\n변경 전:\n(주1) 급여입금 인정 기준: 타인으로부터 건당 50만원 이상 입금시, 급여를 의미하는 적요 (급여,월급,연봉,봉급,상여,성과,보너스,salary,bonus 등)를 포함하여 입금되거나 또는 사전 지정한 급여일 전후 1영업일 내에 입금되는 자금\n변경 후:\n(주1) 급여 인정 기준\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"3년","금리(연율,세전)":"2.0%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (182, 3, '희망저축계좌II', '{"상품특징":"보건복지부 『자산형성지원 통장사업 (운영지침)』에서 정한 대상자가 자산을 형성할 수 있도록\n적립금 납입시 정부지원금을 지원하는 적립식 상품","가입대상":"\"운영지침\"에 따라 지방자치단체가 은행에 가입 승인을 통지한 실명의 개인","예금종류":"자유적립식","가입기간":"3년","가입금액":"10만원 이상 ~ 50만원 이하(만원 단위)","적립한도":"당월 인정기간 內 최소 10만원이상~ 최대 50만원이하 (당월 인정기간 內 최초 입금금액은 10만원이상, 만원 단위)\n【당월 (적립월) 인정기간 : 전월 23일(휴일인 경우 익영업일)~당월22일(휴일인 경우 익영업일)】\n단, 자동이체는 최소 10만원이상 최대 50만원 범위내 만원 단위로 등록 가능","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","정부지원금":"자산형성지원을 위한 정부 지원금으로 근로소득장려금 및 정책대상별 추가지원금 등이 있으며, 지원내용(지원조건, 지원금액, 지급방법 등)은 보건복지부 『자산형성지원 통장사업 (운영지침)』에서 정하는 바에 따릅니다.\n이 예금 신규시 지정한 지원금 수령계좌로 입금됩니다.","적립중지":"실직, 본인 및 부양가족의 질병·사고 등 부득이한 사유가 있는 경우 지방자치단체에 적립 중지를 신청할 수 있으며, 지방자치단체가 은행에 적립중지를 승인 통지한 경우 적립금은 입금제한등록 됩니다.\n적립 중지 기간 중 정부지원금 미지원 됩니다.","금리":"기간: 3년, 금리(연율,세전): 2.0%","우대금리":"아래의 우대항목을 충족한 경우 최대 연 2.50%의 우대금리를 만기해지시 제공합니다. (단, 중도해지시 미제공)\n우대항목 내용\n급여 및 주거래이체 우대\n(연 1.00%) 이 예금 가입후 만기 전전월말 기준, 본인명의 하나은행 입출금 통장을 통해 12회차 이상 급여입금 (주1) (또는 가맹점대금 입금) 및 주거래 이체 (주2) 실적을 같은 월에 모두 보유한 경우 (월1회 인정)\n※ 1회차 인정요건 : 급여입금 (또는 가맹점대금 입금) + 주거래이체\n\n예시\n(’22년 4월) 급여 입금 + 아파트 관리비 출금 : 1회차 인정\n(’22년 5월) 가맹점대금 입금 + 신용(체크)카드 결제대금 출금 : 1회차 인정\n(’22년 7월) 급여 미입금 + 공과금 출금 : 1회차 미인정\n주택청약종합저축 우대\n(연 1.00%) 아래의 ① 또는 ② 에 해당하는 경우 우대금리 제공\n\n① 당행 주택청약종합저축 신규 또는 보유 손님\n이 예금 가입일로부터 3개월이 되는 달의 말일기준 당행 ‘주택청약종합저축’ 을 신규 또는 보유하고 만기 시점까지 유지한 경우\n\n② 타행 주택청약종합저축 보유 확인 손님\n이 예금 가입일까지 ‘하나원큐(스마트폰뱅킹)’를 통하여 오픈뱅킹서비스를 가입하고, 주택청약종합저축을 등록·조회한 경우\n\n※ 오픈뱅킹서비스 등록·조회방법 : 하나원큐(스마트폰뱅킹)로그인 → 오픈뱅킹서비스 신규가입 → 기관추가 및 해당상품 등록 → 하나원큐내 ‘전계좌조회_오픈뱅킹’ 을 통해 정상조회시 완료\n마케팅동의 우대\n(연 0.50%) 이 예금 가입 전 하나은행 상품,서비스 마케팅 동의 항목 모두를 동의한 경우\n(주1) 급여 인정 기준\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n(주2) 주거래이체 인정기준 : 하나카드 결제(신용/체크), 아파트관리비 자동이체 출금, 공과금 자동이체 출금","수수료 우대":"당행의 입출금통장에서 이 예금으로 매월 자동이체를 통하여 입금하는 경우, 익월 1일부터 말일까지 아래의 수수료우대서비스를 제공합니다.\n1. 당행자동화기기, 인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행이체수수료 (월 10회 면제)\n2. 당행자동화기기를 통한 현금인출 수수료 (월 10회 면제)\n※ 이 예금의 신규 가입후 다음달 말일까지는 자동이체 등록여부와 관계없이 수수료 우대서비스 제공\n※ 이 예금으로 자동이체 출금 계좌 등록한 입출금통장을 통한 거래에서 발생하는 수수료에 한하여 제공","세제혜택":"비과세종합저축 가입 가능 (전 금융기관 통합한도 범위 내) 단, 만기 후 이자는 소득세 부과\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됨","유의사항":"정부지원금에 관한 세부내용(지원조건, 지원금액, 지급방법 등)은 보건복지부 『자산형성지원 통장사업 (운영지침)』에서 정하는 바에 따릅니다.\n정부지원금은 신규시 지정한 지원금 수령계좌로 입금됩니다.\n계좌의 해지는 지방자치단체 (‘운영지침’에서 명시한 기관)등에 사전 신청 후, 은행 앞 해지 승인이 통지된 경우에만 가능 합니다.\n적립 한도는 당월 적립 인정기간 內에 최소 10만원이상 최대 50만원이내입니다.\n【당월 (적립월) 인정기간 : 전월 23일(휴일인 경우 익영업일)~당월22일(휴일인 경우 익영업일)】\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및\n이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한 (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경) ■ 기존 가입고객에 대한 적용 여부 : 적용\n<급여인정조건>\n변경 전:\n(주1) 급여입금 인정 기준: 타인으로부터 건당 50만원 이상 입금시, 급여를 의미하는 적요 (급여,월급,연봉,봉급,상여,성과,보너스,salary,bonus 등)를 포함하여 입금되거나 또는 사전 지정한 급여일 전후 1영업일 내에 입금되는 자금\n변경 후:\n(주1) 급여 인정 기준\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"3년","금리(연율,세전)":"2.0%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (183, 3, '미래행복통장', '{"상품특징":"가입자가 목돈 마련을 위해 적립금을 납입 하면, 『북한이탈주민 자산형성지원제도 운영지침(이하 “운영지침”)』에서 정한\n정부지원금 관리기관에서 지원금을 적립하고, “운영지침”의 요건 충족 시 지원금을 지급받을 수 있는 상품","가입대상":"“운영지침”에 따라 미래행복통장 사업 수행기관(이하 “기관” 이라 함)이 은행에 가입승인을 통지한 북한이탈주민\n(실명의 개인, 이하 “고객” 이라 함) 및 기관","예금종류":"① 고객 : 정액적립식 ② 기관 : 자유적립식","가입기간":"① 고객 : 72개월 ② 기관 : 78개월","가입금액":"① 고객 : 10만원 이상~50만원 이내(5만원 단위)\n② 기관 : 제한없음","이자지급방법":"만기일시지급식 : 만기(후)해지시 이자를 지급","적립방법":"고객은 매월 1회에 한해 최초 가입 시 지정한 금액만 입금 가능합니다.\n단, 최초 가입 시 지정한 금액을 기관이 은행에 변경 승인 통지한 경우에 한해 변경 가능합니다.\n고객은 계약기간에도 불구하고 기관이 은행에 통지한 운영지침에 따른 적립 종료일까지만 적립이 가능합니다.\n기관은 적립금액 및 적립횟수에 제한 없이 자유롭게 입금 가능합니다.","금리":null,"우대금리":"이 예금은 아래 항목을 충족하는 경우, 고객의 적립금에 대하여 최대 연 2.5%의 우대 금리를 만기해지 시 지급합니다.\n이 예금 가입 후 만기 전전월말 기준, 24회차 이상 본인명의의 하나은행 입출금통장을 통해 급여입금실적(주1)을 보유하거나 가맹점(결제)대금 입금 실적을 보유한 경우 (연 1.0%)\n이 예금 가입 후 3개월 이내 주택청약종합저축을 신규하고 만기시점까지 보유한 경우 (연 1.0%)\n이 예금 가입 후 만기 전전월말 기준, 본인명의 하나은행 입출금통장을 통해 하나카드(신용/체크)의 결제실적을\n보유한 경우 (연 0.5%)\n(주1)급여이체 인정기준\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n\n※ 2022.2.16이후 가입자에 한해 적용(기존 가입자 미적용)","수수료 우대":"고객이 본인명의의 당행 입출금통장에서 이 예금 계좌로 매월 자동이체를 통하여 적립금을 입금하는 경우, 익월 1일부터\n말일까지 아래의 수수료우대서비스를 제공합니다.\n당행자동화기기, 인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행이체 수수료(월 10회 면제)\n당행자동화기기를 통한 현금인출 수수료(월 10회 면제)\n※ 이 예금의 신규 가입 후 다음달 말일까지는 자동이체 등록여부와 관계없이 수수료 우대서비스 제공","세제혜택":"전 금융사 통합한도 내에서 비과세 저축으로 가입 가능","기타":"기관은 “운영지침”이 정하는 바에 따라, 월 적립금을 납입하는 고객을 대상으로 지원금을 적립합니다.\n기관은 지원금을 적립하여, 고객이 “운영지침”의 요건 충족 시 일괄 지급합니다.","유의사항":"고객은 “운영지침”에 명시한 기관에 해지 신청을 하고, 기관이 은행에 해지 승인을 통지한 경우에 한하여 적립금을\n해지할 수 있습니다.\n기관은 “운영지침”이 정하는 바에 따라 해지 요건 충족 시 지원금을 해지하고, 지원금은 신규 시 고객이 지정한\n지원금 수령계좌로 입금됩니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","원금 및 이자지급제한":"계좌에 압류, 가압류 등이 등록될 경우 원금 및 이자지급이 제한 될 수 있음\n예금잔액증명서 발급 당일 잔액 변동이 불가","위법계약해지권":"설명의무 위반 등 금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당계약의 해지를 요구 할 수 있습니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<급여인정기준>\n변경 전 :\n타인으로부터 건당 50만원 이상 입금 (급여적요 포함 입금 또는 급여지정일 전후 1영업일내 입금)\n(급여 적요: 급여, 월급, 봉급, 연봉, 급료, 상여, 성과, 보너스, 월보수, SALARY, PAY, BONUS 등)\n변경 후 :\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (184, 3, '대전시미래두배청년통장', '{"상품특징":"대전광역시 청년기본조례에 따른 『미래두배청년통장 사업운영지침(이하, 운영지침)』에서 정한 대상자가 자산을 형성할 수 있도록 적립금 납입시 시지원금을 지원하는 적립식 상품","가입대상":"“운영지침”에 따라 ‘대전광역시 미래두배청년통장 사업 수행기관’(이하 “기관”이라 함)이 은행에 가입 승인을 통지한 실명의 개인","상품유형":"적립식 예금","가입기간":"24개월 또는 36개월(택일)","가입 및 적립금액":"10만원 또는 15만원 중 택일하며, 매월 가입금액으로 적립기간동안 입금이 가능","이자지급방법":"만기일시지급식 : 만기(후)해지 시 이자를 지급","금리":"기간: 2년, 금리(연율,세전): 2.8%\n기간: 3년, 금리(연율,세전): 2.8%","우대금리":"최대 연 1.20% (2024.07.04 기준, 세전)\n아래 우대항목을 충족하는 경우, 최대 연 1.20%의 우대금리를 만기 해지 시 제공합니다.(단, 중도해지 시 미적용)\n우대항목 우대금리 내용\n급여이체 연 0.60% 이 예금 가입후 3개월이 되는 달의 말일 기준, 본인명의 하나은행 입출금통장을 통해 급여 입금 (주1)된 경우\n카드 결제 연 0.30% 이 예금 가입후 3개월이 되는 달의 말일 기준, 본인명의 하나은행 입출금통장을 통해 하나카드(체크/신용) 결제 실적이 있는 경우\n주택청약종합저축\n보유 또는\n공과금 자동이체 연 0.30% 이 예금 가입후 3 개월이 되는 달의 말일 기준, 본인명의 주택청약종합저축을 보유하고 있거나, 본인명의의 하나은행 입출금통장을 통하여 2건 이상의 공과금 자동이체 거래를 한 경우\n(주1) 급여입금 인정기준:\n급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","수수료 우대":"당행의 입출금통장에서 이 예금으로 매월 자동이체를 통하여 입금하는 경우, 익월 1일부터 말일까지 아래의 수수료우대서비스를 제공합니다.\n1. 당행자동화기기, 인터넷뱅킹, 스마트폰뱅킹, 폰뱅킹(ARS)을 통한 타행이체수수료 (월 10회 면제)\n2. 당행자동화기기를 통한 현금인출 수수료 (월 10회 면제)\n※ 이 예금의 신규 가입후 다음달 말일까지는 자동이체 등록여부와 관계없이 수수료 우대서비스 제공\n※ 이 예금으로 자동이체 출금 계좌 등록한 입출금통장을 통한 거래에서 발생하는 수수료에 한하여 제공","만기앞당김지급":"불가","일부해지":"불가","양도 및 담보제공":"불가","세제혜택":"비과세종합저축 가입 가능 (전 금융기관 통합한도 범위 내) 단, 만기 후 이자는 소득세 부과\n관련 세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있으며, 계약기간 이후의 이자는 과세됨","유의사항":"시지원금에 관한 세부내용(지원조건, 지원금액, 지원방법 등)은 대전광역시 청년기본조례에 따른 『미래두배청년통장 사업운영 지침』에서 정하는 바에 따릅니다.\n시지원금은 신규시 지정한 지원금 수령계좌로 입금됩니다.\n계좌의 상품가입 및 해지를 위해서는 ‘대전광역시 미래두배청년통장 사업 수행기관’이 정한 방법에 따라, 기관에 사전 신청 후, 승인이 통지된 경우에만 상품가입 및 해지가 가능 합니다.\n※ 금융상품에 관한 계약을 체결하기전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한 (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.01 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<우대금리 급여입금 인정조건>\n변경 전\n급여입금 인정조건: 건당 50만원 이상 입금시, 급여적요(급여, 월급, 봉급, 상여, SALARY, BONUS 등)를 포함하여 입금 되거나 사전등록한 급여지정일 전후 1영업일 내 자금이 입금되는 월의 다음달에 제공합니다.(단 타인으로부터 이 통장에 입금된 건에 한함)\n변경 후\n급여입금 인정조건:\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드’로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (185, 3, '하나 청년도약계좌', '{"상품특징":"청년의 중장기 자산형성 지원을 위한 금융 상품으로, 정부 기여금 및 비과세 혜택을 제공하는 적립식 상품","가입대상":"가입대상 기준에 따라 선정된 개인 및 개인사업자","가입기간":"5년","가입금액":"1천원 이상 ~ 70만원 이하(천원 단위)\n※ 단 「청년희망적금」 만기해지자가 「청년도약계좌」를 가입하는 경우, 「청년희망적금」 만기지급금 이내에서 최초 납입금액으로 일시납입이 가능함","적립한도":"매월 1천원 이상 ~ 70만원 이하(천원 단위)\n연간 840만원 이하(가입일 기준으로 1년)","이자지급방법":"만기일시지급식 : 만기(후)해지 시 이자를 지급","적립방법":"자유적립식","금리":"금리안내\n※ 최초3년 고정, 3년 초과~5년 이내 [매 1년마다 변동]\n- 경과시점의 고시된 금리로 1년 단위 변동 적용","우대금리":"최대 연 1.50% (2025.07.10 기준, 세전)\n아래 우대항목을 충족하는 경우, 최대 연 1.50%의 우대금리를 만기 해지 시 제공합니다.(단, 중도해지 시 미적용)\n우대항목 우대금리 내용\n급여(가맹점대금) 이체 연 0.60% 이 예금 가입 후 만기 전전월말 기준, 본인명의 하나은행 입출금통장을 통해, 36회차 이상 급여입금 (주5) 또는 가맹점(결제)대금 입금 실적을 보유한 경우 (월 1회 인정)\n카드 결제 연 0.20% 이 예금 가입 후 만기 전전월말 기준, 본인명의 하나은행 입출금통장을 통해, 36회차 이상, 월 합산 10만원 이상의 하나카드 결제 실적(신용/체크)을 보유한 경우\n목돈마련 응원 연 0.10% 이 예금 가입일로부터 직전 1년간 적금 또는 예금을 미 보유한 경우\n(단, 청년희망적금, 청년내일저축계좌, 청년도약계좌, 주택청약종합저축 예외)\n마케팅 동의 연 0.10% 이 예금 가입전 하나은행 상품,서비스 마케팅 동의 항목 모두를 동의한 경우\n소득 + 플러스 최대 연 0.50% 이 예금 가입신청 시점 및 가입후 1년 주기로 심사한 개인소득금액의 소득요건 충족 (주6) 횟수에 따라 우대금리 제공\n- 5회 : 0.5% / 4회 : 0.4% / 3회 : 0.3% / 2회 : 0.2% / 1회 : 0.1%\n(주5) 급여이체 인정기준\n① 급여를 의미하는 문구가 포함된 적요*로 입금된 금액이 월 50만원 이상인 경우\n(ex. *급여, 월급, 봉급, 연봉, 상여, 성과, 보너스, SALARY, PAY 등/ 등록직장명)\n② 회사에 의한 ‘급여코드‘로 입금된 금액이 월 50만원 이상인 경우\n③ 손님이 ‘급여일’을 지정(등록)하고, 해당일 포함 전후 1영업일 사이에 건당 50만원 이상 입금된 경우\n※ 본인거래 및 비급여성(카드대금, 보험료 등)거래, 자동화기기 입금분, 가지급 등 비급여성 적요는 제외\n※ 직장명 또는 급여일 등록 이후 입금 건부터 적용\n(주6) 총급여 2,400만원 이하, 종합소득과세표준에 합산되는 종합소득 1,600만원 이하, 연말정산한 사업소득 · 연금소득 · 종교인소득1,600만원 이하인 경우\n만기수취이자 예시(최고금리 기준)\n월 납입액 70만원/계약기간 60개월/연 6.0% 적용 시 = 이자 6,405,000원(세전)\n※ 위 사항은 예시이며 납입액, 계약기간, 적용금리 등은 세부사항에 따라 변동될 수 있음\n※ 「청년도약계좌」의 기본금리는 최초 3년 【고정】, 3년초과~5년이내 【매1년마다 변동】 적용됨","정부기여금":"청년의 경제적 안정을 위해 본인 적립액에 비례하여 소득 구간별 지급 비율로 계산하여 적립하고, 만기(특별중도) 해지한 경우에는 정부기여금의 100%를, 3년이상 경과후 중도해지한 경우에는 정부기여금의 60%를 이자소득과 함께 지원 (단, 외국인은 정부기여금 지원 비대상)\n정부기여금은 신청 시점 및 가입후 1년 주기로 실시하는 개인소득 심사결과(총5회)에 따라 결정되며 결정된 정부기여금은 매년 가입일이 속한 달의 익월 초부터 적용됨 (단, 신청시점에 실시한 개인소득 심사결과에 따른 정부기여금은 가입일부터 익년도 가입일이 속한 달까지 적용)\n정부기여금 지급기준\n소득구간 개인소득 정부기여금(Ⅰ)\n산정 기준금액 정부기여금(Ⅰ)\n지급비율\n총급여 기준 종합소득 기준\n① 2,400만원 이하 1,600만원 이하 월 적금 납입원금과 40만원 중 적은 금액 정부기여금(Ⅰ) 산정기준금액의 6.0%\n②\n2,400만원 초과\n3,600만원 이하\n1,600만원 초과\n2,600만원 이하\n월 적금 납입원금과 50만원 중 적은 금액 정부기여금(Ⅰ) 산정기준금액의 4.6%\n③\n3,600만원 초과\n4,800만원 이하\n2,600만원 초과\n3,600만원 이하\n월 적금 납입원금과 60만원 중 적은 금액 정부기여금(Ⅰ) 산정기준금액의 3.7%\n④\n4,800만원 초과\n6,000만원 이하\n3,600만원 초과\n4,800만원 이하\n월 적금 납입원금과 70만원 중 적은 금액 정부기여금(Ⅰ) 산정기준금액의 3.0%\n⑤ 6,000만원 초과 4,800만원 초과 미지급 미지급\n소득구간 개인소득 정부기여금(Ⅱ)\n산정 기준금액 정부기여금(Ⅱ)\n지급비율\n총급여 기준 종합소득 기준\n① 2,400만원 이하 1,600만원 이하 월 적금 납입원금 중 40만원 초과금액 정부기여금(Ⅱ) 산정기준금액의 3.0%\n②\n2,400만원 초과\n3,600만원 이하\n1,600만원 초과\n2,600만원 이하\n월 적금 납입원금 중 50만원 초과금액 정부기여금(Ⅱ) 산정기준금액의 3.0%\n③\n3,600만원 초과\n4,800만원 이하\n2,600만원 초과\n3,600만원 이하\n월 적금 납입원금 중 60만원 초과금액 정부기여금(Ⅱ) 산정기준금액의 3.0%\n④\n4,800만원 초과\n6,000만원 이하\n3,600만원 초과\n4,800만원 이하\n미지급 미지급\n⑤ 6,000만원 초과 4,800만원 초과 미지급 미지급\n유의사항\n정부기여금은 서민금융진흥원이 정부예산으로 지급하는 지원금으로, 정책 및 관련 법률 등이 변경될 경우 지급이 중단될 수 있음\n정부기여금은 기본금리로 계산한 이자를 정부기여금에 더하여 만기(특별중도)해지시 지급함\n단, 정부기여금에 대한 이자는 서민금융진흥원이 은행에 입금한 날부터 해지일 전일까지의 기간 동안 계산한 이자를 적용\n정부기여금이 부정 이익으로 확인되는 경우에는 관련 법령 (주7)에 따라 부정이익 환수 등의 제재를 받을 수 있음\n중도해지 후 재가입하는 경우 정부기여금의 지급비율이 변경 될 수 있음\n계약기간 동안 추심, 상계등으로 가입자가 본인 적립금을 수령할 수 없는 경우 정부기여금을 지급하지 않음\n가입일로부터 3년 미만 중도해지시 정부기여금 미지급(특별중도해지 제외) 단, 가입 3년 경과 후 중도해지시 정부기여금의 60%만 지급\n부분인출시 인출금액에 대한 정부기여금은 환수됨. 다만, 가입 3년 경과 후 부분인출시 인출금액에 대한 정부기여금 중 40%는 환수되며, 나머지 60%는 해지시점에 지급\n상품 가입후 직전년도 금융소득종합과세자로 확인이 되어 납입중지 처리시, 정부기여금 미지급\n「소득세법」 제12조제3호마목에 따른 육아휴직급여, 육아휴직수당이 있는 사람, 「소득세법」 제12조제3호가목에 따른 복무 중인 병(兵)이 받는 급여가 있는 사람은 비과세 소득만 있는 자로 보지않으며 이에 따라 가입한 사람은 정부기여금 지급비율 6%를 적용함\n(주7) 「공공재정 부정청구 금지 및 부정이익 환수 등에 관한 법률」에 따른 부정이익 환수 등","일시납입 신규\n(청년희망적금 만기해지자)":"「청년희망적금」 만기해지 시, 「청년희망적금」 만기지급금 이내에서 「청년도약계좌」 일시납입 신규 가능\n※ 일시납입을 희망하는 경우, 은행 앱에서 신청 후 청년도약계좌 웹사이트(ylaccount.kinfa.or.kr)를 통해 일시납입 정보를 등록해야 일시납입 가입신청이 완료됨\n일시납입 금액을 선납으로 인정하여, 일시납입 기간 동안 별도 추가납입을 할 수 없으며, 최초 납입한 일시납입 금액만큼 가입 월을 차감\n일시납입한 금액에 대한 정부기여금은 가입 시 적용되는 지급기준의 지급비율을 적용하여 지급하며 일시납입 기간 종료 전에 특별중도해지 할 경우 가입유지기간에 해당하는 기여금만 지급\n「청년희망적금」만기해지자가 아니거나, 일시납입 금액이 「청년희망적금」 만기지급금을 초과하는 경우, 정부기여금을 지급하지 않으며 납입이 중지됨","재가입":"중도해지(특별중도해지 포함) 후 해지일이 포함된 월을 기준으로 2개월 후부터 가입신청 가능\n재가입 시 정부기여금은 계약기간에서 기(旣) 가입기간 만큼 차감한 비율(이하 ‘조정비율’)을 적용하여 산출 (원단위 미만은 절사)\n※ 조정비율 = (계약기간-재가입 전 가입기간) / 계약기간\n※ 기 가입기간 중 일부 기간에만 납입한 경우에도 가입기간을 기준으로 계산\n※ 가입기간 : 가입일부터 해지일까지 기간을 개월수로 환산하여 올림한 기간으로, 가입기간\n             산정은 ‘월’ 기준이며, 일단위는 월로 올림하여 계산\n             [예시] ‘23.8.1 가입후’  23.9.15해지 -> 재가입시 2개월을 차감하여 계산한 조정비율 적용\n※ 2회이상 재가입할 경우 각각 가입기간을 계산하여 합산","특별중도해지":"아래의 사유 발생시 특별중도해지 가능 (특별해지 사유신고서 및 증빙서류 제출 필수)\n가입자의 사망ㆍ해외이주\n계약 해지일 전 6개월 이내에 발생한 다음 각 항목의 사유\n가. 천재지변\n나. 가입자의 퇴직\n다. 사업장의 폐업\n라. 가입자의 3개월 이상의 입원치료 또는 요양이 필요한 상해ㆍ질병의 발생\n마. 저축취급기관의 영업의 정지, 영업인가ㆍ허가의 취소, 해산결의 또는 파산선고\n바. 가입자의 주택 취득: 과거에 주택을 소유한 사실이 없는 가입자가 본인이 거주할 목적으로 주택을 취득한 경우로서 취득일 당시 「소득세법」 제99조 1항에 따른 기준시가가 5억원 이하인 「주택법」 제2조 제6호에 따른 국민주택규모의 주택을 취득한 경우로 한정\n사. 가입자의 혼인 또는 출산(배우자의 출산 포함)","부분인출":"가능 (가입 2년 경과자에 한해 납입액의 최대 40% 이내로 1회 인출 가능, 법적제한 계좌는 거래불가)\n단, 부분인출 시점에 따라 적용되는 이자 및 과세여부, 정부기여금 환수여부 등이 상이함\n- 가입 3년 미만 : 부분인출금액에 대한 정부기여금은 환수, 중도해지금리 적용, 이자소득세 과세\n- 가입 3년 이상 : 부분인출금액에 대한 정부기여금의 40% 환수, 기본금리 적용, 이자소득세 비과세","세제혜택":"비과세저축으로 가입\n- 신규일부터 만기일 전일까지 본인납입금액과 정부기여금에 발생한 이자에 대하여 비과세 적용\n- 단, 아래의 경우 비과세 미적용\n만기일 이후 이자\n중도해지계좌 (단, 가입 3년 이상 경과자의 중도해지, 특별중도해지 제외)\n부분인출금액에 대한 이자(단, 가입 3년 경과 후 부분인출하는 경우 제외)\n관련세법이 개정될 경우 세율이 변경되거나 세금이 부과될 수 있음\n단, 조세특례법 제 91조의 22에 따른 과세특례적용요건을 갖추지 못한 것이 확인된 경우, 이 적금에서 발생한 이자소득에 대해 소득세 부과","유의사항":"상품가입전 서민금융진흥원을 통해 가입자격 충족여부를 확인합니다.\n만기일이 휴일인 경우, 익영업일 이후 해지 가능합니다.\n이 적금 가입시 서민금융진흥원이 가입자에게 지급하는 정부기여금의 지급조건, 지급액, 지급시기 및 방법 등에 동의한것으로 봅니다.\n이 예금은 비과세 혜택 및 본인 적립금에 매칭하여 정부기여금을 지급하는 상품입니다.\n(본인 적립금 미납입시, 정부기여금 지급되지 않음)\n이 예금은 ‘하나은행’과 ‘서민금융진흥원’의 업무협약에 의해 운영되는 상품으로, 향후 업무협약 및 관련법 등의 변경에 따라 상품내용이 변경될 수 있습니다.\n서민금융진흥원에서 정한 정부기여금 지급기준 및 세제혜택 관련 법령(조세특례제한법 제91조의22)에서 정하는 바를 따릅니다.\n입금거래 정정(취소·변경 등)은 당월(입금월 초일부터 말일까지)에만 가능합니다.\n※ 금융상품에 관한 계약을 체결하기전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","원금 및 이자지급제한":"계좌에 압류, 가압류, 질권설정 등이 등록될 경우 원금 및 이자지급 제한 (일부해지 포함)\n※ 민사집행법에 따라 최저생계비 이하 등 압류금지채권에 해당하는 경우에는 법원에 압류금지채권 범위 변경 신청 등을 통해 압류를 취소할 수 있습니다.\n예금잔액증명서 발급 당일에는 잔액 변동 불가","위법계약해지권":"금융소비자 보호에 관한 법률 제47조에 따른 위법계약해지 사유가 발생한 경우, 계약체결일로부터 5년 이내 범위에서 위반사실을 안 날로부터 1년 이내에 서면 등으로 해당 계약의 해지를 요구할 수 있습니다. 이 경우 금융회사는 해지를 요구받은 날부터 10일 이내에 금융소비자에게 수락 여부를 통지하여야 하며, 거절할 때에는 거절사유를 함께 통지하여야 합니다. 만약 금융소비자의 요구가 정당한 것으로 판단될 경우 수수료 등 계약해지와 관련한 추가 비용 부담없이 계약해지가 가능합니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2025.07.10 변경) ■ 기존 가입고객에 대한 적용 여부 : 적용\n<부분인출 서비스 시행>\n변경 전\n해당사항 없음\n변경 후\n가능 (가입 2년 경과자에 한해 납입액의 최대 40% 이내로 1회 인출 가능, 법적제한 계좌는 거래불가)\n단, 부분인출 시점에 따라 적용되는 이자 및 과세여부, 정부기여금 환수여부 등이 상이함\n가입 3년 미만 : 부분인출금액에 대한 정부기여금은 환수, 중도해지금리 적용, 이자소득세 과세\n가입 3년 이상 : 부분인출금액에 대한 정부기여금의 40% 환수, 기본금리 적용, 이자소득세 비과세","예금자 보호":"이 예금은 예금자보호법에 따라 원금과 소정의 이자를 합하여 1인당 \"5천만원까지\"\n(본 은행의 여타 보호상품과 합산) 보호됩니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["기간","금리(연율,세전)"],"데이터":[{"기간":"5년","금리(연율,세전)":"4.5%"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (186, 4, 'BEST 신용대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"국민인 거주자로 연소득 22백만원 이상이고 재직(사업영위) 기간 6개월 이상인 손님","대출한도":"최대 1억원","대출기간":"만기일시상환(일반대출, 통장대출) : 1년\n분할상환: 5년","상환방식":"만기일시상환(일시대출,통장대출) 원(리)금 균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"소득증명서류\n재직증명서(급여소득자)\n사업자등록증(사업소득자)","금리인하 요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다)이내에 해당 계약의 해지를 요구할 수 있습니다\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터 (1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%\n변경 후\n중도상환해약금율 0.04%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (187, 4, 'CSS대출', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"하나은행의 신용평가시스템(CSS)을 이용하여 평가된 고객의 신용도에 따라 무보증으로 최대 1억원까지 대출하여 드립니다.","대출대상":"당행의 신용평가시스템(CSS)을 이용한 신용평가 후 대출한도가 산정되는 분","대출한도":"최대 1억원","상환방식 및 대출기간":"만기일시상환, 통장대출 : 1년 이내\n원(리)금균등분할상환 : 3년 이내","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율 : 0.04%","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 5천만원 이하 5천만원 초과\n1억원 이하 1억원 초과\n10억원 이하 10억원 초과\n인지세액 비과세 7만원 15만원 35만원\n고객부담 - 3만 5천원 7만 5천원 17만 5천원\n은행부담 - 3만 5천원 7만 5천원 17만 5천원","필요서류":"소득 및 재직 증명서류\n(상담 시 추가 제출서류가 있을 수 있습니다.)","기타":"신용평가시스템(CSS:Credit Scoring System)\n고객의 개인정보 및 거래상황 등을 종합하여 고객의 신용상태를 객관적으로 평가하는 첨단 시스템입니다. CSS는 방대한 데이터베이스와 시스템 운영 노하우, 효율적인 모델을 보유해야만 정확한 신용평가가 가능합니다. 하나은행은 국내은행 최초로 1996년 12월부터 가계신용대출에 신용평가시스템을 도입, 운영해 오고 있습니다. 가장 오래 운영한 만큼 누구보다 많은 자료와 강력한 모델을 구축하고 있어 가장 합리적으로 고객의 신용을 평가할 수 있습니다.","금리인하요구권":"금리인하요구권 행사가능 상품입니다.\n본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약철회권 행사가능 상품입니다.\n대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n대출계약 철회권을 행사한 경우에는 중도상환수수료가 먼제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권융행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년(계약체결일로부터 5년이내) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구 받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","자료열람요구권":"자료열람요구권 행사가능 상품입니다.\n금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지·관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 법령, 제3자의 이익침해, 영업비밀의 침해 등의 사유가 있는 경우 이를 금융소비자에게 알리고 자료열람을 제한하거나 거절할 수 있습니다.","유의사항":"손님의신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.\n- 연체이자율[대출 이자율+연체가산이자율(연3%)] : 최고 연 15%입니다.\n상품에 따라 연체이자율이 일부 달라지는 경우가 있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n대출원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지( www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n기존 가입 고객에 대한 적용 여부 : 미적용\n<중도상환해약금율 변경>\n변경 전 : 0.7% (단,신용대출 인터넷/모바일 신청&약정시 0.5%)\n변경 후 : 0.04%","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"91물 CD유통수익률","금리변동주기":"3개월","기준금리":"2.500","가산금리":"4.088","우대금리":"0.900","최저~최고금리":"5.688 ~ 6.588"},{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"4.079","우대금리":"0.900","최저~최고금리":"5.695 ~ 6.595"},{"금리구분":"1년물 금융채 유통수익률","금리변동주기":"1년","기준금리":"2.498","가산금리":"3.990","우대금리":"0.900","최저~최고금리":"5.588 ~ 6.488"},{"금리구분":"COFIX 신규취급액","금리변동주기":"6개월","기준금리":"2.540","가산금리":"4.055","우대금리":"0.900","최저~최고금리":"5.695 ~ 6.595"},{"금리구분":"COFIX 신잔액","금리변동주기":"6개월","기준금리":"2.630","가산금리":"3.525","우대금리":"0.900","최저~최고금리":"5.255 ~ 6.155"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (188, 4, 'HANA 전문직 소호대출', '{"대출대상":"변호사, 수의사, 공인회계사, 변리사, 공인노무사, 손해사정인, 법무사, 세무사, 관세사, 건축설계사","대출금리":"변동금리 최저 연 4.887% ~ 최고 연 11.437%\n[2025.07.07 기준금리(91일물 CD유통수익률) 2.510% + 가산금리 (최저)3.637%, (최고)8.927% – 부수거래감면 등 우대금리 감면 1.26% 적용 기준]\n* 이자 산정방법 : 최저이율[기준금리+가산금리(ASS 1등급 기준)-우대금리] / 최고이율[기준금리+가산금리(ASS 10등급 기준)]\n* 상기 산정 금리는 대출금액 5천만원, 대출기간 1년, 당행 내부 신용(ASS) 최저 1등급/최고 10등급, 일시상환, 운전자금대출 기준입니다.\n* 가산금리는 신용등급, 대출금액, 담보종류, 시설자금대출 취급여부 등에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경될 수 있습니다.\n* 부수거래감면 등 우대금리: 사업자 신용카드 가입 및 실적 보유 / 결제성 보유 각 0.3%, 상품성 보유 0.2%, 공공기관 또는 지자체 산하기관의 「소상공인 경영컨설팅」 이수 0.2%, 금융연수원 「자영업자가 꼭 알아야 할 금융상식」 교육이수 0.1%, 사업자 주거래 우대통장 월 평잔 3백만원 이상 0.1%, 등 거래실적종류에 따라 최대 1.260% 우대\n* 연체대출금리는 적용금리+3.0%과 연 15.0%중 낮은 금리를 적용합니다.\n* 이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n* 대부업등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고금리는 연20%입니다.\n* 기준금리 안내( 바로가기) : 상품공시실 → 대출상품 → 금리안내 → 대출(가계대출기준금리) → 91일물 CD유통수익률","대출한도":"최대 4억원","상환방식 및 대출기간":"만기일시상환, 마이너스통장대출 : 1년 이내(당초 약정기간 포함 최장 10년 이내)\n매월 원(리)금분할상환 : 최장 3년 이내","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율 : 고정금리 0.04%, 변동금리 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","한도약정\n수수료":"산식 : 한도약정액 X 한도약정수수료율(0.3%) X 약정기간 ÷ 365일(윤년 366일)\n※ 통장대출 취급시 부과됩니다.(단, 약정한도미사용수수료 납부 손님은 면제)","약정한도미사용\n수수료":"산식 : (약정한도평잔 - 운용기간 중 여신평잔) X 약정한도미사용수수료율(0.5%) X 운용기간 ÷ 365일(윤년은 366일)\n※ 통장대출 취급시 부과됩니다.(단, 한도약정수수료 납부 손님은 면제)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결 시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원","필요서류":"사업자등록증 사본, 신분증, 부가세과세표준증명원, 소득금액증명원, 납세증명서 등\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하요구권":"기업신용평가 등에 따라 손님의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n회사채 등급상승, 재무상태 개선(감사보고서 등), 특허 취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약 해지권 행사가능 상품입니다.\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날‘부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"은행의 정책 및 시장금리 상황에 따라 금리는 변동될 수 있습니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류,경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용 점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래관련 불이익을 받을 수 있습니다.\n대출원리금 연체시 계약만료기한전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.05.29 변경)\n■ 기존 가입고객에 대한 적용 여부: 적용\n<최고대출한도 변경>\n변경전 : 수의사 3억5천만원\n변경후 : 수의사 4억원","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (189, 4, 'INNO-BIZ 대출', '{"대출대상":"중소벤처기업부의 기술혁신형 중소기업(INNO-BIZ) 육성계획에 의해 기술혁신형 중소기업(INNO-BIZ)으로 선정한 중소기업을 대상으로 한다.","대출한도":"시설자금대출: 당해 시설을 위한 총 소요자금 범위 내\n운전자금대출: 1회전 소요자금 범위에 의한 여신취급 가능액 범위내","대출금리":"변동금리 최저 연 3.00%\n[2019.12.16 현재, 기준금리 1.53%(91일물 CD유통수익률) + 가산금리 1.470%(내부신용등급 B1등급, 대출기간1년,\n일시상환, 시설자금, 기술신용보증서 100%, 대출금액 1억원 기준)]\n* 가산금리는 신용등급, 대출기간, 대출금액, 대출과목, 상환방식에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경될 수 있습니다.\n* 기준금리 안내(바로가기 ) : 상품공시실 → 대출상품 → 금리안내 → 대출(가계대출기준금리) → 91일물 CD유통수익률","상환방식 및 대출기간":"만기일시상환(건별대출, 한도대출) : 기술보증기금보증서 보증기간 이내\n분할상환 : 기술보증기금보증서 보증기간 이내","이자계산방법":"대출금에 연이율과 대출일수를 곱한 후 이를 360일로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출자율 x 이자일수 ÷ 360\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등)단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/인터넷(스마트폰) 연장 등의 방법을 통해 신청 가능합니다.","대상담보":"기술보증기금보증서","중도상환해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율\n- 부동산/동산담보 : 고정금리 1.4%, 변동금리 1.2%\n- 신용/기타담보 : 고정금리 1.1%, 변동금리 1.0%","수수료":"한도대출의 경우 아래의 ①, ②의 수수료 중 택1\n①한도약정수수료 = 한도약정액 x 한도약정수수료율 (0.2%) x 약정기간 ÷ 360일\n②약정한도미사용수수료 = (약정한도평잔 – 운용기간 중 여신평잔) x 한도미사용수수료율(0.3%) x 운용기간 ÷ 360일","대출관련비용":"대출금액 5천만원 초과시 손님 및 은행 각 인지세 50% 부담하며, 담보설정에 따른 국민주택채권 매입비용, 담보해지 시\n말소비용이 발생할 수 있습니다.\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"사업자등록증 사본, 법인등기부등본, 소요자금 증빙서류, 재무제표 등\n※ 신용조사 및 대출심사 시 필요한 서류가 추가될 수 있습니다","금리인하요구권 대상여부":"본 상품은 ''금리인하요구권''의 신청이 가능합니다.","유의사항":"INNO-BIZ기업 선정후라도 기술력 미달 및 성장미흡기업, 도덕성해이기업 등에 해당되면, 선정된 INNO-BIZ기업에서 제외될수 있습니다.\n손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n상기 대출금리는 대출 신규시 적용금리이며 기준금리 변동시 금리가 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n대출원리금 납입이 지연되는 경우 연체이자가 부과되며 채무자의 재산/신 용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n자세한 사항은 상품설명서를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2019.04.17 변경)\n<중도상환해약금율 변경>\n변경 전 : 부동산담보:1.4%, 신용/기타담보 1.1%\n변경 후 :\n부동산담보:고정금리시 1.4% 변동금리시 1.2%\n신용/기타담보:고정금리시 1.1% 변동금리시 1.0%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (190, 4, '공무원가계자금대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"공무원연금법 적용 대상 재직 공무원","대출한도":"최대 5천만원","대출기간":"만기일시상환, 통장대출 : 1년 이내\n원(리)금균등분할상환, 통장대출분할감액 : 5년 이내","상환방식":"만기일시상환, 통장대출(분할감액 포함), 원(리)금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"없음\n(재직 및 소득은 스크래핑을 통해 확인하며, 융자추천서의 경우 비대면 발급 진행합니다.\n필요시 추가 제출서류가 있을 수 있습니다.)","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n* 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제할 의무가 발생될 수 있습니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n※ 대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%(단, 인터넷/모바일 신청&약정시 0.5%)\n변경 후\n중도상환해약금율 0.04%","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"1.900","우대금리":"0.900","최저~최고금리":"3.516 ~ 4.416"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (191, 4, '공무원클럽대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"공무원연금법 적용대상 재직 공무원 및 공무원시험 최종합격자\n정무직, 일반직, 기능직, 고용직, 공안직, 연구직, 교육직, 지도직, 소방직, 교정직, 경찰직 등\n※ 공무원연금 대상자 중 판사 검사 제외","대출한도":"최대 2억원(마이너스 통장대출은 최대 5천만원)","대출기간":"만기일시상환(일시대출,통장대출) : 1년\n원(리)금균등분할상환 : 5년 이내","상환방식":"만기일시상환(일시대출,통장대출), 원(리)금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"소득 및 재직 증명서류\n(상담 시 추가 제출서류가 있을 수 있습니다.)","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n* 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%\n변경 후\n중도상환해약금율 0.04%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (192, 4, '군간부월세자금대출', '{"대출대상":"국방부(소속부대) “전세대부(지원) 추천서”를 받은 군인으로 아래조건을 모두 충족하는 손님\n부동산중개업소를 통해 보증부월세 주택임대차계약을 체결한 임차인(임대인이 공공주택사업자, (주)부영주택인 경우 생략)\n※ 공공주택사업자 : 국가, 지방자치단체, 한국토지주택공사, ‘지방공무원법’ 제49조에 따라 주택사업을 목적으로 설립된 지방공사 및 공무원연금공단\n주택임대차보호법상 대항력과 우선변제권을 확보한 임차인\n성년인 세대주 또는 세대원으로 서울보증보험 보증 가입이 가능한 임차인","대상주택":"아파트, 연립, 다세대, 단독, 주거용 오피스텔","대출금리":"[기준일자 : 2024.11.29, 단위:연%]\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최종금리\n신잔액 COFIX 6개월 3.090 1.550 0.000 4.640\n※ 대출금리는 기준금리 변동 시 금리가 변경됩니다.\n※ 2024.11.29 현재, 내부신용(ASS) 1~13등급, 대출기간 3년, 대출금액 2백만원 기준\n- 이자 산정방법: 최저이율(기준금리 + 가산금리 – 우대금리)\n                최고이율(기준금리 + 가산금리)\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.(상품에 따라 연체이자율이 일부 달라지는 경우가\n있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.)\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","대출한도":"10만원~2백만원 이내 , 매월 월세지원액(추천서상 금액)의 2배 이내에서 산정 \n※ 매월 월세지원액 : MIN[((급지별 대부지원 상한액 – 보증금) X 대출금리 / 12) , 월세]\n- 추천서 발급 시 부대에서 금액 산출, 은행에서 추가 검증","대출기간":"임차계약 만기일 범위 내 최장 3년 이내로 하되 임차계약 만기일, 국방부 “전세대부(지원)추천서”의 융자기간, 대출 만기일은\n일치시키도록 한다.\n※ 대출만기 시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","상환방식 및\n이자납입":"만기일시상환(통장대출), 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급 시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료 시 은행이 정한 기준에 따라 기간연장 가능여부 심사 후 승인을 득한 경우 계약갱신 가능하며, 고객님의\n신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장\n가능합니다.","중도상환\n해약금":"면제","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"확정일자부 임대차계약서(원본)\n전입완료된 주민등록등본(대출신규 후 3일 이내)\n소득·재직·세대주 증명서류\n전세대부(지원) 추천서\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"이사 전 최소 7영업일 전에 대출신청을 하셔야 합니다.\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기 · 연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2024.11.29 변경)\n<대출금리>\n기존 가입고객에 대한 변경 적용여부: 미적용\n변경 전: 신잔액 COFIX (6개월변동) +1.70%\n변경 후: 신잔액 COFIX (6개월변동) +1.55%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (193, 4, '군간부전세자금대출(기본)', '{"대출대상":"국방부(소속부대) “전세대부(이자지원) 추천서”를 받은 군인으로 아래조건을 모두 충족하는 손님\n부동산중개업소를 통해 주택임대차계약을 체결한 임차인(임대인이 공공주택사업자, (주)부영주택인 경우 생략)\n※ 공공주택사업자 : 국가, 지방자치단체, 한국토지주택공사, ‘지방공무원법’ 제49조에 따라 주택사업을 목적으로 설립된 지방공사 및 공무원연금공단\n주택임대차보호법상 대항력과 우선변제권을 확보한 임차인\n성년인 세대주 또는 세대원으로 서울보증보험 보증 가입이 가능한 임차인","대상주택":"아파트, 연립, 다세대, 단독, 주거용 오피스텔","대출금리":"[기준일자 : 2024.11.29, 단위:연%]\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최종금리\n신잔액 COFIX 6개월 3.090 1.550 0.000 4.640\n※ 대출금리는 기준금리 변동 시 금리가 변경됩니다.\n※ 2024.11.29 현재, 내부신용(ASS) 1~13등급, 대출기간 2년, 대출금액 2억원 기준\n- 이자 산정방법: 최저이율(기준금리 + 가산금리 – 우대금리)\n                최고이율(기준금리 + 가산금리)\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.(상품에 따라 연체이자율이 일부 달라지는 경우가\n있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.)\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","대출한도":"최대 3.6억원(아래 ①~③중 적은 금액으로 서울보증보험 보증 가능한도 이내)\n“전세대부(이자지원) 추천서”의 융자지원 승인금액\n임차보증금의 100% 이내\n[해당물건 선순위 설정최고액 + 군간부전세대출금(기본)] 합계가 시세의 100%이내","대출기간":"임차계약 만기일 범위 내 최장 3년 이내로 하되 임차계약 만기일, 국방부 “전세대부(이자지원) 추천서”의 융자기간, 대출 만기일은 일치시키도록 한다.\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","상환방식 및 이자납입":"만기일시상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"면제","대출관련비용":"질권설정통지비용 3만원 손님부담 (군간부전세자금대출(추가)와 동일자 취급하는경우 면제)\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"확정일자부 임대차계약서(원본)\n전입완료된 주민등록등본(대출신규 후 3일 이내)\n소득·재직·세대주 증명서류\n전세대부(이자지원) 추천서\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"이사전 최소 7영업일 전에 대출신청을 하셔야 합니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 서울보증보험 협약 내용에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2024.11.29 변경)\n<대출금리>\n기존 가입고객에 대한 변경 적용여부: 미적용\n변경 전: 신잔액COFIX (6개월변동) + 협약가산금리 1.70%\n변경 후: 신잔액COFIX (6개월변동) + 협약가산금리 1.55%\n상품내용 변경에 관한 사항\n(2022.01.04 변경)\n<대출한도>\n기존 가입고객에 대한 변경 적용여부 : 미적용\n변경 전 : 최대 3억원\n변경 후 : 최대 3.6억원","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (194, 4, '군간부전세자금대출(추가)', '{"대출대상":"군간부전세자금대출(기본)을 보유 또는 동시 신청하는 군인으로 아래조건을 모두 충족하는 손님\n부동산중개업소를 통해 주택임대차계약을 체결한 임차인(임대인이 공공주택사업자, (주)부영주택인 경우 생략)\n※ 공공주택사업자 : 국가, 지방자치단체, 한국토지주택공사, ‘지방공무원법’ 제49조에 따라 주택사업을 목적으로 설립된 지방공사 및 공무원연금공단\n주택임대차보호법상 대항력과 우선변제권을 확보한 임차인\n성년인 세대주 또는 세대원으로 서울보증보험 보증 가입이 가능한 임차인","대상주택":"군간부전세자금대출(기본)과 동일한 임차주택","대출금리":"[기준일자 : 2024.11.21, 단위:연%]\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최종금리\n91물 CD유통수익률 3개월 3.440 3.172 0.9 5.712 ~ 6.612\n6개월물 금융채 유통수익률 6개월 3.409 3.159 0.9 5.668 ~ 6.568\n1년물 금융채 유통수익률 1년 3.249 3.185 0.9 5.534 ~ 6.434\n신규 COFIX 6개월 3.370 3.198 0.9 5.668 ~ 6.568\n신잔액 COFIX 6개월 3.090 2.988 0.9 5.178 ~ 6.078\n※ 최종금리는 대출금액 2억, 대출기간 2년, 신규 전세자금 용도 내부신용등급(ASS) 3등급인 손님에게 적용한 금리입니다.\n- 이자 산정방법: 최저이율(기준금리 + 가산금리 – 우대금리)\n                최고이율(기준금리 + 가산금리)\n- 우대금리는 급여이체 등 거래실적 종류에 따라 0.1%~0.9% 우대\n단, 매월 실적 체크하여 미 충족할 경우 해당금리만큼 조정됩니다.\n- 실제 적용되는 금리는 신용등급 및 거래조건에 따라 가산 또는 감면되어 결정되오니 자세한 금리는 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.(상품에 따라 연체이자율이 일부 달라지는 경우가 있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","대출한도":"최대 5억원(아래 ①~②중 적은 금액으로 서울보증보험 보증 가능한도 이내)\n단, 부부합산 1주택자는 최대 3억원\n“군간부전세자금대출(기본) + 군간부전세자금대출(추가)” 금액이 임차보증금의 95%이내\n[해당물건 선순위 설정최고액 + 전세대출금액] 합계가 시세의 80%이내","대출기간":"임차계약 만기일 범위 내 최장 3년 이내로 하되 “군간부 전세자금대출(기본)”의 대출 만기일과 일치시키도록 한다.\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","상환방식 및 이자납입":"만기일시상환, 통장대출, (부분)원(리)금균등분할상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n* 중도상환해약금률 : 0.61%\n아래의 경우에는 중도상환해약금을 면제합니다.\n① 대출잔여기간 3개월 이내 상환\n② 임차목적물 변경에 따른 당행 전세자금대출 신규 시\n(당행 대환신규금액과 상환금액이 달라도 중도상환해약금 면제 가능)","대출관련비용":"질권설정통지비용 3만원 손님부담\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"확정일자부 임대차계약서(원본)\n전입완료된 주민등록등본 및 전입세대확인서(대출신규후 3일이내)\n소득·재직·세대주 증명서류\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"시장금리 상황 및 협약내용에 따라 대출금리는 변경될 수 있습니다.\n이사전 최소 7영업일 전에 대출신청을 하셔야 합니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 고가주택 보유여부, 규제대상 주택 취득 등\n서울보증보험 협약 내용에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 콜센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.05.09 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%\n변경 후\n중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n* 중도상환해약금률 : 0.61%\n아래의 경우에는 중도상환해약금을 면제합니다.\n① 대출잔여기간 3개월 이내 상환\n② 임차목적물 변경에 따른 당행 전세자금대출 신규 시\n(당행 대환신규금액과 상환금액이 달라도 중도상환해약금 면제 가능)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (195, 4, '군인공제회 퇴직급여 적립금대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"군인공제회 회원으로 군인공제회에서 대출가능자로 확인된 손님","대출한도":"군인공제회에서 승인한 대출가능 한도 이내","대출기간":"만기일시상환(일시대출) : 1년 (최장 10년까지 연장가능)\n원금분할상환 : 1년이상 10년이내\n※ 대출만기일은 대출 취급일에 관계없이 대출기간이 도래하는 해당월 10일로 합니다.","상환방식":"만기일시상환(일시대출), 원금균등분할상환","이자계산방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일 (윤년인 경우 366일) 로 나누어 산출하되 원단위 미만은 절사\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함)전일까지 계산 (한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일로부터 기일 또는 상환일까지 계산\n대출 당일에 회수되는 대출금\n대외기관으로부터 자금을 차입하는 대출금으로써 이자를 상환일까지 지급하는 대출금\n연체기간이 1일인 연체대출채권 및 지급보증대지급금\n대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정 주기(매 1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매 1개월 등) 단위로 납부\n※ 해당상품은 매월 급여일에 국군재정관리단의 급여공제를 통해 분할상환원금 및 이자를 수납합니다.\n예) 첫 이자납부일 : \n급여일 10일 & 대출실행일이 1~20일인 경우, 익월 10일\n급여일 10일 & 대출실행일이 21~말일인 경우, 익익월 10일","이용시간":"(하나원큐) 대출한도 조회 및 신청\n(1) 영업일\n신규대출 06:00~23:00\n대환대출 06:00~15:00\n(2) 휴일\n신규대출 06:00~23:00\n(하나원큐) 대출 약정 및 실행\n(1) 신규대출 06:00~16:00(영업일)\n(2) 대환대출 06:00~15:00(영업일)\n※ 본 상품은 한도조회 이후 대출신청이 가능하며 한도조회 당일 자정까지 하나원큐 앱을 통해 대출 신청을 완료 해야 합니다.\n※ 본 상품은 대출 승인이후 유효기일 내 약정/실행하지 않으면 신청내역은 자동 취소되며 취소된 이후에는 다시 한도조회부터 신청하셔야 합니다.(신규대출: 익영업일까지, 대환대출: 승인당일)","계약해지 또는 갱신방법":"건별대출 계약해지 : 손님이 대출금 전액을 상환한 때\n계약갱신 : 대출금은 약정 기일(만기일)에 상환이 원칙이나 당행에서 정한 일정요건을 갖춘 경우 기간 연장 가능\n※ 대출기간 만료 시 은행이 정한 기준에 따라 기간연장 가능여부 심사 후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 전자금융채널(스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"없음","대출관련비용":"인지세\n인제세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과~1억원 이하 70,000 35,000 35,000\n1억원 초과~10억원 이하 150,000 75,000 75,000\n10억원 초과 350,000 175,000 175,000","필요서류":"공공마이데이터를 통한 재직 및 소득 확인","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","민원처리 및 분쟁조정절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하요구권":"금리인하요구권 적용 비대상 상품입니다.\n본인의 신용상태가 개선되었다고 판단되는 경우 (예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)의 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS (개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익 : 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자 보호에 관한 법률」 에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구 받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명을 받을 권리가 있습니다.\n이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n본 상품은 군인공제회 웹 또는 앱페이지에 연결된 배너를 통해 하나원큐에서 가입가능합니다.\n본 상품은 한도조회 이후 대출신청이 가능하며 한도조회 당일 자정까지 하나원큐 앱을 통해 대출 신청을 완료 해야 합니다.\n본 상품은 대출 승인이후 유효기일 내 약정/실행하지 않으면 신청내역은 자동 취소되며 취소된 이후에는 다시 한도조회부터 신청하셔야 합니다.(신규대출: 익영업일까지, 대환대출: 승인당일)\n손님의 신용도에 따라 대출한도가 차등 적용되며, 대출 취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/ 신용 상의 불이익(압류,경매 등) 이 발생할 수 있으니 주의하시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"변경사항 없음","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (196, 4, '군인생활안정자금대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"1년이상 재직 및 급여이체를 하는 중사이상의 국군","대출한도":"최대 8천만원","대출기간":"만기일시상환, 통장대출 : 1년 이내\n원금균등분할상환, 통장대출분할감액: 1년 ~ 10년","상환방식":"만기일시상환, 통장대출, 원리금균등분할상환, 통장대출분할감액","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"대출 추천서, 복무확인서\n(상담 시 추가 제출서류가 있을 수 있습니다.)","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n* 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%\n변경 후\n중도상환해약금율 0.04%","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"91물 CD유통수익률","금리변동주기":"3개월","기준금리":"2.500","가산금리":"4.088","우대금리":"0.900","최저~최고금리":"5.688 ~ 6.588"},{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"4.079","우대금리":"0.900","최저~최고금리":"5.695 ~ 6.595"},{"금리구분":"1년물 금융채 유통수익률","금리변동주기":"1년","기준금리":"2.498","가산금리":"3.990","우대금리":"0.900","최저~최고금리":"5.588 ~ 6.488"},{"금리구분":"COFIX 신규취급액","금리변동주기":"6개월","기준금리":"2.540","가산금리":"4.055","우대금리":"0.900","최저~최고금리":"5.695 ~ 6.595"},{"금리구분":"COFIX 신잔액","금리변동주기":"6개월","기준금리":"2.630","가산금리":"3.525","우대금리":"0.900","최저~최고금리":"5.255 ~ 6.155"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (197, 4, '다둥이전세론', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"미성년자(만 19세 미만) 자녀가 2명 이상인 가구를 대상으로 한국주택금융공사의 보증서를 담보로 임차보증금의 90% 범위 내에서 주택의 전세자금을 지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님(임차보증금액이 수도권 7억, 지방 5억을 초과하는 경우 대출 불가)\n현재 미성년자(만 19세미만)인 자녀가 2명 이상인 가구\n임차보증금의 5%이상 지급한 만 19세 이상의 세대주\n세대주와 동일세대를 이루고 있는 세대원 중 다음 하나에 해당하는 자\n배우자\n직계존비속 및 그의 배우자\n신청인 및 배우자의 형제자매\n배우자의 직계존비속 및 그의 배우자\n※본인 및 배우자의 주택보유수가 2주택 이상인 경우 대출 취급이 제한 됩니다.","대상주택":"공부상(등기부등본 등) 주거용 주택(미등기 주택도 가능)","대출한도":"최대 2억원 범위 내 아래 ①과 ②중 적은금액으로 함\n임차(전세)보증금의 90% 이내\n신청인(배우자포함)의 연간소득의 최대 5.0배 이내","대출기간":"전세계약만기일 범위 내 최장 2년(최대 20년까지 1년 단위로 연장 가능)\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내(계약 갱신일 이전에도 보증신청 가능)","담보":"주택금융신용보증서","상환방식 및 이자납입":"만기일시상환,원(리)금균등분할상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%","대출관련비용":"보증료(손님부담) - 대출금액의 0.02%(한국주택금융공사 보증료 등 운용규정에 따름)\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"주민등록등본, 확정일자부 전·월세계약서(원본), 임차보증금의 5%이상을 지급한 영수증, 임차주택의 등기부등본, 현거주지 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서, 금융거래확인서(타 금융기관에 신용대출 보유한 경우) 등","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 한국주택금융공사 보증규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금률 : 0.7%\n변경 후\n중도상환해약금률 : 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (198, 4, '닥터클럽대출 - 골드', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"봉직(한)의사, 전임의, 레지던트, 인턴, 군의관, 공중보건의, 의대생, (치)의학전문대학원생, 개업(한)의사 손님","대출한도":"최대 5억원","대출기간":"만기일시상환(일시대출, 통장대출) : 1년 이내\n부분원(리)금균등분할상환 : 5년 이내\n(부분)통장대출분할감액 : 5년 이내\n전액원(리)금균등분할상환 : 10년이내","상환방식":"만기일시상환(일시대출, 통장대출), (부분)원(리)금균등분할상환, (부분)통장대출분할감액","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"의사면허증, 소득증명서류, 재직증명서(급여소득자) 또는 사업자등록증(사업소득자)\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출기간,대출한도,대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터 (1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.08.08 변경)\n기존 가입고객에 대한 적용여부: 미적용\n<대출한도 변경>\n변경 전 : 최대 4억원\n변경 후 : 최대 5억원 (마이너스 통장대출은 최대 4억원)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (199, 4, '닥터클럽대출-플래티늄(소호)', '{"상품특징":"의료업종 종사중인 개원의원(한의사 포함) 대상 개인사업자 전용 신용대출 상품","대출대상":"개업(한)의사\n- 한의사의 경우 국내대학의 정규교육을 마친 뒤 국내에서 자격을 취득한 의사\n- 대출한도 2 취급시 아래 ①~② 요건 중 한가지 이상을 충족하는 의사\n의료비 이체계좌 당행지정\n3개 카드사 이상의 신용카드 매출대금 계좌를 당행으로 지정한 가입 자\n[BC(필수) + 국민, 삼성, 신한, 롯데, 현대, Woori, 하나 중 택 2]\n※ 공동사업자 명의의 의료비 이체 계좌 및 신용카드 가맹점은 실적으로 인정함.","대출금리":"변동금리 최저 연 4.887% ~ 최고 연 11.437%\n[2025.07.07 기준금리(91일물 CD유통수익률) 2.510% + 가산금리 (최저)3.637%, (최고)8.927% – 부수거래감면 등 우대금리 감면 1.26% 적용 기준]\n* 이자 산정방법 : 최저이율[기준금리 + 가산금리(ASS 1등급 기준) - 우대금리] / 최고이율[기준금리 + 가산금리(ASS 10등급 기준)]\n* 상기 산정 금리는 대출금액 1억원, 대출기간 1년, 당행 내부 신용(ASS) 최저 1등급/최고 10등급, 일시상환, 기업운전자금대출 기준입니다.\n* 가산금리는 신용등급, 대출금액, 담보종류, 기업시설자금대출 취급여부 등에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경될 수 있습니다.\n* 부수거래감면 등 우대금리: 주거래손님 0.1%, 사업자주거래우대통장 월평잔 조건 0.1%, 금융연수원 금융교육이수 0.1%, 소상공인 경영컨설팅 이수 0.2%, 본부 감면 0.76% 등 거래실적종류에 따라 우대\n* 연체대출금리는 적용금리 + 3.0%과 연 15.0%중 낮은 금리를 적용합니다.\n* 이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n* 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고금리는 연 20%입니다.\n* 기준금리 안내( 바로가기) : 상품공시실 → 대출상품 → 금리안내 → 대출(가계대출기준금리) → 91일물 CD유통수익률","대출한도":"개업자금: 최대 7.5억원 (개업전후 3개월 이내)\n운영자금: 최대 8억원 (개업후 3개월 경과)\n※ 최대한도는 매출액 및 자금용도 등에 따라 상이할 수 있습니다.","상환방식 및 대출기간":"만기일시상환(건별대출, 통장대출) : 1년 이내\n- 당초 약정기간을 포함하여 최장 6년 범위 내에서 1년 단위 연장 가능\n(부분)원(리)금균등분할상환, (부분)통장대출분할감액 : 5년 이내\n- 최고 6개월 범위 내 거치기간 신설 가능합니다.\n- 대출기간 3년을 초과하여 취급되는 경우 전액 원(리)금균등분할상환해야 합니다.\n  (부분원(리)금균등분할상환으로 취급 불가)\n- 부분원(리)금균등분할상환으로 취급 시 만기일 지정상환금액은 75%를 초과할 수 없습니다.","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n 1. 대출 당일에 회수되는 대출금\n 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금\n 3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금\n 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로,\n대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환 해약금":"중도상환해약금 : 중도상환대출금액 × 중도상환해약금율* × 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율 : 고정금리 0.04%, 변동금리 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","한도약정 수수료":"산식 : 한도약정액 × 한도약정수수료율(0.3%) × 약정기간 ÷ 365일(윤년 366일)\n※ 통장대출 취급시 부과됩니다.(단, 약정한도미사용수수료 납부 손님은 면제)","약정한도 미사용 수수료":"산식 : (약정한도평잔 – 운용기간 중 여신평잔) × 약정한도미사용수수료율(0.5%) × 운용기간 ÷ 365일(윤년은 366일)\n※ 통장대출 취급시 부과됩니다. (단, 한도약정수수료 납부 손님은 면제)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩\n부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원","필요서류":"사업자등록증사본, 의사면허증, 전문의면허증, 소득금액증명원, 임대차계약서 사본, 매출액 증빙 서류 (면세사업자\n사업자현황신고서, 부가가치세 면세사업자 수입금액증명원, 부가가치세 과세표준증명원 중 하나)\n※ 신용조사 및 대출심사 시 필요한 서류가 추가될 수 있습니다.","금리인하요구권":"기업신용평가 등에 따라 손님의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n회사채 등급상승, 재무상태 개선(감사보고서 등), 특허 취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를\n표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절,\n대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약 해지권 행사가능 상품입니다.\n은행이 「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"은행의 정책 및 시장금리 상황에 따라 금리는 변동될 수 있습니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류,경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용 점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래관련 불이익을 받을 수 있습니다.\n대출원리금 연체시 계약만료기한전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.07.15 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<부수거래 감면금리 변경>\n변경전 : 부수거래감면 등 우대금리 감면 1.27% 적용 기준\n변경후 : 부수거래감면 등 우대금리 감면 1.26% 적용 기준","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (200, 4, '로이어클럽대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"판사, 검사, 변호사(변호사시험 합격자 포함), 법무관, 사법연수원생, 지정법무법인 재직 중인 외국취득 변호사 자격증 소지자, 로스쿨 재학생 손님\n(지정법무법인은 영업점에서 확인이 가능합니다.)","대출한도":"최대 4억원 (마이너스 통장대출은 최대 2억원)","대출기간":"만기일시상환(일시대출,통장대출) : 1년 이내\n부분원(리)금균등분할상환 : 5년 이내\n(부분)통장대출분할감액 : 5년 이내\n전액원(리)금균등분할상환 : 10년이내","상환방식":"만기일시상환, 통장대출(분할감액 포함), (부분)원(리)금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"소득증명서류, 재직증명서(급여소득자), 사업자등록증(사업소득자)\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%(단, 인터넷/모바일 신청&약정시 0.5%)\n변경 후\n중도상환해약금율 0.04%","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"91물 CD유통수익률","금리변동주기":"3개월","기준금리":"2.500","가산금리":"3.738","우대금리":"0.900","최저~최고금리":"5.338 ~ 6.238"},{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"3.729","우대금리":"0.900","최저~최고금리":"5.345 ~ 6.245"},{"금리구분":"1년물 금융채 유통수익률","금리변동주기":"1년","기준금리":"2.498","가산금리":"3.640","우대금리":"0.900","최저~최고금리":"5.238 ~ 6.138"},{"금리구분":"COFIX 신규취급액","금리변동주기":"6개월","기준금리":"2.540","가산금리":"3.705","우대금리":"0.900","최저~최고금리":"5.345 ~ 6.245"},{"금리구분":"COFIX 신잔액","금리변동주기":"6개월","기준금리":"2.630","가산금리":"3.175","우대금리":"0.900","최저~최고금리":"4.905 ~ 5.805"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (201, 4, '목돈 안드는 행복전세', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"임차보증금 주)3억원(수도권 외 2억원) 이하인 임대차계약을 체결한 자\n주)월세가 있는 경우 월세보증금 및 월세에 대해 전월세전환율을 적용하여 계산한 금액\n(월세보증금+(월세X12÷전월세전환율))\n임차보증금의 5%이상을 지급한 *무주택 세대주\n* 무주택 확인은 보증신청인 및 그 배우자에 대하여 국토교통부 주택전산망에 의한 무주택여부를 보증취급 이전에 확인\n부부합산 연소득 60백만원 이하","대상주택":"공부상(등기부등본 등) 주거용 주택(미등기 주택도 가능)","대출한도":"최대 266백만원 (서울, 경기, 인천 이외의 지역은 최대 177백만원)범위 내 아래 ①과 ② 중 적은 금액으로 함\n임차(전세)보증금의 80% 이내\n신청인(배우자포함)의 연간소득의 최대 4.5배 이내","대출기간":"전세계약만기일 범위 내 최장 2년(최대 20년까지 1년 단위로 연장 가능)\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 갱신 계약을 체결한날로부터 3개월 이내","담보":"주택금융신용보증서 담보(90% 담보)","상환방식 및 이자납입":"만기일시상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%","대출관련비용":"보증료(손님부담) - 한국주택금융공사 보증료등 운용규정에 따름\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"주민등록등본, 확정일자부 전·월세계약서(원본), 임차보증금의 5%이상을 지급한 영수증, 임차주택의 등기부등본, 현거주지 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서(주민등록등본상 배우자 없는 경우), 금융거래확인서(타 금융기관에 신용대출 보유한 경우) 등","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 콜센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금률 : 0.7%\n변경 후\n중도상환해약금률 : 0.61%","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"91일물 CD유통수익률","금리변동주기":"3개월","기준금리":"2.500","가산금리":"2.450","우대금리":"0.900","최저~최고금리":"4.050 ~ 4.950"},{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"2.442","우대금리":"0.900","최저~최고금리":"4.058 ~ 4.958"},{"금리구분":"1년물 금융채 유통수익률","금리변동주기":"1년","기준금리":"2.498","가산금리":"2.476","우대금리":"0.900","최저~최고금리":"4.074 ~ 4.974"},{"금리구분":"신규취급액기준 COFIX","금리변동주기":"6개월","기준금리":"2.540","가산금리":"2.418","우대금리":"0.900","최저~최고금리":"4.058 ~ 4.958"},{"금리구분":"신 잔액기준 COFIX","금리변동주기":"6개월","기준금리":"2.630","가산금리":"1.888","우대금리":"0.900","최저~최고금리":"3.618 ~ 4.518"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (202, 4, '버팀목 청년전용보증부월세대출', '{"상품특징":"주택도시기금의 무주택 청년의 전세 보증금 및 월세자금 지원을 위한 대출로, 보증금 최대 4천 5백만원 및 월세자금 최대 1천 2백만원까지 지원합니다.","대출대상":"주택임차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 자\n신청일 현재 만 34세 이하인 단독세대주(단독세대주 예정자 포함)로서 무주택인 자\n부부합산 총 연소득 5천만원 이하인 자\n임차보증금 5%이상 계약금을 지불한 경우\n대출신청인 및 배우자의 합산 순자산가액이 통계청에서 발표하는 최근년도 가계금융복지조사의 ''소득 5분위별 자산 및 부채현황'' 중 소득 3분위 전체가구 평균값 이하(십만원 단위에서 반올림)인 자(2024년 기준, 3.37억원)","대상주택":"임차전용면적 60㎡ 이하 주거용 주택 및 오피스텔(전입신고 필수)\n보증금 65백만원 및 월세금 70만원 이하","대출금리":"임차보증금 대출: 연 1.3%\n월세대출: 연 1.0%(단, 매월 실행되는 월세대출금중 20만원까지 0%, 20만원초과분 1%)\n\n※ 2023.01.01 기준, 국토교통부 고시에 따른 변동금리\n※ 대출을 받은 후 자산심사 결과에 따라 본인 및 배우자의 합산 자산기준 초과된 금액이 1천만원 이내인 경우 신규취급된 금리에 연0.1%p 가산하며 1천만원 초과인 경우 신규 취급된 금리에 2.0%p 가산적용\n[자산심사 기준은 국토교통부 주택도시기금포털 (http://nhuf.molit.go.kr)에서 확인 가능]\n※ 기본금리는 만기까지 고정이 아니며, 주택도시기금운용계획 변경 등에 따라 변동\n※ 국토교통부 고시에 따라 변경될 수 있습니다.","대출한도":"보증금 대출은 최대 4,500만원, 월세금 대출은 최대 1,200만원(24개월 기준 월 최대 50만원 이내)\n보증금 대출금과 월세금 대출금(24개월 환산금액)의 합계액이 전세금액의 80% 이내(단 보증금 대출금은 전세금액의 70% 이내)","대출기간":"2년 1개월이내 (최장 10년 5개월)","대출신청시기":"임대차계약서상 잔금일과 주민등록등본상 전입일 중 빠른 날짜로부터 3개월 이내\n임차주택에서 1년이상 거주 후 보증금 증액에 의한 갱신의 경우 계약갱신일로부터 3개월 이내","담보":"주택도시보증공사 보증서(90%)담보","상환방식 및\n이자납입":"만기일시상환(단, 월세대출은 실행회차 단위만 상환 가능)\n이자 매월 후취","이자선납제한":"※ 본 상품은 국토교통부 ''주택도시기금 대출 업무 시행세칙''에 따라 , 대출실행 후 3개월 동안 이자선납이 제한됩니다.","중도상환\n해약금":"없음","대출관련비용":"주택금융공사HF 보증서일때 보증료(손님 부담) : 대출금액의 0.02%~0.2%\n(한국주택금융공사 보증료 등 운용규정에 따름)\n- 주택도시보증공사HUG 보증서일때 보증료(손님 부담): ①+②\n① 전세보증금 반환보증금의 연 0.097%~연 0.211%(공사가 정한 보증료율 기준에 따름)\n② 대출금의 연 0.031%\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원","필요서류":"관련서류\n확정일자부 임대차(전세)계약서\n총 임차보증금의 5% 이상 지급 확인서류 : 무통장 입금증 등\n주민등록등본 (1개월이내 발급분)\n임차주택 건물등기부등본(1개월이내 발급분)\n연간소득자료\n-근로자 : 재직증명서, 직장건강보험증, 근로소득원천징수영수증, 급여명세서\n-서민(자영업자 포함) : 사업자등록증명원, 사업자등록증, 소득금액증명원 또는 사업소득원천징수영수증\n-무소득증빙서류 :최근년도 신고사실없음 사실증명원 또는 퇴직(폐업)증명서, 배우자 및 직계존비속등의 피부양자로 등재되어 있는 직장건강보험증\n추가서류\n임대인 통장사본(임대인 앞으로 지급할 경우)\n가족관계증명서(1개월이내발급분) : 배우자 분리세대, 단독세대주, 결혼예정자인 경우\n배우자의 주민등록등본 : 배우자 분리세대인 경우\n배우자예정자의 주민등록등본 및 결혼예정 확인서류(예식장계약서 등) : 결혼예정자인 경우\n임대인의 임차보증금 반환확약서 : 임대인 확약서로 대출신청하는 경우","기타":"차주 포함 세대원 전원 무주택조회, 임차유지여부 확인 후 기한연장\n대출금액의 10% 상환 또는 대출기간에 따른 가산금리 부과(0.1% 또는 0.2%)","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"만 19세 이상 세대원 전원 당,타행, 기금 전세자금 및 주택담보 대출 있는 경우 불가(채무인수 미필계좌, 제3자 담보대출 경우 제외)\n본인포함 세대원전원 중 당, 타행 기금 지원자금 대출 있는 경우 취급 불가\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 만기에 원금을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익을 받을 수 있습니다.\n자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n본 상품은 금리인하요구권 적용 비대상 상품입니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n본 상품은 위법계약해지권 적용 비대상 상품입니다.\n대출금리는 국토교통부 고시금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.06.23 변경)\n■ 기존 가입 고객에 대한 적용 여부 : 미적용\n<대출기간>\n변경 전: 2년 1개월이내 (4회에 한해 연장가능, 최장 10년 5개월)\n변경 후: 2년 1개월이내 (최장 10년 5개월)\n<기타>\n변경 전: 대출금액의 10% 상환 또는 0.2% 가산금리 적용\n변경 후: 대출금액의 10% 상환 또는 대출기간에 따른 가산금리 부과(0.1% 또는 0.2%)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (203, 4, '버팀목전세자금대출', '{"상품특징":"무주택 국민의 주거안정 도모 및 모든 국민의 주거생활 향상을 목적으로 저금리로 전세자금대출을 지원하며, 중도상환수수료가 없어 상환이 편리한 상품 입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 자\n신청일 현재 만 19세 이상의 부양가족이 있는 세대주로서 세대주 포함한 세대원 전원이 현재 무주택인 자\n부부합산 총연소득 5천만원 (2자녀 이상, 혁신도시 이전 공공기관 종사자, 타지역으로 이주하는 재개발 구역내 세입자인 경우 6천만원, 신혼가구인 경우 7.5천만원)이하인 자\n임차보증금 5%이상 계약금을 지불한 경우\n대출신청인 및 배우자의 합산 순자산 가액이 통계청에서 발표하는 최근년도 가계금융복지조사의 ‘소득 5분위별 자산 및\n부채현황’ 중 소득 3분위 전체가구 평균값 이하(십만원 단위에서 반올림)인 자(순자산가액기준 3.37억원이하)\n(신생아 특례대출)대출신청인과 배우자의 합산 총소득이 1.3억원 (대출신청인이 혼인신고를 하지 않은 경우, 신생아의 가족관계증명서상 등재된 부모의 합산 총소득을 심사) 다만, 대출신청인과 배우자가 모두 소득이 있는경우 2억원이하(부부중 1인의 소득이 1.3억원 이하 충족)인 자로 아래 요건 충족\n대출접수일 기준 2년 이내 출산한 신생아가 있는 자 (단 ’23.1.1일 출생아부터 적용)\n입양아 포함(2살 이하, 단 ‘23.1.1일 출생아부터 적용) 가능\n임신 중인 태아 불가\n혼인신고 없어도 대출 가능\n기금의 구입 또는 전세자금 대출 이용 중 주택도시기금 대출거래약정서를 위반하여 기한의 이익이 상실된 이력이 없는 자(다만, 신청일 현재 해당 기한이익상실일로부터 3년이 경과된 자는 가능)","대상주택":"임차면적 전용 85㎡ (수도권을 제외한 도시지역이 아닌 읍 또는 면 지역은 100㎡) 이하 주거용 주택 및 오피스텔(전입신고 필수)\n단, 임차보증금액이 수도권 3억, 지방 2억을 초과하는 경우 대출 불가\n신혼가구·2자녀이상일 경우 임차보증금이 수도권 4억, 지방 3억을 초과하는 경우 대출 불가\n(신생아특례대출) 임차보증금 수도권 5억원, 수도권외 4억원","대출금리":"기본금리: 연 2.5% ~ 연 3.5%(소득 및 보증금에 따라 차등 적용)\n소득자별 금리표\n대출금리(국토교통부 고시에 따른 변동금리 - 부부합산 소득 및 임차보증금에 따른 차등)\n(2025.03.24 현재, 연이율)\n부부합산 연소득 임차보증금\n5천만원 이하 1억원 이하 1억원 초과\n2천만원 이하 2.50% 2.60% 2.70%\n4천만원 이하 2.70% 2.80% 2.90%\n6천만원 이하 3.00% 3.10% 3.20%\n7.5천만원 이하 3.30% 3.40% 3.50%\n※ 대출 대상주택이 지방소재인 경우 0.2% 인하\n우대금리(중복불가, 단 ④,⑤,⑥ 중복 가능)\n1자녀 0.3%, 2자녀 0.5%, 3자녀이상 0.7%\n(다만, 2025.03.24 신규접수분부터 자녀 1명당 4년간 적용)\n고령자,노인부양,다문화,장애인가구 : 연 0.2% (택1)\n부부합산 연소득 4천만원 이하로서 기초생활수급자, 차상위계층 또는 연소득 5천만원 이하 한부모 가구 : 연 1.0%\n주거안정 월세대출 성실납부자 : 연 0.2%\n국토부 부동산전자계약 시스템을 활용하여 주택의 임대차계약을 체결한자 : 연 0.1%\n(단, ''25.12.31까지 신규접수하는 건에 대해 최초 대출기한 1회에 한하여 한시적 적용)\n대출심사를 통해 산정한 대출금액의 30%이하로 대출신청시 : 0.2%(대출실행일로부터 4년간 적용)\n＊ 대출 심사를 통해 산정한 대출금액은 호당 대출한도, 소요자금에 대한 대출비율로 산정한 금액, 기존 전세자금대출 잔액(대환에 한함) 중 작은 금액으로 한다.\n※ 우대 후 최종금리가 1.0% 미만인 경우에는 1.0%를 적용\n※ 우대금리 적용 상한은0.5%(기초생활수급자, 차상위,한부모가구는 1.0%, 다자녀가구는 0.7%)\n신혼가구인 경우에는 상기 각호에도 불구하고 아래 표와 같이 금리를 적용\n(2025.03.24 현재, 연이율)\n부부합산\n연소득 임차보증금\n5천만원 이하 임차보증금\n5천만원 초과\n~ 1억원 이하 임차보증금\n1억원 초과\n~ 1.5억원 이하 임차보증금\n1.5억원 초과\n2천만원 이하 1.90% 2.00% 2.10% 2.20%\n2천만원 초과 ~\n4천만원 이하 2.20% 2.30% 2.40% 2.50%\n4천만원 초과 ~\n6천만원 이하 2.60% 2.70% 2.80% 2.90%\n6천만원 초과 ~\n7.5천만원 이하 3.00% 3.10% 3.20% 3.30%\n※ 대출 대상주택이 지방소재인 경우 0.2% 인하\n우대금리\n1자녀 0.3%, 2자녀 0.5%, 3자녀 이상 0.7%(다만, 2025.03.24 신규접수분부터 자녀 1명당 4년간 적용)\n국토부 부동산전자계약 시스템을 활용하여 주택의 매매계약을 체결한자 : 연 0.1%\n(단, ''25.12.31까지 신규접수하는 건에 대해 최초 대출기한 1회에 한하여 한시적 적용)\n대출심사를 통해 산정한 대출금액의 30%이하로 대출신청시 : 0.2%(대출실행일로부터 4년간 적용)\n＊ 대출 심사를 통해 산정한 대출금액은 호당 대출한도, 소요자금에 대한 대출비율로 산정한 금액, 기존 전세자금대출 잔액(대환에 한함) 중 작은 금액으로 한다.\n※ 우대 후 최종금리가 1.0% 미만인 경우에는 1.0%를 적용\n※ 우대금리 적용 상한은0.5%(기초생활수급자, 차상위,한부모가구는 1.0%, 다자녀가구는 0.7%)\n신용(임차보증금반환확약서)의 경우 기본금리 + 연 1.0%\n청년가구인 경우에는 상기 각호에도 불구하고 아래 표와 같이 금리를 적용\n(2025.03.24 현재, 연이율)\n부부합산 연소득 임차보증금 3억원 이하\n2천만원 이하 2.20%\n2천만원 초과 ~ 4천만원 이하 2.50%\n4천만원 초과 ~ 6천만원 이하 2.90%\n6천만원 초과 ~ 7.5천만원 이하\n(신혼) 3.30%\n※ 대출 대상주택이 지방소재인 경우 0.2% 인하\n우대금리(중복불가, 단 ③,⑤,⑥,⑦ 중복 가능)\n부부합산 총 연소득 4천만원 이하로서 기초생활수급자, 차상위계층 : 연 1.0%\n연소득 5천만원 이하인 한부모 가구 : 연 1.0%(택1)\n장애인, 노인부양, 다문화, 고령화가구 : 연 0.2%(택1)\n청년가구(만 25세 미만, 전용면적 60㎡ 이하, 보증금 7천만원 이하, 대출금 50백만원 이하 단독세대주) 0.3%(대출실행일로부터 최대 4년간 적용)\n미성년자녀수 : 3자녀이상 0.7%, 2자녀 : 0.5%, 1자녀 : 0.3%(다만, 2025.03.24 신규접수분부터 자녀 1명당 4년간 적용)\n국토부 부동산전자계약 시스템을 활용하여 주택의 임대차계약을 체결한 경우 0.1% 우대금리 적용\n(단, ''25.12.31까지 신규접수하는 건에 대해 최초 대출기한 1회에 한하여 한시적 적용)\n중소기업 취업(창업)청년 0.3%(대출실행일로부터 4년간 적용)\n대출심사를 통해 산정한 대출금액의 30%이하로 대출신청시 : 0.2%(대출실행일로부터 4년간 적용)\n＊ 대출 심사를 통해 산정한 대출금액은 호당 대출한도, 소요자금에 대한 대출비율로 산정한 금액, 기존 전세자금대출 잔액(대환에 한함) 중 작은 금액으로 한다.\n※ 우대 후 최종금리가 1.0% 미만인 경우에는 1.0%를 적용\n신생아 특례 대출의 경우 아래 표와 같이 금리를 적용\n(특례금리적용시) 소득·보증금에 따라 금리 적용\n(2025.03.24 현재, 연이율)\n소득 보증금\n5천이하 1억 이하 1.5억 이하 1.5억 초과\n2천만원 이하 1.30% 1.40% 1.50% 1.60%\n4천만원 이하 1.60% 1.70% 1.80% 1.90%\n6천만원 이하 1.90% 2.00% 2.10% 2.20%\n7.5천만원 이하 2.20% 2.30% 2.40% 2.50%\n1억원 이하 2.55% 2.65% 2.75% 2.85%\n1.3억원 이하 2.90% 3.00% 3.10% 3.20%\n1.5억원 이하 3.25% 3.35% 3.45% 3.55%\n1.7억원 이하 3.60% 3.70% 3.80% 3.90%\n2억원 이하 4.00% 4.10% 4.20% 4.30%\n(특례금리 종료후) 7.5천만원 이하는 기존 특례금리에서 0.6%p 가산, 7.5천만원 초과는 대출 시점의 시중 금리 중 최저치 적용. 단, 연소득 1.3억원 이하인 경우 7.5천만원 이하 특례금리 종료 후 금리의 최고금리를, 연소득 1.3억원 초과인 경우 기 적용 특례금리에 0.2% 가산한 금리를 하한\n예금은행 전세대출 평균 금리(한국은행이 매월 고시), 시중은행 전세대출 최저 금리 (은행연합회가 매월 고시)를 비교하여 작은값으로 선정\n대출대상주택이 지방 소재인 경우 0.2% 인하\n(우대금리) ①,②,③,④ 중복가능, 특례금리 종료 후에도 금리인하 유지\n① 기존자녀 ② 추가 출산 자녀 ③ 전자계약매매(전세)\n0.1%p (1명당)\n(대출실행일로부터 4년간 적용) 0.2%p (1명당)\n(적용일로부터 자녀 1명당 4년간 적용 –최장 12년 적용) 0.1%p (단, ’25.12.31까지 신규접수하는 건에 대해 최초 대출기한 1회에 한하여 한시적 적용)\n대출심사를 통해 산정한 대출금액의 30%이하로 대출신청시 : 0.2%(대출실행일로부터 4년간 적용)\n＊ 대출 심사를 통해 산정한 대출금액은 호당 대출한도, 소요자금에 대한 대출비율로 산정한 금액, 기존 전세자금대출 잔액(대환에 한함) 중 작은 금액으로 한다.\n대출 신청일 기준, 출생 후 24개월 초과\n우대금리 적용 후 최종 금리가 연1.0%미만일 경우 연 1.0%를 적용\n우대금리 적용상한은 0.5%(다자녀가구는 0.7%)\n자산기준 초과 시 가산금리\n대출을 받은 후 자산심사 결과에 따라 본인 및 배우자의 합산 자산기준 초과된 금액이 1천만원 이내인 경우 신규취급된 금리에 연0.1%p 가산하며 1천만원 초과인 경우 신규 취급된 금리에 2.0%p 가산적용\n[자산심사 기준은 국토교통부 주택도시기금포털 (http://nhuf.molit.go.kr)에서 확인 가능]\n※ 기본금리는 만기까지 고정이 아니며, 주택도시기금운용계획 변경 등에 따라 변동\n※ 국토교통부 고시에 따라 변경될 수 있습니다.","대출한도":"최대 1.2억원(수도권외 80백만원), 2자녀 이상 최대 2.5억원 (수도권외 1.6억원), 신혼가구 최대 2.5억원(수도권 외 1.6억원)\n* 만 19세 이상 만 34세 이하의 세대주(세대주 예정자 포함) 대출한도 1.5억원 이하 단, 25세 미만 단독세대주인 경우 대출한도 1.2억원\n신용(임차보증금반환확약서 징구) : 최대 3천만원\n협약기관에 의한 채권양도방식 : 최대 1.2억원(수도권외 80백만원), 2자녀 이상 최대 2.5억원 (수도권외 1.6억원), 신혼가구 최대 2.5억원(수도권 외 1.6억원)\n(신생아특례대출) 2.4억원 이내 (지역제한 없음)","대출기간":"2년 (최장 10년) – 단, 임차종료일 초과불가\n다자녀 가구, 2자녀 가구, 1자녀 가구 : 최장연장기간 이후 자녀수에 따라 추가연장 (1자녀당 2년, 최장 10년)\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.\n- (신생아특례대출) 기본: 2년 (최장 12년) - 단, 임차종료일 초과불가\n  전세금안심대출보증 : 최대 2년 1개월 이내에서 임차종료일 +1개월을 초과할 수 없다 (최장 12년 6개월)","대출신청시기":"임대차계약서상 잔금일과 주민등록등본상 전입일 중 빠른 날짜로부터 3개월 이내\n임차주택에서 1년이상 거주 후 보증금 증액에 의한 갱신의 경우 계약갱신일로부터 3개월이내","담보":"주택금융신용보증서 담보(90%담보), 주택도시보증공사보증서 담보(90% 담보)\n임차보증금반환확약서 담보(보증서 발급 거절자에 해당하는 자)\n협약기관에 의한 채권양도방식\n※ 협약기관 : LH, SH, 공공임대리츠 1~6호, 경기도시공사, 청년희망리츠","상환방식 및 이자납입":"만기일시상환 또는 혼합상환 (2017.07.17 이후 신규 신청 건부터 적용), 이자 매월 후취\n※ 혼합상환방식 : 원(리)금균등분할상환 방식, 만기일시상환방식 혼합하여 신규 후 23개월 동안 대출원금의 10~50%는 분할상환하고 원금의 50~90%는 대출만기일에 일시 상환하는 방식 (각 상환방식별로 대출금액의 10%에서 50%까지 10%p 단위로 선택)","이자선납 제한":"※ 본 상품은 국토교통부 ''주택도시기금 대출 업무 시행세칙''에 따라 , 대출실행 후 3개월 동안 이자선납이 제한됩니다.","중도상환\n해약금":"없음","대출관련비용":"주택금융공사HF 보증서일때 보증료(손님 부담) : 대출금액의 0.02%~0.2%\n(한국주택금융공사 보증료 등 운용규정에 따름)\n- 주택도시보증공사HUG 보증서일때 보증료(손님 부담): ①+②\n① 전세보증금 반환보증금의 연 0.097%~연 0.211%(공사가 정한 보증료율 기준에 따름)\n② 대출금의 연 0.031%\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원","소득공제":"상환원리금의 40% 이내로 연간 3백만원까지(소득세법 제52조 제4항에 해당시)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"관련서류\n확정일자부 임대차(전세)계약서\n총 임차보증금의 5% 이상 지급 확인서류 : 무통장 입금증 등\n주민등록등본 (1개월이내 발급분)\n임차주택 건물등기부등본(1개월이내 발급분)\n연간소득자료\n근로자 : 재직증명서, 직장건강보험증, 근로소득원천징수영수증, 급여명세서\n서민(자영업자 포함) : 사업자등록증명원, 사업자등록증, 소득금액증명원 또는 사업소득원천징수영수증\n무소득증빙서류 :최근년도 신고사실없음 사실증명원 또는 퇴직(폐업)증명서, 배우자 및 직계존비속등의 피부양자로 등재되어 있는 직장건강보험증\n추가서류\n임대인 통장사본(임대인 앞으로 지급할 경우)\n가족관계증명서(1개월이내발급분) : 배우자 분리세대, 단독세대주, 결혼예정자인 경우\n배우자의 주민등록등본 : 배우자 분리세대인 경우\n배우자예정자의 주민등록등본 및 결혼예정 확인서류(예식장계약서 등) : 결혼예정자인 경우\n임대인의 임차보증금 반환확약서 : 임대인 확약서로 대출신청하는 경우\n주택금융신용보증서 담보시 임대인이 자필서명한 임대차계약사실확인서 등\n(신생아특례대출) 출산한 자녀의 경우 출생증명서, 입양한 자녀의 경우 입양관계증명서 또는 친양자 입양관계증명서를 제출","기타":"차주 포함 세대원 전원 무주택조회, 임차유지여부 확인 후 기한연장\n대출금액의 10% 상환 또는 대출기간에 따른 가산금리 부과(0.1% 또는 0.2%)\n버팀목전세 안심대출은 전산개발완료시 추가대출 가능\n부부합산 연소득 5천만원 이하인자가 시중은행 전세자금대출 대환신청시 잔금지급일과 주민등록등본 상 전입일 중 빠른날로부터 6개월 이내까지 신청가능","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"만 19세 이상 세대원 전원 당,타행, 기금 전세자금 및 주택담보 대출 있는 경우 불가(채무인수 미필계좌, 제3자 담보대출 경우 제외)\n본인포함 세대원전원 중 당, 타행 기금 지원자금 대출 있는 경우 취급 불가\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 만기에 원금을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익을 받을 수 있습니다.\n자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n본 상품은 금리인하요구권 적용 비대상 상품입니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n본 상품은 위법계약해지권 적용 비대상 상품입니다.\n대출금리는 국토교통부 고시금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n신생아특례대출은 주택도시기금 e든든 사이트틀 통해 신청가능합니다. 기금e든든 바로가기\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.07.23 변경)\n■ 기존 가입 고객에 대한 적용 여부 : 미적용\n<대출기간>\n변경 전 :\n주택금융신용보증서 담보제공시 : 최대 1.2억원(수도권외 80백만원), 2자녀 이상 최대 3억원(수도권외 2억원), 신혼가구 최대3억원(수도권 외 2억원)\n*만 19세 이상 만 34세 이하의 세대주(세대주 예정자포함)대출한도 2억원이하 단, 25세 미만 단독세대주인 경우 대출한도 1.5억원\n신용(임차보증금반환확약서 징구) : 최대 3천만원\n협약기관에 의한 채권양도방식 : 최대 1.2억원(수도권외 80백만원),2자녀 이상 최대 2.2억원(수도권외 1.8억원), 신혼가구 최대 3억원(수도권 외 2억원)\n(신생아특례대출) 300백만원 이내(지역제한 없음)\n변경 후 :\n최대 1.2억원(수도권외 80백만원), 2자녀 이상 최대 2.5억원(수도권외 1.6억원), 신혼가구 최대2.5억원(수도권 외 1.6억원)\n*만 19세 이상 만 34세 이하의 세대주(세대주 예정자포함)대출한도 1.5억원이하 단, 25세 미만 단독세대주인 경우 대출한도 1.2억원\n신용(임차보증금반환확약서 징구) : 최대 3천만원\n협약기관에 의한 채권양도방식 : 최대 1.2억원(수도권외 80백만원),2자녀 이상 최대 2.5억원(수도권외 1.6억원), 신혼가구 최대 2.5억원(수도권 외 1.6억원)\n(신생아특례대출) 2.4억원 이내(지역제한 없음)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (204, 4, '보증보험 담보대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"대출금액의 100% 이상에 해당하는 금액의 서울보증보험㈜에서 발행한 소액대출 보증보험증권 또는 생활안정자금보증보험증권을 발급받은 개인","대출한도":"최대 1억원","대출기간":"만기일시상환 : 최장 5년\n분할상환 : 최장 10년","상환방식":"만기일시상환, 원(리)금 균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.61%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n※ 보증료(율) : 보증보험의 최저 연 0.522 % ~ 최대 연 1.309% (차주별 상이 자세한 사항은 보증기관 문의)","필요서류":"소득 및 재직 증명서류\n(상담 시 추가 제출서류가 있을 수 있습니다.)","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n* 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%\n변경 후\n중도상환해약금율 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (205, 4, '복지지킴이전세론', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"사회적 가치 창출을 목적으로 하는 사회적 경제 기업 및 사회복지법인·시설에 근무하는 임직원을 대상으로 한국주택금융공사의 보증서를 담보로 임차보증금의 90% 범위 내에서 주택의 지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님(임차보증금액이 수도권 7억, 지방 5억을 초과하는 경우 대출 불가)\n다음 중 어느 하나에 3개월 이상 재직 중인 임직원\n사회적기업 육성법에 따라 고용노동부 장관의 인증을 받은 사회적기업\n사회복지사업법에 따라 설립된 사회복지법인 및 사회복지시설\n협동조합기본법에 따라 기획재정부 장관 또는 관할 지자체의 설립신고확인 또는 인가를 받은 협동조합등\n마을기업 육성사업 시행지침(행정안전부)에 따라 행정안전부의 지정을 받은 마을기업\n국민기초생활 보장법에 따라 관할 지자체의 인정을 받은 자활기업 및 보건복지부 장관의 지정을 받은 광역,지역자활센터\n한국사회적기업진흥원 및 권역별지원기관, 한국사회복지협의회, 한국자활복지 개발원\n임차보증금의 5%이상 지급한 만 19세 이상의 세대주\n세대주와 동일세대를 이루고 있는 세대원 중 다음 하나에 해당하는 자\n(단, 임차인이 위 ①자격을 충족해야함)\n배우자\n직계존비속 및 그의 배우자\n신청인 및 배우자의 형제자매\n배우자의 직계존비속 및 그의 배우자\n보증신청일로부터 3개월 이내에 결혼이 예정된 자\n주택수 및 소득에 따른 대출대상\n부부합산 무주택 : 본인 연소득 50백만원 이하인 자\n부부합산 1주택 : 본인 연소득 50백만원 이하이고, 부부합산 연소득 1억원 이하인 자","대상주택":"공부상(등기부등본 등) 주거용 주택(미등기 주택도 가능)","대출한도":"최대 2억원 범위 내 아래 ①과 ②중 적은금액으로 함\n임차(전세)보증금의 90% 이내\n신청인(배우자포함)의 연간소득의 최대 5.0배 이내","대출기간":"전세계약만기일 범위 내 최장 2년(최대 20년까지 1년 단위로 연장 가능)\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내(계약 갱신일 이전에도 보증신청 가능)","담보":"주택금융신용보증서","상환방식":"만기일시상환,원(리)금균등분할상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%","대출관련비용":"보증료(손님부담) - 대출금액의 0.02%~0.1%(한국주택금융공사 보증료 등 운용규정에 따름)\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"주민등록등본, 확정일자부 전·월세계약서(원본), 임차보증금의 5%이상을 지급한 영수증, 임차주택의 등기부등본, 현거주지 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서(주민등록등본상 배우자 없는 경우), 금융거래확인서(타 금융기관에 신용대출 보유한 경우) 등","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 한국주택금융공사 보증규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금률 : 0.7%\n변경 후\n중도상환해약금률 : 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (206, 4, '부도임대주택 경락자금대출', '{"대출대상":"세대주를 포함한 세대원 전원이 대출신청일 현재 무주택인 자 대출신청인과 배우자의 합산총 연소득 연간 6천만원 이하이며 다음에 해당하는 자\n신청일 현재 민법상 성년인 세대주(아래 어느 하나에 해당하는 경우 제외)\n①만 30세 미만 단독세대주.\n②만 30세 미만 미혼세대주.(단, 직계존속 중 1인 이상과 동일세대를 구성하고 주민등록표상 부양기간(합가일기준)이 계속해서 6개월 이상인 경우 가능)\n기금의 임대주택자금이 지원된 부도임대주택을 경매낙찰받은 당해 임대주택의 임차인\n※ 당해 주택의 임차인이 아닌 자가 기금이 지원된 부도임대주택을 경매낙찰 받은 경우와 기금건설자금이 지원되지 않은 일반주택을 경매(공매)낙찰 받은 개인이 대출을 신청하는 경우에는 제6절 내집마련 디딤돌대출로 지원","대상주택":"기금의 임대건설자금이 지원된 부도임대주택으로서 당해 주택의 임차인이 경매낙찰 받은 85㎡ 이하의 주택\n※부도임대주택이란 기금의 임대주택건설자금이 지원된 주택으로 채무자 또는 해당 임대주택의 소유자에게 다음 각호에 해당하는 사유가 발생하여 부실화된 주택을 말한다.\n1. 어음 및 수표의 부도로 인한 어음교환소 거래정지처분을 받은 자\n2. 채무자회생 및 파산에 관한 법률에서 정하는 회생절차 인가자\n3. 주택도시기금 대출금에 대한 원금 또는 이자를 납부하지 않아 은행연합회 신용정보관리규약의 신용정보관리기준에서 정하는 사유 등록후 3개월이 경과하고 말소되지 아니한 자(07.5.21 개정)\n4. 임대사업자와 ｢상법｣ 제342조의 2에 따른 모회사의 관계에 있는 회사가 ｢임대주택법｣ 제2조제6호가목에 따른 처분(어음교환소의 거래정지처분)을 받은 경우로서 해당 임대사업자의 자기자본의 전부가 잠식된 경우(2007.10.29 신설)","대출금리":"10년간 연 2.3% [변동금리] , 잔여기간은 분양자금 일반 입주자앞 이율 적용\n※ 국토교통부 고시에 따라 변경될 수 있습니다.","대출한도":"① 대출금액은 다음 각호 중 적은 금액이내로 한다.\n매각가격이내(매각허가결정문, 배당표 사본 등으로 확인)\n법원의 최초 매각가격의 80% 이내\n② 주택가격은 법원의 최초 매각가격으로 한다.","대출기간":"20년","대출신청시기":"촉탁등기에 의한 소유권이전등기 접수일로부터 3개월 이내\n다만, 소유권이전전 대출신청의 경우에는 매각허가결정일 이후 재매각기일의 전일로부터 소급하여 3일이 되는 날까지","담보":"1순위 근저당권 110% 설정","상환방식":"1년거치 19년 또는 3년거치 17년 원리금(또는 원금)균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 × 대출이자율 × 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기한연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원","필요서류":"경매개시결정 기입등기일 이전에 임대사업자와 체결된 임대차계약서 사본\n매각허가결정 등본\n배당표 사본\n주민등록등본(1개월이내 발급분)\n건물,토지 등기부등본(1개월이내 발급분)\n인감증명서 2통, 인감도장, 신분증\n소득확인자료\n근로자 : 건강보험자격득실확인서, 재직증명서, 소득금액증명원, 근로소득원천징수영수증 또는 급여명세서\n서민(자영업자 포함) : 사업자등록증, 소득금액증명원 또는 사업소득원천징수영수증(연말정산용)","신청절차":"대출상담 및 대출가능 금액 산정\n대출대상자, 대상주택, 대출조건 등 적격여부 상담\n상품별 기준에 의한 대출가능금액 산정\n대출신청 및 서류제출\n매각허가결정 등본, 건물(토지)등기부등본, 인감증명서, 주민등록등본 등 관련서류 제출\n무주택 및 소득확인\n국토교통부, 국세청에 의뢰(은행)\n대출심사\n신용조사 및 담보주택에 대한 실태조사(은행)\n대출 가·부 통보(은행)\n대출약정 및 담보제공\n대출금액, 대출기간 및 대출이율 등 대출조건 약정\n담보주택에 대한 근저당권 설정\n대출실행\n은행에서 대출을 실행하여 입금\n철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"※ 자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 대출만기 도래시 대출금액을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n자세한 사항은 해당 영업점 담당자 앞 문의 부탁드립니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.04.01 변경) 주택도시기금 부도임대주택 경락자금대출 신규 취급 재개","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (207, 4, '부도임대주택 퇴거자 전세자금대출', '{"대출대상":"부도임대주택에서 퇴거당하거나 퇴거당할 예정인 임차인 중 임차보증금의 5% 이상(임대인이 법인임대사업자인 경우 제외)을 지불하고 주택임대차계약을 체결한 자로서 다음 요건을 모두 구비한 자\n1. 해당 지자체장의 추천을 받은 대출신청일 현재 만19세 이상 세대주(단독세대주는 원칙적으로 제외, 단 만25세 미만 미혼세대주는 직계존속중 1인 이상과 동일세대를 구성하고 주민등록표상 부양기간(합가일 기준)이 계속해서 6개월 이상인 자) 또는 세대주로 인정되는 자로서 세대주를 포함한 세대원 전원이 무주택인 자\n2. 매각허가결정일 이전에 임대차계약을 체결하고 주민등록 전입을 완료하여 대출신청일 현재 해당 주택에 계속 거주(전입)중이거나 경락허가 결정일이후 퇴거당한 자","대상주택":"건물등기부등본 또는 건축물관리대장상 용도가 주택으로 임차전용면적 85㎡ 이하 (수도권을 제외한 도시지역이 아닌 읍 또는 면 지역 100㎡) 및\n임차보증금 수도권3억원(지방소재 2억원)이하의 주택","대출금리":"연 2.8%[변동금리] (기초생활수급자 및 독거노인은 연 0.8%)\n※ 기본금리는 만기까지 고정이 아니며, 주택도시기금운용계획 변경 등에 따라 변동\n※ 국토교통부 고시에 따라 변경될 수 있습니다.","대출한도":"임차보증금 70% 이내 에서 최대 5천만원 이내(단, 임차보증금 반환 확약서에 의한 전세자금신용대출은 30백만원이내)","대출기간":"2년 (2회에 한해 연장가능, 최대 6년)","대출신청시기":"부도임대주택이 매각허가결정일 또는 법원의 부동산 인도명령일 이후 다음 각호와 같이 대출신청한다.\n1. 임대차계약서상 잔금지급일과 주민등록 전입일중 빠른 날짜로부터 3개월이내에 신청하여야 한다. (입주후 신청시에는 당해 임차주택에 주민등록을 전입후 대출신청하여야 함)\n2. 당해 부도임대주택에 계속 거주하면서 경락인과 새로운 임대차계약을 체결한 경우에는 계약갱신일로부터 3개월이내에 신청하여야 하며, 주민등록 전입일로부터 1년 미만인 경우에도 대출취급이 가능하다.","담보":"주택금융신용보증서 또는 다른 담보(예적금, 유가증권 등) 또는 임차보증금 반환 확약서","상환방식 및\n이자납입":"만기일시상환 , 이자 매월 후취\n\n대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 / 3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","이자선납제한":"※ 본 상품은 국토교통부 ''주택도시기금 대출 업무 시행세칙''에 따라 , 대출실행 후 3개월 동안 이자선납이 제한됩니다.","중도상환\n해약금":"없음","대출관련비용":"보증료(손님부담)-대출금액의 0.02%~0.2%\n주택도시기금 및 한국주택금융공사 보증료등 운용규정에 따름\n인지세\n인지세법에 의해 대출약정 체결 시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원","필요서류":"확정일자부 임대차(전세)계약서 사본\n지자체장의 대출대상자 추천서\n부도임대주택의 경매에 의한 배당표 사본(새로운 경락인과 계약체결시)\n주민등록등본 (1개월이내 발급분)\n임차주택 건물등기부등본,(1개월이내 발급분)\n연간소득자료\n근로자 : 재직증명서, 직장건강보험증, 근로소득원천징수영수증, 급여명세서\n서민(자영업자 포함) : 사업자등록증명원, 사업자등록증, 소득금액증명원 또는 사업소득원천징수영수증\n무소득증빙서류 :최근년도 신고사실없음 사실증명원 또는 퇴직(폐업)증명서, 배우자 및 직계존비속등의 피부양자로 등재되어 있는 직장건강보험증\n추가서류\n임대인 통장사본(임대인 앞으로 지급할 경우)\n가족관계증명서(1개월이내발급분) : 배우자 분리세대, 단독세대주, 결혼예정자인 경우\n배우자의 주민등록등본 : 배우자 분리세대인 경우\n배우자예정자의 주민등록등본 및 결혼예정 확인서류(예식장계약서 등) : 결혼예정자인 경우\n임대인의 임차보증금 반환확약서 : 임대인 확약서로 대출신청하는 경우","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"※ 자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n만 19세 이상 세대원 전원 당,타행, 기금 전세자금 및 주택담보 대출 있는 경우 불가(채무인수 미필계좌, 제3자 담보대출 경우 제외)\n본인포함 세대원전원 중 당, 타행 기금 지원자금 대출 있는 경우 취급 불가\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 만기에 원금을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익을 받을 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.04.01 변경) 주택도시기금 부도임대주택 퇴거자 전세자금대출 신규 취급 재개","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (208, 4, '부자되는 가맹점 대출', '{"상품특징":"하나은행 가맹점 입금계좌 보유 사장님을 위한 신용대출 상품","대출대상":"사업업력 1년 이상의 신용카드 가맹점으로 5개 카드사(BC카드 필수) 매출금액의 입금계좌를 당행으로 지정한 개인사업자","대출금리":"변동금리 최저 연 5.041% ~ 최고 연 11.581%\n[2025.07.07 기준금리(91일물 CD유통수익률) 2.510% + 가산금리 (최저)3.791%, (최고)9.071% – 부수거래감면 등 우대금리 감면 1.26% 적용 기준]\n* 이자 산정방법 : 최저이율[기준금리+가산금리(ASS 1등급 기준)-우대금리] / 최고이율[기준금리+가산금리(ASS 9등급 기준)]\n* 상기 산정 금리는 대출금액 5백만원, 대출기간 3년, 당행 내부 신용(ASS) 최저 1등급/최고 9등급, 분할상환, 운전자금대출 기준입니다.\n* 가산금리는 신용등급, 대출기간, 대출금액, 대출과목, 상환방식에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경될 수 있습니다.\n* 부수거래감면 등 우대금리: 주거래손님 0.1%, 사업자주거래우대통장 월평잔 조건 0.1%, 금융연수원 금융교육이수 0.1%, 소상공인 경영컨설팅 이수 0.2%, 본부 감면 0.76% 등 거래실적종류에 따라 우대\n* 연체대출금리는 적용금리+3.0%과 연 15.0%중 낮은 금리를 적용합니다.\n* 이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n* 대부업등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고금리는 연20%입니다.\n* 기준금리 안내( 바로가기) : 상품공시실 → 대출상품 → 금리안내 → 대출(가계대출기준금리) → 91일물 CD유통수익률","대출한도":"최대 5백만원 이내(3개월 매출액 합계 이내)","대출기간":"3년 이내","상환방식":"전액 원리금균등분할상환\n※ 원리금균등분할상환 : 약정된 분할상환 납입일에 원금과 이자를 합하여 매월 동일한 금액을 상환합니다.","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율 : 고정금리 0.04%, 변동금리 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원","필요서류":"사업자등록증 사본, 신분증, 부가세과세표준증명원, 소득금액증명원, 신용카드매출자료 등\n(※ 신용조사 및 대출심사 시 필요한 서류가 추가될 수 있습니다.)","금리인하요구권":"기업신용평가 등에 따라 손님의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허 취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약 해지권 행사가능 상품입니다.\n은행이 「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"은행의 정책 및 시장금리 상황에 따라 금리는 변동될 수 있습니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류,경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용 점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래관련 불이익을 받을 수 있습니다.\n대출원리금 연체시 계약만료기한전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.07.15 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<부수거래 감면금리 변경>\n변경전 : 부수거래감면 등 우대금리 감면 1.27% 적용 기준\n변경후 : 부수거래감면 등 우대금리 감면 1.26% 적용 기준","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (209, 4, '서울특별시 신혼부부 임차보증금대출', '{"상품특징":"서울특별시 융자추천 선정 신혼부부 또는 결혼예정자를 대상으로 이차보전금리를 지원하고, 한국주택금융공사의 보증서를\n담보로 최대 3억원까지 주택의 전세자금을 지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님\n현재 배우자와의 혼인기간이 7년 이내인 신혼부부 또는 보증신청일로부터 6개월 이내에 결혼하기로 한 결혼예정자\n임차보증금 7억원 이하인 임대차계약을 체결하고, 임차보증금의 5% 이상을 지급한 만 19세 이상의 세대주\n본인과 배우자의 합산한 연소득이 1억 3천만원 이하인 무주택 세대주\n서울특별시 융자추천을 받은 손님\n※ 서울주거포털(http://housing.seoul.go.kr) 바로가기","대상주택":"서울특별시 관내에 소재하는 공부상 주거용 주택 및 주거용 오피스텔, 노인복지\n주택. 단, 공공임대주택 및 장기안심주택 제외.","대출금리":"[기준일자 : 2024.06.26, 단위:연%]\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최종금리\nCOFIX신잔액 6개월 3.200 1.450 0.000 4.65\n※ 대출금리는 기준금리 변동 시 금리가 변경됩니다.\n※ 2024.06.26 현재, 내부신용(ASS) 1~13등급, 대출기간 2년, 대출금액 1억원 기준\n- 이자 산정방법: 최저이율(기준금리 + 가산금리 – 우대금리)\n                최고이율(기준금리 + 가산금리)\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.(상품에 따라 연체이자율이 일부 달라지는\n경우가 있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.)\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","이차보전금리":"최대 연 4.5% 이하\n※ 이차보전금리를 차감한 본인 부담 금리가 연 1% 이하인 경우 최저 연 1.0% 적용합니다.\n※ 서울특별시에서 정한 이차보전 조건 충족여부에 따라 기한 연장시 마다 재산정하며 이에 따른 이차보전 지원금리는\n변경되거나 적용되지 않을 수 있습니다.\n연소득에 따른 이자지원금리 : 최대 연 3.0% 이하\n부부합산 연소득 기본 이차보전금리\n0원 ~ 3천만원 이하 3.0%\n3천만원 초과 ~ 6천만원 이하 2.5%\n6천만원 초과 ~ 9천만원 이하 2.0%\n9천만원 초과 ~ 1억1천만원 이하 1.5%\n1억1천만원 초과 ~ 1억3천만원 이하 1.0%\n추가 이자지원금리 : 최대 연 1.5% 이하\n단, 아래 1),2),3)은 중복 적용 불가\n1) 결혼예정자 : 연 0.2%(최초 대출기간동안 적용)\n2) 다자녀가구 : 1자녀 연 0.5%, 2자녀 연 1.0%, 3자녀 이상 연 1.5%\n※ 자녀수 산정은 미성년 자녀를 기준으로 함.\n※ 자녀수에 태아를 포함함.\n※ 다자녀가구 추가 이자지원금리는 기한연장 시 자녀 수에 따라 재산정 합니다.\n3) 만65세이상 직계존속 3년 이상 계속 동거 시 : 연 1.0%","이차보전\n지원기간":"최장 10년 이내 (대출실행 이후 자녀 수 증가에 따라 차등)\n- 기본지원은 2년 + 2년 이내(기한 연장시 소득 등 기준 충족 시 연장가능)\n- 대출기간 중 출산 • 입양 등으로 자녀 수 증가에 따라 최장 6년이내 연장지원 가능","이차보전\n중단사유":"대출기간 중 아래 이차보전 중단사유 발생시 해당 사유를 은행에 즉시 통지하고, 중단일 로부터 대출금리를 환원하며,\n대출금을 즉시 상환해야 합니다. 미통보 및 통보지연 시 이자지원금 회수 및 연체이자 부과 등 불이익을 받을 수 있습니다.\n결혼예정자 혼인사실 확인서류 미제출\n서울시 외 지역으로 전출\n공공임대주택 입주 등\n임대차계약 종료","대출한도":"최대 3억원 범위 내 아래 ① ~ ③중 적은 금액으로 함\n임차(전세)보증금의 90% 이내\n신청인(배우자포함)의 연간소득의 최대 5.0배 이내\n서울특별시에서 통보한 융자추천금액","대출기간":"전세계약 만기일 범위내 1년 이상 2년 이내\n(최장 10년까지 1개월 단위로 연장 가능하나, 이차보전 중단사유에 해당하는 경우 기한연장 불가)\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른 날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내","담보":"주택금융신용보증서","상환방식":"만기일시상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급 시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료 시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%","대출관련비용":"보증료(손님부담) - 한국주택금융공사 보증료등 운용규정에 따름\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"서울특별시 융자추천서, 혼인증빙서류(결혼예정자의 경우 예식장계약서 또는 청첩장), 주민등록등본, 확정일자부 전 • 월세계약서(원본), 임차보증금의 5%이상을 지급한 영수증, 임차주택의 등기부등본, 현거주지 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서(주민등록등본상 배우자 없는 경우), 금융거래확인서(타 금융기관에 신용대출 보유한 경우) 등","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되어 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 한국주택금융공사 보증규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금률 : 0.7%\n변경 후\n중도상환해약금률 : 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (210, 4, '서울특별시 청년 임차보증금대출', '{"상품특징":"서울특별시 융자추천 선정 만 39세 이하 사회초년생, 취업준비생, 대학(원)생을 대상으로 이차보전금리를 지원하고,\n한국주택금융공사의 보증서를 담보로 최대 2억원까지 주택의 전세자금을 지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님\n만 19세 이상 만 39세 이하의 무주택 세대주\n임차보증금 3억원 이하, 월세 70만원 이하인 임대차계약을 체결하고, 임차보증금의 5% 이상을 지급한 임차인\n본인 연소득은 4천만원을 초과하지 않으면서, 부부합산 연소득이 5천만원 이하인 손님\n주거급여대상자가 아닌 손님\n서울특별시 융자추천을 받은 손님\n※ 서울주거포털(http://housing.seoul.go.kr) 바로가기","대상주택":"서울특별시 관내에 소재하는 공부상 주거용 주택 및 주거용 오피스텔, 노인복지\n주택. 단, 공공임대주택 및 장기안심주택 제외.","대출금리":"[기준일 : 2023.04.13, 단위:연%]\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최종금리\nCOFIX신잔액 6개월 3.070 1.450 0.000 4.520\n※ 대출금리는 기준금리 변동 시 금리가 변경됩니다.\n※ 2023.04.13 현재, 내부신용(ASS) 1~13등급, 대출기간 2년, 대출금액 7천만원 기준\n- 이자 산정방법: 최저이율(기준금리 + 가산금리 – 우대금리)\n                최고이율(기준금리 + 가산금리)\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.(상품에 따라 연체이자율이 일부 달라지는 경우가\n있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.)\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","이차보전금리":"최대 연 3.0%\n기본 이차보전금리 : 2.0%\n추가 이차보전금리 : 1.0%(한부모가구일 경우)\n※ 이차보전금리를 차감한 본인 부담 금리가 연 1% 이하인 경우 최저 연 1.0% 적용합니다.\n※ 서울특별시에서 정한 이차보전 조건 충족여부에 따라 기한 연장 시 적용되지 않을 수 있고, 대출금 기한 연장이\n불가할 수 있습니다.","이차보전\n지원기간":"최장 8년 이내\n※ 융자추천서가 발급되는 경우에 한해, 만 39세를 초과하는 날이 포함된 임대차 계약기간 종료 시까지 지원가능 (만 39세 전일까지 은행에서 실행완료된 대출에 한하며, 만 39세 초과일이 포함된 임대차 계약기간이 최초 지원기간부터 총 8년을 초과할 경우 최대 10년 이내까지만 지원가능)","이차보전\n중단사유":"대출기간 중 아래 이차보전 중단사유 발생시 해당 사유를 은행에 즉시 통지하고, 중단일로부터 대출금리를 환원하며,\n대출금을 즉시 상환해야 합니다.\n미통보 및 통보지연 시 이자지원금 회수 및 연체이자 부과 등 불이익을 받을 수 있습니다.\n서울시 외 지역으로 전출\n공공임대주택 입주 등\n임대차계약 종료","대출한도":"최대 2억원 범위 내 아래 ① ~ ②중 적은 금액으로 함\n임차(전세)보증금의 90% 이내\n서울특별시에서 통보한 융자추천금액","대출기간":"전세계약만기일 범위 내 6개월 이상 2년 이내 (최장 8년까지 1개월 단위로 연장 가능)\n※ 단, 만 39세 초과일이 포함된 임대차 계약기간이 최초지원기간으로부터 총 8년을 초과할 경우 최대 10년 이내까지만 연장 가능\n※ 대출만기 시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.\n\n기존가입고객에 대한 적용여부 : 미적용","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른 날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내","담보":"주택금융신용보증서","상환방식":"만기일시상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급 시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료 시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%","대출관련비용":"보증료(손님부담) - 한국주택금융공사 보증료등 운용규정에 따름\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"서울특별시 융자추천서, 주민등록등본, 확정일자부 전·월세계약서(원본), 임차보증금의 5%이상을 지급한 영수증,\n임차주택의 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서 등","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되어 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 한국주택금융공사 보증규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금률 : 0.7%\n변경 후\n중도상환해약금률 : 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (211, 4, '세종시 청년 임차보증금대출', '{"상품특징":"세종특별자치시 융자추천 선정 만 39세 이하의 사회초년생, 취업준비생, 대학(원)생을 대상으로 이차보전금리를 지원하고,\n한국주택금융공사의 보증서를 담보로 최대 1억원 까지 주택의 전세자금을 지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님\n만 19세 이상 만 39세 이하의 무주택 세대주\n임차보증금 2억원 이하인 임대차계약을 체결하고, 임차보증금의 5% 이상을 지급한 임차인\n본인 연소득은 4천5백만원을 초과하지 않으면서, 부부합산 연소득이 7천만원 이하인 손님\n주거급여대상자가 아닌 손님\n세종특별자시 융자추천을 받은 손님","대상주택":"세종시 관내에 소재하는 공부상 주택 및 주거용 오피스텔\n단, 다중주택 제외","대출금리":"[기준일 : 2024.01.17, 단위:연%]\n금리구분 기준금리 가산금리 우대금리 최고-최저금리\n신잔액기준 COFIX 3.29 2.3 0.0 5.59\n※ 대출금리는 세종시와 협약에 따라 변경될 수 있습니다.\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","이차보전금리":"연 4.1%\n※ 세종특별자치시에서 정한 이차보전 조건 충족여부에 따라 적용되지 않을 수 있고, 대출금 기한연장이\n불가할 수 있습니다.","이차보전\n지원기간":"최장 6년 이내\n※ 융자추천서가 발급되는 경우에 한해, 만 34세를 초과하는날이 포함된 임대차계약기간 종료 시까지 지원가능.","이차보전\n중단사유":"대출기간 중 아래 이차보전 중단사유 발생시 중단일 이후 차주가 약정한 대출금리를 전액 부담해야합니다.\n세종시 외 지역으로 전출\n대출실행 후 정당한 사유 없이 1개월 내 미전입\n임대차계약 종료\n1주택 이상 보유(부부합산 기준)\n주거급여 수급 등 사업공고 제외대상자에 해당\n사업공고의 취소 및 환수사유에 해당","기한연장":"기한연장 시 임대차계약기간을 초과하지 않는 범위에서 최대 2년까지 연장가능 (최장6년)\n최대 2회까지 연장할 수 있으며, 연장시 최초 대출금액의 10% 상환 필수","대출한도":"아래 중 적은 금액 이내\n임차(전세)보증금의 90% 이내 최대 1억원\n상환능력별 보증한도에 따른 대출가능금액\n신청인의 보증신청금액","대출기간":"임대차계약기간 내에서 최대 2년 이내","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른 날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내","담보":"주택금융신용보증서","상환방식":"만기일시상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급 시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료 시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%","대출관련비용":"보증료(손님부담) - 한국주택금융공사 보증료등 운용규정에 따름\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"세종특별자치시 융자추천서, 주민등록등본, 확정일자부 전세계약서(원본), 임차보증금의 5%이상을 지급한 영수증, \n임차주택의 등기사항전부증명서, 신분증, 소득 및 재직증명 입증서류, 가족관계증명서 등","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 한국주택금융공사 보증서 발급이 거절되거나, 은행에서 정한 부적격자에 대하여 대출 취급이\n제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가\n결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류,\n경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금률 : 0.7%\n변경 후\n중도상환해약금률 : 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (212, 4, '소상공인 119plus 대출', '{"상품특징":"연장 시점에 기존 대출 연장이 어려운 소상공인에게 만기연장 및 장기분할상환프로그램 등 맞춤형채무조정을 통한 안정적인 대출 상환을 지원하는 상품","대출대상":"기업대출 이용하는 소상공인 중 기존 상품의 연장 · 대환이 불가한 자","대상담보":"신용 및 담보","대출금리":"변동금리 최저 연 5.071% ~ 최고 연 11.611%\n[2025.07.07 기준금리(금융채 1년물) 2.515% + 가산금리 (최저)3.816%, (최고)9.096% – 부수거래감면 등 우대금리 감면 1.26% 적용 기준]\n* 이자 산정방법 : 최저이율[기준금리+가산금리(ASS 1등급 기준)-우대금리] / 최고이율[기준금리+가산금리(ASS 10등급 기준)]\n* 상기 산정 금리는 대출금액 5천만원, 대출기간 3년, 당행 내부 신용(ASS) 최저 1등급/최고 10등급, 전액 원금균등분할상환, 운전자금대출 기준입니다.\n* 가산금리는 신용등급, 대출금액, 담보종류, 시설자금대출 취급여부 등에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경될 수 있습니다.\n* 부수거래감면 등 우대금리: 주거래손님 0.1%, 사업자주거래우대통장 월평잔 조건 0.1%, 금융연수원 금융교육이수 0.1%, 소상공인 경영컨설팅 이수 0.2%, 사회적배려대상자 0.1%, 본부 감면 0.66% 등 거래실적종류에 따라 우대\n* 연체대출금리는 적용금리+3.0%과 연 15.0%중 낮은 금리를 적용합니다.\n* 이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n* 대부업등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고금리는 연20%입니다.\n* 기준금리 안내( 바로가기) : 상품공시실 → 대출상품 → 금리안내 → 대출(가계대출기준금리) → 91일물 CD유통수익률","대출한도":"상환 전 계좌의 대출금액 범위내","대출기간":"1년 이상 ~ 10년 이하\n(일시상환) 최대 1년\n(분할상환) 신용대출 최대 5년, 담보대출 최대 10년","상환방식":"일시상환 또는 전액원금균등분할상환(거치기간 신용대출 최대 1년, 담보대출 최대 3년)","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 이자일수를 곱하고 365로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","수수료":"중도상환해약금 없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","금리인하요구권":"기업신용평가 등에 따라 손님의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n회사채 등급상승, 재무상태 개선(감사보고서 등), 특허 취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약 해지권 행사가능 상품입니다.\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날‘부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"은행의 정책 및 시장금리 상황에 따라 금리는 변동될 수 있습니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류,경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용 점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래관련 불이익을 받을 수 있습니다.\n대출원리금 연체시 계약만료기한전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용 변경에 관한 사항":"상품내용 변경에 관한 사항\n(2025.07.15 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<부수거래 감면금리 변경>\n변경전 : 부수거래감면 등 우대금리 감면 1.27% 적용 기준\n변경후 : 부수거래감면 등 우대금리 감면 1.26% 적용 기준","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (213, 4, '신혼부부전세론', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"신혼부부 또는 결혼예정자를 대상으로 한국주택금융공사의 보증서를 담보로 임차보증금의 90%내에서 주택의 전세자금을\n지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님(임차보증금액이 수도권 7억, 지방 5억을 초과하는 경우 대출 불가)\n현재 배우자와의 혼인기간이 7년 이내인 신혼부부 또는 보증신청일로부터 3개월 이내에 결혼하기로 한 결혼예정자\n※ 기존 가입 손님에 대한 변경약관 적용 여부 : 미적용\n임차보증금의 5%이상 지급한 만 19세 이상의 세대주\n세대주와 동일세대를 이루고 있는 세대원 중 다음 하나에 해당하는 자\n(단, 피보증인이 신혼부부 또는 결혼예정자 자격을 충족해야함)\n배우자\n직계존비속 및 그의 배우자\n신청인 및 배우자의 형제자매\n배우자의 직계존비속 및 그의 배우자","대상주택":"공부상(등기부등본 등) 주거용 주택(미등기 주택도 가능)","대출한도":"최대 2억원 범위 내 아래 ①과 ②중 적은금액으로 함\n임차(전세)보증금의 90% 이내\n신청인(배우자포함)의 연간소득의 최대 4.5배 이내","대출기간":"전세계약만기일 범위 내 최장 2년(최대 20년까지 1년 단위로 연장 가능)\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내(계약 갱신일 이전에도 보증신청 가능)","담보":"주택금융신용보증서","상환방식 및 이자납입":"만기일시상환,원(리)금균등분할상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%","대출관련비용":"보증료(손님부담) - 대출금액의 0.02%(한국주택금융공사 보증료 등 운용규정에 따름)\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"혼인증빙서류, 주민등록등본, 확정일자부 전·월세계약서(원본), 임차보증금의 5%이상을 지급한 영수증, 임차주택의 등기부등본, 현거주지 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서(주민등록등본상 배우자 없는 경우), 금융거래확인서(타 금융기관에 신용대출 보유한 경우) 등","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 한국주택금융공사 보증규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금률 : 0.7%\n변경 후\n중도상환해약금률 : 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (214, 4, '예금담보대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"하나은행에 예금(일부 제외) 거래가 있는 본인 또는 제3자 명의의 당행 예·적·부금 등을 담보로 제공하는 개인\n전자금융을 통한 예금담보대출은 본인명의 당행 예·적·부금 만 가능","담보가능예금":"하나은행에서 신규한 예·적·부금\n예금담보 설정금액은 대출약정금액의 110%로 설정됩니다.\n※ 금전신탁상품은 전자금융을 통한 예금담보대출 취급 시 담보제공이 불가합니다.","대출한도":"당행 예·적·부금의 납입(예치)금액의 100%범위내. 단, 월이자지급식 예금 및 주택청약종합저축은 납입(예치)금액의 95%범위 내(투자신탁 등은 별도의 담보인정비율이 적용되므로 영업점에 대출 신청 시 확인)\n전자금융을 통한 예금담보대출 취급한도는 1인당 최저 10만원 이상 최고 1억원까지 10만원 단위로 대출 가능(신규 및 증액에 한함)\n※ 1억원을 초과하여 예금담보대출을 취급하고자 하시는 경우, 영업점을 방문하여 주시기 바랍니다.(신분증,통장,도장 지참)","대출기간":"담보 예·적·부금의 만기일 이내(만기일이 없는 예·적·부금의 경우 취급 후 1년까지)\n※ 단, 만기일이 다른 2개 이상의 예금을 담보로 한 경우 대출 기일은 최초 도래하는 예금의 만기일","상환방식 및 대출종류":"만기일시상환 (일시대출, 통장대출)\n※ 단, 전자금융을 통한 신청시에는 전자금융 출금계좌로 등록된 계좌만 약정 가능합니다.","통장대출 한도부여방식":"대출한도 고정방식\n하나은행에 가입하신 예금을 담보로 대출한도가 부여되는 마이너스 통장대출\n대출한도 자동증액방식\n하나은행에 가입하신 예금을 담보로 대출신청 가능 금액 범위내 에서 예/적금 불입금액까지 자동으로 한도가 증액되는 마이너스 통장 대출","대출금리":"대출금리는 담보로 제공되는 예/적금 상품금리 + 1.2%(전자금융에서 신청 시 예/적금금리 + 1.0%)로 결정됩니다.\n단, 제공되는 예금이 다수일 경우에는 해당 예금의 가중평균 예금금리 + 1.2 %(전자금융에서 신청 시 예금의 가중평균 예금금리 + 1.0%)로 적용\n담보예금이 주택청약종합저축인 경우 대출금리는 금융채 1년물 + 1.7% (전자금융에서 신청 시 금융채1년물 +1.2%)로 결정됩니다. 통장대출(마이너스통장) 취급시 한도가산금리 없습니다. (단, 2023.3.31일 이후 신규취급분부터)\n담보예금이 지수플러스 정기예금인 경우 해당상품에서 정한 대출금리(확정금리)로 결정됩니다.","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 없음","질권설정 및 해지":"대출약정 시 담보예금에 대하여 자동으로 질권이 설정되고 대출완제 시 담보예금에 대하여 자동으로 질권설정이 해지됩니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"고객님의 신용도에 따라 대출취급이 불가능할 수 있습니다.\n5천만원을 초과하여 예금담보대출 취급 시 담보 예·적·부금의 신규일로부터 2영업일 이후 신청 가능합니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2023.03.30 변경)\n<청약저축대출 통장대출 가산금리 변경>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경전 : 청약저축대출 통장대출 한도가산금리 0.5% 가산\n변경후 : 청약저축대출 통장대출 한도가산금리 없음\n상품내용 변경에\n관한 사항\n(2021.03.25 변경)\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경 전 : 신설\n변경 후 :본 상품은 금리인하요구권 적용 비대상 상품입니다.\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경 전 : 신설\n변경 후 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 미적용\n변경 전 : 신설\n변경 후 :\n은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기본금리"],"데이터":[]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (215, 4, '오피스텔 구입자금 대출', '{"상품특징":"주택도시기금의 오피스텔 구입 자금 대출로, 담보가액 범위 내에서 최대 7천만원(다자녀의 경우 75백만원)까지 지원합니다.","대출대상":"세대주를 포함한 세대원 전원이 대출신청일 현재 무주택인 자 대출신청인과 배우자의 합산총 연소득 연간 6천만원 이하이며 다음 한가지에 해당하는 자\n신청일 현재 민법상 성년인 세대주(아래 어느 하나에 해당하는 경우 제외)\n①만 30세 미만 단독세대주.\n②만 30세 미만 미혼세대주.(단, 직계존속 중 1인 이상과 동일세대를 구성하고 주민등록표상 부양기간(합가일기준)이 계속해서 6개월 이상인 경우 가능)\n대출신청인 및 배우자의 합산 순자산가액이 통계청에서 발표하는 최근년도 가계금융복지조사의 ''소득 5분위별 자산 및 부채현황'' 중 소득 4분위 전체가구 평균값 이하(십만원 단위에서 반올림)인 자(2024년 기준, 4.88억원)\n기금의 구입 또는 전세자금 대출 이용 중 주택도시기금 대출거래약정서를 위반하여 기한의 이익이 상실된 이력이 없는 자(다만, 신청일 현재 해당 기한이익상실일로부터 3년이 경과된 자는 가능)","대상주택":"전용면적 60㎡ 이하의 준공된 주거용 오피스텔(업무시설)\n단, 오피스텔의 가격이 1억 5천만원을 초과하는 경우 제외","대출금리":"기본금리 : 연 2.8% (최저 연 1.8% ~ 최고 연 2.8%)\n우대금리(최대 연 1.0%p이내)\n기본우대금리(가구별 중복 불가)\n다자녀가구(만 19세 미만 3자녀 이상) 연 0.5%p\n다문화가구 연 0.2%p\n장애인가구 연 0.2%p\n저소득우대금리(소득구간별 중복 불가)\n부부합산 연소득 2천만원 이하 : 연 0.5%p 우대\n부부합산 연소득 2천만원 초과 4천만원 이하 : 연 0.3%p 우대\n적용금리: 최저 연 1.8%(다자녀가구 & 부부합산 연소득 2천만원 이하 우대 시) ~ 연 2.8%\n※ 2023.01.01 기준, 국토교통부 고시에 따른 변동금리\n※ 대출을 받은 후 자산심사 결과에 따라 본인 및 배우자의 합산 자산기준 초과된 금액이 1천만원 이내인 경우 신규취급된 금리에 연0.1%p 가산하며 1천만원 초과인 경우 신규 취급된 금리에 2.0%p 가산적용\n[자산심사 기준은 국토교통부 주택도시기금포털 (http://nhuf.molit.go.kr)에서 확인 가능]","대출한도":"담보가액 범위 내에서 최대 7천만원 이내(단, 다자녀의 경우 75백만원 이내)","대출기간":"2년 (2년 단위 9회 연장 최장 20년까지)","대출신청시기":"소유권이전등기 전에 신청\n다만, 소유권이전등기를 한 경우에는 이전등기 접수일로부터 3개월 이내 신청","담보":"1순위 근저당권 설정을 원칙으로 하며 110%설정","상환방식":"만기일시상환 : 이자 매월 후취\n대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","대출조건":"대출취급 시 차주는 대출 실행일로부터 1개월 이내에 본건 담보주택에 전입을 완료하여 전입세대열람표를 은행에 제출하고, 전입한 날로부터 1년까지 담보주택에 거주","이자선납제한":"※ 본 상품은 국토교통부 ''주택도시기금 대출 업무 시행세칙''에 따라 , 대출실행 후 3개월 동안 이자선납이 제한됩니다.","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기한연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)","필요서류":"매매(분양)계약서-주민등록등본(1개월이내 발급분)\n건물,토지 등기부등본(1개월이내 발급분)\n등기권리증 및 인감증명서 2통, 인감도장, 신분증\n소득확인자료\n근로자 : 건강보험자격득실확인서, 재직증명서, 소득금액증명원, 근로소득원천징수영수증 또는 급여명세서\n서민(자영업자 포함) : 사업자등록증, 소득금액증명원 또는 사업소득원천징수영수증(연말정산용)","신청절차":"대출상담 및 대출가능 금액 산정\n대출대상자, 대상주택, 대출조건 등 적격여부 상담\n상품별 기준에 의한 대출가능금액 산정\n대출신청 및 서류제출\n매매계약서, 건물(토지)등기부등본, 인감증명서, 등기권리증, 주민등록등본 등 관련서류 제출\n무주택 및 소득확인\n국토교통부, 국세청에 의뢰(은행)\n대출심사\n신용조사 및 담보주택에 대한 실태조사(은행)\n대출 가·부 통보(은행)\n대출약정 및 담보제공\n대출금액, 대출기간 및 대출이율 등 대출조건 약정\n담보주택에 대한 근저당권 설정\n대출실행\n은행에서 대출을 실행하여 입금\n철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"※ 자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 대출만기 도래시 대출금액을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n자세한 사항은 해당 영업점 담당자 앞 문의 부탁드립니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.01 변경)\n■ 기존 가입 고객에 대한 적용 여부 : 미적용\n<순자산 기준 변경>\n변경 전 :\n2024년 기준 4.69억원\n변경 후 :\n2024년 기준: 4.88억원","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (216, 4, '우량주택전세론', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"아래의 조건을 모두 충족하는 손님\n(단, 외국인의 경우 국내거소신고자이고 서울보증보험 가입가능 비자 유효기간 3개월 이상인 자)\n부동산중개업소를 통해 주택임대차계약을 체결하고 임차보증금의 5% 이상을 지불한 임차인(임차보증금의 5% 지불 조건은 신규 임차자금에만 해당하며, 임대인이 공공주택사업자, (주)부영주택인 경우 생략)\n주택임대차보호법상 대항력과 우선변제권을 확보하였거나 대출취급일로부터 3영업일 이내에 확보할 수 있는 임차인\n성년인 세대주로 소득 대비금융비용부담율(DTI) 40% 이내인 임차인","대상주택":"아파트, 연립, 다세대, 주거용 오피스텔","대출한도":"최대 5억원(아래 ①~③중 적은 금액으로 임대인이 공공주택사업자인 경우는 ①만 적용)\n단, 부부합산 1주택자는 최대 3억원\n① 임차보증금 80% 이내(단, 임대인이 공공주택사업자인 경우는 임차보증금의 95% 이내 / 부부합산 1주택자는 임차보증금의 60%이내)\n② [해당물건지 선순위 설정최고액 + 전세대출금] 합계가 시세의 80% 이내\n③ 통장대출 1억원\n④ 외국인의 경우 최고한도 2억원","대출기간":"임차계약 만기일 범위 내 최장 3년 1개월로 하되 임차계약 만기일과 대출 만기일을 일치시키도록 한다.\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월 이내\n갱신 : 계약갱신일로부터 3개월 이내","상환방식 및 이자납입":"만기일시상환, 통장대출, (부분)원(리)금균등분할상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%\n아래의 경우에는 중도상환해약금을 면제합니다.\n통장대출\n연 3백만원이내 상환\n대출잔여기간 3개월 이내 상환\n임차목적물 변경에 따른 당행 전세자금대출 신규 시 (당행 대환신규금액과 상환금액이 달라도 중도상환해약금 면제 가능)","대출관련비용":"보험료 은행부담\n질권설정(또는 채권양도) 통지비용 3만원 손님부담\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"확정일자부 임대차계약서(원본), 전입완료된 주민등록등본 및 전입세대확인서, 소득ㆍ재직ㆍ세대주 증명서류(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"이사전 최소 7영업일 전에 대출신청을 하셔야 합니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 서울보증보험 협약내용에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.06.11 변경)\n<대출한도>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n최대 5억원(아래 ①~③중 적은 금액으로 임대인이 공공주택사업자인 경우는 ①만 적용)\n단, 부부합산 1주택자는 최대 3억원\n① 임차보증금 80% 이내(단, 임대인이 공공주택사업자인 경우는 임차보증금의 95% 이내)\n② [해당물건지 선순위 설정최고액 + 전세대출금] 합계가 시세의 80% 이내\n③ 통장대출 1억원\n④ 외국인의 경우 최고한도 2억원\n변경 후\n최대 5억원(아래 ①~③중 적은 금액으로 임대인이 공공주택사업자인 경우는 ①만 적용)\n단, 부부합산 1주택자는 최대 3억원\n① 임차보증금 80% 이내(단, 임대인이 공공주택사업자인 경우는 임차보증금의 95% 이내/부부합산 1주택자의 경우 임차보증금의 60%이내)\n② [해당물건지 선순위 설정최고액 + 전세대출금] 합계가 시세의 80% 이내\n③ 통장대출 1억원\n④ 외국인의 경우 최고한도 2억원","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"91일물 CD유통수익률","금리변동주기":"3개월","기준금리":"2.500","가산금리":"3.230","우대금리":"1.300","최저~최고금리":"4.430 ~ 5.730"},{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"3.222","우대금리":"1.300","최저~최고금리":"4.438 ~ 5.738"},{"금리구분":"1년물 금융채 유통수익률","금리변동주기":"1년","기준금리":"2.498","가산금리":"3.256","우대금리":"1.300","최저~최고금리":"4.454 ~ 5.754"},{"금리구분":"신규취급액기준 COFIX","금리변동주기":"6개월","기준금리":"2.540","가산금리":"3.198","우대금리":"1.300","최저~최고금리":"4.438 ~ 5.738"},{"금리구분":"신 잔액기준 COFIX","금리변동주기":"6개월","기준금리":"2.630","가산금리":"2.668","우대금리":"1.300","최저~최고금리":"3.998 ~ 5.298"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (217, 4, '의료인 클럽대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"간호사, 의료기사, 간호조무사, 요양보호사 자격증을 보유하고 해당업종에 재직중인 자, 자격증 최종합격자(취득후1년이내)\n(의료기사 : 임상병리사, 방사선사, 치기공사, 치위생사, 의지보조기기사, 물리치료사, 정신보건임상심리, 작업치료사)","대출한도":"최대 1억 5천만원(통장대출은 최대 5천만원)","대출기간":"만기일시상환(일시대출,통장대출) : 1년 이내\n원(리)금균등분할상환 : 5년 이내 월단위","상환방식":"만기일시상환(일시대출,통장대출), 원(리)금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"자격증(합격증빙서류), 소득서류,\n재직증명서류(상담 시 추가 제출서류가 있을 수 있습니다.)","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n* 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%\n변경 후\n중도상환해약금율 0.04%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (218, 4, '의료인보증서담보대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"간호사 및 의료기사 면허(자격)증 보유 손님\n※ 대상면허(자격)증 : 간호사, 임상병리사, 치과기공사, 치과위생사, 의지보조기사, 정신보건임상심리사, 물리치료사, 작업치료사","대출한도":"최대 5천만원","대출기간":"만기일시상환, 통장대출 : 1년 이내 (1년 이내 단위로 최초 대출기간을 포함 최장 60개월까지 연장 가능)","상환방식":"만기일시상환, 통장대출","이자계산방법":"위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 x 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹)등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.61%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"없음 (재직 및 소득은 공공마이데이터를 통한 서류 징구로 확인하며, 면허(자격)증 정보는 스크래핑을 통해 발급합니다.)","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n* 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지\n(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예: 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내 (고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제할 의무가 발생될 수 있습니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n※ 대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%\n변경 후\n중도상환해약금율 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (219, 4, '전세사기피해자전용 최우선변제금 버팀목 전세대출', '{"대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 자\n신청일 현재 만 19세 이상의 부양가족이 있는 세대주로서 세대주 포함한 세대원 전원이 현재 무주택인 자\n소득 및 자산 요건 제한 없음\n「전세사기피해자 지원 및 주거안정에 관한 특별법」에 의해 전세사기 피해자로 결정된 자\n보증회사의 전세보증금 반환보증 또는 임대인의 반환보증보험 미가입자\n주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 자\n대출신청일 현재 민법상 성년인 세대주(예정자) 또는 세대주로 인정되는 자로 세대원 포함한 전원이 무주택인 자\n대출신청인 및 배우자 합산 소득 및 순자산 가액 요건 제한 없음\n「전세사기피해자 지원 및 주거안정에 관한 특별법」에 의해 전세사기 피해자로 결정된 자 (전세사기피해 등 결정문 징구)\n전세피해주택의 보증금이 3억원이하의 계약을 체결하고 임차보증금의 5% 이상을 지불한 자\n선순위 근저당이 있거나 갱신계약으로 인해 최우선변제금을 지급받을 수 없는 피해자\n보증회사의 전세보증금 반환보증 또는 임대인의 반환보증보험 미가입자","대상주택":"임차전용면적 85㎡ (수도권을 제외한 도시지역이 아닌 읍 또는 면 지역은 100㎡) 이하 및 임차보증금 3억원이하","대출금리":"(2025.03.24 현재, 연이율)\n부부합산\n연소득 임차보증금\n1.4억원 이하 1.7억원 이하 1.7억원 초과\n2천만원 이하 1.2% 1.3% 1.5%\n2천만원 초과 ~ 4천만원 이하 1.5% 1.6% 1.8%\n4천만원 초과 ~ 7천만원 이하 1.8% 1.9% 2.1%\n7천만원 초과 ~ 1억원 이하 2.1% 2.3% 2.4%\n1억원 초과 2.4% 2.7% 3.0%\n우대금리\n1자녀가구 0.3%, 2자녀가구 0.5%, 3자녀 이상 0.7% (다만, 2025.03.24 신규접수분부터 자녀 1명당 4년간 적용)\n국토부 부동산전자계약 시스템을 활용하여 주택의 임대차계약을 체결한자 : 0.1%\n(단, ’25.12.31까지 신규접수하는 건에 대해 최초 대출기한 1회에 한하여 한시적 적용)\n대출심사를 통해 산정한 대출금액의 30%이하로 대출신청시 : 0.2%(대출실행일로부터 4년간 적용)\n＊ 대출 심사를 통해 산정한 대출금액은 호당 대출한도, 소요자금에 대한 대출비율로 산정한 금액, 기존 전세자금대출 잔액(대환에 한함) 중 작은 금액으로 한다.\n※우대금리 적용 후 최종금리가 1.0% 미만인 경우에는 1.0%로 적용\n※최우선변제금 대출 : 무이자\n※우대금리 적용 상한은0.5%(다자녀가구는 0.7%)\n※ 전세사기피해주택의 임차보증금의 전부 또는 일부를 회수하는경우 전세사기피해주택에 대한 전세자금대출을 상환\n※ 대출 실행 후 전세피해결정이 취소 또는 철회되는경우 본 대출금액을 회수하거나 연 3%의 가산금리 적용","대출한도":"아래 1,2,3 중 작은 금액\n호당 대출한도 2.4억원(최우선변제금 +일반 전세피해임차인 대출금)\n전세보증금의 80%이내(소재지 최우선 변제금 포함)\n담보별 대출한도\n한국주택금융공사(HF) 전세대출보증 : 해당 보증 규정에 따름\n주택도시보증공사(HUG) 전세금안심대출보증 : 해당 보증 규정에 따름","대출기간":"한국주택금융공사(HF) 전세대출보증방식 : 2년(최장10년 이용가능)\n주택도시보증공사(HUG) 전세금안심대출보증방식 : 최대2년 1개월(최장 10년 5개월 이용 가능)","담보":"한국주택금융공사(HF) 전세대출보증서 100%\n주택도시보증공사(HUG) 전세금안심대출보증서 90%","상환방식 및 이자납입":"만기일시상환","중도상환\n해약금":"없음","대출관련비용":"주택금융공사HF 보증서일때 보증료(손님 부담) : 대출금액의 0.02%~0.2%\n(한국주택금융공사 보증료 등 운용규정에 따름)\n- 주택도시보증공사HUG 보증서일때 보증료(손님 부담): ①+②\n① 전세보증금 반환보증금의 연 0.097%~연 0.211%(공사가 정한 보증료율 기준에 따름)\n② 대출금의 연 0.031%\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원","필요서류":"확정 일자부 임대차계약서(부동산 전자계약서 포함)\n임차 주택 등기부등본(1개월 이내 발급 분)\n주민등록등본(최근 5개년 주소 변동이력 포함된 1개월 이내 발급 분)\n금리 및 한도우대 받는 경우 가족관계증명서, 혼인관계증명서, 기본증명서(귀화자), 장애인 증명서 등\n연간소득자료(금리구간 판단을 위함)\n근로자 : 재직증명서, 건강보험자격득실확인서, 근로소득원천징수영수증, 급여명세서 등\n자영업자 : 사업자등록증명원, 사업자등록증, 소득금액증명원 등\n전세피해관련 서류 : 전세금입금내역(통장내역 등), 전세사기피해자 등 결정문, 임차권등기명령 완료된 등기부등본, 경매통지서, 경매개시결정문,매각물건명세서, 배당표 등","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"만 19세 이상 세대원 전원 당,타행, 기금 전세자금 및 주택담보 대출 있는 경우 불가(채무인수 미필계좌, 제3자 담보대출 경우 제외)\n본인포함 세대원전원 중 당, 타행 기금 지원자금 대출 있는 경우 취급 불가\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 만기에 원금을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익을 받을 수 있습니다.\n자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n본 상품은 금리인하요구권 적용 비대상 상품입니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n본 상품은 위법계약해지권 적용 비대상 상품입니다.\n대출금리는 국토교통부 고시금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.06.23 변경)\n■ 기존 가입 고객에 대한 적용 여부 : 미적용\n<대출기간>\n변경 전:\n한국주택금융공사(HF) 전세대출보증방식 : 2년(4회 연장, 최장10년 이용가능)\n주택도시보증공사(HUG) 전세금안심대출보증방식 : 최대2년 1개월(4회 연장, 최장 10년 5개월 이용 가능)\n변경 후:\n한국주택금융공사(HF) 전세대출보증방식 : 2년(최장10년 이용가능)\n주택도시보증공사(HUG) 전세금안심대출보증방식 : 최대2년 1개월(최장 10년 5개월 이용 가능)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (220, 4, '전세피해임차인 버팀목 전세대출', '{"대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 자\n대출신청일 현재 민법상 성년인 세대주(예정자) 또는 세대주로 인정되는 자로 세대원 포함한 전원이 무주택인 자\n부부합산 총연소득 1.3억원 이하인 자\n임차보증금 5%이상 계약금을 지불한 경우\n전세피해주택의 보증금이 5억원이하 이고, 보증금의 30%이상을 피해본 아래 전세피해 유형 중 하나 이상에 해당하는 자\n[전세피해 유형]\n가. 전세피해주택에 임차권등기명령을 한 자가 임차권등기명령을 설정한 금액\n나. 전세피해주택의 경,공매 종료 후 임차보증금에서 배당금액을 제외한 임차보증금 미수령액\n다. HUG전세피해지원센터에서 전세피해확인서를 받은 자로 전세피해확인서 내 피해금액\n라. 「전세사기피해자 지원 및 주거안정에 관한 특별법」 제2조 제3호 또는 제4호 다목에 해당하여 전세사기피해자 등으로 결정된 경우 (전세사기피해자 등 결정문)\n대출신청인 및 배우자의 합산 순자산 가액이 통계청에서 발표하는 최근년도 가계금융복지조사의 ‘소득 5분위별 자산 및 부채현황’ 중 소득 4분위 전체가구 평균값 이하(십만원 단위에서 반올림)인 자(2024년 기준, 4.88억원)","대상주택":"임차전용면적 85㎡ (수도권을 제외한 도시지역이 아닌 읍 또는 면 지역은 100㎡) 이하 및 임차보증금 3억원이하","대출금리":"(2025.03.24 현재, 연이율)\n부부합산\n연소득 임차보증금\n1.4억원 이하 1.7억원 이하 1.7억원 초과\n5천만원 이하 1.2% 1.3% 1.5%\n5천만원 초과 ~ 6천만원 이하 1.5% 1.6% 1.8%\n6천만원 초과 ~ 7천만원 이하 1.8% 1.9% 2.1%\n7천만원 초과 ~ 1억원 이하 2.1% 2.2% 2.4%\n1억원 초과 ~ 1.3억원 이하 2.4% 2.5% 2.7%\n우대금리\n1자녀가구 0.3%, 2자녀가구 0.5%, 3자녀 이상 0.7% (다만, 2025.03.24 신규접수분부터 자녀 1명당 4년간 적용)\n국토부 부동산전자계약 시스템을 활용하여 주택의 임대차계약을 체결한자 : 0.1%\n(단, ''25.12.31까지 신규접수하는 건에 대해 최초 대출기한 1회에 한하여 한시적 적용)\n대출심사를 통해 산정한 대출금액의 30%이하로 대출신청시 : 0.2%(대출실행일로부터 4년간 적용)\n＊ 대출 심사를 통해 산정한 대출금액은 호당 대출한도, 소요자금에 대한 대출비율로 산정한 금액, 기존 전세자금대출 잔액(대환에 한함) 중 작은 금액으로 한다.\n※우대금리 적용 후 최종금리가 1.0% 미만인 경우에는 1.0%로 적용\n※우대금리 적용 상한은0.5%(다자녀가구는 0.7%)\n※ 차주가 「전세사기피해자 지원 및 주거안정에 관한 특별법」에 따라 전세사기피해자등 결정이 취소 또는 철회되거나, 고소, 소송 등에 대한 취하나 패소 등으로 전세피해입증이 성립되지 않은 경우 대출금액을 회수하거나 연 3% 가산금리 적용","대출한도":"아래 1,2,3 중 작은 금액\n호당대출한도 2.4억원\n소요자금 대출비율\n신규 : 전세보증금의 80% 이내\n담보별 대출한도\n한국주택금융공사(HF) 전세대출보증 : 해당 보증 규정에 따름\n주택도시보증공사(HUG) 전세금안심대출보증 : 해당 보증 규정에 따름","대출기간":"주택금융신용보증서 담보 또는 협약기관에 의한 채권양도방식\n2년(최장 10년)\n주택도시보증공사보증서 담보\n2년 1개월이내(최장 10년 5개월 이내)","대출신청시기":"임대차계약서상 잔금지급일과 주민등록등본상 전입일 중 빠른 날짜로부터 3개월 이내 신청 가능","담보":"주택금융신용보증서: 2년(최장 10년 이용 가능)\n주택도시보증공사 보증서 : 2년 1개월 (최장 10년 5개월 이용 가능)","상환방식 및 이자납입":"만기일시상환","중도상환\n해약금":"없음","대출관련비용":"주택금융공사HF 보증서일때 보증료(손님 부담) : 대출금액의 0.02%~0.2%\n(한국주택금융공사 보증료 등 운용규정에 따름)\n- 주택도시보증공사HUG 보증서일때 보증료(손님 부담): ①+②\n① 전세보증금 반환보증금의 연 0.097%~연 0.211%(공사가 정한 보증료율 기준에 따름)\n② 대출금의 연 0.031%\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원","필요서류":"확정 일자부 임대차계약서(부동산 전자계약서 포함)\n임차 주택 등기부등본(1개월 이내 발급 분)\n주민등록등본(최근 5개년 주소 변동이력 포함된 1개월 이내 발급 분)\n금리 및 한도우대 받는 경우 가족관계증명서, 혼인관계증명서, 기본증명서(귀화자), 장애인 증명서 등\n연간소득자료\n근로자 : 재직증명서, 건강보험자격득실확인서, 근로소득원천징수영수증, 급여명세서 등\n자영업자 : 사업자등록증명원, 사업자등록증, 소득금액증명원 등\n무소득증빙서류 :최근년도 신고사실없음 사실증명원 또는 퇴직(폐업)증명서, 배우자 및 직계존비속등의 피부양자로 등재되어 있는 건강보험자격득실확인서\n전세피해관련 서류 : 경매통지서, 전세사기피해자 결정문, 전세피해확인서 등","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"만 19세 이상 세대원 전원 당,타행, 기금 전세자금 및 주택담보 대출 있는 경우 불가(채무인수 미필계좌, 제3자 담보대출 경우 제외)\n본인포함 세대원전원 중 당, 타행 기금 지원자금 대출 있는 경우 취급 불가\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 만기에 원금을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익을 받을 수 있습니다.\n자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n본 상품은 금리인하요구권 적용 비대상 상품입니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n본 상품은 위법계약해지권 적용 비대상 상품입니다.\n대출금리는 국토교통부 고시금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.06.23 변경)\n■ 기존 가입 고객에 대한 적용 여부 : 미적용\n<대출기간>\n변경 전 :\n주택금융신용보증서 담보 또는 협약기관에 의한 채권양도방식\n2년(4회 연장, 최장 10년)\n주택도시보증공사보증서 담보\n2년 1개월 이내(4회 연장, 최장 10년 5개월 이내)\n변경 후 :\n주택금융신용보증서 담보 또는 협약기관에 의한 채권양도방식\n2년(최장 10년)\n주택도시보증공사보증서 담보\n2년 1개월 이내(최장 10년 5개월 이내)\n<담보>\n변경 전 :\n주택금융신용보증서: 2년(4회 연장, 최장 10년 이용 가능)\n주택도시보증공사 보증서 : 2년 1개월 (4회 연장, 최장 10년 5개월 이용 가능)\n변경 후 :\n주택금융신용보증서: 2년(최장 10년 이용 가능)\n주택도시보증공사 보증서 : 2년 1개월 (최장 10년 5개월 이용 가능)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (221, 4, '전세피해임차인 버팀목 전세대출(대환)', '{"대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 자\n대출신청일 현재 민법상 성년인 세대주(예정자) 또는 세대주로 인정되는 자로 세대원 포함한 전원이 무주택인 자\n부부합산 총연소득 1.3억원 이하인 자\n버팀목전세자금대출(한국주택금융공사 주택보증서담보)을 이용중이거나, 한국주택금융공사(HF), 서울보증보험(SGI) 주택보증서 담보로 취급된 은행재원 전세자금대출을 이용중인 전세피해자\n임대차계약 종료 후 전세금을 돌려받지 못하여 전세피해주택에 임차권등기를 설정한자\n(「전세사기피해자 지원 및 주거안정에 관한 특별법」제2조 제3호 또는 제4호 다목에 해당하는경우 제외)\n다만, 임대인이 사망하고 상속인이 확정되지 않은 경우에는 임차권등기명령을 신청한 경우에도 임차권등기설정을 한 것과 같이 본다.\n전세피해주택의 보증금이 5억원이하 이고, 보증금의 30%이상을 피해본 자로 아래 전세피해 유형 중 하나 이상에 해당하는 자\n[전세피해 유형]\n가. 임차인의 귀책사유 없이 전입신고일에 근저당권이 설정되어 우선변제권이 침탈된 경우\n나. 임대인이 사망하고 상속인이 확정되지 않은 경우\n다. 전세피해주택의 임대차 계약과 관련하여 사기 사건으로 형사 고소한 경우\n라. 전세피해주택에 경·공매가 개시된 경우\n마. 그 밖에 전세피해에 해당하여 HUG 전세피해지원센터\n전세피해확인서(대환용)발급받은 경우\n바. 「전세사기피해자 지원 및 주거안정에 관한 특별법」제2조 제3호 또는 제4호 다목에 해당하여 전세사기피해자등으로 결정된 자 (전세피해자 등 결정문)\n대출신청인 및 배우자의 합산 순자산 가액이 통계청에서 발표하는 최근년도 가계금융복지조사의 ‘소득 5분위별 자산 및 부채현황’ 중 소득 4분위 전체가구 평균값 이하(십만원 단위에서 반올림)인 자(2024년 기준, 4.69억원)","대상주택":"임차전용면적 85㎡ (수도권을 제외한 도시지역이 아닌 읍 또는 면 지역은 100㎡) 이하 및 임차보증금 3억원이하","대출금리":"(2025.03.24 현재, 연이율)\n부부합산\n연소득 임차보증금\n1.4억원 이하 1.7억원 이하 1.7억원 초과\n5천만원 이하 1.2% 1.3% 1.5%\n5천만원 초과 ~ 6천만원 이하 1.5% 1.6% 1.8%\n6천만원 초과 ~ 7천만원 이하 1.8% 1.9% 2.1%\n7천만원 초과 ~ 1억원 이하 2.1% 2.2% 2.4%\n1억원 초과 ~ 1.3억원 이하 2.4% 2.5% 2.7%\n우대금리\n1자녀가구 0.3%, 2자녀가구 0.5%, 3자녀 이상 0.7%(다만, 2025.03.24 신규접수분부터 자녀 1명당 4년간 적용)\n국토부 부동산전자계약 시스템을 활용하여 주택의 임대차계약을 체결한자 : 0.1%\n(단, ’25.12.31까지 신규접수하는 건에 대해 최초 대출기한 1회에 한하여 한시적 적용)\n대출심사를 통해 산정한 대출금액의 30%이하로 대출신청시 : 0.2%(대출실행일로부터 4년간 적용)\n＊ 대출 심사를 통해 산정한 대출금액은 호당 대출한도, 소요자금에 대한 대출비율로 산정한 금액, 기존 전세자금대출 잔액(대환에 한함) 중 작은 금액으로 한다.\n※우대금리 적용 후 최종금리가 1.0% 미만인 경우에는 1.0%로 적용\n※ 우대금리 적용 상한은0.5%(다자녀가구는 0.7%)\n※ 차주가 「전세사기피해자 지원 및 주거안정에 관한 특별법」에 따라 전세사기피해자등 결정이 취소 또는 철회되거나, 고소, 소송 등에 대한 취하나 패소 등으로 전세피해입증이 성립되지 않은 경우 대출금액을 회수하거나 연 3% 가산금리 적용","대출한도":"4억원\n아래 1,2,3 중 작은 금액\n대환대상 전세대출 잔액범위 내\n전세보증금의 80%이내\n담보별 대출한도\n한국주택금융공사(HF) 전세대출보증 : 해당 보증 규정에 따름\n주택도시보증공사(HUG) 전세금안심대출보증 : 해당 보증 규정에 따름","대출기간":"6개월(보증기관 기준에 따라 6개월 단위로 연장 가능)\n「전세사기피해자 지원 및 주거안정에 관한 특별법」제2조 제3호 또는 제4호 다목에 해당하여 전세피해주택 임대차계약기간 중에 신청한 경우 해당 임대차계약의 종료일까지","담보":"주택금융공사신용보증서 80%","상환방식 및 이자납입":"만기일시상환","중도상환\n해약금":"없음","대출관련비용":"주택금융공사HF 보증서일때 보증료(손님 부담) : 대출금액의 0.02%~0.2%\n(한국주택금융공사 보증료 등 운용규정에 따름)\n- 주택도시보증공사HUG 보증서일때 보증료(손님 부담): ①+②\n① 전세보증금 반환보증금의 연 0.097%~연 0.211%(공사가 정한 보증료율 기준에 따름)\n② 대출금의 연 0.031%\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원","필요서류":"확정 일자부 임대차계약서(부동산 전자계약서 포함)\n임차 주택 등기부등본(1개월 이내 발급 분)\n주민등록등본(최근 5개년 주소 변동이력 포함된 1개월 이내 발급 분)\n금리 및 한도우대 받는 경우 가족관계증명서, 혼인관계증명서, 기본증명서(귀화자), 장애인 증명서 등\n연간소득자료\n근로자 : 재직증명서, 건강보험자격득실확인서, 근로소득원천징수영수증, 급여명세서 등\n자영업자 : 사업자등록증명원, 사업자등록증, 소득금액증명원 등\n전세피해관련 서류 : 임차권등기명령 완료된 등기부등본, 경매통지서, 경매개시결정문 등","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"만 19세 이상 세대원 전원 당,타행, 기금 전세자금 및 주택담보 대출 있는 경우 불가(채무인수 미필계좌, 제3자 담보대출 경우 제외)\n본인포함 세대원전원 중 당, 타행 기금 지원자금 대출 있는 경우 취급 불가\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 만기에 원금을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익을 받을 수 있습니다.\n자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n본 상품은 금리인하요구권 적용 비대상 상품입니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n본 상품은 위법계약해지권 적용 비대상 상품입니다.\n대출금리는 국토교통부 고시금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.04.10 변경)\n■ 기존 가입 고객에 대한 적용 여부 : 적용\n<우대금리 적용기준 변경: 2025.03.24 소급적용>\n변경 전 :\n③ 대출심사를 통해 산정한 대출금액의 30%이하로 대출신청시 : 0.2%\n*대출 심사를 통해 산정한 대출금액은 호당 대출한도, 소요자금에 대한 대출비율로 산정한 금액, 기존 전세자금대출 잔액(대환에 한함)중 작은 금액으로 한다.\n변경 후 :\n③ 대출심사를 통해 산정한 대출금액의 30%이하로 대출신청시 : 0.2%(대출실행일로부터 4년간 적용)\n*대출 심사를 통해 산정한 대출금액은 호당 대출한도, 소요자금에 대한 대출비율로 산정한 금액, 기존 전세자금대출 잔액(대환에 한함)중 작은 금액으로 한다.\n■ 기존 가입 고객에 대한 적용 여부 : 미적용\n변경 전 : 전세보증금 반환보증금의 연 0.115~연 0.154%\n변경 후 : 전세보증금 반환보증금의 연 0.097~연 0.211%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (222, 4, '전문직 클럽대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"공인회계사, 변리사, 세무사, 관세사, 감정평가사, 도선사, 국내 민간 항공사에 재직중인 조종사,\n공인노무사, 법무사, 손해사정사, 기술사, 건축사, 보험계리사, 약사, (우량)지정대학교의 전임강사, 조교수, 부교수,\n정교수, 약대졸업예정자(6학년) (지정업체 및 우량지정대학교는 영업점에서 확인이 가능합니다.)","대출한도":"최대 3억원","대출기간":"만기일시상환(일시대출,통장대출) : 1년 이내\n부분원(리)금균등분할상환 : 5년 이내\n(부분)통장대출분할감액 : 5년 이내\n전액원(리)금균등분할상환 : 10년이내","상환방식":"만기일시상환, 통장대출, 원(리)금균등분할상환, 통장대출분할감액","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"소득증명서류, 재직증명서(급여소득자), 사업자등록증(사업소득자), 자격증(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출기간,대출한도,대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터 (1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%(단, 인터넷/모바일 신청&약정시 0.5%)\n변경 후\n중도상환해약금율 0.04%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (223, 4, '주거안정 주택구입자금대출', '{"상품특징":"주택도시기금의 주거안정 주택구입자금 대출로, 담보가액 범위 내에서 최대 2억원 까지 지원합니다.","대출대상":"세대주를 포함한 세대원 전원이 대출신청일 현재 무주택인 자 대출신청인과 배우자의 합산총 연소득 연간 6천만원 이하이며 다음 한가지에 해당하는 자\n신청일 현재 민법상 성년인 세대주(아래 어느 하나에 해당하는 경우 제외)\n①만 30세 미만 단독세대주.\n②만 30세 미만 미혼세대주.(단, 직계존속 중 1인 이상과 동일세대를 구성하고 주민등록표상 부양기간(합가일기준)이 계속해서 6개월 이상인 경우 가능)\n대출신청인 및 배우자의 합산 순자산가액이 통계청에서 발표하는 최근년도 가계금융복지조사의 ''소득 5분위별 자산 및 부채현황'' 중 소득 4분위 전체가구 평균값 이하(십만원 단위에서 반올림)인 자(2024년 기준, 4.88억원)","대상주택":"전용면적 85㎡(수도권을 제외한 도시지역이 아닌 읍 또는 면지역은 100㎡)이하의 준공된 다음 중 하나에 해당되는 주택\n①LTV 70% 이상 주택\n주택매매가격 *70% ≤ 주택담보 대출 금액\n②임차 매입주택\n임대차계약을 체결한 본인(동일세대원 포함)이 주민등록전입일로부터 계속해서 1년이상 거주한 주택을 매입하는 경우\n단, 주택 가격이 6억원을 초과하는 경우 제외","대출금리":"기본금리 : 연2.8%(대출기간 30년인 경우 연0.2% 가산)\n우대금리 : 최대 연 0.5% (중복불가)\n①다자녀가구(만19세 미만 3자녀 이상) : 연0.5%\n②다문화, 장애인 가구 : 연 0.2%\n※ 국투교통부 고시에 따라 변경될 수 있습니다.","대출한도":"담보가액 범위 내에서 최대 2억원 이내","대출기간":"20년 또는 30년 (30년은 대출신청일 현재 만 45세 이하만 가능)","대출신청시기":"소유권이전등기 전에 신청\n다만, 소유권이전등기를 한 경우에는 이전등기 접수일로부터 3개월 이내 신청","담보":"1순위 근저당권 설정을 원칙으로 하며 110%설정","상환방식":"원리금(또는 원금)균등분할상환","대출조건":"대출취급 시 차주는 대출 실행일로부터 1개월 이내에 본건 담보주택에 전입을 완료하여 전입세대열람표를 은행에 제출하고, 전입한 날로부터 1년까지 담보주택에 거주","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 × 대출이자율 × 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이자선납제한":"※ 본 상품은 국토교통부 ''주택도시기금 대출 업무 시행세칙''에 따라 , 대출실행 후 3개월 동안 이자선납이 제한됩니다.","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기한연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)","필요서류":"매매(분양)계약서-주민등록등본(1개월이내 발급분)\n건물,토지 등기부등본(1개월이내 발급분)\n등기권리증 및 인감증명서 2통, 인감도장, 신분증\n소득확인자료\n-근로자 : 건강보험자격득실확인서, 재직증명서, 소득금액증명원, 근로소득원천징수영수증 또는 급여명세서\n-서민(자영업자 포함) : 사업자등록증, 소득금액증명원 또는 사업소득원천징수영수증(연말정산용)","신청절차":"1. 대출상담 및 대출가능 금액 산정\n-대출대상자, 대상주택, 대출조건 등 적격여부 상담\n-상품별 기준에 의한 대출가능금액 산정\n2. 대출신청 및 서류제출\n-매매계약서, 건물(토지)등기부등본, 인감증명서, 등기권리증, 주민등록등본 등 관련서류 제출\n3. 무주택 및 소득확인\n-국토교통부, 국세청에 의뢰(은행)\n4. 대출심사\n-신용조사 및 담보주택에 대한 실태조사(은행)\n-대출 가·부 통보(은행)\n5. 대출약정 및 담보제공\n-대출금액, 대출기간 및 대출이율 등 대출조건 약정\n-담보주택에 대한 근저당권 설정\n6. 대출실행\n-은행에서 대출을 실행하여 입금\n철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"※ 자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 대출만기 도래시 대출금액을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n자세한 사항은 해당 영업점 담당자 앞 문의 부탁드립니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.01 변경)\n■ 기존 가입 고객에 대한 적용 여부 : 미적용\n<순자산 기준 변경>\n변경 전 :\n2022년 기준 5.6억원\n변경 후 :\n2024년 기준, 4.88억원","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (224, 4, '주거취약계층 이주지원 버팀목전세대출 (舊 비정상거처이주지원 버팀목전세대출)', '{"대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 자로 비정상거처 확인서(민간임대) 또는 주거상향 유형확인서(공공임대)를 받은 자\n신청일 현재 민법상 성년인 세대주(예정자 포함)로서 세대주 포함한 세대원 전원이 현재 무주택인 자\n부부합산 총연소득 5천만원 이하인 자(공공임대주택 입주예정자는 제외)\n임차보증금 5%이상 계약금을 지불한 경우\n대출신청인 및 배우자의 합산 순자산 가액이 통계청에서 발표하는 최근년도 가계금융복지조사의 ‘소득 5분위별 자산 및 부채현황’ 중 소득 3분위 전체가구 평균값 이하(십만원 단위에서 반올림)인 자(2024년 기준, 3.37억원)인 자\n(공공임대주택 제외)\n비정상거처에 신청일 기준 3개월 이상 거주 중인 세입자로 민간임대주택 또는 공공임대주택에 이주하려는 자\n지하층, 쪽방, 고시원, 여인숙, 비닐하우스, 노숙인시설, 컨데이터, 움막, PC방 등\n최저주거기준을 미달하는 주거환경에서 만 18세 미만의 아동과 함께 거주하는 자\n※ 신청기간 : ’23년 4월 10일부터~기금 소진 시까지(공공임대주택은 제외)","대상주택":"임차면적 전용 85㎡ (수도권을 제외한 도시지역이 아닌 읍 또는 면 지역은 100㎡) 이하 주거용 주택 및 오피스텔(전입신고 필수)\n다만, 25세미만 단독세대주의 경우 60 ㎡ 이하\n임차보증금액 2억원 이하\n※ 공공임대주택 : 비정상거처 거주자지원 협약공공임대주택(공공주택특별법 제2조 1항 공공임대주택) (주거상향유형확인서 발급 확인)","대출금리":"대출금 5천만원 까지는 무이자\n대출금 5천만원 초과하는 구간 대출의 임차보증금 및 소득구간별 금리\n(2025.03.24 현재, 연이율)\n보증금 연소득 3억원 이하\n2천만원 이하 1.2%\n4천만원 이하 1.5%\n4천만원 초과 1.8%\n우대금리(중복불가, 단 ③ 항목 중복 가능)\n장애인가구 0.2%\n다자녀 연0.7%, 2자녀가구 연0.5%, 1자녀가구 연 0.3%\n(다만, 2025.03.24 신규접수분부터 자녀 1명당 4년간 적용)\n국토부 부동산전자계약 시스템을 활용하여 주택의 임대차계약을 체결한자 : 0.1%\n(단, ''25.12.31까지 신규접수하는 건에 대해 최초 대출기한 1회에 한하여 한시적 적용)\n※우대금리 적용 후 최종금리 1.0% 미만인 경우에는 1.0%적용\n※우대금리 적용상한은 0.5%(다자녀가구는 0.7%)\n자산기준 초과 시 가산금리\n대출을 받은 후 자산심사 결과에 따라 본인 및 변경 등에 따라 변동배우자의 합산 자산기준 초과된 금액이 1천만원 이내인 경우 신규취급된 금리에 연0.1%p 가산하며 1천만원 초과인 경우 신규 취급된 금리에 2.0%p 가산적용\n[자산심사 기준은 국토교통부 주택도시기금포털 (http://nhuf.molit.go.kr)에서 확인 가능]\n※ 국토교통부 고시 변동금리이므로, 고시에 따라 변경될 수 있습니다.","대출한도":"신규 : 임차보증금의 100%이내(단, 한국주택금융공사 보증서 담보제공 시 80%이내)\n갱신 : 증액금액 이내에서 증액 후 총 보증금의 100% 이내 (단, 한국주택금융공사 보증서 담보제공 시 80%이내)\n※ 호당대출한도 : 공공임대주택 50만원 , 그 외 임대주택 8천만원","대출기간":"공공임대주택 : 2년(최대 20년)\n그외 민간임대주택 : 2년(최대 10년)단, 주택도시보증공사 보증서 방식의 경우 최대 10년 5개월\n단, 공공임대주택과 민간임대주택 모두 임차종료일을 초과할수 없음.","대출신청시기":"임대차계약서상 잔금지급일과 주민등록등본상 전입일 중 빠른 날짜로부터 3개월 이내 신청 가능","담보":"공공임대주택 : 주택금융신용보증서, 협약기관과의 채권양도방식\n그외 민간임대주택 : 주택금융신용보증서, 주택도시보증공사보증서","상환방식 및 이자납입":"만기일시상환, 이자 매월 후취","중도상환\n해약금":"없음","대출관련비용":"주택금융공사HF 보증서일때 보증료(손님 부담) : 대출금액의 0.02%~0.2%\n(한국주택금융공사 보증료 등 운용규정에 따름)\n- 주택도시보증공사HUG 보증서일때 보증료(손님 부담): ①+②\n① 전세보증금 반환보증금의 연 0.097%~연 0.211%(공사가 정한 보증료율 기준에 따름)\n② 대출금의 연 0.031%\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원","필요서류":"확정 일자부 임대차계약서(부동산 전자계약서 포함)\n비정상거처확인서(민간임대) 또는 주거상향확인서(공공임대)\n임차 주택 등기부등본(1개월 이내 발급 분)\n주민등록등본(최근 5개년 주소 변동이력 포함된 1개월 이내 발급 분)\n금리 및 한도우대 받는 경우 가족관계증명서, 혼인관계증명서, 기본증명서(귀화자), 장애인 증명서 등\n연간소득자료\n근로자 : 재직증명서, 건강보험자격득실확인서, 근로소득원천징수영수증, 급여명세서 등\n자영업자 : 사업자등록증명원, 사업자등록증, 소득금액증명원 등\n무소득증빙서류 :최근년도 신고사실없음 사실증명원 또는 퇴직(폐업)증명서, 배우자 및 직계존비속등의 피부양자로 등재되어 있는 건강보험자격득실확인서","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"만 19세 이상 세대원 전원 당,타행, 기금 전세자금 및 주택담보 대출 있는 경우 불가(채무인수 미필계좌, 제3자 담보대출 경우 제외)\n본인포함 세대원전원 중 당, 타행 기금 지원자금 대출 있는 경우 취급 불가\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 만기에 원금을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익을 받을 수 있습니다.\n자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n본 상품은 금리인하요구권 적용 비대상 상품입니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n본 상품은 위법계약해지권 적용 비대상 상품입니다.\n대출금리는 국토교통부 고시금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.06.23 변경)\n■ 기존 가입 고객에 대한 적용 여부 : 미적용\n<대출기간>\n변경 전 :\n공공임대주택 : 2년(9회 연장 최대 20년)\n그외 민간임대주택 : 2년(4회 연장 최대 10년)단, 주택도시보증공사 보증서 방식의 경우 최대 10년 5개월\n변경 후 :\n공공임대주택 : 2년(최대 20년)\n그외 민간임대주택 : 2년(최대 10년)단, 주택도시보증공사 보증서 방식의 경우 최대 10년 5개월","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (225, 4, '주택신보 전세자금대출', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"한국주택금융공사의 보증서를 담보로 임차보증금의 80%범위 내에서 주택의 전세자금을 지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님\n- 임차보증금액주)이 7억원(수도권 외 5억원) 초과하는 경우 대출 불가\n주)월세가 있는 경우 월세보증금 및 월세에 대해 전월세전환율을 적용하여 계산한 금액\n(월세보증금+(월세X12÷전월세전환율))\n임차보증금의 5% 이상 지급한 만 19세 이상의 세대주\n세대주와 동일세대를 이루고 있는 세대원 중 다음 하나에 해당하는 자\n배우자\n직계존비속 및 그의 배우자\n신청인 및 배우자의 형제자매\n배우자의 직계존비속 및 그의 배우자\n보증신청일로부터 3개월 이내에 결혼이 예정된 자","대상주택":"공부상(등기부등본 등) 주거용 주택(미등기 주택도 가능)","대출한도":"최대 444백만원 범위 내 아래 ①과 ② 중 적은 금액으로 함\n임차(전세)보증금의 80% 이내\n신청인(배우자포함)의 연간소득의 최대 4.5배 이내","대출기간":"전세계약만기일 범위 내 최장 2년(최대 20년까지 1년 단위로 연장 가능)\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내(계약 갱신일 이전에도 보증신청 가능)","담보":"주택금융신용보증서 담보\n(임차목적물이 서울특별시, 인천광역시, 경기도 또는 규제지역에 소재하는 경우 80% 그 외 90%)","상환방식 및 이자납입":"만기일시상환,원(리)금균등분할상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"주민등록등본, 확정일자부 전·월세계약서(원본), 임차보증금의 5%이상을 지급한 영수증, 임차주택의 등기부등본, 현거주지 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서(주민등록등본상 배우자 없는 경우), 금융거래확인서(타 금융기관에 신용대출 보유한 경우) 등","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 한국주택금융공사 보증규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.07.21 변경)\n<담보>\n기존 가입고객에 대한 적용여부: 미적용\n변경전\n주택금융신용보증서담보 (90% 담보)\n변경후\n주택금융신용보증서담보\n(서울특별시, 인천광역시, 경기도 또는 규제지역에 소재하는 경우 80% 그 외 90%)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (226, 4, '지방중소기업지원프로그램 (구 C2자금대출)', '{"대출대상":"한국은행 각 지역본부별 중소기업 지원자금 운영기준에 따름","대출한도":"한국은행 각 지역본부별 중소기업 지원자금 운영기준에 따름","대출금리":"변동금리 운전대 연 2.32-4.94%/시설대 연 1.95~4.73% (2021.03.24 현재)\n[기준금리 0.75%(91일물 CD유통수익률) + 가산금리 운전대 (최저)1.57%/(최고)4.19% / 시설대 (최저)1.20%/(최고)3.98%]\n* 이자 산정방법 : 최저이율(기준금리+가산금리-우대금리) / 최고이율(기준금리+가산금리)\n* 상기 산정 금리는 대출금액 1억원, 대출기간 1년, 당행 내부 신용등급 (최고)B1등급/(최저)B5, 일반상가 담보유효가 100%, 분할상환, 운전/시설자금, 한은차입지원 (운전)0.57%/(시설) 0.57% 경기본부 특별지원 기준\n* 가산금리는 신용등급, 대출기간, 대출금액, 대출과목, 상환방식, 담보종류, 한국은행 차입비율에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경될 수 있습니다.\n* 연체대출금리는 적용금리+3.0%과 연 15.0%중 낮은 금리를 적용합니다.\n* 이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n* 기준금리 안내( 바로가기) : 상품공시실 → 대출상품 → 금리안내 → 대출(가계대출기준금리) → 91일물 CD유통수익률","상환방식 및 대출기간":"시설자금대출\n- 만기일시상환(건별대출) : 3년 이내\n- 분할상환 : 15년 이내(단, 업무용/상업용 부동산의 매입자금인 경우에는 20년 이내)\n운전자금대출\n- 만기일시상환(건별대출): 3년 이내\n- 분할상환: 5년 이내\n상기 시설자금 및 운전자금의 상환방식 및 대출기간은 한은 각 지역본부별 중소기업 지원자금 운영기준에 따라 제한을 받을 수 있습니다.","이자계산방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등)단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 시설자금대출 지원대상이 되는 당해 시설(토지, 건물, 기계기구 등 물리적 형태가 있는 자산)","대상담보":"고객별 신용도에 따라 부동산 등 담보제공이 필요할 수 있음","중도상환해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율\n- 부동산/동산담보 : 고정금리 1.4%, 변동금리 1.2%\n- 신용/기타담보 : 고정금리 1.1%, 변동금리 1.0%","한도약정수수료":"한국은행 각 지역본부별 중소기업 지원자금 운영기준에서 한도거래를 허용하지 않습니다.","약정한도미사용\n수수료":"한국은행 각 지역본부별 중소기업 지원자금 운영기준에서 한도거래를 허용하지 않습니다.","대출관련비용":"대출금액 5천만원 초과시 손님 및 은행 각 인지세 50% 부담하며, 담보설정에 따른 국민주택채권 매입비용, 담보해지 시\n말소비용이 발생할 수 있습니다.\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"사업자등록증 사본, 법인등기부등본, 소요자금 증빙서류, 재무제표 등\n※ 신용조사 및 대출심사 시 필요한 서류가 추가될 수 있습니다","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출〮만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n상기 대출금리는 대출 신규시 적용금리이며 기준금리 변동시 금리가 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재\n산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n대출원리금 납입이 지연되는 경우 연체이자가 부과되며 채무자의 재산/신 용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n자세한 사항은 상품설명서를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2021.03.25 변경)\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경전 : 금리인하요구권대상여부 : 본상품은 금리인하요구권의 신청이 가능합니다\n변경후 : 금리인하요구권(‘대상여부’ 삭제)\n- 기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n- 금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n- 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경전 : 신설\n변경후 :\n- 대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n- 대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출〮만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경전 : 신설\n변경후 :\n- 은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n- 은행은 해지를 요구 받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n상품내용 변경에 관한 사항\n(2019.04.17 변경)\n■기존 가입고객에 대한 적용 여부 : 적용\n<중도상환해약금율 변경>\n변경 전 : 부동산담보:1.4%, 신용/기타담보 1.1%\n변경 후 :\n부동산담보:고정금리시 1.4% 변동금리시 1.2%\n신용/기타담보:고정금리시 1.1% 변동금리시 1.0%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (227, 4, '충청남도 청년 임차보증금대출', '{"상품특징":"충청남도 융자추천 선정 만39세 이하의 사회초년생, 취업준비생, 대학(원)생을 대상으로 이차보전 금리를 지원하고,\n한국주택금융공사의 보증서를 담보로 최대 1억5천만원 까지 전세자금을 지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님\n충청남도에 주소를 두거나, 충청남도 소재 대학 또는 직장에 재적·재직중인 만19세 이상 만39세 이하의 무주택 세대주\n충청남도 소재 임차보증금 2억원 이하로 임대차계약을 체결하고 임차보증금의 5%이상을 계약금으로 지급한 임차인\n주민등록등본 상 총 가구원 기준, 기준 중위소득 120%이하인 자\n충청남도 융자추천을 받은 손님","대상주택":"충청남도 관내에 소재하는 주택 및 주거용 오피스텔\n단, 공공임대 주택 및 다중주택 제외","대출금리":"[기준일자: 2024.02.27. 단위:연%]\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최저-최고금리\n신잔액기준 COFIX 6개월 3.290 2.100 0 5.39\n※ 대출금리는 기준금리 변동 시 금리가 변경됩니다.\n※ 대출금리는 충청남도와의 협약에 따라 변경될 수 있습니다.\n※ 2024.02.27 현재, 내부신용(ASS)1~11등급, 대출기간 2년, 대출금액 7천만원 기준\n- 금리 산정방법: 최저금리(기준금리 + 가산금리 – 우대금리)\n                최고금리(기준금리 + 가산금리)\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","이차보전금리":"최대 연 4.50% 이하\n※ 이차보전금리를 차감한 본인 부담 금리가 연 1.0% 이하인 경우 최저 연 1.0% 적용합니다.\n※ 손님이 부담하는 실제 적용금리는 대출금리에서 아래의 충청남도 이차보전금리를 차감한 금리입니다.\n기본 이자지원 금리 (최대 연 3.0%)\n- 대출금리의 50%(대출금리 = 신잔액COFIX 6개월 + 협약가산금리 2.1%)\n추가 이자지원 금리 (최대 연 1.5%)\n1) 기준 중위소득 120% 이하 0.2%, 100% 이하 0.3%, 80% 이하 0.4%, 60% 이하 0.5%\n2) 자녀수 1자녀 0.3%, 2자녀 0.5%, 3자녀 이상 0.7%\n3) 신혼부부 0.3%","이차보전\n지원기간":"기본 2년(최대 6년)","이차보전\n중단사유":"대출기간 중 아래 이차보전 중단사유 발생시 은행에 즉시 해당사유를 통지하고 대출금 전액을 즉시 상환해야합니다.\n충청남도 외 지역으로 전출\n임대차계약 종료\n1주택 이상 보유(부부합산 기준)\n주거급여 대상자에 해당하는 경우","대출한도":"최대 1억5천만원 범위 내, 아래 중 적은 금액\n임차보증금의 80% 이내\n상환능력별 보증한도에 따른 대출가능금액\n신청인의 보증신청금액\n충청남도의 융자추천 금액","대출기간":"전세계약 만기일 범위 내 최대 2년 이내(최장 6년)","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른 날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내","담보":"주택금융신용보증서","상환방식":"만기일시상환, 이자 매월 후취","기한연장":"업무협약에 따라 충청남도의 이자지원기간 경과 후 기한연장 불가","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급 시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료 시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률: 0.61%","대출관련비용":"보증료(손님부담) : 대출금액의 0.02%~0.20% (한국주택금융공사 보증료 등 운용규정에 따름)\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"충청남도 융자추천서, 주민등록등본, 확정일자부 전세계약서, 임차보증금의 5%이상을 지급한 영수증,\n임차주택의 등기사항전부증명서, 신분증, 소득 및 재직증명 입증서류, 가족관계증명서 등","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"협약대출 한도 소진에 따라 조기에 판매 종료될 수 있습니다.\n손님의 신용도에 따라 한국주택금융공사 보증서 발급이 거절되거나, 은행에서 정한 부적격자에 대하여 대출 취급이\n제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가\n결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류,\n경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금률 : 0.7%\n변경 후\n중도상환해약금률 : 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (228, 4, '클러스터 기업대출', '{"상품특징":"클러스터 지역내 부동산을 매입하거나 분양 받은 기업에게 부동산 취득에 필요한 시설자금 및 경상적인 생산\n및 영업활동 등을 위한 운전자금 대출을 지원하기 위한 상품","대출대상":"클러스터 지역내 소유권이전 등기된 부동산을 담보로 제공하거나 토지 분양대금 반환채권 또는 소유권이전등기청구권을\n담보로 제공하는 법인(컨소시엄 포함) 또는 개인사업자","대출한도":"부동산 감정가의 100%이내 최대 500억원\n(신용등급 및 담보조건 등에 따라 다르게 적용될 수 있습니다.)","대출금리":"변동금리 최저 연 4.703% ~ 최고 연 7.053% (2025.02.27현재)\n[기준금리 연 2.84%(91일물CD유통수익률) + 가산금리(최저) 연 1.86% / (최고) 연 4.21%]\n최저금리기준 : 2025.02.27 현재, 내부신용등급 A1, 대출기간 3년, 대출금액 10억원, 시설자금대출, 분할상환, 담보유효가 100%, 우대금리적용\n최고금리적용 : 2025.02.27 현재, 내부신용등급 B4, 대출기간 3년, 대출금액 10억원, 시설자금대출, 분할상환, 담보유효가 100%\n상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n가산금리는 신용등급, 대출기간, 대출금액, 대출과목, 상환방식, 담보종류 등에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경 될 수 있습니다.\n연체이자율은 [대출이자율+연체가산이율(연3%)], 최고 연 15%입니다.\n이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연20%입니다.","대출기간":"운전자금 – 만기일시상환 : 3년 이내 / 분할상환 : 5년 이내\n시설자금 – 만기일시상환 : 3년 이내 / 분할상환 : 15년 이내(다만, 업무용/상업용 매입자금은 20년 이내)","상환방식":"만기일시상환 / 원(리)금분할상환방식","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산.\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편 넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금\n2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금\n3. 연체기간이 1일인 연체대출채권 및 지급보증 대지급금\n4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급 시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사 후 승인을 득한 경우 계약갱신 가능하며,\n고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해\n연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율＊ X (중도상환약정잔여일수 ÷ 중도상환약정기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율\n담보종류 고정 변동\n순수신용 0.04%\n부동산/동산 0.51%\n보증서/기타담보 0.04% 0.03%","대출관련비용":"담보설정에 따른 국민주택채권 매입비용, 담보해지 시 말소비용이 발생할 수 있습니다.\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","금리인하요구권":"금리인하요구권\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n대출계약철회권\n대출실행일(대출금수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 당행 대출 계약 신규가 제한될 수 있습니다.\n위법계약해지권\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"※ 손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n※ 대출금리는 기준금리 변동 시 변경될 수 있습니다.\n※ 대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n※ 상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n※ 신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n※ 원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객 적용여부 : 적용\n변경 전:\n중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n대출잔여기간 1개월 이내 상환 시 면제\n중도상환해약금율 : 고정금리 1.4%, 변동금리 1.2%\n변경 후:\n중도상환해약금: 중도상환대출금액 X 중도상환해약금율＊ X (중도상환약정잔여일수 ÷ 중도상환약정기간)\n최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출잔여기간 1개월 이내 상환 시 면제\n중도상환해약금율\n담보종류 고정 변동\n순수신용 0.04%\n부동산/동산 0.51%\n보증서/기타담보 0.04% 0.03%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (229, 4, '파워메디론', '{"상품특징":"약국을 운영하고 있는 개업 약사(개인사업자)에 대한 신용대출 상품입니다.","대출대상":"국민건강보험공단 요양급여 수령계좌 또는 3개 이상 신용카드사의 가맹점 매출대금 수령계좌를 당행으로 지정하고,\n사업자등록증을 발급받은 개업 약사 (개인사업자)","대출금리":"변동금리 최저 연 4.887% ~ 최고 연 11.437%\n[2025.07.07 기준금리(91일물 CD유통수익률) 2.510% + 가산금리 (최저)3.637%, (최고)8.927% – 부수거래감면 등 우대금리 감면 1.26% 적용 기준]\n* 이자 산정방법 : 최저이율[기준금리+가산금리(ASS 1등급 기준)-우대금리] / 최고이율[기준금리+가산금리(ASS 10등급 기준)]\n* 상기 산정 금리는 대출금액 5천만원, 대출기간 1년, 당행 내부 신용(ASS) 최저 1등급/최고 10등급, 일시상환, 운전자금대출 기준입니다.\n* 가산금리는 신용등급, 대출금액, 담보종류, 시설자금대출 취급여부 등에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경될 수 있습니다.\n* 부수거래감면 등 우대금리: 주거래손님 0.1%, 사업자주거래우대통장 월평잔 조건 0.1%, 금융연수원 금융교육이수 0.1%, 소상공인 경영컨설팅 이수 0.2%, 본부 감면 0.76% 등 거래실적종류에 따라 우대\n* 연체대출금리는 적용금리+3.0%과 연 15.0%중 낮은 금리를 적용합니다.\n* 이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n* 대부업등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고금리는 연20%입니다.\n* 기준금리 안내( 바로가기) : 상품공시실 → 대출상품 → 금리안내 → 대출(가계대출기준금리) → 91일물 CD유통수익률","대출한도":"개업자금: 최대 3.5억원 (사업기간 6개월 미만)\n운영자금: 최대 5억원 (사업기간 6개월 이상)\n※ 최대한도는 매출액 및 자금용도 등에 따라 상이할 수 있습니다.","상환방식 및 대출기간":"만기일시상환(개별대출, 통장대출) : 1년 이내\n원(리)금균등분할상환 : 6년 이내","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"하나원큐앱 (App) 또는 하나원큐기업 앱(App)을 통한\n신청가능시간: 오전 9시 ~ 오후 10시 (공휴일 포함)\n실행가능시간: 오전 9시 ~ 오후 6시 (공휴일 제외 은행 영업일만 가능)","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율 : 고정금리 0.04%, 변동금리 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","한도약정\n수수료":"산식 : 한도약정액 X 한도약정수수료율(0.3%) X 약정기간 ÷ 365일(윤년 366일)\n※ 통장대출 취급시 부과됩니다.(단, 약정한도미사용수수료 납부 손님은 면제)","약정한도미사용\n수수료":"산식 : (약정한도평잔 – 운용기간 중 여신평잔) x 약정한도미사용수수료율(0.5%) x 운용기간 ÷ 365일(윤년은 366일)\n※ 통장대출 취급시 부과됩니다. (단, 한도약정수수료 납부 손님은 면제)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원","필요서류":"사업자등록증 사본, 약사면허증사본, 신분증, 요양급여확인원, 연간신고매출(부가세과세표준증명원), 소득금액증명원 등\n(※ 신용조사 및 대출심사 시 필요한 서류가 추가될 수 있습니다.)","금리인하요구권":"기업신용평가 등에 따라 손님의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n회사채 등급상승, 재무상태 개선(감사보고서 등), 특허 취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약 해지권 행사가능 상품입니다.\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날‘부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"은행의 정책 및 시장금리 상황에 따라 금리는 변동될 수 있습니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류,경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용 점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래관련 불이익을 받을 수 있습니다.\n대출원리금 연체시 계약만료기한전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.07.15 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<부수거래 감면금리 변경>\n변경전 : 부수거래감면 등 우대금리 감면 1.27% 적용 기준\n변경후 : 부수거래감면 등 우대금리 감면 1.26% 적용 기준","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (230, 4, '폐업지원대환대출(부동산_주택,오피스텔,기타부동산)', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"기폐업 혹은 *폐업이 예정된 개인사업자 중 보유 중인 소호 담보대출을 가계대출로 대환을 희망하는 자\n*단, 폐업 예정자의 경우 여신실행 시 폐업사실이 확인된 경우에만 취급가능","대출한도":"상환대상 계좌의 대출 잔액 범위 내","대출기간":"최장 30년 이내 ※ 잔액별 금리 및 만기 상이\n구분 유형 대출기간\n담보 주택 10년~30년\n오피스텔,기타부동산 10년","상환방식":"원(리)금균등분할상환","이자계산방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수 계산은 여신당일로부터 기일 또는 상환일 (일부상환 또는 분할상환 포함)전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금\n2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금\n4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등)단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등)단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로,\n대출 취급시 작성하시는 대출거래약정서의 상환 방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 기한연장 불가","중도상환\n해약금":"없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"소득 및 재직 증명서류 ※ 필요시 추가 서류제출을 요청드릴 수 있습니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n* 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지\n(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하 요구권":"금리인하요구권 적용 비대상 상품입니다.\n본인의 신용상태가 개선되었다고 판단되는 경우(예: 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내 (고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리인하요구 수용 여부를 통지해드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명을 받을 권리가 있습니다.\n이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n손님의 신용도에 따라 대출한도가 차등 적용되며, 대출 취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/ 신용 상의 불이익(압류,경매 등) 이 발생할 수 있으니 주의하시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지 (www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"변경사항 없음","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (231, 4, '폐업지원대환대출(보증서)', '{"상품별, 일자별 금리 조회":"신규금액 1억원 이하 : 금융채 5년물 + 0.1%\n신규금액 1억원 초과 : CD 3개월 + 1.6%\n※ 대출금리는 기준금리 변동 시 금리가 변경됩니다.\n적용금리 산정기준 : 기준금리 + 가산금리 (부수거래 감면금리 조건 없음)\n기준금리 : 대출 개시일에 결정되며, 개시일 기준 기준금리를 적용합니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.\n연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.\n이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수합니다.","대출대상":"기폐업 혹은 *폐업이 예정된 개인사업자 중 보유 중인 사업자 보증서대출(지역보증재단 보증서에 한함)을 가계대출로 대환을 희망하는 자\n*단, 폐업 예정자의 경우 여신실행 시 폐업사실이 확인된 경우에만 취급가능","대출한도":"상환대상 계좌의 대출 잔액 범위 내","대출기간":"최장7년 이내 ※ 잔액별 금리 및 만기 상이\n구분 잔액 금리 분할상환(만기)\n보증서 1억원 이하 금융채5년물+0.1%p 최대 7년\n1억원 초과 CD3개월+1.6% 최대 5년","상환방식":"원금균등분할상환","이자계산방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수 계산은 여신당일로부터 기일 또는 상환일 (일부상환 또는 분할상환 포함)전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금\n2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금\n4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등)단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등)단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로,\n대출 취급시 작성하시는 대출거래약정서의 상환 방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 기한연장 불가","중도상환\n해약금":"없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"소득 및 재직 증명서류 ※ 필요시 추가 서류제출을 요청드릴 수 있습니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n* 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지\n(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하 요구권":"금리인하요구권 적용 비대상 상품입니다.\n본인의 신용상태가 개선되었다고 판단되는 경우(예: 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내 (고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리인하요구 수용 여부를 통지해드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"보증료는 지역신용보재단 등 운영 규정에 따라 손님이 부담합니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명을 받을 권리가 있습니다.\n이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n손님의 신용도에 따라 대출한도가 차등 적용되며, 대출 취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/ 신용 상의 불이익(압류,경매 등) 이 발생할 수 있으니 주의하시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지 (www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"변경사항 없음","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (232, 4, '폐업지원대환대출(신용)', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"기폐업 혹은 *폐업이 예정된 개인사업자 중 보유 중인 사업자 신용대출(소호 신용대출)을 가계대출로 대환을 희망하는 자\n*단, 폐업 예정자의 경우 여신실행 시 폐업사실이 확인된 경우에만 취급가능","대출한도":"상환대상 계좌의 대출 잔액 범위 내","대출기간":"최장 30년 이내 ※ 잔액별 금리 및 만기 상이\n구분 잔액 금리 분할상환(만기)\n보증서 1억원 이하 금융채5년물+0.1%p 최대 30년\n1억원 초과* ※ 금리안내 참고 최대 10년","상환방식":"원금균등분할상환","이자계산방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수 계산은 여신당일로부터 기일 또는 상환일 (일부상환 또는 분할상환 포함)전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금\n2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금\n4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등)단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등)단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로,\n대출 취급시 작성하시는 대출거래약정서의 상환 방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 기한연장 불가","중도상환\n해약금":"없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"소득 및 재직 증명서류 ※ 필요시 추가 서류제출을 요청드릴 수 있습니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n* 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지\n(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하 요구권":"금리인하요구권 적용 비대상 상품입니다.\n본인의 신용상태가 개선되었다고 판단되는 경우(예: 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내 (고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리인하요구 수용 여부를 통지해드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명을 받을 권리가 있습니다.\n이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n손님의 신용도에 따라 대출한도가 차등 적용되며, 대출 취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/ 신용 상의 불이익(압류,경매 등) 이 발생할 수 있으니 주의하시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지 (www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"변경사항 없음","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (233, 4, '프랜차이즈 가맹점 대출', '{"상품특징":"은행이 선정한 우수 프랜차이즈 브랜드 가맹점을 운영하는 개인사업자에 대한 신용대출 상품입니다.","대출대상":"은행이 선정한 프랜차이즈 브랜드와 가맹계약을 맺고, 1개 이상 신용카드사의 가맹점 매출대금 수령계좌를 당행으로 지정한\n개인사업자\n※ 2024.06.21 현재 선정브랜드 : 파리바게트, 롯데리아, 뚜레쥬르, 배스킨라빈스, 던킨도너츠, 블루핸즈, 오토큐 등\n(상기 선정 브랜드는 은행 자체 기준에 따라 수시 변동될 수 있습니다.)","대출금리":"변동금리 최저 연 4.887% ~ 최고 연 11.437%\n[2025.07.07 기준금리(91일물 CD유통수익률) 2.510% + 가산금리 (최저)3.637%, (최고)8.927% – 부수거래감면 등 우대금리 감면 1.26% 적용 기준]\n* 이자 산정방법 : 최저이율[기준금리+가산금리(ASS 1등급 기준)-우대금리] / 최고이율[기준금리+가산금리(ASS 10등급 기준)]\n* 상기 산정 금리는 대출금액 5천만원, 대출기간 1년, 당행 내부 신용(ASS) 최저 1등급/최고 10등급, 일시상환, 운전자금대출 기준입니다.\n* 가산금리는 신용등급, 대출금액, 담보종류, 시설자금대출 취급여부 등에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경될 수 있습니다.\n* 부수거래감면 등 우대금리: 주거래손님 0.1%, 사업자주거래우대통장 월평잔 조건 0.1%, 금융연수원 금융교육이수 0.1%, 소상공인 경영컨설팅 이수 0.2%, 본부 감면 0.76% 등 거래실적종류에 따라 우대\n* 연체대출금리는 적용금리+3.0%과 연 15.0%중 낮은 금리를 적용합니다.\n* 이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n* 대부업등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고금리는 연 20%입니다.\n* 기준금리 안내( 바로가기) : 상품공시실 → 대출상품 → 금리안내 → 대출(가계대출기준금리) → 91일물 CD유통수익률","대출한도":"창업자금 : 최대 200백만원 (사업기간 6개월 미만)\n운영자금 : 최대 200백만원 (사업기간 6개월 이상)\n(브랜드별로 한도 차등)","상환방식 및 대출기간":"만기일시상환(개별대출, 통장대출) : 1년 이내\n원(리)금균등분할상환 : 3년 이내","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"하나원큐앱 (App) 또는 하나원큐기업 앱(App)을 통한\n신청가능시간: 오전 9시 ~ 오후 10시 (공휴일 포함)\n실행가능시간: 오전 9시 ~ 오후 6시 (공휴일 제외 은행 영업일만 가능)","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율 : 고정금리 0.04%, 변동금리 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","한도약정\n수수료":"산식 : 한도약정액 X 한도약정수수료율(0.3%) X 약정기간 ÷ 365일(윤년 366일)\n※ 통장대출 취급시 부과됩니다.(단, 약정한도미사용수수료 납부 손님은 면제)","약정한도미사용\n수수료":"산식 : (약정한도평잔 – 운용기간 중 여신평잔) x 약정한도미사용수수료율(0.5%) x 운용기간 ÷ 365일(윤년은 366일)\n※ 통장대출 취급시 부과됩니다. (단, 한도약정수수료 납부 손님은 면제)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원","필요서류":"사업자등록증 사본, 신분증, 부가세과세표준증명원, 소득금액증명원, 신용카드매출자료 등\n(※ 신용조사 및 대출심사 시 필요한 서류가 추가될 수 있습니다.)","금리인하요구권":"기업신용평가 등에 따라 손님의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n회사채 등급상승, 재무상태 개선(감사보고서 등), 특허 취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약 해지권 행사가능 상품입니다.\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날‘부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"은행의 정책 및 시장금리 상황에 따라 금리는 변동될 수 있습니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류,경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용 점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래관련 불이익을 받을 수 있습니다.\n대출원리금 연체시 계약만료기한전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.07.15 변경)\n■ 기존 가입고객에 대한 적용 여부 : 적용\n<부수거래 감면금리 변경>\n변경전 : 부수거래감면 등 우대금리 감면 1.27% 적용 기준\n변경후 : 부수거래감면 등 우대금리 감면 1.26% 적용 기준","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (234, 4, '프리미엄 직장인론', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"연소득 28백만원 이상의 지정업체 임직원 및 공무원(교사 및 연구기관 포함)\n* 대출대상은 영업점 및 콜센터에서 확인이 가능합니다.","대출한도":"최대 3억5천만원","대출기간":"만기일시상환(일시대출, 통장대출) : 1년 이내\n(부분)통장대출분할감액 : 5년 이내\n부분원(리)금균등분할상환 : 5년 이내\n전액원(리)금균등분할상환 : 15년 이내","상환방식":"만기일시상환(일시대출, 통장대출), (부분)통장대출분할감액\n\n부분원(리)금균등분할상환 ,전액원(리)금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","우대혜택":"은행거래 수수료 면제 (2017년 12월 31일 이전 취급계좌에만 적용됩니다.)\n급여이체를 하는 손님께 아래의 5가지의 은행거래수수료를 무제한으로 면제해 드립니다.\n면제대상 수수료 : 인터넷뱅킹, 폰뱅킹, 스마트폰뱅킹 등의 즉시 이체수수료, 마감 후 자동화기기의 현금인출 및 당행 이체수수료, 납부자 자동이체수수료\n면제기간 : 최초 급여 이체일 ~ 대출 만기일","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"소득 및 재직 증명서류\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.07.01 변경)\n<대출기간>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n전액원(리)금균등분할상환: 10년 이내\n변경 후\n전액원(리)금균등분할상환: 15년 이내","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (235, 4, '핀크생활비대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"만 19세 이상 내국인\nCB사 소득에 의해 한도가 산정되는 손님\n하나은행 대출심사 통과 손님(자동심사)","대출한도":"최소 50만원 ~ 최대 5백만원","대출기간":"만기일시상환(일시상환) : 1년 (최대 10년까지 연장가능)\n분할상환(원금균등분할상환) : 1~5년 중 선택","상환방식":"만기일시상환, 분할상환(원금균등분할상환)","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 × 대출이자율 × 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환):원금은 만기에 일시상환, 이자는 일정주기(매 1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매 1개월 등) 단위로 납부\n대출실행응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","중도상환\n해약금":"없음","대출관련비용":"없음","필요서류":"없음","금리인하요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약 해지권":"은행이 「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"해당상품은 한도조회 이후 대출신청이 가능하며, 한도조회 당일 신청기간까지 핀크앱을 통해 대출 신청을 완료해야 합니다.\n해당상품은 승인 당일 포함 6일안에 약정을 실행하지 않으면 신청내역은 자동취소되며, 취소된 이후에는 재신청해야 합니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"2.819","우대금리":"0.000","최저~최고금리":"5.335 ~ 5.335"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (236, 4, '하나-공사 주택연금 역모기지론', '{"상품특징":"주택연금이란?\n만 55세 이상(주택소유자 또는 배우자)의 대한민국 국민(주택소유자 또는 배우자)이 소유주택을 담보로 맡기고 평생 혹은 일정한 기간 동안 매월 연금방식으로 노후생활 자금을 지급받는 국가 보증의 금융상품(역모기지론)입니다. 이를 위하여 공사는 연금\n가입자를 위해 은행에 보증서를 발급하고 은행은 공사의 보증서에 의해 가입자에게 주택연금을 지급합니다.","대출대상":"주택소유자 또는 배우자가 만 55세 이상(근저당권 설정일 기준)\n확정기간 방식은 연소자가 만 55세 ~ 만 74세\n우대방식은 주택소유자 또는 배우자가 만 65세 이상(기초연금 수급자)\n주택소유자 또는 배우자가 대한민국 국민(외국인 단독 및 부부 모두 외국인인 경우에는 가입 불가)\n부부기준 12억원 이하 주택소유자\n다주택자라도 합산 가격이 12억원 이하면 가능\n12억원 초과 2주택자는 3년 이내 1주택 처분시 가입 가능\n우대방식의 경우 부부기준 2억원 미만의 1주택 소유이면서, 1인 이상이 기초연금 수급권자일 경우 가입가능","대상주택":"주택법상의 주택인 단독주택, 아파트, 연립, 다세대, 단독, 복합, 노인복지주택, 주거목적 오피스텔* 등 가능\n- 일반 주택연금 : 공시가격 등이 12억원 이하 주택\n- 확정기간 주택연금 : 공시가격 등이 12억원 이하 주택\n*주거목적 오피스텔 : 등기사항증명서 상 용도가 업무시설 또는 오피스텔이면서 주거목적으로 사용되는 경우","대출금리":"연 3.55% ~ 연 3.80%\n(기준 : 2025.05.21 현재)\n(단위 : 연 %)\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최종금리\nCD 수익률 3개월 2.7 1.10 0.00 3.80\nCOFIX 신규취급액 6개월 2.7 0.85 0.00 3.55","대출한도":"최대 6억원 (한국주택금융공사에서 산정)","대출기간":"종신원칙: 부부중 적은 나이 기준 100세 말까지(부부 모두 사망시까지 보장) 단, 확정기간 방식은 10~30년, 5년단위 (확정기간 종료후 종신거주는 보장)","담보":"대상주택\n주택법상의 주택인 단독주택, 아파트, 연립, 다세대, 다가구, 주거목적 오피스텔 등 (실버주택, 상가, 주거외 목적 오피스텔 등은 제외)\n주택소재지가 투기(과열)지구 여부와 관계없이 취급 가능\n복합용도주택은 전체건물 면적에서 주택면적이 차지하는 비중이 1/2이상인경우 가능\n복합용도주택이란 등기증명서상 \"근린생활시설 및 주택\", \"상가 및 주택\"등으로 표시되고 통상 주택부분만 별도로 구분 등기가 되지않은 근린주택, 상가주택, 점포주택 등\n연금지급방식 : 종신방식, 확정기간혼합방식, 대출상환방식, 우대방식\n종신방식\n종신지급방식 : 인출한도 설정 없이 평생동안 매월 연금형태로 지급\n종신혼합방식 : 인출한도(대출한도의 50% 이내) 범위 안에서 수시로 찾아 쓰고 나머지 부분을 평생 동안 매월 연금형태로 지급받는 방식\n확정기간방식\n인출한도 범위(대출한도의 50%, 재건축등 분담금 납부자금의 경우 70%) 안에서 수시로 찾아 쓰고 나머지 부분을 일정한 기간 동안만 매월 연금형태로 지급받는 방식\n다만, 인출한도 중 대출한도의 5%에 해당하는 금액(이하 ‘의무설정인출한도’라 함)은 매월 연금형태로 지급받는 기간이 종료된 이후 담보주택관리비, 의료비의 용도로만 사용\n지급기간 10년형 15년형 20년형 25년형 30년형\n대상연령* 65세~74세 60세~74세 55세~68세 55세~63세 55세~57세\n* 부부 중 연령이 낮은 자 기준\n대출상환방식\n담보주택을 담보로 대출받은 금액 중 잔액을 상환하는 용도로 인출한도 범위(대출한도의 50%~90%) 안에서 일시에 지급받고, 나머지 부분을 평생동안 매월 연금형태로 지급받는 방식\n(소상공인대출 상환용도) 부부중 한분이 ｢소상공인기본법｣ 제2조에 따른 소상공인으로서 대출 상환 후 폐업하려는 경우,\n최초 보증부대출 실행일*로부터 6개월 내 폐업조건으로 개별인출 허용\n우대방식\n우대지급방식 : 부부 중 한분이 기초연금 수급권자(65세 이상)이며 부부기준 2억5천만원 미만의 1주택만 소유하신 경우 인출한도 설정없이 평생동안 매월 연금형태로 지급받되 종신지급방식보다 더 많은 월지급금을 지급받는 방식\n우대혼합방식 : 부부 중 한분이 기초연금 수급자이고, 2억5천만원 미만의 1주택만 소유하신 경우 인출한도(우대지급방식 대출한도의 50%,재건축등 분담금 납부자금의 경우 70%) 범위 안에서 수시로 찾아 쓰고 나머지 부분을 평생동안 매월 연금형태로 지급받는 방식 (우대지급방식보다 월지급금이 적어짐)","상환방식":"주택연금 이용자 사망후 주택처분 가격으로 일시 상환","이자계산 방법":"금융기관과 약정한 금리수준에 따라 매월 대출원금에 가산되어 복리로 늘어납니다.","원금 또는\n이자의\n상환시기 및\n방법":"주택연금 이용기간 동안에는 상환할 필요가 없으며 변제시기가 도래한 경우 상환하시면 됩니다.\n※ 고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서 및 주택담보노후연금보증약정서의 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지":"손님이 대출금 전액을 상환한 때","우대혜택":"세제혜택\n근저당권 설정 시 교육세, 농어촌특별세, 국민주택채권 매입의무 면제 및 등록면허세 감면\n대출이자비용 소득공제, 재산세 감면","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)\n보증료(손님부담) : 한국주택금융공사 운용규정에 따름","대출이자율의\n구성요소":"대출이자율의 구성요소\n「CD수익률」 : 「CD수익률」이란 신용평가회사로부터 AAA이상의 신용등급을 받은 시중은행이 발행한 만기가 91일(만기가 80일 이상 100일 이내면 만기 91일로 본다)인 CD의 발행수익률로서 기초자료제출기관이 금융투자협회(이하 ''협회'')에 제출한 기초수익률을 기반으로 협회가 산출한 수익률을 말합니다.\n「신규취급액기준COFIX」 : 「신규취급액기준COFIX」의 금리재산정주기는 6개월이며 대출실행일과 그날로부터 매6개월되는 날 각각에 대해 직전일 전국은행연합회에 최종 고시되어 있는 금리를 말합니다.\n시장기준금리의 적용기준은 금융 환경의 변화(예 : 91일물CD 거래없음) 등 불가피한 사정이 발생할 경우 변경될 수 있습니다. 이 경우 은행은 영업점 게시판, 인터넷 매체 등을 통해 고시하기로 합니다.\n대출이자율의 변동\n대출이자율의 구성요소 1 「CD수익률」,2 「신규취급액기준COFIX」 에 따라 금리재산정주기(매3개월 또는 매6개월)에 변동되는 경우 대출이자율도 이에 연동되어 매 3개월 또는 매 6개월마다 변동됩니다.\nCD수익률 산출업무 중단 시의 비상계획\n비상사태 발생으로 인하여 CD수익률의 산출업무가 중단된 경우, CD수익률 설명서 절차에 따른다.","기타":"주택연금보증 관계의 성립\n하나-공사 주택연금 역모기지론 업무처리 절차\n주택연금 종료사유\n주택연금 이용자와 배우자가 모두 사망한 경우\n주택연금 이용자가 사망한 후 배우자가 6개월 이내 담보주택 소유권이전 등기 및 채무인수를 하지 않은 경우\n주택연금 이용자와 배우자가 모두 주민등록을 이전한 경우. 다만, 한국주택금융공사에서 정하는 실거주 예외 인정사유로\n공사의 승인을 받은 경우는 제외\n주택연금 이용자와 배우자가 1년 이상 계속하여 담보주택에서 거주하지 않는 경우. 다만 한국주택금융공사 인터넷 홈페이지에 공고하는 사유로 거주하지 않는 경우는 제외\n주택 연금 이용자가 담보주택의 소유권을 상실한 경우\n주택연금 대출의 원리금이 저당권의 채권최고액을 초과할 것으로 예상되는 경우로서 금융기관이나 공사의 채권 최고액\n변경요구에 응하지 않는 경우\n주택연금 지급정지사유\n주택연금 이용자가 사망한 경우\n주택연금 이용자와 배우자가 모두 담보주택에서 다른 장소로 주민등록을 이전한 경우\n주택연금 이용자와 배우자가 1년 이상 계속하여 담보주택에서 거주하지 않는 경우\n주택연금 이용자 또는 연대보증인인 배우자가 담보주택의 소유권을 상실한 경우\n보증부대출 잔액이 근저당권의 채권최고액을 초과할 것으로 예상되어 공사나 채권자가 채권최고액 변경을 요구하였으나 응하지 않는 경우\n주택연금 이용자 또는 배우자가 선순위채권 등의 상환, 신용관리정보 해제 등 보증약정 또는 별도의 서약서를 통해 이행하기로 정한 사항을 기한 내 이행하지 않는 경우\n공사나 채권자가 보증기한 연장, 보증금액 증액, 연대보증인 입보 등 조건변경 신청을 요청하였으나 응하지 않는 경우\n상속, 이사 등으로 보유하게 된 2주택 중 담보주택 이외의 주택을 일정기간 이내에 처분하는 것을 조건으로 하는 보증으로서 보증약정 또는 별도의 서약서에서 정한 사항을 이행하지 않는 경우\n담보주택을 담보주택조사에서 확인한 사항과 다른 용도로 사용하는 경우\n피보증인이 재건축등에 참여한 결과 청산금을 수령하는 경우\n근저당권 설정계약의 무효·취소 청구가 법원에 제기된 경우\n피보증인이 지급정지를 요청하여 공사가 승낙한 경우\n피보증인이 「채무자 회생 및 파산에 관한 법률」에 따라 회생절차를 진행하는 경우\n보증료 / 초기보증료 및 연보증료를 대출실행시 납부\n초기보증료 : 주택가격의 1.5%를 최초 대출실행 시 1회 납부\n연보증료 : 보증잔액의 연 0.75%를 매월 납부\n담보의 제공 /공사 에서 1순위 근저당권 설정 혹은 신탁등기\n제3자(자녀, 형제 등) 소유 주택을 담보로 하는 주택연금보증은 이용불가\n근저당방식 주택연금 이용시 1순위로 근저당권 설정\n신탁방식 주택연금 이용시 ''주택담보노후연금 부동산 신탁계약서''를 통해 담보주택에 신탁 등기\n주택연금전용계좌(하나 주택연금 지킴이 통장) 도입\n제3자(자녀, 형제 등) 소유 주택을 담보로 하는 주택연금보증은 이용불가\n주택연금전용계좌 : 공사법 제43조의13제1항에 따라 「민사집행법」 제195조제3호에서 정하는\n금액 이하의 연금대출이 입금되는 채무자 명의의 계좌로 압류방지의 기능을 갖는다.\n대출금 상환 / 이용자 사망 후 주택처분 가격으로 일시상환\n대출금 상환은 연금보증 계약종료 시 담보주택 범위 내에서 대출금을 회수함이 원칙\n공사는 이용자 사망 등 계약해지 사유 발생 시 주택처분(경매)으로 대출금 회수 후 부족 분이 발생하여도 다른 재산 및 상속인 등에게 청구권을 행사하지 않음\n주택처분(경매)금액으로 대출금을 회수하고도 남은 잉여금은 법정상속인 등에게 배당 교부","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"3년이내 처분조건부로 보증받은 경우 최초 보증부대출 실행일부터 3년이 되는 날까지 처분하지 않은 경우 연금대출 지급이 정지됩니다.\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 대출만기 도래시 대출금액을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지( www.kebhana.com), 한국주택금융공사 홈페이지( www.hf.go.kr)를 참조하시거나 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용\n변경에 관한 사항\n(2025.05.23 변경)\n<대출금리>\n변경 전 :\n인지세\n구분항목 : 대출금액, 인지세\n변경 후 :\n인지세\n구분항목 : 대출금액, 인지세, 인지세 부담(손님 부담, 은행 부담)\n표 항목 상세구분","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (237, 4, '하나 개인택시대출', '{"대출대상":"개인택시면허 및 자기차량을 소지한 개인택시사업 영위자","대출담보":"㈜서울보증보험의 보증보험증권","가계대출금리":"변동금리 연 4.191%\n[2020.04.22.현재 기준금리 1.001%(금융채 6개월 변동)+가산금리* 3.19%\n* 가산금리 : 당행 내부신용등급 ASS4등급, 대출신청금액 40백만원, 대출기간 5년 기준","대출한도":"최대 5천만원(서울보증보험증권 발급금액 범위 내)","상환방식 및 대출기간":"만기일시상환은 판매중단, 기존 대출 연장만 가능\n전액원리금균등분할상환 : 12개월 이상 72개월 이내(거치기간 없음)","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 또는 갱신대환 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.\n※ 갱신대환 : 기존 만기일시상환방식 하나개인택시대출 만기 도래 시 동일 금액 내에서 신규 절차에 따라 당일 중에 상환과\n동시에 분할상환 대출로 재취급(전액원리금 균등분할상환방식 12~72개월 이내 취급 가능)","중도상환해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 소호대출: 대출잔여기간 1개월 이내 상환 시 면제\n*중도상환해약금율: 고정금리0.04%, 변동금리0.03%\n※ 가계대출: 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.61%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","수수료":"한도약정수수료/약정한도미사용수수료\n해당사항없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결 시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"본인확인서류, 개인택시운송사업면허증 또는 택시운전자격증, 개인택시 사업자등록증, 차량등록원부(보험청약일 기준 3영업일 이내 발급 분) 등\n(신용조사 및 대출심사 시 필요한 서류가 추가될 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율\n소호대출: 고정금리 1.1%, 변동금리1.0%\n가계대출: 0.7%\n변경 후\n중도상환해약금율\n소호대출: 고정금리 0.04%, 변동금리0.03%\n가계대출: 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (238, 4, '하나 내집연금 연계 대출', '{"대출대상":"인출한도 90%를 전액 상환재원으로 사용시에도 주택담보대출이 남아 있는 주택담보대출 상환용 주택연금 신청 손님\n서울보증보험의 보험가입 요건을 충족하는 손님","대출금리":"연 3.873% ~ 연 4.773%\n(기준 : 2021.03.12 현재, 당행 내부신용(ASS) 4등급, 대출기간 5년, 대출금액 1천만원 기준)\n기준금리 0.793% 부수거래 항목별\n감면금리*** 0.30% 급여이체\n(금융채 6개월물) 0.20% 주택청약종합저축납입\n**가산금리(+) 3.980% 각 0.1% 제휴카드결제, 기타자동이체, 적금상품납입, 하나원큐이체\n부수거래 감면금리(-) 0.90% 최대감면 조건 부수거래 항목 최대 0.9%\n(단위 : 연 %)\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최종금리\n91일 CD유통수익률 3개월 0.750 3.980 0.900 3.873 ~ 4.730\n6개월물 금융채 유통수익률 6개월 0.793 3.980 0.900 3.873 ~ 4.773\n1년물 금융채 유통수익률 1년 0.883 4.030 0.900 4.013 ~ 4.913\nCOFIX 신규취급액 6개월 0.860 3.913 0.900 3.873 ~ 4.773\nCOFIX 신잔액 6개월 0.900 3.643 0.900 3.643 ~ 4.543\n* 이자 산정방법: 최저이율(기준금리+가산금리-부수거래감면금리) / 최고이율(기준금리+가산금리)\n** 가산금리는 신용등급, 대출기간, 대출금액 등에 따라 차등 적용됩니다.\n*** 부수거래 항목별 감면금리 : 대출 실행시 선택한 부수거래 항목의 감면금리가 적용되며, 이후에는 선택하지 않은 항목을 포함하여 모든 항목에 대해서 매월 감면조건을 충족하는 경우 1개월 단위로 금리가 감면되고, 매월 조건을 충족하지 않을 경우 감면 받은 금리가 자동으로 인상됩니다.\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 24%입니다.\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.(상품에 따라 연체이자율이 일부 달라지는 경우가 있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","대출한도":"최대 1천만원 [(주택가격 – 한국주택금융공사 주택연금 인출한도*) × 5% 이내]\n※ 한국주택금융공사 주택연금 인출한도 = (인지세법상기재금액 – 초기보증료) × 90%","대출기간":"85세에서 채무자 가입연령을 차감하여 나온 기간 이내로 하나 월 원리금상환금액이 채무자가 수령하는 주택연금 월지급금의 80%를 초과하지 않는 범위내","상환방식":"전액원리금균등분할상환\n한국주택금융공사에서 매월 채무자에게 지급하는 주택연금 월지급금으로 자동상환\n한국주택금융공사에서 정한 주택연금 월지급금 지급정지 사유가 발생하는 경우에는 채무자가 대출원리금을 직접 납부","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 대출만기 도래 시 대출금액을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익(압류, 경매 등)이 발행할수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 KEB하나은행 홈페이지(www.kebhana.com), 한국주택금융공사 홈페이지(www.hf.go.kr)를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2024.06.05 변경)\n<대출기간 변경>\n기존 가입고객에 대한 적용여부 : 미적용\n변경 전 : 인출한도 70%를 전액 상환재원으로 사용시에도 주택담보대출이 남아 있는 주택담보대출 상환용 주택연금 신청 손님\n변경 후 : 인출한도 90%를 전액 상환재원으로 사용시에도 주택담보대출이 남아 있는 주택담보대출 상환용 주택연금 신청 손님","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (239, 4, '하나 변동금리 모기지론', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"아파트 및 주택을 담보로 제공하는 손님","대출한도":"담보 가능액 범위내 (단, 통장대출은 최대 3억원)","대출기간":"만기일시상환 : 5년 이내 (단, 구입자금대출은 10년 이내)\n통장대출 : 5년이내\n분할상환 : 1년 ~ 40년","담보":"아파트 및 주택","상환방식":"만기일시상환(일시대출, 통장대출), (부분)원(리)금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.66%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)\n모기지신용보증료 : (한국주택금융공사 보증서(MCG) 이용시) 보증료는 고객이 부담(보증금액의 최저 연 0.05% ~ 최고 연 0.30%), (서울보증보험 보험(MCI) 이용시) 보험료는 은행이 부담","필요서류":"등기권리증, 인감증명서, 주민등록등(초)본, 소득증명서류(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n투기지역여부, DTI 충족 여부, 주택관련대출보유여부, 주택보유수 등에 따라 대출한도 및 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com), 한국주택금융공사 홈페이지(www.hf.go.kr)를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율\n고정금리 1.4%(비거치식 주탬담보대출 1.3%)\n변동금리 1.2%(비거치식 주택담보대출 1.1%)\n변경 후\n중도상환해약금율 0.66%","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"91물 CD유통수익률","금리변동주기":"3개월","기준금리":"2.500","가산금리":"2.990","우대금리":"1.300","최저~최고금리":"4.190 ~ 5.490"},{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"2.981","우대금리":"1.300","최저~최고금리":"4.197 ~ 5.497"},{"금리구분":"1년물 금융채 유통수익률","금리변동주기":"1년","기준금리":"2.498","가산금리":"3.013","우대금리":"1.300","최저~최고금리":"4.211 ~ 5.511"},{"금리구분":"COFIX 신규취급액","금리변동주기":"6개월","기준금리":"2.540","가산금리":"2.957","우대금리":"1.300","최저~최고금리":"4.197 ~ 5.497"},{"금리구분":"COFIX 신잔액","금리변동주기":"6개월","기준금리":"2.630","가산금리":"2.427","우대금리":"1.300","최저~최고금리":"3.757 ~ 5.057"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (240, 4, '하나 사잇돌 중금리대출', '{"대출대상":"아래 조건중 하나에 해당하고 서울보증보험 보험증권 발급이 가능한 손님으로 하나은행 심사기준을 충족한 손님\n1. 급여소득자 : 재직기간 3개월 이상으로 연소득 15백만원 이상인 손님\n2. 개인사업자 : 사업영위기간 6개월 이상으로 연소득 10백만원 이상인 손님\n3. 연금소득자 : 4대 공적연금 수령기간 1회 이상으로 연소득 10백만원 이상인 손님","대출금리":"연 8.494% ~ 연 13%\n(기준 : 2024.03.22 현재, 당행 내부신용(ASS) 및 서울보증보험 내부신용 1등급, 대출기간 5년 기준)\n기준금리 3.642% 부수거래 항목별\n감면금리*** 0.4% 급여이체\n(금융채 6개월물) 0.20% 주택청약종합저축납입\n**가산금리(+) 5.752% 0.2% 제휴카드결제\n[기본] 월 30만원 이상 결제\n0.1% [추가] 월 70만원 이상 결제\n부수거래 감면금리(-) 0.90% 최대감면 조건 부수거래 항목 최대 0.9%\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최종금리\n91일 CD유통수익률 3개월 3.650 5.766 0.9 8.516 ~ 13\n6개월물 금융채 유통수익률 6개월 3.642 5.752 0.9 8.494 ~ 13\n1년물 금융채 유통수익률 1년 3.612 6.105 0.9 8.817 ~ 13\nCOFIX 신규취급액 6개월 3.620 5.774 0.9 8.494 ~ 13\nCOFIX 신잔액 6개월 3.240 5.574 0.9 7.914 ~ 13\n* 이자 산정방법: 최저이율(기준금리+가산금리-부수거래감면금리) / 최고이율(기준금리+가산금리)\n** 가산금리는 신용등급, 대출기간, 대출금액 등에 따라 차등 적용됩니다.\n*** 부수거래 항목별 감면금리 : 대출 실행시 선택한 부수거래 항목의 감면금리가 적용되며, 이후에는 선택하지 않은 항목을 포함하여 모든 항목에 대해서 매월 감면조건을 충족하는 경우 1개월 단위로 금리가 감면되고, 매월 조건을 충족하지 않을 경우 감면 받은 금리가 자동으로 인상됩니다\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 24%입니다.\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.(상품에 따라 연체이자율이 일부 달라지는 경우가 있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","대출한도":"최대 2천만원 이내","대출기간":"60개월이내(거치기간 없음)","상환방식":"전액 원(리)금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n※ 보증료(율) : 보증보험의 최저 연 2.26 % ~ 최대 연 6.65% (차주별 상이 자세한 사항은 보증기관 문의)","필요서류":"(대출실행일 기준 1개월이내 발급분)\n재직확인서류\n1) 급여소득자 (다음중 하나) : 재직증명서, 공무원증 등 재직확인서류, 국민연금 가입자 가입증명, 건강보험 자격득실 확인서\n2) 사업소득자 (다음중 하나) : 사업자등록증, 사업자등록증명원등\n3) 연금소득자 : 연금수급증명서 또는 연금수령을 확인할 수 있는 지급기관의 증명서\n소득확인서류\n1) 급여소득자 (다음중 하나) :\n연말정산용 근로소득원천징수영수증 , 세무서 발급 소득금액 증명원, 국민연금 ''연금정산용 가입내역 확인서, 국민건강보험 ''건강 · 장기요양보험 보험료 납부 확인서, 급여내역이 포함된 확인서 또는 급여통장 사본, 재직회사가 날인한 급여명세표, 임금대장, 갑근세 원천징수 확인서\n2) 사업소득자 (다음중 하나) :\n세무서 발급 소득금액 증명원, 국민연금 ''연금정산용 가입내역 확인서, 국민건강보험 ''건강·장기요양보험 보험료 납부 확인서, 종합소득세 과세표준 확정신고 및 납부 계산서(세무사 확인분), 사업소득원천징수 영수증(연말정산용), 거주자의 사업소득\n원천징수 영수증\n3) 연금소득자\n연금수급권자 확인서 또는 연금수령을 확인할 수 있는 지급기관의 증명서(연금수령액이 표기되지 않은 경우 연금수령 통장\n사본)\n신분증\n※ 상담시 추가 제출서류가 있을 수 있습니다.","자료열람요구권":"금융소비자는 분쟁조정 또는 소송의 수행 등 권리구제를 위한 목적으로 은행이 기록 및 유지· 관리하는 다음의 자료에 대한 열람(사본 및 청취 포함)을 요구할 수 있습니다.\n* 계약체결에 관한 자료, 계약의 이행에 관한 자료, 금융상품 등에 관한 광고 자료, 금융소비자의 권리행사에 관한 자료, 내부통제기준의 제정 및 운영 등에 관한 자료, 업무 위탁에 관한 자료","민원처리 및 분쟁조정 절차":"본 상품에 대한 문의사항 또는 민원 상담이 필요하시면 하나은행 영업점, 고객센터 (1599-1111) 또는 인터넷 홈페이지(www.kebhana.com)를 통해 문의 주시기 바랍니다. 또한 분쟁이 발생한 경우에는 금융소비자보호법에 따라 금융감독원 민원상담 센터(국번없이 1332) 또는 e-금융민원센터 (http://www.fcsc.kr)에 분쟁조정을 신청할 수 있습니다.","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 「금융소비자보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용\n변경에 관한 사항\n(2020.04.28 변경)\n<상품명 변경>\n변경 전 : KEB하나 사잇돌 중금리대출\n변경 후 : 하나 사잇돌 중금리대출","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (241, 4, '하나 새희망홀씨ll - 건설근로자', '{"상품별, 일자별 금리 조회":"금리안내","이자산정방법":"최저이율(기준금리+가산금리-부수거래감면금리 / 최고이율(기준금리+가산금리)\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.","상품특징":"퇴직공제금을 적립 중인 건설근로자를 위한 무보증, 무담보, 신용대출로 최대 2천만원까지 지원하는 서민금융상품","대출대상":"대출신청일 기준 당행 여신심사에 의해 대출적격으로 판정된 퇴직공제금을 적립 중인(최근 6개월 90일 이상 또는 최근 12개월 180일 이상 적립) 손님으로서 전자카드 근무관리 시스템에서 적립내역 확인이 가능한 손님","감면금리":"<부수거래 항목별 감면금리>\n- 급여이체(0.4%), 제휴카드결제(최대 0.3%), 적금 또는 주택청약종합저축납입(0.2%)\n<기타감면 항목별 감면금리>\n① 각 0.1% : 청년층(만34세 이하), 고령자(만65세 이상), 장애인, 금융교육 이수자, 만19세 미만 2자녀 가정\n② 각 0.2% : 기초생활수급권자, 한부모가정, 다문화가정, 만60세 이상 부모부양자, 만19세 미만 3자녀 이상 가정\n※ 부수거래 항목별 감면금리는 대출 실행시 선택한 부수거래 항목의 감면금리가 적용되며, 이후에는 선택하지 않은 항목을 포함하여 모든 항목에 대해서 매월 감면조건을 충족하는 경우 1개월 단위로 금리가 감면되고, 매월 조건을 충족하지 않을 경우\n감면 받은 금리가 자동으로 인상됩니다.\n** 가산금리는 신용등급, 대출기간, 대출금액 등에 따라 차등 적용됩니다.","대출한도":"최대 2천만원. 단, 최저 취급금액은 1백만원","대출기간":"5년이내","상환방식":"(부분)원(리)금 균등분할상환. 단, 비대면 신청 건의 경우, 원(리)금 균등분할상환 방식만 가능\n부분 원(리)금 균등분할상환 방식의 경우, 만기상환금은 대출금의 30% 이내","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 × 대출이자율 × 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이자의 부과시기":"후취 방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수합니다.","연체이자율":"[대출이자율+연체가산이자율(연3%)] : 최고 연 15%입니다. (상품에 따라 연체이자율이 일부 달라지는 경우가 있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.)","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기한연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","우대혜택":"혜택 1. 중도상환해약금 없음\n대출취급수수료, 한도미사용수수료, 중도상환해약금 모두 면제\n혜택 2. 아래 은행거래수수료 면제\n폰뱅킹, 인터넷뱅킹, 스마트폰뱅킹, ATM 사용(당행 카드 출금 및 당행 내 이체)시 수수료 무제한 면제\n(인터넷 및 모바일 대출 신규자에 한하여 대출신규일~대출상환 전 까지)\n혜택 3. 대출받은 이후 연체없이 사용하시는 경우 금리 감면\n성실상환자(1년 단위로 연체일수가 10일 이내인 자)에 대하여 매년 0.3%씩 누적하여\n최대 1.2% 범위 내에서 대출금리 감면","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"신분증, 건설하나로 통장 또는 건설하나로 전자카드 중 1건 이상 발급 필수\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용시 불이익 : 당행에서 최근 1개월 내에 2회이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장여부가 결정되며, 만기연장 조치없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com), 한국주택금융공사 홈페이지(www.hf.go.kr)를 참조하시거나 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.04.10 변경)\n<기타감면 항목별 감면금리 변경>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전 :\n만19세 미만 2자녀 가정 (0.2%), 만19세 미만 3자녀 이상 가정 (0.4%)\n변경 후 :\n만19세 미만 2자녀 가정 (0.1%), 만19세 미만 3자녀 이상 가정 (0.2%)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (242, 4, '하나 새희망홀씨Ⅱ', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"근로소득자, 사업소득자, 연금소득자를 위한 무보증, 무담보, 신용대출로 최대 3천5백만원까지 지원하는 서민금융상품","대출대상":"대출신청일 기준 당행 여신심사에 의해 대출 적격으로 판정된 1개월 이상 근로소득, 사업소득, 연금소득이 있는 손님으로서 연소득 4천만원 이하 또는 CB신용평점 하위 20%의 경우 연소득 5천만원 이하인 손님을 대상으로 한다. 이 경우 CB신용평점은 KCB CB평점, NICE CB평점 중 낮은 점수를 적용한다.\n단, 하나원큐 새희망홀씨ll주1)는 신청일 기준 6개월 이상의 근로소득이 있는 자만 가능하다.\n주1) 하나원큐 새희망홀씨ll는 하나원큐신용대출을 통해서만 신청 가능","감면금리":"<부수거래 항목별 감면금리>\n- 급여이체(0.4%), 제휴카드결제(최대 0.3%), 적금 또는 주택청약종합저축납입(0.2%)\n<기타감면 항목별 감면금리>\n① 각 0.1% : 청년층(만34세 이하), 고령자(만65세 이상), 장애인, 금융교육 이수자, 만19세 미만 2자녀 가정\n② 각 0.2% : 기초생활수급권자, 한부모가정, 다문화가정, 만60세 이상 부모부양자, 만19세 미만 3자녀 이상 가정\n※ 부수거래 항목별 감면금리는 대출 실행시 선택한 부수거래 항목의 감면금리가 적용되며, 이후에는 선택하지 않은 항목을 포함하여 모든 항목에 대해서 매월 감면조건을 충족하는 경우 1개월 단위로 금리가 감면되고, 매월 조건을 충족하지 않을 경우\n감면 받은 금리가 자동으로 인상됩니다.\n** 가산금리는 신용등급, 대출기간, 대출금액 등에 따라 차등 적용됩니다.","대출한도":"최대 3천5백만원. 단, 최저 취급금액은 1백만원","대출기간":"5년이내","상환방식":"(부분)원(리)금 균등분할상환. 단, 비대면 신청 건의 경우, 원(리)금 균등분할상환 방식만 가능\n부분 원(리)금 균등분할상환 방식의 경우, 만기상환금은 대출금의 30% 이내","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","우대혜택":"혜택 1. 중도상환해약금 없음\n대출취급수수료, 한도미사용수수료, 중도상환해약금 모두 면제\n혜택 2. 아래 은행거래수수료 면제\n폰뱅킹, 인터넷뱅킹, 스마트폰뱅킹, ATM 사용(당행 카드출금 및 당행내 이체)시 수수료 무제한 면제(인터넷 및 모바일 대출 신규자에 한하여 대출신규일~대출상환전까지)\n혜택3. 대출받은 이후 연체없이 사용하시는 경우 금리 감면\n성실상환자(1년 단위로 연체일수가 10일 이내인자)에 대하여 매년 0.3%씩 누적하여 최대 1.2% 범위내에서 대출금리 감면","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"재직 및 소득서류\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com), 한국주택금융공사 홈페이지(www.hf.go.kr)를 참조하시거나 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.04.10 변경)\n<기타감면 항목별 감면금리 변경>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전 :\n만19세 미만 2자녀 가정 (0.2%), 만19세 미만 3자녀 이상 가정 (0.4%)\n변경 후 :\n만19세 미만 2자녀 가정 (0.1%), 만19세 미만 3자녀 이상 가정 (0.2%)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (243, 4, '하나 수의사클럽대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"개업(예정)수의사, 봉직수의사, 수의장교, 공중방역수의사, 수의학과대학생","대출한도":"최대 3억원","대출기간":"만기일시상환(일시대출,통장대출) : 1년 이내\n부분원(리)금균등분할상환 : 5년 이내\n(부분)통장대출분할감액 : 5년 이내\n전액원(리)금균등분할상환 : 10년이내","상환방식":"만기일시상환, 통장대출(분할감액 포함), 원(리)금분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"소득 및 매출 증명서류, 수의사면허증, 재직증명서(급여소득자), 사업자등록증(사업소득자)\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출기간,대출한도,대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터 (1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%\n변경 후\n중도상환해약금율 0.04%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (244, 4, '하나 아파트론', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"아파트를 담보로 제공하는 손님","대출한도":"담보대출 가능액 범위내 (단, 통장대출은 최대 3억원)","대출기간":"만기일시상환 : 5년 이내 (단, 구입자금대출은 10년 이내)\n통장대출 : 5년이내\n분할상환 : 1년 ~ 40년","담보":"아파트","상환방식":"만기일시상환(일시대출, 통장대출), (부분)원(리)금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.66%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)\n모기지신용보증료 : (한국주택금융공사 보증서(MCG) 이용시) 보증료는 고객이 부담(보증금액의 최저 연 0.05% ~ 최고 연 0.30%), (서울보증보험 보험(MCI) 이용시) 보험료는 은행이 부담","필요서류":"등기권리증, 인감증명서, 주민등록등(초)본, 소득증명서류\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n투기지역여부, DTI 충족 여부, 주택관련대출보유여부, 주택보유수 등에 따라 대출한도 및 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com), 한국주택금융공사 홈페이지(www.hf.go.kr)를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율\n고정금리 1.4%(비거치식 주탬담보대출 1.3%)\n변동금리 1.2%(비거치식 주택담보대출 1.1%)\n변경 후\n중도상환해약금율 0.66%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (245, 4, '하나 역모기지론', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"공시가격 12억원을 초과하는 아파트를 소유하고 있는 만 60세 이상의 개인자체 주택연금 상품","대출한도":"최대 2억원","대출기간":"10년~30년 (5년 단위로 산정)","담보":"아파트","상환방식":"만기일시상환(일시대출)","이자계산방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"고객이 대출금 전액을 상환한 때","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.66%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)","필요서류":"등기권리증, 인감증명서, 주민등록등(초)본, 소득증명서류\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n투기지역여부, DTI 충족 여부, 주택관련대출보유여부, 주택보유수 등에 따라 대출한도 및 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 변동금리 1.2%(비거치식 주택담보대출 1.1%)\n변경 후\n중도상환해약금율 0.66%","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"신 잔액기준 COFIX","금리변동주기":"6개월","기준금리":"2.630","가산금리":"0.847","우대금리":"0.000","최저~최고금리":"3.477 ~ 3.477"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (246, 4, '하나 인테리어대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"인테리어 회사와 인테리어 시공 계약을 체결하고, 아파트를 소유 혹은 소유(매매/분양 등) 예정인 2년 이상 재직 또는 2년 이상 사업영위 사실을 증빙 가능한 만 19세 이상 대한민국 국민\n현재 취급가능 인테리어 업체 : ㈜한샘 소속 리하우스/키친바흐 대리점","대상주택":"본인 소유(소유예정 및 공동소유 포함) 아파트","대출한도":"최저 3백만원 ~ 최대 3천만원\n단. 인테리어시공 체결 계약 금액 이내","대출기간":"12개월~60개월(1개월 단위, 거치기간 없음)","담보":"서울보증보험 담보(100%)","상환방식":"원리금균등분할상환","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.61%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","필요서류":"인테리어 시공 계약 관련 서류\n인테리어 시공 계약서\n재직 관련 서류 (택1)\n급여소득자 : 재직증명서 등\n사업소득자 : 사업자등록증 등","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"‘차주단위 DSR’ 적용에 따라 대출 가능 금액이 감소하거나 취급이 불가할 수 있습니다.\n손님의 신용평점으로 산출된 서울보증보험의 보증 한도 내에서 대출 가능합니다.\n아파트 소유 예정인 경우 대출 실행 전 매매잔금 완납이 확인되어야 대출 실행가능합니다.\n시공 하자 등으로 인한 대출계약 취소가 불가능한 상품으로 대출 신청시 유의하시기 바랍니다.\n인테리어 공사 현장확인 후 대출 실행가능합니다.\n대출금은 ‘대출금 지급 위임장’에 따라 기 지정된 인테리어 공사 업체의 계좌로 입금됩니다.\n은행의 정책 및 시장금리 상황에 따라 금리는 변동될 수 있습니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n상기 대출금리는 기준일 현재의 적용금리 이며 기준금리(금융채 6개월물) 변동시 금리가 변경될 수 있으며, 최저금리는 부수거래 약정을 통해 최대로 감면 받을 경우의 금리이므로 부수거래 미약정, 미충족시 개인별로 달라질 수 있습니다.\n대출원리금의 납부지연 또는 미납 발생 시 연체이자 부과, 신용등급 하락, 재산 압류·경매 등의 조치가 취해질 수 있으므로 유의바랍니다.\n\n※ 금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n※ 상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n※ 신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n※ 원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 대출철회권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나 하나은행 고객센터(1599-2222)또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%\n변경 후\n중도상환해약금율 0.61%","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"3.231","우대금리":"0.900","최저~최고금리":"4.847 ~ 5.747"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (247, 4, '하나 전세금안심대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"주택임대차계약을 1년 이상 체결하고 임차보증금의 5%이상을 기지급한 임차인으로 주택도시보증공사의 보증서 발급요건 충족하는 개인(미성년자, 외국인, 재외국민 제외)으로 주택임대차보호법상 대항력과 우선변제권을 확보한 임차인","대상주택":"아파트, 도시형생활주택, 주거용오피스텔, 연립주택, 다세대주택\n(임차보증금 수도권 7억 이하, 수도권외 5억 이하)\n*월세가 있는 경우 월세보증금 및 월세에 대해 전월세전환율을 적용하여 계산한 임차보증금\n(=월세보증금+(월세x12÷전월세전환율))","대출한도":"최대 4억원 (단, 부부합산 1주택자는 최대 2억원)\n임차보증금의 80%(신혼부부, 청년가구 및 배당이의 소송 당사자는 90%)이내 이면서, 연간 금융비용부담율 40%이내 해당 금액\n( 다만, 신혼부부, 청년가구 및 배당이의 소송 당사자는 수도권은 4.5억원, 그 외 지역은 3.6억원 )\n* 보증부 월세의 경우 임대차보증금은 임대차 기간 전체에 해당하는 월세 합계액을 차감하여 산출\n※ 단, 실제 대출금액은 주택도시보증공사 보증서 발급 가능금액에 달라질 수 있음","대출기간":"최장 25개월이내(대출만기일은 전세계약 종료일 + 1개월)\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 갱신 계약을 체결한날로부터 3개월 이내","상환방식 및 이자납입":"만기일시상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%","대출관련비용":"주택도시보증공사 보증료 (손님부담)\n전세보증금 반환보증금의 연 0.097~연 0.211% (주택도시보증공사가 정한 보증료율 기준에 따르며 보증료율은 변동될 수 있음.)+대출금의 연 0.031%\n채권양도통지비용 3만원 면제\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택 세대주인 근로소득자\n국민주택규모 주택\n임대차계약서상의 입주일 또는 주민등록등본 전입일로부터 3개월 이내 차입한 자금\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40%(월세공제와 주택마련저축공제 포함 3백만원)","필요서류":"임대차계약서(원본), 소득·재직 확인서류, 주민등록등본 (상담 시 추가 제출서류가 있을 수 있습니다)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"이사 전 최소 10영업일 전에 대출신청을 하셔야 합니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 주택도시보증공사 보증규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래시 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/ 신용상의 불이익(압류,경매 등) 이 발생할 수 있으니 주의하시기 바랍니다.\n주택도시보증공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.06.19 변경)\n<대출한도>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n임차보증금의 80%(신혼부부, 청년가구 및 배당이의 소송당사자는 90%)이내 이면서, 주택도시보증공사 보증한도 이내\n변경 후\n임차보증금의 80%(신혼부부, 청년가구 및 배당이의 소송당사자는 90%)이면서 연간 금융비용부담율 40%이내 해당 금액\n※ 단, 실제 대출금액은 주택도시보증공사 보증서 발급 가능금액에 달라질 수 있음","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"91일물 CD유통수익률","금리변동주기":"3개월","기준금리":"2.500","가산금리":"3.210","우대금리":"1.100","최저~최고금리":"4.610 ~ 5.710"},{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"3.202","우대금리":"1.100","최저~최고금리":"4.618 ~ 5.718"},{"금리구분":"1년물 금융채 유통수익률","금리변동주기":"1년","기준금리":"2.498","가산금리":"3.236","우대금리":"1.100","최저~최고금리":"4.634 ~ 5.734"},{"금리구분":"신규취급액기준 COFIX","금리변동주기":"6개월","기준금리":"2.540","가산금리":"3.178","우대금리":"1.100","최저~최고금리":"4.618 ~ 5.718"},{"금리구분":"신 잔액기준 COFIX","금리변동주기":"6개월","기준금리":"2.630","가산금리":"2.648","우대금리":"1.100","최저~최고금리":"4.178 ~ 5.278"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (248, 4, '하나 징검다리론', '{"대출대상":"대출신청일 현재 4대 서민금융상품(새희망홀씨, 햇살론, 미소금융, 바꿔드림론)을 성실상환 한 자이며 성실상환자 기준은 아래와 같습니다.\n1) 4대 서민금융상품 중 어느 하나를 2년 이상 거래한 후 해당 대출 원금의 75% 이상을 상환한 자 또는 거래기간에 상관없이 전액 상환한 자\n2) CB신용평점 : 상위 80%","대출금리":"최저 연 6.116% ~ 최고 연 9.0% 주1)\n- 최저 금리 기준 : 2023.05.24 현재, 내부신용(ASS) 1등급, 대출기간 1년, 부수거래 감면금리 및 기타감면금리(만19세 미만 3자녀 이상 가정, 기초생활수급권자, 한부모가정, 다문화가정, 만60세 이상 부모 부양자) 최대 적용시\n- 최고 금리 기준 : 2023.05.24 현재, 내부신용(ASS) 8등급, 대출기간 5년, 부수거래 감면금리 및 기타감면금리 미적용 시\n기준금리 주2)\n(금융채 12개월) 3.796% 기타감면금리 0.1% 만19세 미만 \n2자녀 가정\n가산금리 주3) 4.430%~5.204% 각 0.2% 기초생활수급권자,\n한부모가정,\n다문화가정,\n만60세 이상 부모 부양자,\n만19세 미만\n3자녀 이상 가정\n부수거래 항목별\n감면금리 주4) 0.40% 급여이체\n최대\n0.30% 제휴카드결제\n0.20% 적금 또는\n주택청약종합저축\n납입\n주1) 적용금리 : 기준금리 + 가산금리 – 부수거래 감면금리 및 기타감면금리\n주2) 기준금리 : 대출개시일에 결정되며, 금리변동주기에 따라 적용금리가 변동됩니다. (변동주기 : 12개월)\n주3) 가산금리 : 신용등급, 대출기간 등에 따라 차등 적용됩니다.\n주4) 부수거래 항목별 감면금리 : 대출 실행시 선택한 부수거래 항목의 감면 금리가 적용되며, 이후에는 선택하지 않은 항목을 포함하여 모든 항목에 대해서 매월 감면 조건을 충족하는 경우 1개월 단위로 금리가 감면되고, 매월 조건을 충족하지 않을 경우 감면 받은 금리가 자동으로 인상됩니다.\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고금리는 연 20%입니다.\n- 연체이자율[대출 이자율+연체가산이자율(연3%)] : 최고 연 15%입니다.","대출한도":"최대 30백만원. 단, 최저취급금액은 1백만원","대출기간":"5년 이내 (거치기간 최대 1년 이내)","상환방식":"원(리)금 균등분할상환","담보":"무보증, 무담보","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표현한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사 후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"재직 및 소득서류\n(상담 시 추가 제출서류가 있을 수 있습니다.)\n서민금융상품중 햇살론, 미소금융은 해당기관이 1개월내 발급한 완제확인서 또는 금융거래 확인서 등 성실상환 확인이 가능한 서류를 제출해야 합니다.","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 이 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.04.10 변경)\n<기타감면 항목별 감면금리 변경>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전 : 만19세 미만 2자녀 가정 (0.2%), 만19세 미만 3자녀 이상 가정 (0.4%)\n변경 후 : 만19세 미만 2자녀 가정 (0.1%), 만19세 미만 3자녀 이상 가정 (0.2%)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (249, 4, '하나 청년월세론', '{"상품특징":"만 19세이상 만 34세 이하의 무주택(배우자 포함)세대주를 대상으로 한국주택금융공사의 보증서 담보로\n대출기간 13년 내에서 최대 12백만원까지 주택의 월세자금을 지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님\n만 19세 이상 만 34세 이하인 무주택인 세대주\n월세보증금이 1억원 이하이고, 월세금이 70만원 이하인 월세계약을 체결한 임차인\n잔여 임대차계약기간이 6개월 이상인 손님\n본인과 배우자의 합산한 연소득이 7천만원 이하이면서 주거급여수급자가 아닌 손님","대상주택":"공부상(등기부등본 등) 주거용 주택(미등기 주택도 가능)","대출금리":"[기준일자 : 2025.05.20, 단위:연%]\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최종금리\n6개월물 금융채 유통\n수익률 6개월 2.588 1.140 0.000 3.728\n※ 대출금리는 기준금리 변동 시 금리가 변경됩니다.\n※ 2025.05.20 현재, 내부신용(ASS) 1~13등급, 대출기간 3년이상, 대출금액 6백만원 기준\n※ 실제 적용 금리는 대출금리에서 이자지원*율(연 0.3%)을 차감하여 적용됩니다.\n* 이자지원 : 은행권청년창업재단에서 연 0.3% 이자를 지원하며, 이자지원 약정위반(주택보유 등) 사유 확인시 동 이자지원은\n중단됩니다.\n- 이자 산정방법: 최저이율(기준금리 + 가산금리 – 우대금리)\n                최고이율(기준금리 + 가산금리)\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.(상품에 따라 연체이자율이 일부 달라지는 경우가\n있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","대출한도":"최대 12백만원(임대차계약서상 계약기간 내 월 최대 50만원 한도)\n(하나 청년전세론과 동시 이용시 6백만원)","대출기간":"최대 13년 (거치기간 : 최대 8년(연단위), 분할상환기간 : 3년 또는 5년(택일))","대출신청시기":"잔여 임대차계약기간이 6개월 이상인때","담보":"주택금융신용보증서","상환방식":"원금균등분할상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"면제","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"주민등록등본, 확정일자부 전·월세계약서(원본), 계약금 지급 영수증, 임차주택의 등기부등본, 현거주지 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서, 주거급여 비수급자 확인서 등","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 한국주택금융공사 보증서 발급이 거절되거나, 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출금액을 상환하지 않을시 연체이자가 부과되며 채무자의 재산/신용상의 불이익 (압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2020.04.28 변경) <상품명 변경>\n변경 전 : KEB하나 청년월세론\n변경 후 : 하나 청년월세론","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (250, 4, '하나 청년전세론', '{"상품특징":"만 19세이상 만 34세 이하의 무주택(배우자 포함)세대주를 대상으로 한국주택금융공사의 보증서 담보로\n임차보증금의 90%범위 내에서 최대 2억원까지 주택의 전세자금을 지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님\n- 임차보증금액주)이 7억원(수도권 외 5억원) 초과하는 경우 대출 불가\n주)월세가 있는 경우 월세보증금 및 월세에 대해 전월세전환율을 적용하여 계산한 금액\n(월세보증금+(월세X12÷전월세전환율))\n만 19세 이상 만 34세 이하인 무주택 세대주\n임차보증금의 5%이상을 지급한 임차인\n본인과 배우자의 합산한 연소득이 7천만원 이하인 손님","대상주택":"공부상(등기부등본 등) 주거용 주택(미등기 주택도 가능)","대출금리":"[ 기준일자 : 2025.05.20, 단위: 연% ]\n금리구분 금리변동주기 기준금리 가산금리 우대금리 최종금리\n6개월물 금융채 유통\n수익률 6개월 2.588 1.04 0.000 3.628\n※ 대출금리는 기준금리 변동 시 금리가 변경됩니다.\n※ 2025.05.20 현재, 내부신용(ASS) 1~13등급, 대출기간 2년, 대출금액 5천만원 기준\n- 이자 산정방법: 최저이율(기준금리 + 가산금리 – 우대금리)\n                최고이율(기준금리 + 가산금리)\n- 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20%입니다.\n- 연체이자율[대출이자율+연체가산이자율(연3%)]: 최고 연 15%입니다.(상품에 따라 연체이자율이 일부 달라지는 경우가\n있으므로 세부적인 사항은 대출거래약정서 등을 참고하시기 바랍니다.)\n- 이자의 부과시기: 후취방식 징구를 원칙으로 1개월 이내의 기간을 단위로 징수한다.","대출한도":"최대 2억원 범위 내 아래 ①과 ② 중 적은 금액으로 함\n① 임차(전세)보증금의 90% 이내\n② 주택금융공사의 상환능력별 보증한도 이내","대출기간":"6개월이상 3년이내 (단, 임대차 계약기간 범위내)\n(만 34세 이하 임차인은 임대차 계약 기간 범위에서 횟수 제한없이 기한연장 가능하며, 만 35세 이상 임차인은 1회에 한하여 기한연장 가능. 이후 연장불가)","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내(계약 갱신일 이전에도 보증신청 가능)","담보":"주택금융신용보증서","상환방식":"만기일시상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"면제","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"주민등록등본, 확정일자부 전·월세계약서(원본), 임차보증금의 5%이상을 지급한 영수증, 임차주택의 등기부등본, 현거주지 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서, 금융거래확인서 등","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되어 해당 대출과 관련한 대출정보가 삭제됩니다.\n대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 한국주택금융공사 보증규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2024.09.30 변경)\n<대출대상>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전 : 임차보증금 기준 (월세 미고려)\n변경 후 : 전월세전환율 반영하여 임차보증금 재산정\n상품내용 변경에 관한 사항\n(2024.03.29 변경)\n<대출한도 변경>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 :\n최대 2억원(임차보증금의 90% 이내)\n변경 후 :\n최대 2억원 범위 내 아래 ①과 ② 중 적은 금액으로 함\n① 임차(전세)보증금의 90% 이내\n② 주택금융공사의 상환능력별 보증한도 이내\n상품내용 변경에 관한 사항\n(2023.01.26 변경)\n<대출한도 변경>\n기존 가입고객에 대한 적용 여부 : 적용\n변경 전 :\n최대 1억원\n변경 후 :\n최대 2억원\n상품내용 변경에 관한 사항\n(2022.03.25 변경)\n<대출신청시기>\n기존가입고객에 대한 적용여부 : 미적용\n변경 전 : 임대차계약서 상 잔금지급일 이전\n변경 후 :\n신규 : 임대차계약서 상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월이내\n갱신 :\n 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내\n(계약갱신일 이전에도 보증신청 가능)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (251, 4, '하나 혼합금리 모기지론', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"혼합금리로 최장 40년간 대출이용이 가능한 상품입니다.","대출대상":"주택을 담보로 제공하는 손님","대상주택":"아파트 및 주택","대출한도":"담보 가능액 범위내","대출기간":"6년 이상 ~ 40년 이내","담보":"아파트 및 주택","상환방식":"(부분)원(리)금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.66%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)\n모기지신용보증료 : (한국주택금융공사 보증서(MCG) 이용시) 보증료는 고객이 부담(보증금액의 최저 연 0.05% ~ 최고 연 0.30%), (서울보증보험 보험(MCI) 이용시) 보험료는 은행이 부담","필요서류":"등기권리증, 인감증명서, 주민등록등(초)본, 소득증명서류(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n투기지역여부, DTI 충족 여부, 주택관련대출보유여부, 주택보유수 등에 따라 대출한도 및 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com), 한국주택금융공사 홈페이지(www.hf.go.kr)를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율\n고정금리 1.4%(비거치식 주탬담보대출 1.3%)\n변동금리 1.2%(비거치식 주택담보대출 1.1%)\n변경 후\n중도상환해약금율 0.66%","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","고정금리기간","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"3년물 금융채 유통수익률","고정금리기간":"36개월","기준금리":"2.609","가산금리":"2.930","우대금리":"1.300","최저~최고금리":"4.239 ~ 5.539"},{"금리구분":"5년물 금융채 유통수익률","고정금리기간":"60개월","기준금리":"2.794","가산금리":"2.720","우대금리":"1.300","최저~최고금리":"4.214 ~ 5.514"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (252, 4, '하나 혼합금리 모기지론 변동금리대환전용', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"기존에 당행에 변동금리 주택담보대출을 보유하고 있는 손님이 혼합금리대출로 최장 40년으로 갈아탈 수 있는 대환 전용 상품입니다.","대출대상":"대출 신청 시점 혼합금리로 대환하고자 하는 당행 주택담보대출 보유 손님","대상주택":"아파트 및 주택","대출한도":"담보 가능액 범위내","대출기간":"6년이상 ~ 40년 이내 (거치기간 없음)","담보":"아파트 및 주택","상환방식":"(부분)원(리)금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.66%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)\n모기지신용보증료 : (한국주택금융공사 보증서(MCG) 이용시) 보증료는 고객이 부담(보증금액의 최저 연 0.05% ~ 최고 연 0.30%), (서울보증보험 보험(MCI) 이용시) 보험료는 은행이 부담","필요서류":"등기권리증, 인감증명서, 주민등록등(초)본, 소득증명서류(상담 시 추가 제출서류가 있을 수 있습니다.)","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n투기지역여부, DTI 충족 여부, 주택관련대출보유여부, 주택보유수 등에 따라 대출한도 및 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n금융소비자는 해당금융상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com), 한국주택금융공사 홈페이지(www.hf.go.kr)를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율\n고정금리 1.4%(비거치식 주탬담보대출 1.3%)\n변동금리 1.2%(비거치식 주택담보대출 1.1%)\n변경 후\n중도상환해약금율 0.66%\n상품내용 변경에 관한 사항\n(2024.02.13 변경)\n<대출기간 변경>\n변경 전 : 최장 50년\n변경 후 : 최장 40년\n※ 기존 가입고객에 대한 적용여부 : 적용\n상품내용 변경에 관한 사항\n(2024.01.31 변경)\n<대출대상 변경>\n변경 전 : 대출 신청 시점 기준 주택가격 9억 초과인 주택을 담보로 제공하는 손님\n변경 후 : 대출 신청 시점 혼합금리로 대환하고자 하는 당행 주택담보대출 보유 손님\n※ 기존 가입고객에 대한 적용여부 : 미적용","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (253, 4, '하나 주거용 오피스텔 담보대출 (MCI / MCG)', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"주거용으로 사용하는 오피스텔을 담보로 제공하고 서울보증보험 MCI 또는 한국주택금융공사 구입자금보증이 가능한 개인손님","대상부동산":"주거용으로 확인 가능한 오피스텔","대출한도":"담보대출 가능액 범위내","상환방식":"만기일시상환, (부분)원(리)금균등분할상환","대출기간":"만기일시상환 : 최장 3년이내(한국주택금융공사 구입자금보증서 발급대상만 가능)\n분할상환 : 최장 30년 이내(은행에서 지정한 오피스텔의 경우는 40년 이내)","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.66%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)\n한국주택금융공사 보증서 발급시 보증료 손님부담(보증금액의 0.05%~0.3%)","필요서류":"등기권리증, 인감증명서, 주민등록등(초)본, 소득증명서류(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n서울보증보험 MCI 또는 한국주택금융공사 일반구입자금보증 가능여부에 따라 대출이 제한될 수 있습니다.\n자금용도가 기업자금(임대업 등)에 해당하는 경우 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출 금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지( www.kebhana.com), 한국주택금융공사 홈페이지( www.hf.go.kr)를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율\n고정금리 1.4%(비거치식 주탬담보대출 1.3%)\n변동금리 1.2%(비거치식 주택담보대출 1.1%)\n변경 후\n중도상환해약금율 0.66%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (254, 4, '하나 주거래 버스사업자 우대 대출', '{"대출대상":"준공영제를 실시하는 지방자치단체에 소재하는 준공영제 적용 대상인 시내버스 운송사업자\n※ 준공영제 적용 지역 : 서울, 부산, 대구, 인천, 광주, 대전, 제주도","대상담보":"버스운수사업 목적으로 사용중인 부동산 및 버스를 담보 취득할 수 있습니다.","대출금리":"변동금리 최저 연 4.693% ~ 최고 연 6.823% (2025.02.27현재)\n[기준금리 연 2.84%(91일물CD유통수익률) + 가산금리(최저) 연 1.85% / (최고) 연 3.98%]\n최저금리기준 : 2025.02.27 현재, 내부신용등급 A1, 대출기간 3년, 대출금액 10억원, 시설자금대출, 분할상환, 담보유효가 100%, 우대금리적용\n최고금리적용 : 2025.02.27 현재, 내부신용등급 B4, 대출기간 3년, 대출금액 10억원, 시설자금대출, 분할상환, 담보유효가 100%\n이자 산정방법: 기준금리+가산금리\n상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n가산금리는 신용등급, 대출기간, 대출금액, 대출과목, 상환방식, 담보종류 등에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경 될 수 있습니다.\n연체이자율은 [대출이자율+연체가산이율(연3%)], 최고 연 15%입니다.\n이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연20%입니다.","대출한도":"운전자금 대출 : 은행이 정한 운전자금 가용한도 범위내\n※운전자금 가용한도 = 1회전 소요운전자금 – 금융기관 총차입액\n1회전 소요운전자금 = (연간 추정매출액 – 추정감가상각비) × 1회전기간/365\n시설자금 대출 : 은행이 정한 시설자금 가용한도 범위내\n※버스구입자금에 한하여 소요자금의 95%까지 가능\n버스구입자금 가용한도 = (버스구입자금 – 정부 & 지자체 보조금) × 95%","대출기간":"버스 구입목적 시설대의 경우 5년이내를 원칙으로 하되, 최대 9년 범위내 운용 가능","상환방식":"원(리)금분할상환방식, 만기일시상환방식","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산.\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편 넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금\n2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금\n3. 연체기간이 1일인 연체대출채권 및 지급보증 대지급금\n4. 대여유가증권","원금또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급 시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능(최장 3년 범위 내에서 연장가능)\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사 후 승인을 득한 경우 계약갱신 가능하며,\n고객님의 신용도, 대출상품 등에 따라 영업점 방문/전자금융채널(기업인터넷뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율＊ X (중도상환약정잔여일수 ÷ 중도상환약정기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율\n담보종류 고정 변동\n순수신용 0.04%\n부동산/동산 0.51%\n보증서/기타담보 0.04% 0.03%","대출관련비용":"대출금액 5천만원 초과시 손님 및 은행 각 인지세 50% 부담하며, 담보설정에 따른 국민주택채권매입비용, 담보해지 시 말소비용이 발생할 수 있습니다.\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (255, 4, '하나주거래대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"하나은행과 6개월이상 거래한 손님","대출한도":"최저 3백만원 ~ 최대 5천만원(단, 10만원 단위로 신청가능)","대출기간":"일시상환, 통장대출 : 1년\n분할상환 : 5년 이내 연 단위만 가능","상환방식":"일시상환, 통장대출 : 만기일시상환\n분할상환 : (부분)원(리)금균등분할상환 월단위 (단, 비대면은 원금균등분할상환 및 전액 분할상환만 가능)","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함)전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금\n2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금\n4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365 (윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 x 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이 할 수 있으므로, 대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기한연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹,스마트폰뱅킹)등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"없음","서비스 운영시간":"대출 한도/금리조회 : 24시간 365일\n대출 신청 및 약정/실행 : 06:00 ~ 익일 01:00, 365일\n※ 단, 일자변경시간(00:00 ~ 00:30)은 서비스가 제한될 수 있습니다.","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예: 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내 (고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다)이내에 해당 계약의 해지를 요구할 수 있습니다\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"한도/금리조회 이후 대출신청이 가능하며, 대출승인 전에 취소하거나 화면이탈 및 어플을 종료, 또는 대출승인 후 승인 유효기일 안에 약정 및 실행하지 않는 경우에는 자동취소되며, 취소된 이후에는 다시 한도/금리조회부터 신청하셔야 합니다.\n손님의 거래 실적/패턴 등을 분석하여 산출한 금액은 손님의 신용등급과 당행 및 타 금융기관 기보유 대출규모와는 무관하게 산출한 것으로, 내부 심사 및 승인 과정에서 대출가능금액 변동 및 대출 취급이 제한될 수 있습니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출 약정시 필수 준비사항\n1. 하나은행 입출금통장 보유\n2. 스마트폰뱅킹(하나원큐) 가입\n3. 사설인증 가입 또는 인증서 등록(간편비밀번호, 얼굴인증, 지문인증, 공동인증서 중 최소 1개)\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n당행 리스크관리 정책 및 심사기준에 의거하여 미사용한도 보유시 한도소진율에 따라 내입비율이 발생할 수 있으니 유의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n※ 금융소비자는 해당상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n명의도용 피해방지를 위해 추가본인확인 절차가 필요한 경우 영업점 방문(신분증 지참) 또는 휴대전화 추가인증이 필요할 수 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%(단, 비대면을 통해 신청 및 실행/약정한 경우 0.5%)\n변경 후\n중도상환해약금율 0.04%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (256, 4, '하나원큐 비상금대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"직업, 소득에 관계없이 만 19세 이상 대한민국 국민으로 서울보증보험(주) 보험증권 발급이 가능한 손님","대출한도":"최저 50만원 ~ 최대 300만원 (서울보증보험㈜의 보증가능금액 이내에서 가능)","대출기간":"1년","상환방식":"만기일시상환(마이너스통장)","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365 (윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 x 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이 할 수 있으므로, 대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기한연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹,스마트폰뱅킹)등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"없음 (대출 기간 내에 얼마를 상환하든, 그에 따른 중도상환해약금이 없음)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","서비스\n운영시간":"대출신청 및 약정/실행 : 24시간 365일\n※ 단, 일자변경시간(00:00~00:30)은 서비스가 제한됩니다.","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"신청 당일 중 대출을 실행하지 않으면, 신청내역은 자동취소됩니다. 취소된 이후에는 처음부터 다시 신청 하셔야 합니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도의 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류,경매, 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n명의도용 피해방지를 위해 추가본인확인 절차가 필요한 경우 영업점 방문(신분증 지참) 또는 휴대전화 추가인증이 필요할 수 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (257, 4, '하나원큐 주택담보대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"스크래핑에 의한 소득 및 재직 증빙이 일치하며 당행 심사 기준을 충족한 개인","대출한도":"가용담보가액 범위 내 최대 7억원 이내\n단, 최저 1천만원 이상, 10만원 단위로 신청","대출기간":"10년, 15년, 20년, 25년, 30년, 35년, 40년 중 택일","담보":"KB시세가 없는 아파트, 연립빌라, 다세대주택 (미등기주택, 도시형생활주택, 다가구주택, 다중주택, 오피스텔 제외)","상환방식":"전액 원리금 균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"한도 및 금리조회 / 대출신청 : 08:00~22:00\n모바일 서류작성 : 24시간 가능\n단, 일자변경시간(00:00~00:30)은 서비스가 제한될 수 있습니다.","계약해지":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.66%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"대출금액에 따라 아래와 같이 인지세를 부담합니다.\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원\n근저당권 설정시 : 국민주택채권 매입비용 손님부담(근저당권 말소 또는 채권최고액 감액시 비용 손님부담)\n서울보증보험 보험(MCI) : 보험료는 은행이 부담","필요서류":"없음(필요시 추가 제출서류가 있을 수 있습니다.)","금리인하요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예: 취업,승진,재산증가,개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날 부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해 드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외 되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구 받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"한도 및 금리조회 이후 대출신청이 가능하며, 당일 신청시간까지 하나원큐앱을 통해 대출 신청을 완료하셔야 합니다.\n대출 신청 후 당일 포함 3영업일안에 세대원 동의가 완료되지 않으면 신청내역은 자동취소되며, 취소된 이후에는 다시 신청하셔야 합니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 투기지역여부, DSR충족 여부, 주택관련대출보유여부, 주택보유수, 담보용도 등에 따라 감독규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출한도 및 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과다할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n상품관련 자세한 사항은 상품설명서를 참고하시거나 하나은행 고객센터(1599-2222)을 또는 영업점으로 문의하시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.05.29 변경)\n<대출한도 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\nKB시세가 있는 아파트\n가용담보가액 범위 내 최대 5억원 이내\nKB시세 미보유 아파트, 연립빌라, 다세대주택\n가용담보가액 범위 내 최대 5억원 이내\n변경 후\nKB시세가 있는 아파트\n가용담보가액 범위 내 최대 10억원 이내\nKB시세 미보유 아파트, 연립빌라, 다세대주택\n가용담보가액 범위 내 최대 7억원 이내","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (258, 4, '하나원큐 아파트론', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"스크래핑에 의한 소득 및 재직 증빙이 일치하며 당행 심사 기준을 충족한 개인","대출한도":"가용담보가액 범위 내 최대 10억원 이내\n단, 최저 1천만원 이상, 10만원 단위로 신청","대출기간":"10년, 15년, 20년, 25년, 30년, 35년, 40년 중 택일","담보":"KB시세가 있는 아파트","상환방식":"전액 원리금 균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"한도 및 금리조회 / 대출신청 : 08:00~22:00\n모바일 서류작성 : 24시간 가능\n단, 일자변경시간(00:00~00:30)은 서비스가 제한될 수 있습니다.","계약해지":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.66%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"대출금액에 따라 아래와 같이 인지세를 부담합니다.\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원\n근저당권 설정시 : 국민주택채권 매입비용 손님부담(근저당권 말소 또는 채권최고액 감액시 비용 손님부담)\n서울보증보험 보험(MCI) : 보험료는 은행이 부담","필요서류":"없음(필요시 추가 제출서류가 있을 수 있습니다.)","금리인하요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예: 취업,승진,재산증가,개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날 부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해 드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외 되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구 받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"한도 및 금리조회 이후 대출신청이 가능하며, 당일 신청시간까지 하나원큐앱을 통해 대출 신청을 완료하셔야 합니다.\n대출 신청 후 당일 포함 3영업일안에 세대원 동의가 완료되지 않으면 신청내역은 자동취소되며, 취소된 이후에는 다시 신청하셔야 합니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 투기지역여부, DSR충족 여부, 주택관련대출보유여부, 주택보유수, 담보용도 등에 따라 감독규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출한도 및 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과다할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n상품관련 자세한 사항은 상품설명서를 참고하시거나 하나은행 고객센터(1599-2222)을 또는 영업점으로 문의하시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.05.29 변경)\n<대출한도 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\nKB시세가 있는 아파트\n가용담보가액 범위 내 최대 5억원 이내\nKB시세 미보유 아파트, 연립빌라, 다세대주택\n가용담보가액 범위 내 최대 5억원 이내\n변경 후\nKB시세가 있는 아파트\n가용담보가액 범위 내 최대 10억원 이내\nKB시세 미보유 아파트, 연립빌라, 다세대주택\n가용담보가액 범위 내 최대 7억원 이내","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (259, 4, '하나원큐새희망홀씨ll', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"근로소득자를 위한 무보증, 무담보, 신용대출로 최대 3천5백만원까지 지원하는 서민금융상품","대출대상":"대출신청일 기준 당행 여신심사에 의해 대출적격으로 판정된 6개월 이상 근로소득이 있는 손님으로서 연소득 4천만원 이하 또는 CB신용평점 하위 20%의 경우 연소득 5천만원 이하인 손님을 대상으로 한다. 이 경우 CB신용평점은 KCB CB평점, NICE CB평점 중 낮은 점수를 적용한다.","감면금리":"<부수거래 항목별 감면금리>\n급여이체(0.4%), 제휴카드결제(최대 0.3%), 적금 또는 주택청약종합저축납입(0.2%)\n<기타감면 항목별 감면금리>\n각 0.1% : 청년층(만34세 이하), 고령자(만65세 이상)\n※ 부수거래 항목별 감면금리는 대출 실행시 선택한 부수거래 항목의 감면금리가 적용되며, 이후에는 선택하지 않은 항목을 포함하여 모든 항목에 대해서 매월 감면조건을 충족하는 경우 1개월 단위로 금리가 감면되고, 매월 조건을 충족하지 않을 경우 감면 받은 금리가 자동으로 인상됩니다.\n** 가산금리는 신용등급, 대출기간, 대출금액 등에 따라 차등 적용됩니다.","대출한도":"최대 3천5백만원. 단, 최저 취급금액은 1백만원","대출기간":"5년 이내","상환방식":"5년이내 원(리)금 균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 / 3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 X 대출이자율 X 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 X 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동이체등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 대출 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표현한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대충상품 등에 따라 영업점 방문/고객센터 연장/전자금융채녈(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","우대혜택":"혜택 1. 중도상환해약금 없음\n대출취금수수료, 한도미사용수수료, 중도상환해약금 모두 면제\n혜택 2. 아래 은행거래수수료 면제","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"없음","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도의 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com), 한국주택금융공사 홈페이지(www.hf.go.kr)를 참조하시거나 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2024.02.08 변경)\n<대출대상 변경>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전 :\n대출신청일 기준 당행 여신심사에 의해 대출적격으로 판정된 6개월 이상 근로소득이 있는 손님으로서 연소득 4천만원 이하 또는 CB신용평점 하위 20%의 경우 연소득 5천만원 이하인 손님을 대상으로 한다. 이 경우 CB신용평점은 KCB CB평점 , NICE CB평점 중 낮은 점수를 적용한다.\n하나원큐새희망홀씨II는 하나원큐신용대출을 통해서만 신청 가능하다.\n변경 후 :\n대출신청일 기준 당행 여신심사에 의해 대출적격으로 판정된 6개월 이상 근로소득이 있는 손님으로서 연소득 4천만원 이하 또는 CB신용평점 하위 20%의 경우 연소득 5천만원 이하인 손님을 대상으로 한다. 이 경우 CB신용평점은 KCB CB평점 , NICE CB평점 중 낮은 점수를 적용한다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (260, 4, '하나원큐신용대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"1. 현 직장 6개월이상 국민건강보험 직장인 가입 손님으로 스크래핑에 의해 건강보험료 6개월이상 정상 납입 손님\n2. CB사 소득에 의해 대출한도가 산정되는 손님\n3. 하나원큐신용대출 갈아타기(타행대환) : 타행대출 보유하여 대환 요구가 있는 손님","대출한도":"최대 3억5천만원(단, 최저 50만원부터 10만원 단위로 신청가능)","대출기간":"1년","상환방식":"만기일시상환 (일시대출, 통장대출)","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수 계산은 여신당일로부터 기일 또는 상환일 (일부상환 또는 분할상환 포함)전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등)단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등)단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n손님이 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출 취급시 작성하시는 대출거래약정서의 상환 방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 손님이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 손님이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사 후 승인을 득한 경우 계약갱신 가능하며, 손님의 신용도,\n대출상품 등에 따라 영업점방문/고객센터 연장/ 전자금융채널(인터넷뱅킹· 스마트폰뱅킹)등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.04%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"없음(재직 및 소득 내용은 스크래핑을 통해 확인하며, 필요시 추가 제출서류가 있을 수 있습니다)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용 시 불이익 : 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"해당 상품은 한도조회 이후 대출신청이 가능하며 한도조회 당일 자정까지 하나원큐 앱을 통해 대출 신청을 완료하셔야 합니다.\n해당 상품은 승인 당일 포함 6일안에 약정/실행하지 않으면 신청내역은 자동 취소되며, 취소된 이후에는 다시 신청하셔야 합니다.\n영업점 방문 갈아타기 상품의 경우 대출실행 일에 영업점 방문이 필요합니다.\n당행 리스크관리 정책 및 심사기준에 의거하여 미사용한도 보유 시 한도소진율에 따라 내입비율이 발생할 수 있으니 유의하시기 바랍니다.\n명의도용 피해방지를 위해 추가본인확인 절차가 필요한 경우 영업점 방문(신분증 지참) 또는 휴대전화 추가인증이 필요할 수 있습니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.02.03 변경)\n<부수거래 금리감면항목>\n기존 가입고객에 대한 적용 여부: 미적용(단, 연장시 적용대상)\n변경 전\n- 하나원큐신용대출: 금리감면항목 적용\n- 하나원큐신용대출 갈아타기(타행대환): 금리감면항목 미적용\n변경 후\n- 하나원큐신용대출 & 하나원큐신용대출 갈아타기(타행대환): 금리감면항목 적용","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (261, 4, '한사랑전세론', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"아동인 자녀를 양육하는 한부모가구을 대상으로 한국주택금융공사의 보증서를 담보로\n임차보증금의 90%범위 내에서 주택의 전세자금을 지원해드리는 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님(임차보증금액이 수도권 7억, 지방 5억을 초과하는 경우 대출 불가)\n‘한부모가족증명서’가 발급되거나, ‘아동주)인 자녀를 양육중’인 한부모가구 (단, 주민등록등본상 자녀와 동일세대일 것)\n임차보증금의 5%이상 지급한 만 19세 이상의 세대주\n본인이 보유한 주택수가 1주택 이내인 손님 (단, 주택보유수가 1주택인 경우 연소득 1억원 이하)\n주) 아동 : 만 18세 미만인 국민(단, 취학중인 경우에는 만 22세 미만을 말하되, 병역의무를 이행하고 취학중인 경우에는\n병역의무를 이행한 기간을 가산한 연령 미만을 말한다)","대상주택":"공부상(등기부등본 등) 주거용 주택(미등기 주택도 가능)","대출한도":"최대 200백만원 범위 내 아래 ①과 ② 중 적은 금액으로 함\n임차(전세)보증금의 90% 이내\n신청인의 연간소득의 최대 5.0배 이내","대출기간":"전세계약만기일 범위 내 최장 2년(최대 20년까지 1년 단위로 연장 가능)\n※ 대출만기시 자동연장 되지않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담 하시기 바랍니다.","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내(계약 갱신일 이전에도 보증신청 가능)","담보":"주택금융신용보증서","상환방식":"만기일시상환,원(리)금균등분할상환, 이자 매월 후취","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%","대출관련비용":"보증료(손님부담) - 대출금액의 0.02%~0.2%(한국주택금융공사 보증료 등 운용규정에 따름)\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"한부모가족증명서(해당시), 주민등록등본, 확정일자부 전·월세계약서(원본), 임차보증금의 5%이상을 지급한 영수증, 임차주택의 등기부등본, 현거주지 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서, 금융거래확인서(타 금융기관에 신용대출 보유한 경우) 등","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 한국주택금융공사 보증규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금률 : 0.7%\n변경 후\n중도상환해약금률 : 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (262, 4, '햇살론15_서민금융진흥원', '{"상품특징":"최저신용자를 대상으로 서민금융진흥원의 신용보증을 통해 생계자금 또는 고금리대출의 대환자금을 마련하여 주는 상품","대출대상":"서민금융진흥원의 보증서 발급이 가능하고, 아래의 자격기준을 만족하는 손님\n소득구분 재직기준 소득기준\n급여소득자 재직 및 사업기간 3개월 이상 연소득 35백만원 이하 또는 CB개인신용평점 하위 연20%로 연소득45백만원이하\n사업소득자\n연금소득자 연금수령 1회 이상\n* 공적연금(국민연금,공무원연금,군인연금,사학연금)에 한함\n* 위탁보증(비대면)대상 고객은 3개월 이상 재직 중인 직장인만 가능","대출금리":"채무자부담금융비용 : 연15.90%(출금리(은행)+보증료율(서민금융진흥원)\n구분 위탁보증 직접보증, 위탁보증(비대면)\n채무자 부담금리 15.9% 15.9%\n대출금리(은행) 6.0% 6.0%\n보증료율(서민금융진흥원) 9.90% 9.90%\n성실상환금리감면 : 대출기간이 1년을 초과하는 경우 매1년 경과시점부터 과거 1년간 누적\n연체일수가 30일 이하인 손님에게 금리를 감면할 수 있다.\n(1) 대출기간에 따른 금리 감면 적용은 다음과 같으며, 보증료율에서 차감하는 방법에 의함\n- 대출기간 3년인 경우 매년 연 3.0%씩 최대 연 6%까지 감면 적용\n- 대출기간 5년인 경우 매년 연 1.5%씩 최대 연 6%까지 감면 적용\n(2) 금리 감면시점은 대출 응당일이며, 연체중인 경우는 제외함","대출한도":"1. 위탁보증 : 최소50만원이상 최대 20백만원(직접보증포함) 이내로 만원 단위\n- 은행이 서민금융진흥원으로 부터 위탁받아 직접 보증접수하는 생계자금 용도 또는 햇살론17을 대환하기 위한 용도의 신용보증\n2. 직접보증 : 최소50만원이상 최대 20백만원(위탁보증포함) 이내로 만원 단위\n- 서민금융진흥원이 직접 보증접수하는 햇살론15의 생계자금 혹은 대환자금 용도의 신용보증","대출기간":"3년 또는 5년","담보":"1.담보종류 : 서민금융진흥원의 신용보증서(담보권범위 : 특정채무(개별보증))\n2.보증비율 : 대출금액의 100%","상환방식":"원리금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금\n2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금\n4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","이자징수 방법":"1. 매 1개월마다 후취\n2. 연체이자의 징수\n- 은행이자에 대한 연체가산율 : 연3%","원금 또는\n이자의\n상환시기 및\n방법":"분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등)단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >\n단, 보증서 담보대출은 휴일(토요일 포함)에 원금 또는 이자 상환 불가","계약해지 또는\n갱신방법":"계약해지 : 고객이 대출금 전액을 상환한 때\n계약갱신 : 분할상환대출은 기간연장 불가","중도상환\n해약금":"없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"재직(사업영위)확인서류, 소득서류, 신분증, 신용보증신청 접수증(직접보증인경우 서민금융진흥원에서 발급)\n※ 상담시 추가 제출 서류가 있을 수 있습니다.","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약 철회권":"본 상품은 대출계약 철회권 적용 대상 상품입니다.","위법계약 해지권":"본 상품은 위법계약 해지권 적용 비대상 상품입니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.03.20 변경)\n<대출계약철회권>\n기존 가입고객에 대한 적용 여부 : 비대상\n변경 전 :\n본 상품은 대출계약 철회권 적용 비대상 상품입니다.\n변경 후 :\n본 상품은 대출계약 철회권 적용 대상 상품입니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (263, 4, '행복연금대출', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"아래 1~2조건을 모두 충족한 손님\n1. 공적연금(공무원,사학,군인,국민) 수령계좌를 하나은행으로 수령중인 손님 또는 당행으로 이체를 약정한 손님\n2. 연금개시일로부터 15년 이내 수급중인 손님(국민연금은 10년 이내)","대출한도":"최대 5천만원\n- 단, 국민연금은 최대 2천만원(연소득 최대 2배까지 인정)","대출기간":"1년 ~ 5년","상환방식":"원(리)금분할상환, 통장대출\n- 단, 통장대출(만기일시상환)은 연금개시일로부터 3년 이내만 가능","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"비대상","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","필요서류":"연금수령 확인서류\n(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n위 내용은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에\n관한 사항\n(2020.09.22 변경)\n기존 가입 고객에 대한 적용 여부 : 미적용\n<대출한도 변경>\n변경 전 : 최대 2천만원\n변경 후 : 최대 5천만원\n기존 가입 고객에 대한 적용 여부 : 미적용\n<대출대상 변경>\n변경 전 : 아래 1~2조건을 모두 충족한 손님\n1. 공적연금(공무원, 사학, 군인, 국민) 수령계좌를 하나은행으로 수령중인 손님 또는\n당행으로 이체를 약정한 손님(연간수령금액 600만원 이상)\n2. 연금개시일로부터 15년 이내 수급중인 손님(국민연금은 10년 이내)\n변경 후 : 아래 1~2조건을 모두 충족한 손님\n1. 공적연금(공무원, 사학, 군인, 국민) 수령계좌를 하나은행으로 수령중인 손님 또는\n당행으로 이체를 약정한 손님\n2. 연금개시일로부터 15년 이내 수급중인 손님(국민연금은 10년 이내)\n기존 가입 고객에 대한 적용 여부 : 미적용\n<상환방식 변경>\n변경 전 : 원(리)금분할상환, 통장대출\n변경 후 : 원(리)금분할상환, 통장대출\n- 단, 통장대출(만기일시상환)은 연금개시일로부터 3년 이내만 가능","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"91물 CD유통수익률","금리변동주기":"3개월","기준금리":"2.500","가산금리":"2.588","우대금리":"0.600","최저~최고금리":"4.488 ~ 5.088"},{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"2.579","우대금리":"0.600","최저~최고금리":"4.495 ~ 5.095"},{"금리구분":"1년물 금융채 유통수익률","금리변동주기":"1년","기준금리":"2.498","가산금리":"2.480","우대금리":"0.600","최저~최고금리":"4.378 ~ 4.978"},{"금리구분":"COFIX 신규취급액","금리변동주기":"6개월","기준금리":"2.540","가산금리":"2.555","우대금리":"0.600","최저~최고금리":"4.495 ~ 5.095"},{"금리구분":"COFIX 신잔액","금리변동주기":"6개월","기준금리":"2.630","가산금리":"2.025","우대금리":"0.600","최저~최고금리":"4.055 ~ 4.655"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (264, 4, '1Q오토론', '{"상품별, 일자별 금리 조회":"금리안내","대출대상":"6개월 이상 재직(또는 1년 이상 사업영위)및 소득증빙 가능한 만19세 이상 대한민국 국민으로서 다음의 용도로 자금이 필요한 손님\n신차구입자금: 자동차판매회사에서 신차를 구매하는 용도로 필요한 자금\n신차대환자금: 은행을 제외한 금융회사(캐피탈 등)에서 받은 신차할부를 갚기 위한 자금\n중고차구입자금: 중고차매매업체에서 중고차를 구매하는 용도로 필요한 자금\n※ 중고차 구입의 경우 개인간 매매거래는 대출 불가능\n중고차대환자금: 은행을 제외한 금융회사(캐피탈 등)에서 받은 중고차할부를 갚기 위한 자금. 개인간 직거래는 대출불가.","대상차량":"신차구입자금: 승용차(전차종), 승합차(전차종), 화물차(5톤 이하), 대형이륜차(260CC 초과), 캠핑용차량\n신차대환자금/중고차구입자금/중고차대환자금: 승용차(전차종), 승합차(전차종), 화물차(5톤 이하)","대출한도":"신차구입/신차대환자금 : 최대 6천만원\n중고차구입/중고차대환자금 : 최대 4천만원\n차량판매가격 또는 자동차할부대출 상환액 이내, 단 최저 취급금액은 3백만원 이상\n※ 손님의 신용등급 및 연소득 기준으로 산출된 서울보증보험의 보증한도내에서 대출 가능합니다.\n※ 서울보증보험에서 정한 저연령&저신용자 등 취약손님은 위 한도에도 불구하고 차량가의 80%까지 대출 가능","대출기간":"12개월~120개월\n(단, 중고차구입/중고차대환자금은 12개월~60개월)","담보":"서울보증보험 담보 (100%)","상환방식":"원리금균등분할상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율* x (대출잔여일수 ÷ 대출기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금율: 0.61%\n* 중도상환해약금율은 매년 재산출되어 변경될 수 있으며, 대출계좌에 적용된 해약금율은 변경되지 않습니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결 시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액 인지세 부담\n손님 부담 은행부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 75,000원 35,000원 35,000원\n1억원 초과 150,000원 75,000원 75,000원","필요서류":"재직증명서(급여소득자)\n사업자등록증명(사업소득자)\n자동차매매계약서\n자동차등록원부\n소득증빙서류\n신분증(상담 시 추가 제출서류가 있을 수 있습니다.)","금리인하요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출 취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 대출만기 도래시 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 자산(압류, 경매 등) 및 신용상의 불이익이 발생할 수 있으니 채무상환능력을 고려하여 신청하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n신차/중고차 구입자금의 경우 대출 실행 후 본인 명의로 소유권 이전해야 합니다.\n신차/중고차 대환자금의 경우 대출 실행 후 완제영수증 제출해야 합니다.\n중고차의 경우 최근 6개월 이내 1Q오토론 대출이 취급된 동일차량 구매자금 신청시 진행 불가합니다.\n대출 실행 후 6개월 내에 자동차를 제3자에게 매도시 대출금을 상환해야 합니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금율 변경>\n기존 가입고객에 대한 적용 여부: 미적용\n변경 전\n중도상환해약금율 0.7%\n변경 후\n중도상환해약금율 0.61%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (265, 4, '한국주택금융공사 U-보금자리론', '{"상품특징":"기본형 : 대출실행일부터 만기까지 고정금리가 적용되는 상품으로 향후 금리 변동의 위험을 피하고자 하는 손님에게 적합한 상품\n주택연금 사전 예약형 : 만 40세 이상(본인 또는 배우자)의 손님이 보금자리론을 이용하면서 주택연금 가입을 사전 예약하고 주택연금으로 전환 시 우대금리(연0.2%) 누적액을 장려금으로 드리는 상품","대출대상":"무주택 또는 1주택 소유자(부부기준)로 6억원 이하의 주택을 담보로 제공하는 부부합산소득 1억원 이하의 성년 손님\n(대체 취득을 위한 일시적2주택허용 -> 대체 취득 후 기한 내 기존주택 처분조건)\n주택보유수 사후검증 : 이용자격 유지여부 확인을 위해 대출 실행 후 매 1년이 되는 날을 기준으로\n담보주택 외 채무자 및 배우자(결혼예정자 및 세대 분리된 배우자를 포함하며, 본건 대출 신청시점의 배우자로 한정)의 추가주택 보유 여부 검증\n추가주택처분기한 : 추가주택은 검증기준일로부터 6개월 내 처분( 단, ①과 ②의 경우 검증기준일로부터 3년 내 처분\n① 분양권(조합원 입주권 포함) 또는 상속으로 추가주택을 취득한 경우\n② 전세사기피해자등이 임대인으로부터 임차보증금을 반환받지 못해 경매 또는 공매로 추가주택을 낙찰받은 경우 )","대상주택":"실거주용으로 사용되는 『주택법』 제 2조 1호의 공부상 주택\n- 아파트, 연립주택, 다세대주택, 단독주택등(단, 6억원 초과 주택 제외)","대출금리":"고정금리\n기준일자 : 2025-03-01(단위: 연, %)\n만기 10년 15년 20년 30년 40년 50년\n기본형 3.75 3.85 3.90 3.95 4.00 4.05\n우대금리\n우대금리 항목 상세\n최대\n1.0%\n적용가능 저출생해소 지원층\n(신혼가구,\n신생아출산가구\n중복적용불가) 신혼가구 0.3%p 부부 합산 연소득 7천만원 이하로 신청일 기준 신혼가구 요건을 충족하는 경우\n신생아출산 0.2%p 부부 합산 연소득 7천만원 이하로 신청일 기준 신생아출산가구 요건을 충족하는 경우\n다자녀가구(2자녀) 0.5%p 부부 합산 연소득 1억원 이하로 신청일 기준 채무자(배우자)의 19세 미만 자녀가 2자녀인 경우\n다자녀가구(3자녀) 0.7%p 부부 합산 연소득 1억원 이하로 신청일 기준 채무자(배우자)의 19세 미만 자녀가 3자녀인 경우\n사회적배려\n(최대 2가지\n중복적용가능) 한부모가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 한부모가구 요건을 충족하는 경우\n장애인가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 장애인가구 요건을 충족하는 경우\n다문화가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 다문화가구 요건을 충족하는 경우\n그 외 저소득청년 0.1%p 부부 합산 연소득 7천만원 이하로 채무자가 신청일 기준 만 39세 이하인 경우\n녹색건축물 0.1%p 신청일 기준 담보주택에 대해 「건축법」 및 「녹색건축 인증에 관한 규칙」에 따라 지정된 인증기관에서\n1·2등급(최우수·우수)의 “녹색건축 (예비)인증서”를 받은 경우 인증기관은 녹색건축인증제 G-SEED통합운영시스템(www.gseed.or.kr)에서 확인 가능하며\n국토안전관리원, 크레비즈인증원, 한국부동산원 등이 해당\n미분양주택입주자 0.2%p 신청일 기준 무주택자(일시적 2 주택자 허용하지 않음)로서 부부 합산 연소득 7천만원 이하이고\n구입 용도이여야 하며 채무자 또는 배우자가 서울특별시·인천광역시·경기도를 제외한 미분양 관리지역*\n내 미분양 아파트(신청일 기준)의 최초 수분양자, 분양계약서상 분양계약일이 아래 중 어느 하나에 해당하는 경우\n* 미분양관리지역(국토교통부가 발표) 여부는 전산으로 별도 관리하며,\n미분양 아파트 여부는 채무자 또는 배우자의 분양계약 체결 시기로 판단\n(ⅰ) 공부상 (임시)사용 승인일 또는 소유권보존등기일 이후\n(ⅱ) 입주자 모집공고 상 최초 공급계약 체결일로부터 60일 경과 시\n전세사기\n피해자 1.0%p 주택금융공사 업무처리기준,전세사기피해자 보금자리론을 적용하는 경우\n용어 정의\n신혼가구 혼인관계증명서상 혼인신고일이 신청일로부터 7년 이내인 가구(결혼예정 가구* 포함)\n* 청첩장 또는 예식장계약서 상 결혼예정일이 신청일로부터 3개월 이내인 가구 또는 “결혼예정사실 확인서”(별지17)를 제출하여 신청일로부터 3개월 이내 결혼예정임을 입증하는 가구\n신생아 출산가구 신청일 기준 2년 이내에 출산한 가구\n1자녀가구 신청인(배우자)의 만 19세 미만 자녀가 1인(세대분리된 자녀 포함)인 가구\n다자녀가구 신청인(배우자)의 만 19세 미만 자녀가 2인(세대분리된 자녀 포함)인 가구\n한부모가구 「한부모가족지원법」에 의거 신청인이 한부모가족증명서 발급대상인 가구(공공데이터를 통해 확인되는 경우 포함)\n장애인가구 신청인 또는 배우자, 신청인(배우자 포함)의 직계존비속 중 1인 이상이 장애인증명서, 국가유공자확인원(전상군경, 공상군경, 4·19혁명부상자, 공상공무원, 국가사회발전 특별공로상이자, 6·18자유상이자, 전투종사군무원상이자) 발급대상인 가구 또는 국가보훈부로부터 지원공상군경, 재해부상군경, 재해부상공무원, 지원공상공무원임을 확인받은 가구(공공데이터로 확인되는 경우 포함). 다만, 직계존비속(미성년자인 직계비속 제외)은 신청일 현재 주민등록등본 상 신청인(배우자 포함)과 동일세대 구성기간이 계속해서 1년 이상(합가일 기준)인 경우에 한함\n※ 장애인 본인(배우자가 장애인일 경우 배우자, 제3자가 장애인일 경우 제3자)에 대하여 “개인(민감)정보 수집·이용·조회·제공 동의서(필수적동의)”(서식22)를 반드시 징구\n다문화가구 「다문화가족지원법」제2조 제1호에 따라 다음 중 어느 하나에 해당하는 경우\n1)신청인(배우자)이 귀화자로서 배우자(신청인) 또는 자녀가 대한민국 국민\n* 신청인과 배우자가 모두 귀화자인 경우 포함\n2) 신청인(배우자)이 외국국적동포로서 배우자(신청인) 또는 자녀가 대한민국 국민\n* 배우자가 외국인인 경우 포함하나, 신청인과 배우자가 모두 외국인(외국국적동포)인 경우 제외\n3) 신청인(배우자)의 부모 중 1명이 귀화자이거나 외국인(외국국적동포 포함)\n* 다만, 배우자의 부모가 귀화자이나 외국인일 경우 신청인과 동일세대를 구성하여야 함\n4) 신청인과 동일세대를 구성하는 직계비속의 배우자가 귀화자이거나 외국인(외국국적동포 포함)\n생애최초 주택구입자 채무자와 배우자가 과거에 주택을 소유한 사실이 없는 자로서, 제11장 2. 생애최초 주택구입자 요건 및 자금용도에 해당하는 자\n전세사기피해자등 「전세사기피해자 지원 및 주거안정에 관한 특별법」(이하 ‘전세사기특별법’) 제2조제4호가목 또는 다목에 해당하는 자\n소상공인 신청인 또는 배우자가 「소상공인기본법」제2조에 해당하는 소상공인(법인사업자 제외)으로서 소상공인확인서를 발급 받은 경우\n연체금리\n대출이자를 납부하여야 하는 날에 납입하지 않거나 대출계약이 종료되는 날에 대출금을 상환하지 않으면 원래 약정한 이자에 가산되는 금리\n연체금리는 대출이자율에 연체가산금리를 더하여 최고 연 12% 이내로 적용\n연체가산금리는 연체기간에 따라 차등적용\n- 연체기간이 3개월 이내 : 당초 약정이자율 + 3%\n- 연체기간이 3개월 초과 : 당초 약정이자율 + 3%","대출한도":"담보주택 당 한도 : 3.6억원(다자녀가구·전세사기피해자등 4억원, 생애최초 주택구입자 4.2억원) 이내 (1백만원 단위로 취급)","소득요건":"부부 합산 연소득 70백만원 이하\n- 단, 아래의 경우 해당 소득 금액 이내로 적용\n구분 신혼가구 1자녀가구 다자녀가구 전세사기피해자등\n소득기준 85백만원 90백만원 1억원 없음","대출기간":"10년, 15년, 20년, 30년, 40년, 50년\n단 40년,50년 만기는 신청일 현재 채무자가 아래의 요건을 충족하는 경우 취급 가능\n대출만기 요건\n40년 채무자 만 39세 이하(신혼가구인 경우 만 49세 이하)\n50년 채무자 만 34세 이하(신혼가구인 경우 만 39세 이하)","담보":"주택","상환방식":"원(리)금균등분할상환, 체증식분할상환(만 39세 이하 채무자 및 공사가 사전 심사한 경우에 한해 취급 가능하며 50년 만기 적용 불가)","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"3년 이내에 조기(중도)상환된 원금에 대하여 대출실행일로부터 경과일수별로 0.5% 한도 내에서 부과\n(1) 조기(중도)상환수수료 = 조기(중도)상환원금×조기(중도)상환수수료율(0.5%) × [(3년-대출경과일수)/3년]\n(2) 신청일 기준 사회적배려층·다자녀가구(3자녀이상) 우대금리 적용 대상자의 경우 조기(중도)상환수수료 면제(‘24.1.30. 이후 신청접수건에 한함","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)\n모기지신용보증료(손님 부담) : 한국주택금융공사 보증서(MCG) 이용시 대출금액의 연 0.05%~0.2%(한국주택금융공사 보증료 등 운용규정에 따름)","필요서류":"등기권리증, 인감증명서, 주민등록등(초)본, 소득 및 재직증명서류 (상담 시 추가 제출서류가 있을 수 있습니다.)\n소득증명서류\n근로소득: 소득금액증명원 또는 소득확인증명서(ISA용), 근로소득원천징수영수증, 급여내역이 포함된 증명서(재직회사가 확인 날인한 급여명세표, 갑근세 원천징수 확인서 등)\n사업소득: 소득금액증명원 또는 소득확인증명서(ISA용), 연말정산용 사업소득원천징수영수증, 거주자의사업소득원천징수영수증, 과세표준확정신고 및 납부계산서\n연금소득: 연금수급권자 확인서 등\n추정소득: 연금산정용 가입내역 확인서, 건강·장기요양보험료 납부확인서","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"하나은행은 한국주택금융공사와의 협약에 따른 대출실행업무를 수행하며, 상품내용은 한국주택금융공사의 업무처리기준에 따라 정하고 있습니다.\n상품가입시 반드시 상품설명서를 교부받아 확인하시기 바랍니다.\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 대출만기 도래시 대출금액을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n사업자대출·신용대출·대부업대출 등을 포함하여 한국신용정보원에서 조회되는 주택담보대출에 해당하지 않을 시에는 상환용도 이용이 불가합니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지( www.kebhana.com), 한국주택금융공사 홈페이지( www.hf.go.kr)를 참조하시거나 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.04.01 추가)\n<우대금리>\n변경 전\n▪ 청년 우대금리\n부부 합산 연소득 7천만원 이하로 채무자가 신청일 기준 만 39세 이하인 경우 0.1%p우대\n▪ 신혼가구 우대금리\n부부 합산 연소득 7천만원 이하로 신청일 기준 신혼가구 요건을 충족하는 경우 0.2%p우대\n▪ 녹색건축물 우대금리\n신청일 기준 담보주택에 대해 「건축법」 및 「녹색건축 인증에 관한 규칙」에 따라 지정된 인증기관에서 1·2등급(최우수·우수)의 “녹색건축 (예비)인증서”를 받은 경우 0.1% 우대\n* 인증기관은 녹색건축인증제 G-SEED통합운영시스템(www.gseed.or.kr)에서 확인 가능하며, 국토안전관리원, 크레비즈인증원, 한국부동산원 등이 해당\n▪ 사회적 배려층 우대금리\n부부 합산 연소득 7천만원 이하로 신청일 기준 ①한부모가구, ②장애인가구, ③다문화가구, ④다자녀가구 중 해당하는 요건을 충족하는 경우\n(①~④ 각 항목별 0.7%p, 최대 2가지 항목 중복적용 가능)\n▪ 미분양관리지역 내 미분양주택 입주자에 대한 우대금리\n① 부부 합산 연소득 7천만원 이하로 서울특별시·인천광역시·경기도를 제외한 미분양 관리지역* 내 미분양 아파트를 최초 구입하는 경우로서, 신청일 현재 무주택자(일시적 2주택자 허용하지 않음)\n* 미분양관리지역(국토교통부가 발표) 여부는 전산으로 별도 관리하며, 미분양 아파트 여부는 채무자 또는 배우자의 분양계약 체결시기로 판단\n② 채무자 또는 배우자가 위 담보주택의 최초 수분양자로서, 분양계약서상 분양계약일이 아래 중 어느 하나에 해당하는 경우 0.2%우대\n(ⅰ) 공부상 (임시)사용승인일 또는 소유권보존등기일 이후\n(ⅱ) 입주자 모집공고상 최초 공급계약 체결일로부터 60일 경과 시\n▪ 전세사기피해자등 우대금리\n전세사기피해자등에 해당하는 경우 1.0%p우대\n▪ 신생아출산가구 우대금리\n부부 합산 연소득 7천만원 이하로, 신청일 기준 신생아출산가구 요건을 충족하는 경우 0.2%p우대\n※ 전자약정 및 전자등기 우대금리를 제외하고 위 우대금리 항목 최대 1.0%p를 한도로 중복적용 가능\n※ 신혼가구 우대금리와 신생아출산가구 우대금리는 중복 적용 불가\n변경 후\n우대금리 항목 상세\n최대\n1.0%\n적용가능 저출생해소 지원층\n(신혼가구, 신생아출산가구\n중복적용불가) 신혼가구 0.3%p 부부 합산 연소득 7천만원 이하로 신청일 기준 신혼가구 요건을 충족하는 경우\n신생아출산 0.2%p 부부 합산 연소득 7천만원 이하로 신청일 기준 신생아출산가구 요건을 충족하는 경우\n다자녀가구(2자녀) 0.5%p 부부 합산 연소득 1억원 이하로 신청일 기준 채무자(배우자)의 19세 미만 자녀가 2자녀인 경우\n다자녀가구(3자녀) 0.7%p 부부 합산 연소득 1억원 이하로 신청일 기준 채무자(배우자)의 19세 미만 자녀가 3자녀인 경우\n사회적배려\n(최대 2가지 중복적용가능) 한부모가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 한부모가구 요건을 충족하는 경우\n장애인가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 장애인가구 요건을 충족하는 경우\n다문화가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 다문화가구 요건을 충족하는 경우\n그 외 저소득청년 0.1%p 부부 합산 연소득 7천만원 이하로 채무자가 신청일 기준 만 39세 이하인 경우\n녹색건축물 0.1%p 신청일 기준 담보주택에 대해 「건축법」 및 「녹색건축 인증에 관한 규칙」에 따라 지정된 인증기관에서\n1·2등급(최우수·우수)의 “녹색건축 (예비)인증서”를 받은 경우 인증기관은 녹색건축인증제 G-SEED통합운영시스템(www.gseed.or.kr)에서 확인 가능하며\n국토안전관리원, 크레비즈인증원, 한국부동산원 등이 해당\n미분양주택입주자 0.2%p 신청일 기준 무주택자(일시적 2 주택자 허용하지 않음)로서 부부 합산 연소득 7천만원 이하이고\n구입 용도이여야 하며 채무자 또는 배우자가 서울특별시·인천광역시·경기도를 제외한 미분양 관리지역*\n내 미분양 아파트(신청일 기준)의 최초 수분양자, 분양계약서상 분양계약일이 아래 중 어느 하나에 해당하는 경우\n* 미분양관리지역(국토교통부가 발표) 여부는 전산으로 별도 관리하며,\n미분양 아파트 여부는 채무자 또는 배우자의 분양계약 체결 시기로 판단\n(ⅰ) 공부상 (임시)사용 승인일 또는 소유권보존등기일 이후\n(ⅱ) 입주자 모집공고 상 최초 공급계약 체결일로부터 60일 경과 시\n전세사기피해자 1.0%p 주택금융공사 업무처리기준, 전세사기피해자 보금자리론을 적용하는 경우\n※ 기존 가입고객에 대해서는 적용되지 않습니다.\n변경 전\n용어 정의\n신혼가구 혼인관계증명서상 혼인신고일이 신청일로부터 7년 이내인 가구(결혼예정 가구* 포함)\n* 청첩장 또는 예식장계약서 상 결혼예정일이 신청일로부터 3개월 이내인 가구 또는 “결혼예정사실 확인서”(별지24)를 제출하여 신청일로부터 3개월 이내 결혼예정임을 입증하는 가구\n장애인가구 신청인 또는 배우자, 신청인(배우자 포함)의 직계존비속 중 1인 이상이 장애인증명서, 국가유공자확인원(전상군경, 공상군경, 4·19혁명부상자, 공상공무원, 국가사회발전 특별공로상이자, 6·18자유상이자, 전투종사군무원상이자) 발급대상인 가구 또는 국가보훈부로부터 지원공상군경, 재해부상군경, 재해부상공무원, 지원공상공무원임을 확인받은 가구(공공데이터로 확인되는 경우 포함). 다만, 직계존비속(미성년자인 직계비속 제외)은 신청일 현재 주민등록등본 상 신청인(배우자 포함)과 동일세대 구성기간이 계속해서 1년 이상(합가일 기준)인 경우에 한함\n※ 장애인 본인(배우자가 장애인일 경우 배우자, 제3자가 장애인일 경우 제3자)에 대하여 “개인(민감)정보 수집·이용·조회·제공 동의서(선택적 동의)”(별지23)를 반드시 징구\n2자녀가구 신청인(배우자)의 만 19세 미만 자녀가 2인(세대분리된 자녀 포함)인 가구\n다자녀가구 신청인(배우자)의 만 19세 미만 자녀가 3인 이상(세대분리된 자녀 포함)인 가구\n변경 후\n용어 정의\n신혼가구 혼인관계증명서상 혼인신고일이 신청일로부터 7년 이내인 가구(결혼예정 가구* 포함)\n* 청첩장 또는 예식장계약서 상 결혼예정일이 신청일로부터 3개월 이내인 가구 또는 “결혼예정사실 확인서”(서식17)를 제출하여 신청일로부터 3개월 이내 결혼예정임을 입증하는 가구\n장애인가구 신청인 또는 배우자, 신청인(배우자 포함)의 직계존비속 중 1인 이상이 장애인증명서, 국가유공자확인원(전상군경, 공상군경, 4·19혁명부상자, 공상공무원, 국가사회발전 특별공로상이자, 6·18자유상이자, 전투종사군무원상이자) 발급대상인 가구 또는 국가보훈부로부터 지원공상군경, 재해부상군경, 재해부상공무원, 지원공상공무원임을 확인받은 가구(공공데이터로 확인되는 경우 포함). 다만, 직계존비속(미성년자인 직계비속 제외)은 신청일 현재 주민등록등본 상 신청인(배우자 포함)과 동일세대 구성기간이 계속해서 1년 이상(합가일 기준)인 경우에 한함\n※ 장애인 본인(배우자가 장애인일 경우 배우자, 제3자가 장애인일 경우 제3자)에 대하여 “개인(민감)정보 수집·이용·조회·제공 동의서(필수적동의)”(서식22)를 반드시 징구\n소상공인 신청인 또는 배우자가 「소상공인기본법」제2조에 해당하는 소상공인(법인사업자 제외)으로서 소상공인확인서를 발급 받은 경우\n다자녀가구 신청인(배우자)의 만 19세 미만 자녀가 2인(세대분리된 자녀 포함)인 가구\n※ 기존 가입고객에 대해서는 적용되지 않습니다.\n<소득요건>\n변경 전\n부부 합산 연소득 70백만원 이하\n- 단, 아래의 경우 해당 소득 금액 이내로 적용\n구분 신혼가구 1자녀가구 2자녀가구 다자녀가구 전세사기피해자등\n소득기준 85백만원 80백만원 90백만원 1억원 없음\n변경 후\n부부 합산 연소득 70백만원 이하\n- 단, 아래의 경우 해당 소득 금액 이내로 적용\n구분 신혼가구 1자녀가구 다자녀가구 전세사기피해자등\n소득기준 85백만원 90백만원 1억원 없음\n※ 기존 가입고객에 대해서는 적용되지 않습니다.\n<중도상환 해약금>\n변경 전\n\n3년 이내에 조기(중도)상환된 원금에 대하여 대출실행일로부터 경과일수별로 0.7% 한도 내에서 부과\n(1) 조기(중도)상환수수료 = 조기(중도)상환원금×조기(중도)상환수수료율(0.7%) × [(3년-대출경과일수)/3년]\n(2) 신청일 기준 사회적배려층 우대금리 적용 대상자의 경우 조기(중도)상환수수료 면제(‘24.1.30. 이후 신청접수건에 한함)\n변경 후\n\n3년 이내에 조기(중도)상환된 원금에 대하여 대출실행일로부터 경과일수별로 0.5% 한도 내에서 부과\n(1) 조기(중도)상환수수료 = 조기(중도)상환원금×조기(중도)상환수수료율(0.5%) × [(3년-대출경과일수)/3년]\n(2) 신청일 기준 사회적배려층·다자녀가구(3자녀이상) 우대금리 적용 대상자의 경우 조기(중도)상환수수료 면제(‘24.1.30. 이후 신청접수건에 한함)\n※ 기존 가입고객에 대해서는 적용되지 않습니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (266, 4, '한국주택금융공사 아낌e-보금자리론', '{"상품특징":"기본형 : 대출실행일부터 만기까지 안정적인 고정금리가 적용되는 상품으로 향후 금리 변동의 위험을 피하고자 하는 손님에게 적합한 상품\n주택연금 사전 예약형 : 만 40세 이상(본인 또는 배우자)의 손님이 보금자리론을 이용하면서 주택연금 가입을 사전 예약하고 주택연금으로 전환 시 우대금리(연0.2%) 누적액을 장려금으로 드리는 상품","대출대상":"무주택 또는 1주택 소유자(부부기준)로 6억원 이하의 주택을 담보로 제공하는 부부합산소득 1억원 이하의 성년 손님\n(대체 취득을 위한 일시적2주택허용 → 대체 취득 후 기한 내 기존주택 처분조건)\n* 기존주택 처분기한 : 대출실행일로부터 3년\n주택보유수 사후검증 : 이용자격 유지여부 확인을 위해 대출 실행 후 매 1년이 되는 날을 기준으로\n담보주택 외 채무자 및 배우자(결혼예정자 및 세대 분리된 배우자를 포함하며, 본건 대출 신청시점의 배우자로 한정)의 추가주택 보유 여부 검증\n추가주택 처분기한 : 추가주택은 검증기준일로부터 6개월 내 처분( 단, ①과 ②의 경우 검증기준일로부터 3년 내 처분\n① 분양권(조합원 입주권 포함) 또는 상속으로 추가주택을 취득한 경우\n② 전세사기피해자등이 임대인으로부터 임차보증금을 반환받지 못해 경매 또는 공매로 추가주택을 낙찰받은 경우 )\n처분기한 내 미처분 시, 미처분 사실 확인일을 기준으로 기한이익 상실 처리하고 확인일로부터 향후 3년 간 보금자리론 이용 제한","대상주택":"실거주용으로 사용되는 『주택법』 제 2조 1호의 공부상 주택\n- 아파트, 연립주택, 다세대주택, 단독주택등(단, 6억원 초과 주택 제외)","대출금리":"고정금리\n기준일자 : 2025-03-01(단위: 연, %)\n만기 10년 15년 20년 30년 40년 50년\n기본형 3.75 3.85 3.90 3.95 4.00 4.05\n우대금리\n우대금리 항목 상세\n최대\n1.0%\n적용가능 저출생해소 지원층\n(신혼가구,\n신생아출산가구\n중복적용불가) 신혼가구 0.3%p 부부 합산 연소득 7천만원 이하로 신청일 기준 신혼가구 요건을 충족하는 경우\n신생아출산 0.2%p 부부 합산 연소득 7천만원 이하로 신청일 기준 신생아출산가구 요건을 충족하는 경우\n다자녀가구(2자녀) 0.5%p 부부 합산 연소득 1억원 이하로 신청일 기준 채무자(배우자)의 19세 미만 자녀가 2자녀인 경우\n다자녀가구(3자녀) 0.7%p 부부 합산 연소득 1억원 이하로 신청일 기준 채무자(배우자)의 19세 미만 자녀가 3자녀인 경우\n사회적배려\n(최대 2가지\n중복적용가능) 한부모가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 한부모가구 요건을 충족하는 경우\n장애인가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 장애인가구 요건을 충족하는 경우\n다문화가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 다문화가구 요건을 충족하는 경우\n그 외 저소득청년 0.1%p 부부 합산 연소득 7천만원 이하로 채무자가 신청일 기준 만 39세 이하인 경우\n녹색건축물 0.1%p 신청일 기준 담보주택에 대해 「건축법」 및 「녹색건축 인증에 관한 규칙」에 따라 지정된 인증기관에서\n1·2등급(최우수·우수)의 “녹색건축 (예비)인증서”를 받은 경우 인증기관은 녹색건축인증제 G-SEED통합운영시스템(www.gseed.or.kr)에서 확인 가능하며\n국토안전관리원, 크레비즈인증원, 한국부동산원 등이 해당\n미분양주택입주자 0.2%p 신청일 기준 무주택자(일시적 2 주택자 허용하지 않음)로서 부부 합산 연소득 7천만원 이하이고\n구입 용도이여야 하며 채무자 또는 배우자가 서울특별시·인천광역시·경기도를 제외한 미분양 관리지역*\n내 미분양 아파트(신청일 기준)의 최초 수분양자, 분양계약서상 분양계약일이 아래 중 어느 하나에 해당하는 경우\n* 미분양관리지역(국토교통부가 발표) 여부는 전산으로 별도 관리하며,\n미분양 아파트 여부는 채무자 또는 배우자의 분양계약 체결 시기로 판단\n(ⅰ) 공부상 (임시)사용 승인일 또는 소유권보존등기일 이후\n(ⅱ) 입주자 모집공고 상 최초 공급계약 체결일로부터 60일 경과 시\n전세사기\n피해자 1.0%p 주택금융공사 업무처리기준,전세사기피해자 보금자리론을 적용하는 경우\n용어 정의\n신혼가구 혼인관계증명서상 혼인신고일이 신청일로부터 7년 이내인 가구(결혼예정 가구* 포함)\n* 청첩장 또는 예식장계약서 상 결혼예정일이 신청일로부터 3개월 이내인 가구 또는 “결혼예정사실 확인서”(별지17)를 제출하여 신청일로부터 3개월 이내 결혼예정임을 입증하는 가구\n신생아 출산가구 신청일 기준 2년 이내에 출산한 가구\n1자녀가구 신청인(배우자)의 만 19세 미만 자녀가 1인(세대분리된 자녀 포함)인 가구\n다자녀가구 신청인(배우자)의 만 19세 미만 자녀가 2인(세대분리된 자녀 포함)인 가구\n한부모가구 「한부모가족지원법」에 의거 신청인이 한부모가족증명서 발급대상인 가구(공공데이터를 통해 확인되는 경우 포함)\n장애인가구 신청인 또는 배우자, 신청인(배우자 포함)의 직계존비속 중 1인 이상이 장애인증명서, 국가유공자확인원(전상군경, 공상군경, 4·19혁명부상자, 공상공무원, 국가사회발전 특별공로상이자, 6·18자유상이자, 전투종사군무원상이자) 발급대상인 가구 또는 국가보훈부로부터 지원공상군경, 재해부상군경, 재해부상공무원, 지원공상공무원임을 확인받은 가구(공공데이터로 확인되는 경우 포함). 다만, 직계존비속(미성년자인 직계비속 제외)은 신청일 현재 주민등록등본 상 신청인(배우자 포함)과 동일세대 구성기간이 계속해서 1년 이상(합가일 기준)인 경우에 한함\n※ 장애인 본인(배우자가 장애인일 경우 배우자, 제3자가 장애인일 경우 제3자)에 대하여 “개인(민감)정보 수집·이용·조회·제공 동의서(필수적동의)”(서식22)를 반드시 징구\n다문화가구 「다문화가족지원법」제2조 제1호에 따라 다음 중 어느 하나에 해당하는 경우\n1)신청인(배우자)이 귀화자로서 배우자(신청인) 또는 자녀가 대한민국 국민\n* 신청인과 배우자가 모두 귀화자인 경우 포함\n2) 신청인(배우자)이 외국국적동포로서 배우자(신청인) 또는 자녀가 대한민국 국민\n* 배우자가 외국인인 경우 포함하나, 신청인과 배우자가 모두 외국인(외국국적동포)인 경우 제외\n3) 신청인(배우자)의 부모 중 1명이 귀화자이거나 외국인(외국국적동포 포함)\n* 다만, 배우자의 부모가 귀화자이나 외국인일 경우 신청인과 동일세대를 구성하여야 함\n4) 신청인과 동일세대를 구성하는 직계비속의 배우자가 귀화자이거나 외국인(외국국적동포 포함)\n생애최초 주택구입자 채무자와 배우자가 과거에 주택을 소유한 사실이 없는 자로서, 제11장 2. 생애최초 주택구입자 요건 및 자금용도에 해당하는 자\n전세사기피해자등 「전세사기피해자 지원 및 주거안정에 관한 특별법」(이하 ‘전세사기특별법’) 제2조제4호가목 또는 다목에 해당하는 자\n소상공인 신청인 또는 배우자가 「소상공인기본법」제2조에 해당하는 소상공인(법인사업자 제외)으로서 소상공인확인서를 발급 받은 경우\n연체금리\n대출이자를 납부하여야 하는 날에 납입하지 않거나 대출계약이 종료되는 날에 대출금을 상환하지 않으면 원래 약정한 이자에 가산되는 금리\n연체금리는 대출이자율에 연체가산금리를 더하여 최고 연 12% 이내로 적용\n연체가산금리는 연체기간에 따라 차등적용\n- 연체기간이 3개월 이내 : 당초 약정이자율 + 3%\n- 연체기간이 3개월 초과 : 당초 약정이자율 + 3%","대출한도":"담보주택 당 한도 : 3.6억원(다자녀가구·전세사기피해자등 4억원, 생애최초 주택구입자 4.2억원) 이내 (1백만원 단위로 취급)","소득요건":"부부 합산 연소득 70백만원 이하\n- 단, 아래의 경우 해당 소득 금액 이내로 적용\n구분 신혼가구 1자녀가구 다자녀가구 전세사기피해자등\n소득기준 85백만원 90백만원 1억원 없음","대출기간":"10년, 15년, 20년, 30년, 40년, 50년\n단 40년,50년 만기는 신청일 현재 채무자가 아래의 요건을 충족하는 경우 취급 가능\n대출만기 요건\n40년 채무자 만 39세 이하(신혼가구인 경우 만 49세 이하)\n50년 채무자 만 34세 이하(신혼가구인 경우 만 39세 이하)","담보":"주택","상환방식":"원(리)금 균등분할상환, 체증식분할상환(만 39세 이하 채무자 및 공사가 사전 심사한 경우에 한해 취급 가능하며 50년 만기 적용 불가)","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"3년 이내에 조기(중도)상환된 원금에 대하여 대출실행일로부터 경과일수별로 0.5% 한도 내에서 부과\n(1) 조기(중도)상환수수료 = 조기(중도)상환원금×조기(중도)상환수수료율(0.5%) × [(3년-대출경과일수)/3년]\n(2) 신청일 기준 사회적배려층·다자녀가구(3자녀이상) 우대금리 적용 대상자의 경우 조기(중도)상환수수료 면제(‘24.1.30. 이후 신청접수건에 한함)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)\n모기지신용보증료(손님 부담) : 한국주택금융공사 보증서(MCG) 이용시 대출금액의 연 0.05%~0.2%(한국주택금융공사 보증료 등 운용규정에 따름)","필요서류":"등기권리증, 인감증명서, 주민등록등(초)본, 소득 및 재직증명서류 (상담 시 추가 제출서류가 있을 수 있습니다.)\n소득증명서류\n근로소득: 소득금액증명원 또는 소득확인증명서(ISA용), 근로소득원천징수영수증, 급여내역이 포함된 증명서(재직회사가 확인 날인한 급여명세표, 갑근세 원천징수 확인서 등)\n사업소득: 소득금액증명원 또는 소득확인증명서(ISA용), 연말정산용 사업소득원천징수영수증, 거주자의사업소득원천징수영수증, 과세표준확정신고 및 납부계산서\n연금소득: 연금수급권자 확인서 등\n추정소득: 연금산정용 가입내역 확인서, 건강·장기요양보험료 납부확인서","금리인하 요구권":"본 상품은 금리인하요구권 적용 비대상 상품입니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"하나은행은 한국주택금융공사와의 협약에 따른 대출실행업무를 수행하며, 상품내용은 한국주택금융공사의 업무처리기준에 따라 정하고 있습니다.\n상품가입시 반드시 상품설명서를 교부받아 확인하시기 바랍니다.\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 대출만기 도래시 대출금액을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야할 의무가 발생될 수 있습니다.\n사업자대출·신용대출·대부업대출 등을 포함하여 한국신용정보원에서 조회되는 주택담보대출에 해당하지 않을 시에는 상환용도 이용이 불가합니다.\n※ 자세한 사항은 상품설명서 및 하나은행 홈페이지( www.kebhana.com), 한국주택금융공사 홈페이지( www.hf.go.kr)를 참조하시거나 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.\n※ 본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.04.01 추가)\n<우대금리>\n변경 전\n▪ 청년 우대금리\n부부 합산 연소득 7천만원 이하로 채무자가 신청일 기준 만 39세 이하인 경우 0.1%p우대\n▪ 신혼가구 우대금리\n부부 합산 연소득 7천만원 이하로 신청일 기준 신혼가구 요건을 충족하는 경우 0.2%p우대\n▪ 녹색건축물 우대금리\n신청일 기준 담보주택에 대해 「건축법」 및 「녹색건축 인증에 관한 규칙」에 따라 지정된 인증기관에서 1·2등급(최우수·우수)의 “녹색건축 (예비)인증서”를 받은 경우 0.1% 우대\n* 인증기관은 녹색건축인증제 G-SEED통합운영시스템(www.gseed.or.kr)에서 확인 가능하며, 국토안전관리원, 크레비즈인증원, 한국부동산원 등이 해당\n▪ 사회적 배려층 우대금리\n부부 합산 연소득 7천만원 이하로 신청일 기준 ①한부모가구, ②장애인가구, ③다문화가구, ④다자녀가구 중 해당하는 요건을 충족하는 경우\n(①~④ 각 항목별 0.7%p, 최대 2가지 항목 중복적용 가능)\n▪ 미분양관리지역 내 미분양주택 입주자에 대한 우대금리\n① 부부 합산 연소득 7천만원 이하로 서울특별시·인천광역시·경기도를 제외한 미분양 관리지역* 내 미분양 아파트를 최초 구입하는 경우로서, 신청일 현재 무주택자(일시적 2주택자 허용하지 않음)\n* 미분양관리지역(국토교통부가 발표) 여부는 전산으로 별도 관리하며, 미분양 아파트 여부는 채무자 또는 배우자의 분양계약 체결시기로 판단\n② 채무자 또는 배우자가 위 담보주택의 최초 수분양자로서, 분양계약서상 분양계약일이 아래 중 어느 하나에 해당하는 경우 0.2%우대\n(ⅰ) 공부상 (임시)사용승인일 또는 소유권보존등기일 이후\n(ⅱ) 입주자 모집공고상 최초 공급계약 체결일로부터 60일 경과 시\n▪ 전세사기피해자등 우대금리\n전세사기피해자등에 해당하는 경우 1.0%p우대\n▪ 신생아출산가구 우대금리\n부부 합산 연소득 7천만원 이하로, 신청일 기준 신생아출산가구 요건을 충족하는 경우 0.2%p우대\n※ 전자약정 및 전자등기 우대금리를 제외하고 위 우대금리 항목 최대 1.0%p를 한도로 중복적용 가능\n※ 신혼가구 우대금리와 신생아출산가구 우대금리는 중복 적용 불가\n변경 후\n우대금리 항목 상세\n최대\n1.0%\n적용가능 저출생해소 지원층\n(신혼가구, 신생아출산가구\n중복적용불가) 신혼가구 0.3%p 부부 합산 연소득 7천만원 이하로 신청일 기준 신혼가구 요건을 충족하는 경우\n신생아출산 0.2%p 부부 합산 연소득 7천만원 이하로 신청일 기준 신생아출산가구 요건을 충족하는 경우\n다자녀가구(2자녀) 0.5%p 부부 합산 연소득 1억원 이하로 신청일 기준 채무자(배우자)의 19세 미만 자녀가 2자녀인 경우\n다자녀가구(3자녀) 0.7%p 부부 합산 연소득 1억원 이하로 신청일 기준 채무자(배우자)의 19세 미만 자녀가 3자녀인 경우\n사회적배려\n(최대 2가지 중복적용가능) 한부모가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 한부모가구 요건을 충족하는 경우\n장애인가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 장애인가구 요건을 충족하는 경우\n다문화가구 0.7%p 부부 합산 연소득 7천만원 이하로 신청일 기준 다문화가구 요건을 충족하는 경우\n그 외 저소득청년 0.1%p 부부 합산 연소득 7천만원 이하로 채무자가 신청일 기준 만 39세 이하인 경우\n녹색건축물 0.1%p 신청일 기준 담보주택에 대해 「건축법」 및 「녹색건축 인증에 관한 규칙」에 따라 지정된 인증기관에서\n1·2등급(최우수·우수)의 “녹색건축 (예비)인증서”를 받은 경우 인증기관은 녹색건축인증제 G-SEED통합운영시스템(www.gseed.or.kr)에서 확인 가능하며\n국토안전관리원, 크레비즈인증원, 한국부동산원 등이 해당\n미분양주택입주자 0.2%p 신청일 기준 무주택자(일시적 2 주택자 허용하지 않음)로서 부부 합산 연소득 7천만원 이하이고\n구입 용도이여야 하며 채무자 또는 배우자가 서울특별시·인천광역시·경기도를 제외한 미분양 관리지역*\n내 미분양 아파트(신청일 기준)의 최초 수분양자, 분양계약서상 분양계약일이 아래 중 어느 하나에 해당하는 경우\n* 미분양관리지역(국토교통부가 발표) 여부는 전산으로 별도 관리하며,\n미분양 아파트 여부는 채무자 또는 배우자의 분양계약 체결 시기로 판단\n(ⅰ) 공부상 (임시)사용 승인일 또는 소유권보존등기일 이후\n(ⅱ) 입주자 모집공고 상 최초 공급계약 체결일로부터 60일 경과 시\n전세사기피해자 1.0%p 주택금융공사 업무처리기준, 전세사기피해자 보금자리론을 적용하는 경우\n※ 기존 가입고객에 대해서는 적용되지 않습니다.\n변경 전\n용어 정의\n신혼가구 혼인관계증명서상 혼인신고일이 신청일로부터 7년 이내인 가구(결혼예정 가구* 포함)\n* 청첩장 또는 예식장계약서 상 결혼예정일이 신청일로부터 3개월 이내인 가구 또는 “결혼예정사실 확인서”(별지24)를 제출하여 신청일로부터 3개월 이내 결혼예정임을 입증하는 가구\n장애인가구 신청인 또는 배우자, 신청인(배우자 포함)의 직계존비속 중 1인 이상이 장애인증명서, 국가유공자확인원(전상군경, 공상군경, 4·19혁명부상자, 공상공무원, 국가사회발전 특별공로상이자, 6·18자유상이자, 전투종사군무원상이자) 발급대상인 가구 또는 국가보훈부로부터 지원공상군경, 재해부상군경, 재해부상공무원, 지원공상공무원임을 확인받은 가구(공공데이터로 확인되는 경우 포함). 다만, 직계존비속(미성년자인 직계비속 제외)은 신청일 현재 주민등록등본 상 신청인(배우자 포함)과 동일세대 구성기간이 계속해서 1년 이상(합가일 기준)인 경우에 한함\n※ 장애인 본인(배우자가 장애인일 경우 배우자, 제3자가 장애인일 경우 제3자)에 대하여 “개인(민감)정보 수집·이용·조회·제공 동의서(선택적 동의)”(별지23)를 반드시 징구\n2자녀가구 신청인(배우자)의 만 19세 미만 자녀가 2인(세대분리된 자녀 포함)인 가구\n다자녀가구 신청인(배우자)의 만 19세 미만 자녀가 3인 이상(세대분리된 자녀 포함)인 가구\n변경 후\n용어 정의\n신혼가구 혼인관계증명서상 혼인신고일이 신청일로부터 7년 이내인 가구(결혼예정 가구* 포함)\n* 청첩장 또는 예식장계약서 상 결혼예정일이 신청일로부터 3개월 이내인 가구 또는 “결혼예정사실 확인서”(서식17)를 제출하여 신청일로부터 3개월 이내 결혼예정임을 입증하는 가구\n장애인가구 신청인 또는 배우자, 신청인(배우자 포함)의 직계존비속 중 1인 이상이 장애인증명서, 국가유공자확인원(전상군경, 공상군경, 4·19혁명부상자, 공상공무원, 국가사회발전 특별공로상이자, 6·18자유상이자, 전투종사군무원상이자) 발급대상인 가구 또는 국가보훈부로부터 지원공상군경, 재해부상군경, 재해부상공무원, 지원공상공무원임을 확인받은 가구(공공데이터로 확인되는 경우 포함). 다만, 직계존비속(미성년자인 직계비속 제외)은 신청일 현재 주민등록등본 상 신청인(배우자 포함)과 동일세대 구성기간이 계속해서 1년 이상(합가일 기준)인 경우에 한함\n※ 장애인 본인(배우자가 장애인일 경우 배우자, 제3자가 장애인일 경우 제3자)에 대하여 “개인(민감)정보 수집·이용·조회·제공 동의서(필수적동의)”(서식22)를 반드시 징구\n소상공인 신청인 또는 배우자가 「소상공인기본법」제2조에 해당하는 소상공인(법인사업자 제외)으로서 소상공인확인서를 발급 받은 경우\n다자녀가구 신청인(배우자)의 만 19세 미만 자녀가 2인(세대분리된 자녀 포함)인 가구\n※ 기존 가입고객에 대해서는 적용되지 않습니다.\n<소득요건>\n변경 전\n부부 합산 연소득 70백만원 이하\n- 단, 아래의 경우 해당 소득 금액 이내로 적용\n구분 신혼가구 1자녀가구 2자녀가구 다자녀가구 전세사기피해자등\n소득기준 85백만원 80백만원 90백만원 1억원 없음\n변경 후\n부부 합산 연소득 70백만원 이하\n- 단, 아래의 경우 해당 소득 금액 이내로 적용\n구분 신혼가구 1자녀가구 다자녀가구 전세사기피해자등\n소득기준 85백만원 90백만원 1억원 없음\n※ 기존 가입고객에 대해서는 적용되지 않습니다.\n<중도상환 해약금>\n변경 전\n\n3년 이내에 조기(중도)상환된 원금에 대하여 대출실행일로부터 경과일수별로 0.7% 한도 내에서 부과\n(1) 조기(중도)상환수수료 = 조기(중도)상환원금×조기(중도)상환수수료율(0.7%) × [(3년-대출경과일수)/3년]\n(2) 신청일 기준 사회적배려층 우대금리 적용 대상자의 경우 조기(중도)상환수수료 면제(‘24.1.30. 이후 신청접수건에 한함)\n변경 후\n\n3년 이내에 조기(중도)상환된 원금에 대하여 대출실행일로부터 경과일수별로 0.5% 한도 내에서 부과\n(1) 조기(중도)상환수수료 = 조기(중도)상환원금×조기(중도)상환수수료율(0.5%) × [(3년-대출경과일수)/3년]\n(2) 신청일 기준 사회적배려층·다자녀가구(3자녀이상) 우대금리 적용 대상자의 경우 조기(중도)상환수수료 면제(‘24.1.30. 이후 신청접수건에 한함)\n※ 기존 가입고객에 대해서는 적용되지 않습니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (267, 4, '내집마련 디딤돌 대출', '{"상품특징":"무주택 국민의 주거안정 도모 및 주거생활 향상을 목적으로 저금리로 주택 구입자금을 지원하는 상품입니다.","대출대상":"세대주를 포함한 세대원 전원이 대출신청일 현재 무주택자로 대출신청인과 배우자의 합산 총연소득 연간 6천만원(생애최초 구입자, 다자녀 가구, 2자녀 가구는 연간 7천만원, 신혼가구는 연간 8.5천만원, 청년주택드림 디딤돌대출 신청자중 미혼은 연간 7천만원·신혼가구는 연간 1억원)이하이며 다음 한가지에 해당하는 자.\n대출 신청일 현재 민법상 성년인 세대주(아래 어느 하나에 해당하는 경우 제외)\n① 만 30세 미만 단독세대주. (단, 민법상 미성년인 형제·자매 중 1인 이상과 동일세대를 구성하고 주민등록등본상 합가일\n기준 부양기간이 계속해서 6개월 이상인 경우 가능)\n② 만 30세 미만 미혼세대주. (단, 직계존속 중 1인 이상과 동일세대를 구성하고 주민등록등본상 합가일 기준 부양기간이\n계속해서 6개월 이상인 경우 가능)\n세대주의 세대원인 배우자\n대출신청일 현재 3개월 이내에 결혼으로 세대주가 예정된 자\n만 30세 이상의 미혼 단독 세대주(단, 차주의 주민등록등본상 직계존속 또는 미성년 형제·자매 중 1인과의 합가일 기준 부양기간이 계속 해서 6개월 미만인 경우 포함) (청년주택드림 디딤돌대출 신청자는 적용 제외)\n유한책임대출은 부부합산 연소득 6천만원 이하인 자(생애최초주택구입자 또는 생애최초로 주택을 구입하는 신혼가구는 8.5천만원, 청년주택드림 디딤돌대출 신청자중 미혼은 연간 7천만원·신혼가구는 연간 1억)를 대상으로 취급한다.\n※ 유한책임대출 : 대출상환책임을 담보물(해당주택)에만 한정하는 대출(허위나 그 밖의 부정한 방법 등에 의한 경우 제외)로 채무불이행시 담보물 외에 추가상환요구가 불가능한 대출을 말한다.\n대출신청인 및 배우자의 합산 순자산 가액이 통계청에서 발표하는 최근년도 가계금융복지조사의 ‘소득 5분위별 자산 및\n부채현황’ 중 소득 4분위 전체가구 평균값 이하(십만원 단위에서 반올림)인 자.(순자산가액기준, 4.88억원)\n(신생아 특례대출)대출신청인과 배우자의 합산 총소득이 1.3억원 (대출신청인이 혼인신고를 하지 않은 경우, 신생아의 가족관계증명서상 등재된 부모의 합산 총소득을 심사) 다만, 대출신청인과 배우자가 모두 소득이 있는경우 2억원이하(부부중 1인의 소득이 1.3억원 이하 충족)인 자로 아래 요건 충족\n대출접수일 기준 2년 이내 출산한 신생아가 있는 자 (단 ’23.1.1일 출생아부터 적용) 다만, 신생아 특별공급 또는 우선공급 당첨자인 경우, 대출신청일 현재 자녀가 만 2세 초과시에도 가능\n입양아 포함(2살 이하, 단 ‘23.1.1일 출생아부터 적용) 가능\n임신 중인 태아 불가\n혼인신고 없어도 대출 가능\n(청년주택드림) 드림통장으로 청약당첨된 무주택세대주(당첨시 만39세이하, 가입(전환)일로부터 1년 이상 경과 및 1천만원 이상 납입 실적 보유 필요)\n기금의 구입 또는 전세자금 대출 이용 중 주택도시기금 대출거래약정서를 위반하여 기한의 이익이 상실된 이력이 없는 자(다만, 신청일 현재 해당 기한이익상실일로부터 3년이 경과된 자는 가능)","대상주택":"주거전용면적 85㎡(수도권을 제외한 도시지역이 아닌 읍 또는 면 지역은 100㎡)이하의 준공된 주택\n대출신청일 현재 담보주택의 평가액이 5억원 이하의 주택(다만, 신혼가구·2자녀 이상·청년주택드림 가구 6억원 이하의 주택)\n단, 만 30세 이상의 미혼 단독 세대주(차주의 주민등록등본상 직계존속 또는 미성년 형제·자매 중\n1인과의 합가일 기준 부양기간이 계속 해서 6개월 미만인 경우 포함)의 경우 아래 ①,② 적용\n① 주거전용면적 60m²(수도권을 제외한 도시지역이 아닌 읍 또는 면 지역은 70m²)이하의 준공된 주택\n② 대출신청일 현재 담보주택의 평가액이 3억원 이하의 주택\n(신생아 특례) 주택가격 9억원 이하","대출금리":"(2025.03.24 현재, 연이율)\n부부연간 합산소득 만기별금리\n10년 15년 20년 30년\n2천만원 이하 2.85% 2.95% 3.05% 3.10%\n2천만원 초과 ~ 4천만원 이하 3.20% 3.30% 3.40% 3.45%\n4천만원 초과 ~ 7천만원 이하 3.35% 3.65% 3.75% 3.80%\n7천만원 초과 ~ 8.5천만원 이하\n(신혼) 3.90% 4.00% 4.10% 4.15%\n※ 국토교통부 고시에 따라 변경될 수 있습니다.\n※ 대출 대상주택이 지방소재인 경우 0.2% 인하\n우대금리 (③,④,⑤,⑥,⑦ 항목 중복 가능)\n다자녀 연0.7%, 2자녀가구 연0.5%, 1자녀가구 연 0.3%\n(다만, 2025.03.24 신규접수분부터 자녀 1명당 5년간 적용)\n생애최초주택구입, 다문화, 장애인가구, 신혼가구(결혼예정자 포함) : 연 0.2%(신혼가구 및 생애최초 주택구입자 우대금리는 대출실행일로부터 5년간 적용)\n한부모가구(연소득6천만원이하) : 연 0.5% (택 1)\n주택청약(종합)저축 가입자(본인 또는 배우자): 연 0.3%(가입기간이 5년 이상이고, 60회차 이상 납입), 연 0.4%(가입기간이 10년이상이고, 120회차 이상 납입), 연 0.5%(가입기간이 15년 이상이고, 180회차 이상 납입)(대출실행일로부터 최대 5년간 적용)\n(청약예금, 청약부금, 청약저축을 주택청약종합저축 또는 청년 주택드림 청약통장으로 전환 가입하는 경우 기존 청약(종합)저축, 청약예금, 청약부금의 납입기간 및 회차를 포함.\n단, 대출 신청일로부터 6개월 이내 일괄 납부된 경우 우대금리 회차 인정대상에서 제외하고 선납은 포함)\n-민영주택 청약별 최소 예치금액 납입 후 5년/10년/15년 이상 : 연 0.3%/0.4%/0.5%\n\n※ 대출 기간 중 청약(종합)저축 해지시 금리우대 적용이 제외되며, 대출기간 중 금리 우대조건을 충족하더라도 금리우대 적용 불가\n국토부 부동산전자계약 시스템을 활용하여 주택의 매매계약을 체결한자 : 연 0.1%\n(단, ''25.12.31까지 신규접수하는 건에 대해 대출실행일로부터 5년간 한시적 적용)\n지방 준공 후 미분양주택 가구 연0.2%. 다만, 대출실행일로부터 5년간 적용\n대출 심사를 통해 산정한 대출금액의 30% 이하로 대출신청시 연0.1%(대출실행일로부터 5년간 적용)\n대출 실행일로부터 1년 경과 후 대출원금의 40% 이상 중도상환 연0.2%\n(2024.07.31 이후 중도상환분에 한함)\n※ 우대 후 최종금리가 1.5% 미만인 경우에는 1.5%를 적용\n※ 5년단위 변동금리 선택 시 0.1%, 고정금리 10년적용 시 0.2%, 순수 고정금리 0.3% 가산\n생애최초로 주택을 구입하는 신혼가구인 경우에는 상기 각호에도 불구하고 아래 표와 같이 금리를 적용\n(부부합산 연소득 8.5천만원 이하인 경우)\n(2025.03.24 현재, 연이율)\n부부연간 합산소득 만기별금리(%)\n10년 15년 20년 30년\n2천만원 이하 2.55% 2.65% 2.75% 2.80%\n2천만원 초과 ~ 4천만원 이하 2.90% 3.00% 3.10% 3.15%\n4천만원 초과 ~ 7천만원 이하 3.25% 3.35% 3.45% 3.50%\n7천만원 초과 ~ 8.5천만원 이하 3.60% 3.70% 3.80% 3.85%\n※ 국토교통부 고시에 따라 변경될 수 있습니다.\n※ 대출 대상주택이 지방소재인 경우 0.2% 인하\n우대금리 (②,③,④,⑤,⑥ 항목 중복 가능)\n다자녀 연0.7%, 2자녀가구 연0.5%, 1자녀가구 연 0.3%\n다만, 2025.03.24 신규접수분부터 자녀 1명당 5년간 적용\n주택청약(종합)저축 가입자(본인 또는 배우자): 연 0.3%(가입기간이 5년 이상이고, 60회차 이상 납입), 연 0.4%(가입기간이 10년이상이고, 120회차 이상 납입), 연 0.5%(가입기간이 15년 이상이고, 180회차 이상 납입)(대출실행일로부터 최대 5년간 적용)\n(청약예금, 청약부금, 청약저축을 주택청약종합저축 또는 청년 주택드림 청약통장으로 전환 가입하는 경우 기존 청약(종합)저축, 청약예금, 청약부금의 납입기간 및 회차를 포함.\n단, 대출 신청일로부터 6개월 이내 일괄 납부된 경우 우대금리 회차 인정대상에서 제외하고 선납은 포함)\n-민영주택 청약별 최소 예치금액 납입 후 5년/10년/15년 이상 : 연 0.3%/0.4%/0.5%\n\n※ 대출 기간 중 청약(종합)저축 해지시 금리우대 적용이 제외되며, 대출기간 중 금리 우대조건을 충족하더라도 금리우대 적용 불가\n국토부 부동산전자계약 시스템을 활용하여 주택의 매매계약을 체결한자 : 연 0.1%\n(단, ''25.12.31까지 신규접수하는 건에 대해 대출실행일로부터 5년간 한시적 적용)\n지방 준공 후 미분양주택 가구 연0.2%. 다만, 대출실행일로부터 5년간 적용\n대출 심사를 통해 산정한 대출금액의 30% 이하로 대출신청시 연0.1%(대출실행일로부터 5년간 적용)\n대출 실행일로부터 1년 경과 후 대출원금의 40%이상 중도상환 연0.2%\n(2024.07.31 이후 중도상환분에 한함)\n※ 생애최초 주택구입 신혼가구는 우대 후 최종금리가 1.2%미만인 경우에는 1.2% 적용\n※ 우대금리 적용상한은 0.5%(다자녀가구는 0.7%)\n신생아 특례 대출의 경우 아래 표와 같이 금리를 적용\n(특례금리 적용시) 소득·대출기간에 따라 금리 적용\n(2025.03.24 현재, 연이율)\n소득 만기\n10년 15년 20년 30년\n2천만원 이하 1.80% 1.90% 2.00% 2.05%\n4천만원 이하 2.15% 2.25% 2.35% 2.40%\n6천만원 이하 2.40% 2.50% 2.60% 2.65%\n8.5천만원 이하 2.65% 2.75% 2.85% 2.90%\n1억원 이하 2.90% 3.00% 3.10% 3.20%\n1.3억원 이하 3.20% 3.30% 3.40% 3.50%\n1.5억원 이하 3.50% 3.60% 3.70% 3.80%\n1.7억원 이하 3.85% 3.95% 4.05% 4.15%\n2억원 이하 4.20% 4.30% 4.40% 4.50%\n(특례금리 종료후) 8.5천만원 이하는 기존 특례금리에서 0.75%p~0.95%p 가산, 8.5천만원 초과는 대출시점의 시중 금리 중 최저치 적용. 단, 연소득 1.3억원 이하인 경우 8.5천만원 이하 특례금리 종료 후 금리의 최고금리, 연소득 1.3억원 초과인 경우 기 적용 특례금리에 0.2% 가산한 금리를 하한\n* 예금은행 주택담보대출 평균금리(한국은행이 매월 고시), 시중은행 주택담보대출 최저금리 (은행연합회가 매월 고시)를 비교하여 작은값으로 선정\n* 대출대상주택이 지방 소재인 경우 0.2% 인하\n(우대금리) ①,②,③,④,⑤ 중복 가능 , 특례금리 종료 후에도 금리인하 유지\n① 기존자녀 * ② 추가 출산 ③\n청약가입 지방 준공 후 미분양주택 가구 전자계약매매\n0.1%p (1명당)\n(적용일로부터 자녀 1명당 5년간적용 –최장 15년적용) 0.2%p (1명당)\n(대출실행일로부터 5년간 적용) 0.3~0.5%p** 0.2%p 0.1%p\n(대출실행일로부터 5년간 적용)\n* 대출신청일 기준, 출생 후 24개월 초과\n** 청약(종합)저축통장 가입 5년 이상 0.3%, 10년이상 0.4%, 15년이상 0.5%(대출실행일로부터 최대 5년간 적용)\n대출 심사를 통해 산정한 대출금액의 30% 이하로 대출신청시 연0.1%(대출실행일로부터 5년간 적용)\n대출 실행일로부터 1년 경과 후 대출원금의 40%이상 중도상환 연0.2%\n(2024.07.31 이후 중도상환분에 한함)\n※ 우대금리 적용 후 최종금리가 연1.2%미만일 경우 연1.2%를 적용\n※ 우대금리 적용상한은 0.5%\n청년주택드림 대출의 경우 아래 표와 같이 금리를 적용\n(2025.04.18 현재, 연이율)\n연소득 만기\n10년 15년 20년 30년\n2천만원 이하 2.40% 2.45% 2.55% 2.70%\n4천만원 이하 2.80% 2.85% 2.95% 3.10%\n7천만원 이하 3.20% 3.25% 3.35% 3.50%\n8.5천만원 이하 3.55% 3.60% 3.70% 3.85%\n1억원 이하 3.90% 3.95% 4.05% 4.15%\n* 대출대상주택이 지방 소재인 경우 0.2% 인하\n** 5년 단위 변동금리 선택 시 0.1%, 고정금리 10년 적용 시 0.2%, 순수 고정금리 0.3%가산\n*** 4천만원 이하는 만기 40년 이용 가능하되 30년 금리에서 0.1% 금리가산\n우대금리(①,② 항목 중복 가능)\n대출실행일 이후 결혼한 경우 0.1%(적용일로부터 5년간 적용)\n대출실행일 이후 최초 출산한 경우0.5%, 추가 출산한 경우 자녀 1명당 0.2% 추가우대(출산 우대금리는 자녀 1명당 5년간 적용, 최대 15년 가능)\n※ 우대금리 적용 후 최종금리가 연1.5%미만일 경우 연1.5% 적용\n※ 우대금리 적용 상한은 1.0%\n자산기준 초과 시 가산금리\n대출을 받은 후 자산심사 결과에 따라 본인 및 배우자의 합산 자산기준 초과된 금액이 1천만원 이내인 경우 신규취급된 금리에 연0.2% 가산하며 1천만원 초과인 경우 신규 취급된 금리에 연 3.0% 가산하여 금리 적용\n[자산심사 기준은 국토교통부 주택도시기금포털( http://nhuf.molit.go.kr)에서 확인 가능]\n연체금리\n대출이자를 납부하여야 하는 날에 납입하지 않거나 대출계약이 종료되는 날에 대출금을 상환하지 않으면 원래 약정한 이자에 가산되는 금리\n연체금리는 대출이자율에 연체가산금리를 더하여 최고 연 10% 이내로 적용\n연체가산금리는 연체기간에 따라 차등적용\n- 연체기간이 3개월 이내: 당초 약정이자율 + 연 4%\n- 연체기간이 3개월 초과: 당초 약정이자율 + 연 5%","대출한도":"유효 담보가액 범위내에서 최고 2억원 이내(10만원 단위로 취급)\n단, 만 30세 이상의 미혼 단독 세대주(차주의 주민등록등본상 직계존속 또는 미성년 형제·자매 중 1인과의 합가일 기준\n부양기간이 계속 해서 6개월 미만인 경우 포함)의 경우 유효 담보가액 범위내에서 최고 2억원 이내(10만원 단위로 취급)\n생애최초 주택구입자: 2.4억원 이내\n신혼 부부: 3.2억원 이내\n2자녀 이상: 3.2억원 이내\n신생아 특례 : 4억원이내 (DTI 60%)","대출기간":"10년, 15년, 20년, 30년이며 거치기간은 비거치 또는 1년","대출신청시기":"소유권이전등기 전에 신청\n다만, 소유권이전등기를 한 경우에는 이전등기 접수일로부터 3개월이내 신청","담보":"1순위 근저당권 설정을 원칙으로 하며 110% 설정","상환방식":"원(리)금 균등분할상환, 체증식 분할상환(채무자가 접수일현재 만 40세미만 근로자이고 고정금리를 선택한 경우에만 허용)","대출조건":"대출취급 시 차주는 대출 실행일로부터 1개월 이내에 본건 담보주택에 전입을 완료하여 전입세대열람표를 은행에 제출하고, 전입한 날로부터 2년까지 담보주택에 거주","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이자선납 제한":"※ 본 상품은 국토교통부 ''주택도시기금 대출 업무 시행세칙''에 따라 , 대출실행 후 3개월 동안 이자선납이 제한됩니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환수수료는 중도상환대출금액×중도상환수수료율(1.2%)×(중도상환약정잔여일수÷중도상환약정기간)으로 하며, 최초대출일로부터 3년까지 적용합니다.\n아래의 경우에는 중도상환수수료 면제합니다.\n담보주택의 공용수용, 채무자의 사망, 천재지변으로 인한 중도상환의 경우\n연체, 경매 등으로 기한의이익을 상실시킨 경우\n중도상환수수료 한시적 면제 (24.08.12~25.12.31까지)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n근저당권 설정시 : 국민주택채권 매입비용 고객부담(근저당권 말소 또는 채권최고액 감액시 비용 고객부담)\n모기지신용보증료 : 한국주택금융공사 보증서(MCG) 이용시 보증료는 고객이 부담","필요서류":"매매(분양)계약서-주민등록등본(1개월이내 발급분)\n건물,토지 등기부등본(1개월이내 발급분)\n등기권리증 및 인감증명서 2통, 인감도장, 신분증\n소득확인자료\n근로자 : 건강보험자격득실확인서, 재직증명서, 소득금액증명원, 근로소득원천징수영수증 또는 급여명세서\n서민(자영업자 포함) : 사업자등록증, 소득금액증명원 또는 사업소득원천징수영수증(연말정산용)\n신생아특례 : 출산한 자녀의 경우 출생신고서, 입양한 자녀의 경우 입양관계증명서 또는 친양자 입양관계증명서\n청년주택드림 : 청약당첨사실확인서, 청약통장 거래내역서 등 청년주택드림 디딤돌 대출 청약요건 확인가능서류","신청절차":"1. 대출상담 및 대출가능 금액 산정\n대출대상자, 대상주택, 대출조건 등 적격여부 상담\n상품별 기준에 의한 대출가능금액 산정\n2. 대출신청 및 서류제출\n주택도시기금 내집마련 디딤돌 대출 신청하기기금E든든 바로가기 >\n주택금융공사 내집마련 디딤돌 대출 신청하기주택금융공사 바로가기 >\n매매계약서, 건물(토지)등기부등본, 인감증명서, 등기권리증, 주민등록등본 등 관련서류 제출\n3. 무주택 및 소득확인\n국토교통부, 국세청에 의뢰(은행)\n4. 대출심사\n신용조사 및 담보주택에 대한 실태조사(은행)\n대출 가·부 통보(은행)\n5. 대출약정 및 담보제공\n대출금액, 대출기간 및 대출이율 등 대출조건 약정\n담보주택에 대한 근저당권 설정\n6. 대출실행\n은행에서 대출을 실행하여 입금\n※ 한국주택금융공사로 직접 신청시에는 1~5번까지는 한국주택금융공사에서 이뤄진 다음 대출실행만 해당 은행에서 가능합니다.\n철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"본인포함 세대원전원 중 당, 타행 기금 지원자금 대출 있는 경우 취급 불가\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 만기에 원금을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익을 받을 수 있습니다.\n자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n본 상품은 금리인하요구권 적용 비대상 상품입니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n본 상품은 위법계약해지권 적용 비대상 상품입니다.\n대출금리는 국토교통부 고시금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.07.23 변경)\n■ 기존 가입 고객에 대한 적용 여부 : 미적용\n<대출한도>\n변경 전:\n유효 담보가액 범위내에서 최고 2.5억원이내(10만원 단위로 취급)\n생애최초 주택구입자 : 3억원 이내\n신혼부부 : 4억원 이내\n2자녀이상 : 4억원 이내\n신생아 특례 : 5억원 이내(DTI 60%)\n변경 후:\n유효 담보가액 범위내에서 최고 2억원이내(10만원 단위로 취급)\n생애최초 주택구입자 : 2.4억원 이내\n신혼부부 : 3.2억원 이내\n2자녀이상 : 3.2억원 이내\n신생아 특례 : 4억원 이내(DTI 60%)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (268, 4, '내집마련디딤돌대출(전세사기피해자전용)', '{"대출대상":"세대주를 포함한 세대원 전원이 대출신청일 현재 무주택자로 대출신청인과 배우자의 합산 총연소득 연간 7천만원 이하인 자\n대출 신청일 현재 민법상 성년인 세대주\n「전세사기피해자 지원 및 주거안정에 관한 특별법」 제2조 제3호 또는 제4호 다목에 해당하여 전세사기피해자 등으로 결정된 자\n대출신청인 및 배우자의 합산 순자산 가액이 통계청에서 발표하는 최근년도 가계금융복지조사의 ‘소득 5분위별 자산 및 부채현황’ 중 소득 4분위 전체가구 평균값 이하(십만원 단위에서 반올림)인 자.(2024년 기준, 4.88억원)","대상주택":"임차전용면적 85㎡ (수도권을 제외한 도시지역이 아닌 읍 또는 면 지역은 100㎡) 이하의 주택 중 대출신청일 현재 담보주택의 평가액이 5억원 이하의 주택","대출금리":"(2025.03.24 기준)\n부부합산\n연소득 대출기간\n10년 15년 20년 30년\n2천만원 이하 1.85% 1.95% 2.05% 2.1%\n2천만원 초과\n4천만원 이하 2.2% 2.3% 2.4% 2.45%\n4천만원 초과\n7천만원 이하 2.45% 2.55% 2.65% 2.7%\n※ 국토교통부 고시에 따라 변경될 수 있습니다.\n우대금리 (③,④,⑤,⑥,⑦ 항목 중복 가능)\n다자녀 연0.7%, 2자녀가구 연0.5%, 1자녀가구 연 0.3%\n다만, 2025.03.24 신규접수분부터 자녀 1명당 5년간 적용\n택1: \n생애최초주택구입 0.2%, 다문화 0.2%, 장애인가구 0.2%, 신혼가구 0.2%, 한부모가구(연소득6천만원이하) 0.5% (신혼가구 및 생애취초 주택구입자 우대금리는 대출실행일로부터 5년간 적용)\n주택청약(종합)저축 가입자(본인 또는 배우자)\n1) 가입기간 및 납입회차 기준(대출실행일로부터 최대 5년간 적용)\n가입기간 5년 이상 및 납입 60회차 이상: 연 0.3%\n가입기간 10년 이상 및 납입 120회차 이상: 연 0.4%\n가입기간 15년 이상 및 납입 180회차 이상: 연 0.5%\n2) 민영주택 청약 지역별 최소 예치금액 납입 완료일 기준\n최소 예치금액 납입 후 5년 이상 0.3%, 10년 이상 0.4%, 15년 이상 0.5%\n3) 유의사항\n대출 기간 중 청약(종합)저축 해지시 금리우대 적용이 제외되며, 대출기간 중 금리 우대조건을 충족하더라도 금리우대 적용 불가.\n신규 분양아파트(담보목적물) 당첨에 따라 해지된 계좌도 포함\n청약예금, 청약부금, 청약저축을 주택청약종합저축 또는 청년 주택드림 청약통장으로 전환 가입하는 경우 기존 청약(종합)저축, 청약예금, 청약부금의 납입기간 및 회차를 포함.\n대출신청일 이전 6개월 이내 연체납입 회차에 대해 일괄 납부한 경우 우대금리 회차 인정대상에서 제외. 단, 미도래 약정납입 회차에 대해 선납한 경우 우대금리 회차 인정대상에 포함\n국토부 부동산전자계약 시스템을 활용하여 주택의 매매계약을 체결한 자: 연 0.1%\n(단, ’25.12.31까지 신규접수하는 건에 대해 대출실행일로부터 5년간 한시적 적용\n지방 준공 후 미분양주택 가구 연0.2%. 다만, 대출실행일로부터 5년간 적용\n대출 심사를 통해 산정한 대출금액의 30% 이하로 대출신청시 연0.1%(대출실행일로부터 5년간 적용)\n대출 실행일로부터 1년 경과 후 대출원금의 40% 이상 중도상환시 연 0.2%\n(2024.07.31 이후 중도상환분에 한함)\n※ 우대 후 최종금리가 1.2% 미만인 경우에는 1.2%를 적용\n* 대출대상주택이 지방 소재인 경우 0.2% 인하\n** 5년 단위 변동금리 선택 시 0.1%, 고정금리 10년 적용 시 0.2%, 순수 고정금리 0.3% 가산","대출한도":"유효 담보가액 범위내에서 최고 4억원 이내\n경매.공매 : LTV100% 이내(단, 주택평가액과 낙찰가, 경공매 담당기관 최초 감정평가금액 중 가장 낮은 금액 이내)\n일반매매 : LTV80% 이내","대출기간":"10년,15년,20년,30년이며 거치기간은 비거치 또는 3년 이내 (연단위)","대출신청시기":"소유권이전등기 전에 신청\n다만, 소유권이전등기를 한 경우에는 이전등기 접수일로부터 3개월 이내 신청","담보":"1순위 근저당권 설정을 원칙으로 하여 대출금액의 110% 설정","상환방식":"원(리)금 균등분할상환, 체증식 분할상환(채무자가 접수일 현재 만 40세 미만 근로자이고 고정금리를 선택한 경우에만 허용)","중도상환\n해약금":"중도상환수수료는 중도상환대출금액×중도상환수수료율(1.2%)×(중도상환약정잔여일수÷중도상환약정기간)으로 하며, 최초대출일로부터 3년까지 적용합니다.\n아래의 경우에는 중도상환수수료 면제합니다.\n담보주택의 공용수용, 채무자의 사망, 천재지변으로 인한 중도상환의 경우\n연체, 경매 등으로 기한의이익을 상실시킨 경우","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원","필요서류":"매매계약서\n주민등록등본(1개월 이내 발급된 서류만 인정)\n재직 및 소득 입증서류(본인 및 배우자)\n건강보험자격득실확인서, 재직증명서, 사업자등록증, 소득금액증명원, 연말정산용 원천징수 영수증 등\n인감증명서, 인감도장\n전세사기피해가 등 결정문 또는 경공매 통지서 및 배당표 등","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 최초 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","유의사항":"본인포함 세대원전원 중 당, 타행 기금 지원자금 대출 있는 경우 취급 불가\n손님의 신용도에 따라 대출취급이 제한될 수 있습니다.\n대출원리금 납입이 지연되거나 만기에 원금을 상환하지 않을 경우 연체이자가 부과되며 재산 및 신용상 불이익을 받을 수 있습니다.\n자산기준 초과 시 가산금리 적용자는 기한연장 및 기타 조건변경 불가합니다.\n본 상품은 금리인하요구권 적용 비대상 상품입니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어보시기 바랍니다.\n본 상품은 위법계약해지권 적용 비대상 상품입니다.\n대출금리는 국토교통부 고시금리 변동 시 변경될 수 있습니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생 할 수 있으니 주의하시기 바랍니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.04.10 변경)\n■ 기존 가입 고객에 대한 적용 여부 : 적용\n<우대금리 적용기준 변경>\n변경 전 :\n② 생애최초주택구입 0.2%, 다문화 0.2%, 장애인가구 0.2%, 신혼가구 0.2%, 한부모가구(연소득6천만원이하) 0.5%\n③ 주택청약(종합)저축 가입자(본인 또는 배우자)\n1) 가입기간 및 납입회차 기준\n가입기간 5년 이상 및 납입 60회차 이상: 연 0.3%\n가입기간 10년 이상 및 납입 120회차 이상: 연 0.4%\n가입기간 15년 이상 및 납입 180회차 이상: 연 0.5%\n2) 민영주택 청약 지역별 최소 예치금액 납입 완료일 기준\n최소 예치금액 납입 후 5년 이상 0.3%, 10년 이상 0.4%, 15년 이상 0.5%\n3) 유의사항\n대출 기간 중 청약(종합)저축 해지시 금리우대 적용이 제외되며, 대출기간 중 금리 우대조건을 충족하더라도 금리우대 적용 불가.\n신규 분양아파트(담보목적물) 당첨에 따라 해지된 계좌도 포함\n청약예금, 청약부금, 청약저축을 주택청약종합저축 또는 청년 주택드림 청약통장으로 전환 가입하는 경우 기존 청약(종합)저축, 청약예금, 청약부금의 납입기간 및 회차를 포함.\n대출신청일 이전 6개월 이내 연체납입 회차에 대해 일괄 납부한 경우 우대금리 회차 인정대상에서 제외. 단, 미도래 약정납입 회차에 대해 선납한 경우 우대금리 회차 인정대상에 포함\n⑥ 대출 심사를 통해 산정한 대출금액의 30% 이하로 대출신청시 연0.1%\n변경 후 :\n② 생애최초주택구입 0.2%, 다문화 0.2%, 장애인가구 0.2%, 신혼가구 0.2%, 한부모가구(연소득6천만원이하) 0.5%(신혼가구 및 생애취초 주택구입자 우대금리는 대출실행일로부터 5년간 적용)\n③ 주택청약(종합)저축 가입자(본인 또는 배우자)\n1) 가입기간 및 납입회차 기준(대출실행일로부터 최대 5년간 적용)\n가입기간 5년 이상 및 납입 60회차 이상: 연 0.3%\n가입기간 10년 이상 및 납입 120회차 이상: 연 0.4%\n가입기간 15년 이상 및 납입 180회차 이상: 연 0.5%\n2) 민영주택 청약 지역별 최소 예치금액 납입 완료일 기준\n최소 예치금액 납입 후 5년 이상 0.3%, 10년 이상 0.4%, 15년 이상 0.5%\n3) 유의사항\n대출 기간 중 청약(종합)저축 해지시 금리우대 적용이 제외되며, 대출기간 중 금리 우대조건을 충족하더라도 금리우대 적용 불가.\n신규 분양아파트(담보목적물) 당첨에 따라 해지된 계좌도 포함\n청약예금, 청약부금, 청약저축을 주택청약종합저축 또는 청년 주택드림 청약통장으로 전환 가입하는 경우 기존 청약(종합)저축, 청약예금, 청약부금의 납입기간 및 회차를 포함.\n대출신청일 이전 6개월 이내 연체납입 회차에 대해 일괄 납부한 경우 우대금리 회차 인정대상에서 제외. 단, 미도래 약정납입 회차에 대해 선납한 경우 우대금리 회차 인정대상에 포함\n⑥ 대출 심사를 통해 산정한 대출금액의 30% 이하로 대출신청시 연0.1%(대출실행일로부터 5년간 적용)","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (269, 4, '하나 부분분할상환 전세론', '{"상품별, 일자별 금리 조회":"금리안내","상품특징":"무주택자에 대한 자산형성 및 가계부채 감축을 위하여, 대출기간 중 대출원금의 5%의\n이상을 상환하기로 약정하는 한국주택금융공사 보증서 담보의 전세자금대출 상품입니다.","대출대상":"주택임대차계약을 체결한 국민인 거주자로 아래의 조건을 모두 충족하는 손님\n- 임차보증금액주)이 7억원(수도권 외 5억원) 초과하는 경우 대출 불가\n주)월세가 있는 경우 월세보증금 및 월세에 대해 전월세전환율을 적용하여 계산한 금액\n(월세보증금+(월세X12÷전월세전환율))\n대출기간 중 원금의 5% 이상을 분할상환하기로 약정한 자\n임차보증금의 5% 이상을 지급한 만 19세 이상의 세대주\n세대주와 동일세대를 이루고 있는 세대원 중 다음 하나에 해당하는 자\n(단, 피보증인이 신혼부부 또는 결혼예정자 자격을 충족해야함)\n배우자\n직계존비속 및 그의 배우자\n신청인 및 배우자의 형제자매\n배우자의 직계존비속 및 그의 배우자\n본인과 배우자(결혼예정자 포함)의 합산 주택보유수가 무주택인 자","대상주택":"공부상(등기부등본 등) 주거용 주택(미등기 주택도 가능)","대출한도":"최대 2.2억원 범위 내 아래 ①과 ②중 적은금액으로 함\n임차(전세)보증금의 80% 이내\n신청인(배우자포함)의 연간소득의 최대 4.5배 이내\n* 상환방식별 우대금액 적용 (20% 가산)","대출기간":"전세계약만기일 범위 내 최장 2년(최대 10년까지)\n※대출 만기시 자동연장 되지 않으며, 임대차계약내용 변경(종료) 및 신용도에 따라 연장이 제한될 수 있으므로 반드시\n만기 1개월 전에 대출받은 영업점으로 사전 상담하시기 바랍니다.","대출신청시기":"신규 : 임대차계약서상 잔금지급일과 주민등록전입일 중 빠른날로부터 3개월 이내\n갱신 : 주민등록전입일로부터 3개월 이상 경과하고 계약갱신일로부터 3개월 이내(계약 갱신일 이전에도 보증신청 가능)\n단, 원금상환이 어려워 ''하나 부분분할상환 전세론(대환전용)'' 상품으로 대환할 경우에는 보증신청시기 적용하지 않음. (1회에 한함)","담보":"주택금융신용보증서 담보 (100% 보증, 단, ''하나 부분분할상환(대환전용)''은 90% 보증)","상환방식 및 이자납입":"원금균등분할상환, 이자 매월 후취\n대출기간 중 상환방식 변경불가\n단, 원금상환이 어려운 경우에는 1회에 한하여* ''하나 부분분할상환 전세론(대환전용)으로 대환가능.\n*부분분할상환 전세론을 재이용하더라도 대환에 따른 특례 적용 불가","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)","원금 또는\n이자의\n상환시기 및\n방법":"분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n대출원금의 5% 이상 미상환된 경우 기한연장 불가. 연장시 잔여대출금의 5% 이상 분할상환 약정 필수.\n단, 이사 또는 전세계약 간 불일치 등에 따른 단기 기한연장(최대 3개월 이내*)시에는 별도의 분할상환 약정 없이도 연장 가능.\n*연속하여 단기 기한연장을 하는 경우에는 단서 사항을 인정하지 않음.\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 3개월 이내 상환 시 면제\n* 중도상환해약금률 : 0.61%\n단, 원금상환이 어려워 ‘하나 부분분할상환 전세론(대환전용)’으로 대환을 희망할 경우에는 중도상환해약금 면제(1회에 한함**)\n** 부분분할상환 전세론을 재이용하여도 중도상환해약금 면제 특례사항 적용되지 않음.","대출관련비용":"보증료(손님부담) - 대출금액의 0.02%~0.2%(한국주택금융공사 보증료 등 운용규정에 따름)\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 손님이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","소득공제":"아래의 요건에 모두 해당하는 경우에는 소득공제 혜택이 있습니다.\n무주택세대주인 근로소득자\n국민주택규모 주택\n입주 또는 전입일로부터 3개월 이내\n임대인 계좌로 입금된 임차차입금\n원리금상환액의 40% (월세 공제와 주택마련저축 공제 포함 3백만원 한도)\n※ 소득공제의 기준 및 금액은 납세자의 상황 등에 따라 다르며, 향후 관련 법률의 변경에 따라 변동될 수 있습니다.","필요서류":"주민등록등본, 확정일자부 전·월세계약서(원본), 임차보증금의 5%이상을 지급한 영수증, 임차주택의 등기부등본, 현거주지 등기부등본, 신분증, 소득 및 재직증빙 입증서류, 가족관계증명서, 금융거래확인서(타 금융기관에 신용대출 보유한 경우) 등","금리인하 요구권":"본인의 신용상태가 개선되었다고 판단되는 경우(예 : 취업, 승진, 재산증가, 개인신용평점 상승) 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하요구 수용 여부를 통지해드립니다.\n※ 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며 CSS(개인신용평가시스템) 평가결과, 은행 내부정책 등에 따라 금리인하요구가 수용되지 않을 수 있습니다.","대출계약 철회권":"자산기준을 초과하는 경우 기본약관 제4조의 2 제1항에도 불구하고 대출계약에 대하여 계약서류를 제공받은 날, 대출계약체결일, 대출실행일 또는 사후자산심사결과 부적격 확정통지일 중 늦은 날로부터 14일 이내 대출 계약 철회가 가능합니다. 그 외 사항은 은행여신거래 기본약관 제4조의2를 적용합니다.\n- 대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며 해당 대출과 관련한 대출정보가 삭제됩니다.\n- 대출계약 철회권의 효력이 발생한 이후에는 이를 취소할 수 없습니다.","위법계약해지권":"은행이「금융소비자 보호에 관한 법률」에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ''법 위반사실을 안 날''부터 1년 (계약체결일로부터 5년 이내의 범위에 있어야 합니다.) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며, 주택보유수, 규제대상 주택 취득 등 한국주택금융공사 보증규정에 부적합하거나 은행에서 정한 부적격자에 대하여 대출 취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등 에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n한국주택금융공사의 보증업무 지침 위반시 기한의 이익 상실 등 손님께 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n※ 금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n※ 자세한 사항은 상품설명서를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점에 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객에 대한 적용 여부 : 미적용\n변경 전\n중도상환해약금률 : 0.7%\n변경 후\n중도상환해약금률 : 0.61%","금리표":[],"상품설명서_경로":"","crawled_rate":{"헤더":["금리구분","금리변동주기","기준금리","가산금리","우대금리","최저~최고금리"],"데이터":[{"금리구분":"91일물 CD유통수익률","금리변동주기":"3개월","기준금리":"2.500","가산금리":"3.000","우대금리":"1.100","최저~최고금리":"4.400 ~ 5.500"},{"금리구분":"6개월물 금융채 유통수익률","금리변동주기":"6개월","기준금리":"2.516","가산금리":"2.992","우대금리":"1.100","최저~최고금리":"4.408 ~ 5.508"},{"금리구분":"1년물 금융채 유통수익률","금리변동주기":"1년","기준금리":"2.498","가산금리":"3.026","우대금리":"1.100","최저~최고금리":"4.424 ~ 5.524"},{"금리구분":"신규취급액기준 COFIX","금리변동주기":"6개월","기준금리":"2.540","가산금리":"2.968","우대금리":"1.100","최저~최고금리":"4.408 ~ 5.508"},{"금리구분":"신 잔액기준 COFIX","금리변동주기":"6개월","기준금리":"2.630","가산금리":"2.438","우대금리":"1.100","최저~최고금리":"3.968 ~ 5.068"}]}}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (270, 5, 'B2B전자상거래대출', '{"판매기업에게\n유리한 점":"기업인터넷뱅킹 거래를 통해 업무부담 및 관리비용 절감\n대출(선입금)을 통해 판매대금의 조기회수 가능\n매매보호 기능으로 거래 안전성 확보","구매기업에게\n유리한 점":"대출종류에 따라 다양한 결제수단 가능\n매매보호 기능으로 거래의 안정성 확보\n(기술)신용보증기금 등의 보증기관의 보증서 담보 활용 가능","B2B전자상거래\n대출의 종류":"B2B구매자금대출 : 기업구매자금대출 결제제도를 이용하여 B2B전자상거래대출을 지원하는 B2B전자결제 제도임\nB2B일반자금대출 : 기업운전일반자금대출을 이용하여 B2B전자상거래대출을 지원하는 B2B전자결제 제도임\nB2B구매론 : e-구매론 결제제도를 이용하여 B2B전자상거래대출을 지원하는 B2B전자결제 제도임\nB2B상생벤더구매론 : 상생벤더구매론 결제제도를 이용하여 B2B전자상거래대출을 지원하는 B2B전자결제 제도임\n※ 각 상품별 상세 조건 등은 개별상품 공시자료를 참고하시기 바랍니다.","약정이율":"최저 연 5.914% ~ 최고 연 6.624% [2025.06.13 현재]\n최저금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 2.794%\n최고금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 3.504%\n가산금리\n최저금리 : 내부신용등급 A1등급, 약정기간 1년, 대출한도 10억원, 신용보증기금보증서 90% 보증비율 기준\n최고금리 : 내부신용등급 B6등급, 약정기간 1년, 대출한도 10억원, 신용보증기금보증서 90% 보증비율 기준\n* 가산금리는 신용등급, 약정기간, 대출한도, 담보 등에 따라 차등 적용됩니다.\n※ 상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n※ 기준금리는 건별 실행된 대출 만기의 기간에 따라 FTP 1개월물, 2개월물 등 해당 기간별 금리가 적용됩니다.\n※ 연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용\n※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n※ 개별대출의 이자는 대출취급시 일괄선취함을 원칙으로 합니다. (B2B구매론 기준임)","이자계산방법 등":"이자계산방법\n1년을 365일(윤년은 366일)로 보고 1일 단위로 계산하여 적용\n원금 또는 이자의 상환시기 및 방법\n대출금의 만기일에 구매기업의 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"B2B 관련상품\n수수료항목 거래금액 수수료\n(정액) 수수료\n(정률) 비고\nB2B\n이용수수료 일백만원 이하 1,000원 0.2%\n(최저500원) B2B일반대/B2B구매대\n구매기업 부담 : 대출취급 시\n판매기업 부담 : 대금입금 시\nB2B구매론\n구매기업 부담 : 계약체결 시\n판매기업 부담 : 대금입금 시\n동반성장론 (수취기업 부담)\nB2B이용수수료 : 채권 만기입금 시\nMP수수료 : 대출 취급 시\n일천만원 이하 3,000원 0.2%\n(최저500원)\n오천만원 이하 7,000원 0.2%\n(최저5,000원)\n일억원 이하 12,000원 0.015%\n(최저10,000원)\n일억원 초과 20,000원 20,000원\nMP수수료 MP와 이용기업간의 약정에 따라 정해지며 은행은 MP의\n요청에 따라 수납대행업무만 수행함\n※ B2B이용수수료 기준표는 위와 같으나, 실제 적용되는 수수료는 MP사별로 다를 수 있으므로 매매계약 체결시에 실제 적용수수료를 MP사에 문의하거나 MP사 사이트에서 확인하시기 바랍니다.\n\n※ B2B이용수수료는 매매계약 체결시 부담주체 지정이 가능하며 (매매계약 건별로 부과), 구매기업이 부담하는 경우에는 최초 이용약정시 신청한 구매기업 수수료 결제계좌에서 수수료가 출금 납부되고, 판매기업이 부담하는 경우에는 대금입금시 결제대금에서 수수료 차감 후 입금됩니다.","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n서면에 의한 방법\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"최저 1만원 이상 취급 가능","담보 또는\n보증의 필요여부":"예금, 보증서, 부동산 등 하나은행의 여신업무내규에서 인정하는 담보 또는 신용 취급 가능","대출거래\n제한사항 등":"대출거래 제한사항\nB2B상거래정보조회(국세청 전자세금계산서 진위확인 등) 절차에 따라 세금계산서 합계 금액 초과하거나 국세청 전자세금계산서 진위확인이 되지 않는 경우에는 대출거래 제한됨\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"신용정보관리규약 상 신용거래정보 중 연체정보 등의 등록","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로\n철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나\n불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년\n(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<약정이율>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 24%입니다.\n(단, 2021.07.07 이후 법정최고금리는 연 20%임)\n변경 후 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결 일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (271, 5, 'CP매입(기타어음할인)', '{"대출대상":"하나은행의 일정 내부 신용등급 이상의 등급을 부여 받은 개인사업자 또는 법인","대상어음":"지급장소가 타행인 어음(단, 당행에만 당좌계좌가 있는 경우 예외취급 가능)\n할인의뢰인이 어음발행인인 어음(단, 할인의뢰인이 금융기관인 경우 예외취급가능)\n‘기업어음증권’이라는 문자가 인쇄된 어음","대출과목":"CP매입","대출금리":"2021.05.11 기준 기본금리 기준금리 가산금리\nCP매입(기타어음할인) 최저금리(A1) 4.580% 1.400% 3.180%\n취급가능등급 최고금리(B6) 12.290% 1.400% 10.890%\n대출금액 1억, 대출기간 1년, 1년 고정금리 기준, 전액 신용, 만기일시상환을 가정하여 산정한 것으로, 실제 대출 취급 시 대출 대상 기업의 신용등급, 담보비율 등의 요소에 따라 달라질 수 있습니다\n기본금리 = 기준금리 + 가산금리\n기준금리는 CD 3개월물, 금융채 6개월, 금융채 1년, 기간별 고정금리 중 선택 가능합니다\n가산금리는 신용등급, 대출기간, 담보, 대출금액 등에 따라 차등적용됩니다.\n연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용됩니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 20%입니다.","대출한도":"은행이 정한 운전자금 가용한도 범위 내\n※ 운전자금 가용한도 = 1회전 소요운전자금 - 금융기관 총차입액\n1회전 소요운전자금 = (연간 추정매출액 － 추정감가상각비) × 1회전기간/365","대출기간":"3년이내","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 어음만기에 일시상환, 이자는 어음만기일 전일까지 일괄 선취납부\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출 계약해지 : 고객이 대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문 등의 방법을 통해 신청 가능합니다.","대출관련비용":"수입인지(대출금액 5천만원 초과시 인지세의 50% 손님부담)\n근저당권 설정시 국민주택채권매입비용\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","금리인하요구권":"재무상태개선, 신용등급 상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금리인하요구권 등 상품관련 자세한 사항은 상품설명서를 참조하시거나 하나은행 고객센터(1599-1111) 또는 영업점으로 문의하시기 바랍니다.\n이 금융상품을 가입(계약)하시기 전에 ‘금융상품 설명서‘ 및 ‘약관’을 반드시 읽어보시기 바랍니다.\n이 금융상품을 가입(계약)하시는 경우 금융소비자보호법 제19조 제1항에 따라 상품에 관한 중요한 사항을 설명 받으실 수 있습니다.","상품내용 변경에 관한 사항":"2021.03.25\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경 전 : 금리인하요구권대상여부 : 본 상품은 금리인하요구권의 신청이 가능합니다\n변경 후 : 금리인하요구권\n재무상태개선, 신용도상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경 전 : 신설\n변경 후 :\n대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 미적용\n변경 전 : 신설\n변경 후 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (272, 5, 'e-구매론', '{"판매기업에게\n유리한 점":"금융기관이 만기대금지급을 보장(무보증구매론 제외)\n기존 어음에 비해 업무의 간소화로 관리비용 절감\n기업인터넷뱅킹을 통해서 판매기업(또는 협력기업) 약정 및 판매대금 조기회수(=선입금) 가능","구매기업에게\n유리한 점":"구매대금 지급업무의 효율화 및 관리비용 절감 가능\n기업ERP와 연계한 지급결제 기능","상품의 종류":"(상생)일반구매론 : 지급승인금액에 대하여 은행이 대금지급을 보증하는 상품\n무보증구매론 : 지급승인금액에 대해서는 대금지급을 보증하지 않으며, 선입금실행분에 대해서만 구매기업이 상환의무를 부담하는 상품\n역구매론 : 판매기업이 제공하는 신용공여 금액 범위내에서 구매기업에게 지급승인한도를 부여하는 상품으로 거래조건에 따라서는 최장 36개월까지 분할상환이 가능한 상품\nB2B구매론 : 구매기업과 판매기업이 MP(마켓플레이스)를 통해 체결한 B2B전자상거래 매매계약의 결제수단으로 이용 가능한 상품\n벤더구매론 : 일반구매론으로 받은 지급승인금액을 선입금을 실행하여 2차벤더에게 입금시켜 줄 수 있는 상품\n상생벤더구매론 : 원청기업으로부터 받은 확정채권을 근거로 2차 협력기업에게 지급승인 할 수 있는 상품 (상생벤더구매론 공시자료 참조)","약정이율":"최저 연 6.064% ~ 최고 연 12.854% [2025.06.13 현재]\n최저금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 2.944%\n최고금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 9.734%\n가산금리\n최저금리 : 내부신용등급 A1등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n최고금리 : 내부신용등급 B6등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n* 가산금리는 신용등급, 약정기간, 대출한도, 담보 등에 따라 차등 적용됩니다.\n※ 상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n※ 기준금리는 건별 실행된 대출 만기의 기간에 따라 FTP 1개월물, 2개월물 등 해당 기간별 금리가 적용됩니다.\n※ 연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용\n※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n※ 개별대출의 이자는 대출취급시 일괄선취함을 원칙으로 합니다. (일반구매론 기준임)","이자계산방법 등":"이자계산방법\n1년을 365일(윤년은 366일)로 보고 1일 단위로 계산하여 적용\n원금 또는 이자의 상환시기 및 방법\n지급승인의 결제일에 구매기업의 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"취급수수료\n상품유형 결제계좌 수수료(원) 비고\n역구매론 당행 2,000원\n부담 주체\n약정에 따라 구매기업 또는 판매기업 부담\n납부 시기\n- 구매기업 부담: 결제일 또는 최초 도래하는 분할상환일에 결제계좌 출금 납부\n- 판매기업 부담: 대금입금 시 차감 납부\n타행 5,000원\n일반구매론\n무보증구매론 당행 1,000원\n타행 2,000원\nB2B구매론 당행 1,000원\n부담 주체: 판매기업\n납부시기: 대금입금 시 차감납부\n벤더구매론 당행 1,000원\n부담 주체\n벤더입금 시 판매기업이 지정\n납부 시기\n- 판매기업부담 : 벤더입금 시 계좌출금납부\n- 벤더기업 부담: 대금입금 시 차감납부\n선입금미실행수수료\n선입금미실행금액 수수료(원) 비고(원)\n일백만원 이하 면제\n승인 건별 선입금소진율 70% 이상인 경우 자동면제\n승인일로부터 판매기업 입금일까지의 잔여기간이 7일 이하인 경우 자동면제\n판매기업이 부담하며 대금입금 시 차감납부\n일천만원 이하 1,000원\n오천만원 이하 3,000원\n일억원 이하 5,000원\n일억원 초과 10,000원","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n서면에 의한 방법(단, 역구매론의 구매기업은 자동기한 연장)\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과\n원금균등분할상환방식의 역구매론의 경우, 분할상환원리금을 그 기일에 지급하지 아니한 때에는 지급하여야 할 금액에 대하여 그 다음날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"지급승인 금액 범위 내 최저 1만원 이상 취급 가능\n단, 판매기업의 약정계좌가 타행계좌일 경우 선입금 별 최고금액은 10억원 이하 가능","담보 또는\n보증의 필요여부":"예금, 보증서, 부동산 등 하나은행의 여신업무내규에서 인정하는 담보 또는 신용 취급 가능","대출거래\n제한사항 등":"대출거래 제한사항\nB2B상거래정보조회(국세청 전자세금계산서 진위확인 등) 절차에 따라 세금계산서 합계 금액 초과하거나 국세청 전자세금계산서 진위확인이 되지 않는 경우에는 대출거래 제한됨\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"신용정보관리규약 상 신용거래정보 중 연체정보 등의 등록","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로\n철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나\n불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년\n(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<약정이율>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 24%입니다.\n(단, 2021.07.07 이후 법정최고금리는 연 20%임)\n변경 후 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결 일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (273, 5, '공공구매론', '{"판매기업에게\n유리한 점":"기존 할인어음이나 구매자금대출 등의 결제성 상품과 달리 납품 전 대출이므로 실질적인 생산자금 지원효과가 있는 상품\n인터넷뱅킹을 이용하여 간단하게 발주서 조회, 대출실행 가능\n공공기관이 납품대금 결제시 대출 자동상환과 차액 자동입금 방식 지원","약정이율":"최저 연 6.064% ~ 최고 연 12.854% [2025.06.13 현재]\n최저금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 2.944%\n최고금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 9.734%\n가산금리\n최저금리 : 내부신용등급 A1등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n최고금리 : 내부신용등급 B6등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n* 가산금리는 신용등급, 약정기간, 대출한도, 담보 등에 따라 차등 적용됩니다.\n\n※ 상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n※ 기준금리는 건별 실행된 대출 만기의 기간에 따라 FTP 1개월물, 2개월물 등 해당 기간별 금리가 적용됩니다.\n※ 연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용\n※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n※ 개별대출의 이자는 월 후취방식 징구를 원칙으로 합니다.","이자계산방법 등":"이자계산방법\n1년을 365일(윤년은 366일)로 보고 1일 단위로 계산하여 적용\n원금 또는 이자의 상환시기 및 방법\n발주서의 만기일에 구매기업의 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"아래의 ①, ②의 수수료 중 택1\n① 한도약정수수료 = 한도약정액 x 한도약정수수료율 0.2% x 약정기간 ÷ 365일(윤년 366일)\n② 약정한도미사용수수료 = (약정한도평잔 – 운용기간 중 여신평잔) x 한도미사용수수료율 0.3% x 운용기간 ÷ 365일(윤년은 366일)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n서면에 의한 방법\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"발주서 금액 범위 내 최저 1만원 이상 취급 가능","담보 또는\n보증의 필요여부":"예금, 보증서, 부동산 등 하나은행의 여신업무내규에서 인정하는 담보 또는 신용 취급 가능","대출거래\n제한사항 등":"대출거래 제한사항\n발주서의 납품기한 + 60일 범위 내 취급가능하며, 발주일로부터 1년을 초과하는 경우 대출 제한됨\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"신용정보관리규약 상 신용거래정보 중 연체정보 등의 등록","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로\n철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나\n불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년\n(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<약정이율>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 24%입니다.\n(단, 2021.07.07 이후 법정최고금리는 연 20%임)\n변경 후 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결 일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (274, 5, '기업구매자금대출', '{"판매기업에게\n유리한 점":"기업인터넷뱅킹을 통한 업무처리 시, 환어음 추심수수료 비용 절감 가능\n구매기업에 따라 거래은행을 변경할 필요 없음","구매기업에게\n유리한 점":"구매대금지급업무의 효율화 및 비용절감 수단\n기업ERP와 연계한 지급결제 가능","상품의 종류":"일반구매자금대출\n판매기업의 추심의뢰에 의해 구매기업이 구매대를 실행하여 추심대금을 결제하는 상품\n일괄구매자금대출\n구매기업의 구매대금 결제자료에 의해 은행이 판매기업을 대신하여 추심등록하고 구매기업이 구매대를 실행하여 추심대금을 결제하는 상품\n역구매자금대출\n판매기업이 구매기업을 위하여 신용한도를 공여하고 판매기업이 추심의뢰하는 것만으로 구매기업명의 구매대 실행 및 자금결제가 이루어지는 상품으로 구매기업 명의 대출 연체시 판매기업이 상환의무를 부담하는 상품\nB2B구매자금대출\n구매기업과 판매기업이 MP(마켓플레이스)를 통해 체결한 B2B전자상거래 매매계약의 결제수단으로 이용 가능한 상품","약정이율":"최저 연 6.064% ~ 최고 연 12.854% [2025.06.13 현재]\n최저금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 2.944%\n최고금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 9.734%\n가산금리\n최저금리 : 내부신용등급 A1등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n최고금리 : 내부신용등급 B6등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n* 가산금리는 신용등급, 약정기간, 대출한도, 담보 등에 따라 차등 적용됩니다.\n※ 상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n※ 기준금리는 건별 실행된 대출 만기의 기간에 따라 FTP 1개월물, 2개월물 등 해당 기간별 금리가 적용됩니다.\n※ 연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용\n※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n※ 개별대출의 이자는 월 후취방식 징구를 원칙으로 합니다.","이자계산방법 등":"이자계산방법\n1년을 365일(윤년은 366일)로 보고 1일 단위로 계산하여 적용\n원금 또는 이자의 상환시기 및 방법\n대출금의 만기일에 구매기업의 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"1. 구매기업\n아래의 ①, ②의 수수료 중 택1\n① 한도약정수수료 = 한도약정액 x 한도약정수수료율 0.3% x 약정기간 ÷ 365일(윤년 366일)\n② 약정한도미사용수수료 = (약정한도평잔 – 운용기간 중 여신평잔) x 한도미사용수수료율 0.5% x 운용기간 ÷ 365일(윤년은 366일)\n2. 판매기업\n추심수수료\n항목 구분 수수료(원) 비고\n추심수수료\n(창구이용시) 지급지 : 타행/당행 타지점 10,000원 추심의뢰 시\n추심 건별로 현금 납부\n또는 결제계좌 출금 납부\n지급지 : 당행내 동일지점 5,000원\n추심수수료\n(인터넷이용시) 지급지 : 당/타행/지점 구분없음 2,000원","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n서면에 의한 방법\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"최저 1만원 이상 취급 가능","담보 또는\n보증의 필요여부":"예금, 보증서, 부동산 등 하나은행의 여신업무내규에서 인정하는 담보 또는 신용 취급 가능","대출거래\n제한사항 등":"대출거래 제한사항\nB2B상거래정보조회(국세청 전자세금계산서 진위확인 등) 절차에 따라 세금계산서 합계 금액 초과하거나 국세청 전자세금계산서 진위확인이 되지 않는 경우에는 대출거래 제한됨\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"신용정보관리규약 상 신용거래정보 중 연체정보 등의 등록","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로\n철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나\n불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년\n(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<약정이율>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 24%입니다.\n(단, 2021.07.07 이후 법정최고금리는 연 20%임)\n변경 후 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결 일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (275, 5, '미래채권담보대출', '{"판매기업에게\n유리한 점":"기존 할인어음, 외담대 등과 달리 납품전 대출이므로 실질적인 생산자금 지원효과\n인터넷뱅킹을 이용한 발주서의 조회 및 대출실행으로 업무처리시간 단축\n납품대금 입금시 자동으로 대출이 상환되므로 관리업무의 간편화 및 업무량 감소효과","상품의 종류":"일반미래채권담보대출\n발주서방식 미래대 : 구매기업이 발행한 발주서 금액의 80%까지 대출을 취급한 후 납품대금으로 대출금을 상환하는 방식\n발주서전환방식 미래대 : 구매기업이 발행한 발주서 금액의 80%까지 대출을 취급한 후 납품이 완료되면 구매기업은 전자방식 외상매출채권을 발행하며, 발행된 전자방식 외상매출채권을 근거로 외담대를 실행하여 미래대를 상환하는 방식\n실적부 미래대 : 판매기업이 구매기업에 납품한 과거의 납품실적을 기준으로 기업종합통장대출 한도를 부여하여 자유롭게 입출금하고 한도약정만기일에 대출금을 상환하는 방식\n상생미래채권 담보대출\n구매기업이 발행한 발주서의 대금지급확약비율 해당 금액의 80% 범위내에서 대출을 취급한 후 납품이 완료되면 구매기업은 확정채권(전자방식 외상매출채권/지급승인 등)을 발행하며, 발행된 확정채권을 근거로 관련대출(외담대/구매론 등)을 실행하여 미래대를 상환하는 방식","약정이율":"최저 연 6.064% ~ 최고 연 12.854% [2025.06.13 현재]\n최저금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 2.944%\n최고금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 9.734%\n가산금리\n최저금리 : 내부신용등급 A1등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n최고금리 : 내부신용등급 B6등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n* 가산금리는 신용등급, 약정기간, 대출한도, 담보 등에 따라 차등 적용됩니다.\n\n※ 상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n※ 기준금리는 건별 실행된 대출 만기의 기간에 따라 FTP 1개월물, 2개월물 등 해당 기간별 금리가 적용됩니다.\n※ 연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용\n※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n※ 개별대출의 이자는 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.","이자계산방법 등":"이자계산방법\n1년을 365일(윤년은 366일)로 보고 1일 단위로 계산하여 적용\n원금 또는 이자의 상환시기 및 방법\n발주서의 만기일에 구매기업의 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"해당사항 없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n서면에 의한 방법\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"발주서 금액 범위 내 최저 1만원 이상 취급 가능","담보 또는\n보증의 필요여부":"예금, 보증서, 부동산 등 하나은행의 여신업무내규에서 인정하는 담보 또는 신용 취급 가능","대출거래\n제한사항 등":"대출거래 제한사항\n연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 경우\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"신용정보관리규약 상 신용거래정보 중 연체정보 등의 등록","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로\n철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나\n불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년\n(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"고객님의 신용도에 따라 대출한도가 차등 적용되며 대출취급이 거절될 수 있습니다.\n대출금의 상환의무는 본인에게 있으며, 발주서 건별대출의 기한연장은 불가합니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<약정이율>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 24%입니다.\n(단, 2021.07.07 이후 법정최고금리는 연 20%임)\n※ 개별대출의 이자는 대출취급시 일괄선취함을 원칙으로 합니다. (발주서방식 미래채권담보대출 기준임)\n변경 후 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n※ 개별대출의 이자는 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결 일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (276, 5, '사모전환사채인수', '{"대상업체":"당행 내부 신용등급 B4등급 이상으로 사모전환사채 발행이 가능한 기업","대상사채":"상환기간이 3년 이내에서 사채상환기일 내 미리 결정된 조건대로 발행회사의 주식으로 전환할 수 있는 권리가 부여된 사모전환사채","인수한도":"발행주식(출자지분 포함)의 15%를 초과 할 수 없으며, 15% 이내인 경우에도 하나은행이 최대 지분보유자가 아니어야함","인수방법":"총액인수","대출과목":"사모사채(은행)-전환사채","대출기간":"3년 이내","상환방식":"분할상환 또는 일시상환","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등)단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다.바로가기 >","계약해지 또는\n갱신방법":"건별대출(만기일시)계약해지 : 고객이 대출금 전액을 상환한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 차환 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 차환가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문 등의 방법을 통해 신청 가능합니다.","적용이자율":"표면이율 + 만기보장수익율\n표면이율은 발행회사의 신용등급 등에 따라 차등 적용됩니다.\n만기보장수익율은 전환 청구기간내 전환권을 행사하지 아니한 사채권에 적용되는 이자율로서 선택항목으로 별도 약정가능함.\n※ 만기보장수익율 별도 약정 체결시, 사채액면금액에 사채인수일(발행일)부터 상환일 전일까지의 기간에 대하여 사채인수 당시의 만기보장수익율과 표면이율의 차이를 연복리로 계산하여 만기시에 별도 징구함\n※ 연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용됩니다.\n※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 20%입니다.","대출관련비용":"채무자에 의한 조기상환권이 있는 경우를 제외하고는 은행의 동의 등에 의하여 중도상환이 가능함\n※ 중도상환해약금 : 중도상환대출금액 X 중도상환해약금률 X 운용기간 ÷ 365일(윤년은 366일)\n※ 중도상환해약금률 : 부동산담보 1.4%, 신용/기타대출 1.1%\n※ 대출 잔여일수 및 대출기간은 3년을 초과하더라도 3년으로 함\n수입인지(대출금액 5천만원 초과시 인지세의 50% 손님부담)\n인수수수료는 인수금액의 일정비율로 은행과 협의하여 정한다.\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","금리인하요구권":"재무상태개선, 신용등급 상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금리인하요구권 등 상품관련 자세한 사항은 상품설명서를 참조하시거나 하나은행 고객센터(1599-1111) 또는 영업점으로 문의하시기 바랍니다.\n이 금융상품을 가입(계약)하시기 전에 ‘금융상품 설명서‘ 및 ‘약관’을 반드시 읽어보시기 바랍니다.\n이 금융상품을 가입(계약)하시는 경우 금융소비자보호법 제19조 제1항에 따라 상품에 관한 중요한 사항을 설명 받으실 수 있습니다.","상품내용 변경에 관한 사항":"2021.03.25\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경 전 : 금리인하요구권대상여부 : 본 상품은 금리인하요구권의 신청이 가능합니다\n변경 후 : 금리인하요구권\n재무상태개선, 신용도상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경 전 : 신설\n변경 후 :\n대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 미적용\n변경 전 : 신설\n변경 후 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (277, 5, '상업어음할인', '{"대출대상":"상업어음을 소지한 기업(개인사업자) 포함","할인대상어음":"상거래에 수반하여 발생(배서)된 상업어음","대출금리":"2021.05.11 기준 기본금리 기준금리 가산금리\n상업어음할인 최저금리(A1) 4.470% 1.400% 3.070%\n취급가능등급 최고금리(B6) 6.280% 1.400% 4.880%\n대출금액 1억, 대출기간 1년, 1년 고정금리 기준, 전액 신용, 만기일시상환을 가정하여 산정한 것으로, 실제 대출 취급 시 대출 대상 기업의 신용등급, 담보비율 등의 요소에 따라 달라질 수 있습니다\n기본금리 = 기준금리 + 가산금리\n기준금리는 기간별 고정금리 선택 가능합니다\n가산금리는 신용등급, 대출기간, 담보, 대출금액 등에 따라 차등적용됩니다.\n연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용됩니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 20%입니다.","대출한도":"최근결산일 현재 손익계산서에 의한 과거1년간 매출액의 1/3 범위내\n위에도 불구하고, 최근 받을어음 실적이 급격히 증가한 기업에 대해서는 부가가치세 과세표준증명원 또는 부가가치세확정(예정) 신고서에 의한 최근 3개월 매출액 범위내","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 어음만기에 일시상환, 이자는 어음만기일 전일까지 일괄 선취납부\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출 계약해지 : 고객이 대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문 등의 방법을 통해 신청 가능합니다.","대출관련비용":"한도대출의 경우 아래의 ①, ②의 수수료 중 택1\n한도약정수수료 = 한도약정액 x 한도약정수수료율 (0.1%) x 약정기간 ÷ 365일(윤년 366일)\n※ 한도약정일 징구\n약정한도미사용수수료 = (약정한도평잔 – 운용기간 중 여신평잔) x 한도미사용수수료율(0.2%) x 운용기간 ÷ 365일(윤년은 366일)\n※ 한도내 개별대출 실행시 또는 약정해지일\n수입인지(대출금액 5천만원 초과시 인지세의 50% 고객부담)\n근저당권 설정시 국민주택채권매입비용\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","금리인하요구권":"재무상태개선, 신용등급 상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금리인하요구권 등 상품관련 자세한 사항은 상품설명서를 참조하시거나 하나은행 고객센터(1599-1111) 또는 영업점으로 문의하시기 바랍니다.\n이 금융상품을 가입(계약)하시기 전에 ‘금융상품 설명서‘ 및 ‘약관’을 반드시 읽어보시기 바랍니다.\n이 금융상품을 가입(계약)하시는 경우 금융소비자보호법 제19조 제1항에 따라 상품에 관한 중요한 사항을 설명 받으실 수 있습니다.","상품내용\n변경에 관한\n사항":"2021.03.25\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경 전 : 금리인하요구권대상여부 : 본 상품은 금리인하요구권의 신청이 가능합니다\n변경 후 : 금리인하요구권\n재무상태개선, 신용도상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경 전 : 신설\n변경 후 :\n대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 미적용\n변경 전 : 신설\n변경 후 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (278, 5, '상생미래대 plus 상생외담대', '{"약정이율":"최저 연 6.064% ~ 최고 연 12.854% [2025.06.13 현재]\n최저금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 2.944%\n최고금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 9.734%\n가산금리\n최저금리 : 내부신용등급 A1등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n최고금리 : 내부신용등급 B6등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n* 가산금리는 신용등급, 약정기간, 대출한도, 담보 등에 따라 차등 적용됩니다.\n※ 상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n※ 기준금리는 건별 실행된 대출 만기의 기간에 따라 FTP 1개월물, 2개월물 등 해당 기간별 금리가 적용됩니다.\n※ 연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용\n※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n※ 개별대출의 이자는 월 후취방식 징구를 원칙으로 합니다.","이자계산방법 등":"이자계산방법\n1년을 365일(윤년은 366일)로 보고 1일 단위로 계산하여 적용\n원금 또는 이자의 상환시기 및 방법\n외상매출채권의 만기일에 구매기업의 외상매출채권 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"해당사항 없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n서면에 의한 방법(단, 협력기업은 자동기한 연장)\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"외상매출채권 금액 범위 내 최저 1만원 이상 취급 가능","담보 또는\n보증의 필요여부":"예금, 보증서, 부동산 등 하나은행의 여신업무내규에서 인정하는 담보 또는 신용 취급 가능","대출거래\n제한사항 등":"대출거래 제한사항\nB2B상거래정보조회(국세청 전자세금계산서 진위확인 등) 절차에 따라 세금계산서 합계 금액 초과하거나 국세청 전자세금계산서 진위확인이 되지 않는 경우에는 대출거래 제한됨\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"구매기업이 발행하는 외상매출채권의 상환청구권 여부에 따른 차주 본인의 대출금 상환 의무","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<약정이율>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 24%입니다.\n(단, 2021.07.07 이후 법정최고금리는 연 20%임)\n변경 후 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (279, 5, '상생벤더구매론', '{"약정이율":"구매기업(=원정기업)이 발행하는 외상매출채권 등(지급승인 포함)과 관련한 약정이율을 적용함","이자계산방법 등":"이자계산방법\n1년을 365일(유년은 366일)로 보고 1일 단위로 계산\n원금 또는 이자의 상환시기 및 방법\n지급승인의 결제일에 구매기업의 외상매출채권 등의 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"벤더승인취급수수료 1,000원, 벤더승인기업 부담(벤더승인 시 약정계좌에서 출금하여 납부)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n자동기한 연장\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"지급승인 금액 범위 내 최저 1만원 이상 취급 가능","담보 또는\n보증의 필요여부":"구매기업(=원청기업)이 발행하는 외상매출채권 등을 활용하여 신용 취급","대출거래\n제한사항 등":"대출거래 제한사항\nB2B상거래정보조회(국세청 전자세금계산서 진위확인 등) 절차에 따라 세금계산서 합계 금액 초과하거나 국세청 전자세금계산서 진위확인이 되지 않는 경우에는 대출거래 제한됨\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"구매기업이 발행하는 외상매출채권 등의 상환청구권 여부에 따른 차주 본인의 대출금 상환 의무","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"상생벤더구매론은 외담대와는 달리 지급승인 시점에서 대금지급의무가 발생하므로 2차이하 협력기업의 선입금여부와 관계없이 만기에 결제됩니다.\n상생벤더구매론은 원청채권의 거래조건이 그대로 적용되므로 원청채권이 상환청구권있는 외담대 일 경우 원청채권이 결제되지 않으면 1차협력기업이 대출금 상환의무를 부담합니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (280, 5, '상생패키지론', '{"상생미래대\n(선택사항)\n+ 상생외담대\n(or 상생일반\n구매론, 이하\n\"상생외담대 등\")":"원청기업인 구매기업과 1차 협력기업간의 상거래에 수반되는 물품 등의 구매대금 지급을 위한 기업간 결제성 상품\n상생미래대를 추가하여 약정하는 경우, 원청기업인 구매기업의 대금지급확약을 기반으로 발주시점부터 구매기업의 신용을 기반으로 1차 협력기업의 납품 前 (=세금계산서 등 작성 前) 금융 지원 가능","상생벤더 구매론":"1차 협력기업이 구매기업으로부터 받은 “상생외담대 등”의 외상매출채권을 담보로 하위 협력기업에게 납품대금을 지급하는 기업간 결제성 상품\n2차 ~ N차 협력기업이 대출 의뢰(=선입금 신청) 시 적용금리는 원청기업인 구매기업의 신용에 기반한 저금리의 대출금리가 적용되므로 금융비용 절감이 가능함","약정이율":"각 상품별 금리체계를 따름","이자계산방법 등":"이자계산방법\n1년을 365일(윤년은 366일)로 보고 1일 단위로 계산하여 적용\n원금 또는 이자의 상환시기 및 방법\n외상매출채권 등의 만기일에 구매기업의 외상매출채권 등의 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"해당사항 없음(단, 상생벤더구매론 관련 수수료는 상생벤더구매론 공시자료에 따름)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n서면에 의한 방법(단, 협력기업은 자동기한 연장)\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"발주서 또는 외상매출채권 금액 범위 내 최저 1만원 이상 취급 가능","담보 또는\n보증의 필요여부":"예금, 보증서, 부동산 등 KEB하나은행의 여신업무내규에서 인정하는 담보 또는 신용 취급 가능","대출거래\n제한사항 등":"대출거래 제한사항\nB2B상거래정보조회(국세청 전자세금계산서 진위확인 등) 절차에 따라 세금계산서 합계 금액 초과하거나 국세청 전자세금계산서 진위확인이 되지 않는 경우에는 대출거래 제한됨\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"구매기업이 발행하는 외상매출채권의 상환청구권 여부에 따른 차주 본인의 대출금 상환 의무","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (281, 5, '상생협력 기업대출', '{"상품특징":"당행과 대기업간 협력중소기업에 대한 지원 협약을 체결하고, 대기업은 당행에 시장금리보다 낮은 금리로 예금을\n가입하며, 당행은 대기업의 협력중소기업에 대한 대출 취급 시 해당 예금이자 절감 분을 포함하여 금리를\n감면해주는 상품","대출대상":"협력대기업에서 추천한 협력기업","대출한도":"협력대기업에서 지정한 협력기업별 한도","판매한도":"각 협력대기업에서 정한 총 대출 한도 범위 내","대출금리":"변동금리 최저 연 3.842% ~ 최고 연 8.072% (2025.02.27현재)\n[기준금리 연 2.84%(91일물CD유통수익률) + 가산금리(최저) 연 1% / (최고) 연 5.23%]\n최저금리기준 : 2025.02.27 현재, 내부신용등급 A1, 대출기간 1년, 대출금액 10억원, 운전자금대출, 일시상환, 담보유효가 100% , 상생우대금리 2.50% 감면기준 (업체별 우대금리 상이)\n최고금리적용 : 2025.02.27 현재, 내부신용등급 B4, 대출기간 1년, 대출금액 10억원, 운전자금대출, 일시상환, 전액신용대출, 상생우대금리 2.50% 감면기준 (업체별 우대금리 상이)\n상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n가산금리는 신용등급, 대출기간, 대출금액, 대출과목, 상환방식, 담보종류 등에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경 될 수 있습니다.\n연체이자율은 [대출이자율+연체가산이율(연3%)], 최고 연 15%입니다.\n이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연20%입니다.","대출기간":"운전자금 – 1년 단위 만기 일시상환/분할상환\n시설자금 – 1년 단위 만기 일시상환/분할상환\n＊협력대기업 예금 연장 여부에 따라 대출 연장 결정","상환방식":"만기일시상환/원(리)금분할상환방식","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사.\n원리금 균등분할상환 대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산.\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편 넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금\n2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금\n3. 연체기간이 1일인 연체대출채권 및 지급보증 대지급금\n4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금또는\n이자의\n상환시기 및\n방법":"건별 대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매 1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매 1개월 등) 단위로 납부\n대출실행 유당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로,\n대출 취급 시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","계약해지 또는\n갱신방법":"건별 대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료 시 은행이 정한 기준에 따라 기간연장 가능여부 심사 후 승인을 득한 경우 계약갱신 가능하며,\n고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의\n방법을 통해 연장 가능합니다.","중도상환\n해약금":"중도상환해약금: 중도상환대출금액 X 중도상환해약금율＊ X (중도상환약정잔여일수 ÷ 중도상환약정기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율\n담보종류 고정 변동\n순수신용 0.04%\n부동산/동산 0.51%\n보증서/기타담보 0.04% 0.03%","대출관련 비용":"담보설정에 따른 국민주택채권 매입비용, 담보해지 시 말소비용이 발생할 수 있습니다.\n인지세\n인지세법에 의해 대출약정 체결 시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원","금리인하요구권":"금리인하요구권\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n대출계약철회권\n대출실행일(대출금수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 당행 대출 계약 신규가 제한될 수 있습니다.\n위법계약해지권\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객 적용여부 : 적용\n변경 전:\n중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n대출잔여기간 1개월 이내 상환 시 면제\n중도상환해약금율 : 고정금리 1.4%, 변동금리 1.2%\n변경 후:\n중도상환해약금: 중도상환대출금액 X 중도상환해약금율＊ X (중도상환약정잔여일수 ÷ 중도상환약정기간)\n최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출잔여기간 1개월 이내 상환 시 면제\n중도상환해약금율\n담보종류 고정 변동\n순수신용 0.04%\n부동산/동산 0.51%\n보증서/기타담보 0.04% 0.03%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (282, 5, '수요자금융', '{"대출대상":"일반소비재 또는 산업용 기자재, 부품 및 원자재를 판매자(생산자 및 대리점을 포함) 로 부터 구입하는 최종수요자","대출과목":"일반자금대출(수요자금융)","대출금리":"2021.05.11 기준 기본금리 기준금리 가산금리\n수요자금융 최저금리(A1) 4.580% 1.400% 3.180%\n취급가능등급 최고금리(B6) 12.290% 1.400% 10.890%\n대출금액 1억, 대출기간 1년, 1년 고정금리 기준, 전액 신용, 만기일시상환을 가정하여 산정한 것으로, 실제 대출 취급 시 대출 대상 기업의 신용등급, 담보비율 등의 요소에 따라 달라질 수 있습니다\n기본금리 = 기준금리 + 가산금리\n기준금리는 CD 3개월물, 금융채 6개월, 금융채 1년, 기간별 고정금리 중 선택 가능합니다\n가산금리는 신용등급, 대출기간, 담보, 대출금액 등에 따라 차등적용됩니다.\n연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용됩니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 20%입니다.","대출한도":"계약내용 또는 영업활동에 필요한 적정한도 이내로 구입가격(부가가치세 포함)의 100% 범위내","대출기간":"개별구매자의 대출기간은 판매자와 당행간의 수요자금융약정서에 정하는 바에 따른다.\n일시상환 : 3년이내\n분할상환 : 5년이내","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출 : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등)단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출(만기일시상환)계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출 계약해지 : 고객이 대출약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문 등의 방법을 통해 신청 가능합니다.","중도상환해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율\n- 부동산/동산담보 : 고정금리 1.4%, 변동금리 1.2%\n- 신용/기타담보 : 고정금리 1.1%, 변동금리 1.0%","대출관련비용":"수입인지(대출금액 5천만원 초과시 인지세의 50% 손님부담)\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","금리인하요구권":"재무상태개선, 신용등급 상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금리인하요구권 등 상품관련 자세한 사항은 상품설명서를 참조하시거나 하나은행 고객센터(1599-1111) 또는 영업점으로 문의하시기 바랍니다.\n이 금융상품을 가입(계약)하시기 전에 ‘금융상품 설명서‘ 및 ‘약관’을 반드시 읽어보시기 바랍니다.\n이 금융상품을 가입(계약)하시는 경우 금융소비자보호법 제19조 제1항에 따라 상품에 관한 중요한 사항을 설명 받으실 수 있습니다.","상품내용\n변경에 관한\n사항":"2021.03.25\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경 전 : 금리인하요구권대상여부 : 본 상품은 금리인하요구권의 신청이 가능합니다\n변경 후 : 금리인하요구권\n재무상태개선, 신용도상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경 전 : 신설\n변경 후 :\n대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 미적용\n변경 전 : 신설\n변경 후 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n2019.04.17\n<중도상환해약금율 변경>\n변경 전 : 부동산담보:1.4%, 신용/기타담보 1.1%\n변경 후 :\n부동산담보:고정금리시 1.4% 변동금리시 1.2%\n신용/기타담보:고정금리시 1.1% 변동금리시 1.0%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (283, 5, '스마트공장 지원자금대출', '{"대출대상":"중소벤처기업부 스마트공장 고도화 촉진을 위한 업무협약에 따라 선정 대상 업체에 금융 지원하는 대출상품","대출금액":"기업시설일반자금대출, 기업운전일반자금대출, 기업시설주택자금대출","대출한도":"시설자금 30억원, 운전자금 10억원","판매한도":"협약기간(‘24년 1월~’26년 12월)내 총 대출한도 3,000억원 운영(한도소진 시 조기종료 될 수 있음)","대출기간 및 상환방식":"▶ 시설자금: 최장 3년 (원(리)금 분할상환)\n▶ 운전자금: 1년(일시상환) [최장 3년 범위내에서 1년 단위 연장가능]\n※ 연장시 신용도에 따라 연장 여부가 변경 될 수 있습니다.","대출금리":"변동금리 최저 연 4.003% ~ 최고 연 6.623% (2025.02.27 현재)\n[기준금리 연 2.84%(91일물CD유통수익률) + 가산금리(최저) 연 1.16% / (최고) 연 3.78%]\n최저금리기준 : 2025.02.27 현재, 내부신용등급 A1, 대출기간 3년, 대출금액 10억원, 시설자금대출, 분할상환, 담보유효가 100%, 우대금리적용\n최고금리적용 : 2025.02.27 현재, 내부신용등급 B4, 대출기간 3년, 대출금액 10억원, 시설자금대출, 분할상환, 전액신용대출, 우대금리적용\n상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n가산금리는 신용등급, 대출기간, 대출금액, 대출과목, 상환방식, 담보종류 등에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경 될 수 있습니다.\n연체이자율은 [대출이자율+연체가산이율(연3%)], 최고 연 15%입니다.\n이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연20%입니다.","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산.\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편 넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금\n2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금\n3. 연체기간이 1일인 연체대출채권 및 지급보증 대지급금\n4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급 시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능(최장 3년 범위 내에서 연장가능)\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사 후 승인을 득한 경우 계약갱신 가능하며,\n고객님의 신용도, 대출상품 등에 따라 영업점 방문/전자금융채널(기업인터넷뱅킹) 등의 방법을 통해 연장 가능합니다.","중도상환\n해약금":"▶ 신용대출의 경우 중도상환 해약금 면제\n중도상환해약금: 중도상환대출금액 X 중도상환해약금율＊ X (중도상환약정잔여일수 ÷ 중도상환약정기간)\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율\n담보종류 고정 변동\n순수신용 0.04%\n부동산/동산 0.51%\n보증서/기타담보 0.04% 0.03%","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세 인지세 부담\n손님 부담 은행 부담\n5천만원 이하 비과세 없음 없음\n5천만원 초과 ~ 1억원 이하 70,000원 35,000원 35,000원\n1억원 초과 ~ 10억원 이하 150,000원 75,000원 75,000원\n10억원 초과 350,000원 175,000원 175,000원\n- 근저당권 설정비용 중 국민주택채권 매입비용 손님 부담","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2025.01.13 변경)\n<중도상환해약금>\n기존 가입고객 적용여부 : 적용\n변경 전:\n중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n대출잔여기간 1개월 이내 상환 시 면제\n중도상환해약금율 : 고정금리 1.4%, 변동금리 1.2%\n변경 후:\n중도상환해약금: 중도상환대출금액 X 중도상환해약금율＊ X (중도상환약정잔여일수 ÷ 중도상환약정기간)\n최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출잔여기간 1개월 이내 상환 시 면제\n중도상환해약금율\n담보종류 고정 변동\n순수신용 0.04%\n부동산/동산 0.51%\n보증서/기타담보 0.04% 0.03%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (284, 5, '외담대/e-안심팩토링', '{"전자방식\n외상매출\n채권담보대출\n(외담대)":"구매기업이 제공하는 신용공여를 기반으로 구매기업이 발행한 외상매출채권을 담보로 판매기업 명의로 대출취급,\n외상매출채권의 만기일에 구매기업의 결제대금으로 상환하는 대출상품으로서 대표적인 어음제도대체수단입니다.","e-안심팩토링":"경상적인 영업활동에서 발생하는 판매(납품)기업의 외상매출채권을 은행이 매입하고 자금을 지원하는 대출상품입니다.\n※ 상생외담대 및 상생 e-안심팩토링대출은 상생패키지론을 구성하여 상생벤더구매론 이용가능이 추가된 것 외에는 상품내용은 동일합니다.","판매기업에게\n유리한 점":"물품 판매대금 조기 현금회수 가능\n전자적으로 제공되는 결제시스템을 이용하여 조기 회수 가능\n채권 기한 : 발행일로부터 최장 90일 이내 (단, 유예기간은 전자방식 외상매출채권 결제제도 기본약관에 따름)\n수취한 채권에 대하여 대출(할인)가능\n금융비용 및 담보제공 부담 완화\n상대적으로 신용도가 양호한 구매기업의 신용도에 기반한 대출금리가 적용되므로 본인 신용으로 대출받을 경우보다 저금리 이용 가능\n구매기업이 제공하는 신용공여한도를 이용하므로 외상매출채권외에 별도 담보 없음\n※ 단, 판매기업기준 외담대는 본인의 신용도를 기반으로 하며, 대출취급시 담보제공이 필요할 수 있습니다.\n업무부담 경감 및 판매대금 회수업무의 투명성 제고","구매기업에게\n유리한 점":"관리비용 부담 경감 및 지급업무처리 간편\n어음발행등 수작업 관리 업무 중단으로 관리인력 최소화가 가능합니다.\n전자적으로 발행되는 채권결제제도를 이용하므로 기일관리 및 대금결제 업무가 간편해집니다.\n납품업체의 Loyalty 제고\n구매기업이 제공하는 신용을 이용하여 판매기업의 조기자금 회수가 가능해지므로 판매기업과의 협력관계가 개선됩니다.","약정이율":"최저 연 6.064% ~ 최고 연 12.854% [2025.06.13 현재]\n최저금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 2.944%\n최고금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 9.734%\n가산금리\n최저금리 : 내부신용등급 A1등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n최고금리 : 내부신용등급 B6등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n* 가산금리는 신용등급, 약정기간, 대출한도, 담보 등에 따라 차등 적용됩니다.\n※ 상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n※ 기준금리는 건별 실행된 대출 만기의 기간에 따라 FTP 1개월물, 2개월물 등 해당 기간별 금리가 적용됩니다.\n※ 연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용\n※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n※ 개별대출의 이자는 대출취급시 일괄선취함을 원칙으로 합니다. (외담대 기준임)","이자계산방법 등":"이자계산방법\n1년을 365일(윤년은 366일)로 보고 1일 단위로 계산하여 적용\n원금 또는 이자의 상환시기 및 방법\n외상매출채권의 만기일에 구매기업의 외상매출채권 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"해당사항 없음","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n서면에 의한 방법(단, 판매기업은 자동기한 연장)\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"외상매출채권 금액 범위 내 최저 1만원 이상 취급 가능","담보 또는\n보증의 필요여부":"예금, 보증서, 부동산 등 하나은행의 여신업무내규에서 인정하는 담보 또는 신용 취급 가능","대출거래\n제한사항 등":"대출거래 제한사항\nB2B상거래정보조회(국세청 전자세금계산서 진위확인 등) 절차에 따라 세금계산서 합계 금액 초과하거나 국세청 전자세금계산서 진위확인이 되지 않는 경우에는 대출거래 제한됨\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"구매기업이 발행하는 외상매출채권 등의 상환청구권 여부에 따른 차주 본인의 대출금 상환 의무","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"외상매출채권의 만기결제\n외상매출채권은 채권만기일에 구매기업이 결제하는 경우에만 판매대금을 회수 할 수 있으며, 은행이 대금지급을 보증하지 않습니다.\n대출금의 상환의무 (상환청구권 여부)\n상환청구권이 있는 경우(With Recourse) : 판매기업이 대출금의 채무자이며, 대출금 상환의무를 부담하므로 구매기업의 채권결제여부와 관계없이 대출금 만기일에 대출금을 상환하여야 합니다.\n상환청구권이 없는 경우(Without Recourse) : 판매기업이 대출금의 채무자이나, 구매기업의 결제여부와 관계없이 대출금의 상환의무를 부담하지 않습니다.\n상환청구권의 결정\n상환청구권은 은행과 구매기업과의 약정에 의해 정해지며, 판매기업은 이에 따라야만 대출취급이 가능합니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<약정이율>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 24%입니다.\n(단, 2021.07.07 이후 법정최고금리는 연 20%임)\n변경 후 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년 (계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (285, 5, '운전/시설자금대출', '{"운전자금대출":"경상적인 영업활동을 위한 운영자금이 필요한 기업","대출대상":"당해 시설을 위한 총 소요자금의 80% 범위내","대출금리":"일시상환 : 1년 이내(단, 여신전결권자 인정시 3년 이내까지 일시 상환방식 가능)\n분할상환 : 15년 이내(단, 업무용/상업용 부동산의 매입자금인 경우에는 20년 이내)","대출한도":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금률 X 대출잔여일수 ÷ 대출기간\n※ 중도상환해약금률 : 부동삼담보 1.4%, 신용/기타대출 1.1%\n※ 대출 잔여일수 및 대출기간은 3년을 초과하더라도 3년으로 함\n수입인지(대출금액 5천만원 초과시 인지세의 50% 고객부담)\n근저당권 설정시 국민주택채권매입비용\n담보신탁수수료 : 담보신탁을 이용하여 부동산 담보대출을 받을 경우 담보신탁수수료 및 소유권이전 비용은 은행이 부담합니다.\n(다만, PF대출, 신디케이티드론 등 특수여신은 비용의 부담주체를 달리 적용하기로 특약을 체결한 경우에는 그에 따른다.)\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","대출기간":"재무상태개선, 신용등급 상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.","이자계산 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등)단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","원금 또는\n이자의\n상환시기 및\n방법":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","이용시간":"건별대출(만기일시상환) 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출 계약해지 : 고객이 대출약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문 등의 방법을 통해 신청 가능합니다.","계약해지 또는\n갱신방법":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율\n- 부동산/동산담보 : 고정금리 1.4%, 변동금리 1.2%\n- 신용/기타담보 : 고정금리 1.1%, 변동금리 1.0%","중도상환해약금":"한도대출의 경우 아래의 ①, ②의 수수료 중 택1\n한도약정수수료 = 한도약정액 x 한도약정수수료율 (0.2%) x 약정기간 ÷ 365일(윤년 366일)\n※ 한도약정일 징구\n약정한도미사용수수료 = (약정한도평잔 – 운용기간 중 여신평잔) x 한도미사용수수료율(0.3%) x 운용기간 ÷ 365일(윤년은 366일)\n※ 한도내 개별대출 실행시 또는 약정해지일\n수입인지(대출금액 5천만원 초과시 인지세의 50% 고객부담)\n근저당권 설정시 국민주택채권매입비용\n담보신탁수수료 : 담보신탁을 이용하여 부동산 담보대출을 받을 경우 담보신탁수수료 및 소유권이전 비용은 은행이 부담합니다.\n(다만, PF대출, 신디케이티드론 등 특수여신은 비용의 부담주체를 달리 적용하기로 특약을 체결한 경우에는 그에 따른다.)\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","대출관련비용":"대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","시설자금대출":"2021.05.11 기준 기본금리 기준금리 가산금리\n운전/시설자금대출 시설자금 최저금리(A1) 4.210% 1.400% 2.810%\n취급가능등급 최고금리(B6) 11.920% 1.400% 10.520%\n시설자금, 대출금액 1억, 대출기간 1년, 1년 고정금리 기준, 전액 신용, 만기일시상환을 가정하여 산정한 것으로, 실제 대출 취급 시 대출 대상 기업의 신용등급, 담보비율 등의 요소 에 따라 달라질 수 있습니다\n기본금리 = 기준금리 + 가산금리\n기준금리는 CD 3개월물, 금융채 6개월, 금융채 1년, 단기 코픽스 금리, 기간별 고정금리 중 선택 가능합니다\n가산금리는 신용등급, 대출기간, 담보, 대출금액 등에 따라 차등적용됩니다.\n연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용됩니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 20%입니다.","금리인하요구권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","대출계약철회권":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금리인하요구권 등 상품관련 자세한 사항은 상품설명서를 참조하시거나 하나은행 고객센터(1599-1111) 또는 영업점으로 문의하시기 바랍니다.\n이 금융상품을 가입(계약)하시기 전에 ‘금융상품 설명서‘ 및 ‘약관’을 반드시 읽어보시기 바랍니다.\n이 금융상품을 가입(계약)하시는 경우 금융소비자보호법 제19조 제1항에 따라 상품에 관한 중요한 사항을 설명 받으실 수 있습니다.","위법계약해지권":"2021.03.25\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경 전 : 금리인하요구권대상여부 : 본 상품은 금리인하요구권의 신청이 가능합니다\n변경 후 : 금리인하요구권\n재무상태개선, 신용도상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경 전 : 신설\n변경 후 :\n대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 미적용\n변경 전 : 신설\n변경 후 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n2019.04.17\n<중도상환해약금율 변경>\n변경 전 : 부동산담보:1.4%, 신용/기타담보 1.1%\n변경 후 :\n부동산담보:고정금리시 1.4% 변동금리시 1.2%\n신용/기타담보:고정금리시 1.1% 변동금리시 1.0%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (286, 5, '이차보전대출', '{"대출대상":"지방자치단체 등과 은행이 체결한 이차보전협약에 따라, 지방자치단체에서 추천하는 기업","대출한도":"지방자치단체 등과 은행이 체결한 이차보전협약에 따라, 지방자치단체에서 추천하는 금액 범위 이내","대출금리":"변동금리 연 2.18-3.64% (2021.03.24 현재)\n[기준금리 0.75%(91일물 CD유통수익률) + 가산금리 (최저)2.43%/(최고)3.89% - 이차보전지원 1% 적용기준]\n* 이자 산정방법 : 최저이율(기준금리+가산금리-우대금리) / 최고이율(기준금리+가산금리)\n* 상기 산정 금리는 대출금액 1억원, 대출기간 5년, 당행 내부 신용등급 B1등급, 일시상환, 운전자금, 보증서담보비율100%, 이차보전지원 1% 기준.\n* 가산금리는 신용등급, 대출기간, 대출금액, 대출과목, 상환방식에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경될 수 있습니다.\n* 연체대출금리는 적용금리+3.0%과 연 15.0%중 낮은 금리를 적용합니다.\n* 이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n* 기준금리 안내( 바로가기) : 상품공시실 → 대출상품 → 금리안내 → 대출(가계대출기준금리) → 91일물 CD유통수익률","상환방식 및 대출기간":"시설자금대출\n- 만기일시상환(건별대출) : 3년 이내\n- 분할상환 : 15년 이내(단, 업무용/상업용 부동산의 매입자금인 경우에는 20년 이내)\n운전자금대출\n- 만기일시상환(건별대출) : 3년 이내\n- 분할상환: 5년 이내\n※ 상기 시설자금 및 운전자금의 상환방식 및 대출기간은 지방자치단체 등과 은행이 체결한 이차보전협약에 따라 제한을 받을 수 있습니다.","이자계산방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등)단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/인터넷(스마트폰) 연장 등의 방법을 통해 신청 가능합니다.","대상담보":"고객별 신용도에 따라 부동산 등 담보제공이 필요할 수 있음","중도상환해약금":"중도상환해약금 : 중도상환대출금액 X 중도상환해약금율* X 대출잔여일수 ÷ 대출기간\n※ 최초 대출일로부터 3년(기한연장 포함)까지 적용합니다.\n※ 대출잔여기간 1개월 이내 상환 시 면제\n* 중도상환해약금율\n- 부동산/동산담보 : 고정금리 1.4%, 변동금리 1.2%\n- 신용/기타담보 : 고정금리 1.1%, 변동금리 1.0%","한도약정수수료":"지방자치단체 등과 은행이 체결한 이차보전협약에 따라, 한도거래대출은 불가합니다.","약정한도미사용\n수수료":"지방자치단체 등과 은행이 체결한 이차보전협약에 따라, 한도거래대출은 불가합니다.","대출관련비용":"대출금액 5천만원 초과시 손님 및 은행 각 인지세 50% 부담하며, 담보설정에 따른 국민주택채권 매입비용, 담보해지 시\n말소비용이 발생할 수 있습니다.\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"사업자등록증 사본, 법인등기부등본, 소요자금 증빙서류, 재무제표 등\n※ 신용조사 및 대출심사 시 필요한 서류가 추가될 수 있습니다.","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출〮만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n상기 대출금리는 대출 신규시 적용금리이며 기준금리 변동시 금리가 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n대출원리금 납입이 지연되는 경우 연체이자가 부과되며 채무자의 재산/신 용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n자세한 사항은 상품설명서를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2021.03.25 변경)\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경전 : 금리인하요구권대상여부 : 본상품은 금리인하요구권의 신청이 가능합니다\n변경후 : 금리인하요구권(‘대상여부’ 삭제)\n- 기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n- 금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n- 신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경전 : 신설\n변경후 :\n- 대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n- 대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출〮만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경전 : 신설\n변경후 :\n- 은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n- 은행은 해지를 요구 받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n상품내용 변경에 관한 사항\n(2019.04.17 변경)\n■기존 가입고객에 대한 적용 여부 : 적용\n<중도상환해약금율 변경>\n변경 전 : 부동산담보:1.4%, 신용/기타담보 1.1%\n변경 후 :\n부동산담보:고정금리시 1.4% 변동금리시 1.2%\n신용/기타담보:고정금리시 1.1% 변동금리시 1.0%","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (287, 5, '일괄/역구매자금대출', '{"판매기업에게\n유리한 점":"은행내점 없이 판매대금 조기 회수\n추심수수료 부담 없음\n구매자금어음 발행, 추심업무 등 업무량 경감","구매기업에게\n유리한 점":"자금관리의 효율성 제고\n관리비용 절감 및 처리시간 단축\n시스템 개발 비용 부담 없음(기업뱅킹 이용)","상품의 종류":"일반구매자금대출\n판매기업의 추심의뢰에 의해 구매기업이 구매대를 실행하여 추심대금을 결제하는 상품\n일괄구매자금대출\n구매기업의 구매대금 결제자료에 의해 은행이 판매기업을 대신하여 추심등록하고 구매기업이 구매대를 실행하여 추심대금을 결제하는 상품\n역구매자금대출\n판매기업이 구매기업을 위하여 신용한도를 공여하고 판매기업이 추심의뢰하는 것만으로 구매기업명의 구매대 실행 및 자금결제가 이루어지는 상품으로 구매기업 명의 대출 연체시 판매기업이 상환의무를 부담하는 상품\nB2B구매자금대출\n구매기업과 판매기업이 MP(마켓플레이스)를 통해 체결한 B2B전자상거래 매매계약의 결제수단으로 이용 가능한 상품","약정이율":"최저 연 6.064% ~ 최고 연 12.854% [2025.06.13 현재]\n최저금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 2.944%\n최고금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 9.734%\n가산금리\n최저금리 : 내부신용등급 A1등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n최고금리 : 내부신용등급 B6등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n* 가산금리는 신용등급, 약정기간, 대출한도, 담보 등에 따라 차등 적용됩니다.\n※ 상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n※ 기준금리는 건별 실행된 대출 만기의 기간에 따라 FTP 1개월물, 2개월물 등 해당 기간별 금리가 적용됩니다.\n※ 연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용\n※ ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n※ 개별대출의 이자는 월 후취방식 징구를 원칙으로 합니다. (역구매자금대출 기준임)","이자계산방법 등":"이자계산방법\n1년을 365일(윤년은 366일)로 보고 1일 단위로 계산하여 적용\n원금 또는 이자의 상환시기 및 방법\n대출금의 만기일에 구매기업의 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"아래의 ①, ②의 수수료 중 택1\n① 한도약정수수료 = 한도약정액 x 한도약정수수료율 0.3%x 약정기간 ÷ 365일(윤년 366일)\n② 약정한도미사용수수료 = (약정한도평잔 – 운용기간 중 여신평잔) x 한도미사용수수료율 0.5% x 운용기간 ÷ 365일(윤년은 366일)","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n서면에 의한 방법\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"최저 1만원 이상 취급 가능","담보 또는\n보증의 필요여부":"예금, 보증서, 부동산 등 하나은행의 여신업무내규에서 인정하는 담보 또는 신용 취급 가능","대출거래\n제한사항 등":"대출거래 제한사항\nB2B상거래정보조회(국세청 전자세금계산서 진위확인 등) 절차에 따라 세금계산서 합계 금액 초과하거나 국세청 전자세금계산서 진위확인이 되지 않는 경우에는 대출거래 제한됨\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"신용정보관리규약 상 신용거래정보 중 연체정보 등의 등록","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로\n철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나\n불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년\n(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<약정이율>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 24%입니다.\n(단, 2021.07.07 이후 법정최고금리는 연 20%임)\n변경 후 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결 일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (288, 5, '재정기금대출', '{"대출대상":"정부, 지자체, 공공기관 등 정책자금운용기관이 추천한 기업","대출한도":"정부, 지자체, 공공기관 등 정책자금운용기관이 추천하는 금액 이내","대출금리":"정부, 지자체, 공공기관 등 정책자금운용기관이 정한 금리\n예시) 에너지이용합리화자금 중 중소기업에 대한 절약시설 시설설치사업은 1.50% (2021.03.24 현재-분기별 변동)","상환방식 및 대출기간":"정부, 지자체, 공공기관 등 정책자금운용기관이 정한 상환방식 및 대출기간\n예)에너지이용합리화자금중 절약시설 시설설치사업은 3년거치 5년 분할상환","이자계산방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12\n※정부, 지자체, 공공기관 등 정책자금운용기관별로 다를 수 있습니다.","원금 또는 이자의 상환시기 및 방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등)단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시 작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기바랍니다.\n※정부, 지자체, 공공기관 등 정책자금운용기관별로 다를 수 있습니다.\n예)에너지이용합리화자금중 절약시설 시설설치사업은 원금 및 이자상환주기가 3/6/9/12월의 15일임","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기\n※정부, 지자체, 공공기관 등 정책자금운용기관별로 다를 수 있습니다.","계약해지 또는 갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도, 대출상품 등에 따라 영업점 방문/고객센터 연장/인터넷(스마트폰) 연장 등의 방법을 통해 신청 가능합니다.\n※정부, 지자체, 공공기관 등 정책자금운용기관별로 다를 수 있습니다.","대상담보":"고객별 신용도에 따라 부동산 등 담보제공이 필요할 수 있음","중도상환해약금":"없음","한도약정수수료":"정부, 지자체, 공공기관 등 정책자금운용기관은 한도거래를 지원하지 않습니다.","약정한도미사용\n수수료":"정부, 지자체, 공공기관 등 정책자금운용기관은 한도거래를 지원하지 않습니다.","대출관련비용":"대출금액 5천만원 초과시 손님 및 은행 각 인지세 50% 부담하며, 담보설정에 따른 국민주택채권 매입비용, 담보해지 시 말소비용이 발생할 수 있습니다.\n\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","필요서류":"사업자등록증 사본, 법인등기부등본, 소요자금 증빙서류, 재무제표 등\n(※ 신용조사 및 대출심사 시 필요한 서류가 추가될 수 있습니다)","금리인하요구권":"비대상","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출〮만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n상기 대출금리는 대출 신규시 적용금리이며 기준금리 변동시 금리가 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n대출원리금 납입이 지연되는 경우 연체이자가 부과되며 채무자의 재산/신 용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n자세한 사항은 상품설명서를 참조하시거나 하나은행 콜센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2021.03.25 변경)\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경전 : 신설\n변경후 : 비대상\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경전 : 신설\n변경후 :\n- 대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n- 대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출〮만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경전 : 신설\n변경후 :\n- 은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n- 은행은 해지를 요구 받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n상품내용 변경에 관한 사항\n(2017.02.16 변경)\n■기존 가입고객에 대한 적용 여부 : 적용\n<시설자금대출 기한>\n변경 전 :\n분할상환 : 10년 이내(단, 업무용/상업용 부동산의 매입자금인 경우에는 20년 이내)\n변경 후 :\n분할상환 : 15년 이내(단, 업무용/상업용 부동산의 매입자금인 경우에는 20년 이내)\n※정부, 지자체, 공공기관 등 정책자금 운용기관의 운용기준 변경에 따라 변경됩니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (289, 5, '전자채권', '{"판매기업에게\n유리한 점":"원하는 거래은행 계좌를 이용하여 전자채권 수취 및 담보대출 가능\n기업인터넷뱅킹을 활용한 간편한 업무처리 가능\n대출의 분할 실행이 가능하므로 이자부담 감소 가능","구매기업에게\n유리한 점":"신용도가 낮은 기업도 이용 가능한 전자결제수단\n판매기업의 거래은행에 제한 없음\n판매기업이 대출(할인)이자를 부담 하므로 이자부담 없음","전자채권의\n종류":"보증전자채권 : 구매기업이 발행한 전자채권에 대하여 발행은행이 지급을 보증하는 전자채권\n무보증전자채권 : 구매기업이 발행한 전자채권에 대하여 발행은행이 지급을 보증하지 않는 전자채권","전자채권\n담보대출":"판매기업이 본인의 신용(또는 담보)를 기반으로 하여 전자채권 보관은행과 약정하고 본인 명의로 취급하는 대출","약정이율":"최저 연 5.914% ~ 최고 연 6.374%(무보증 전자채권담보대출 기준임) [2025.6.13 현재]\n최저금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 2.794%\n최고금리 : 기준금리 3.12%(FTP 1개월물) + 가산금리 3.254%\n가산금리\n최저금리 : 내부신용등급 A1등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n최고금리 : 내부신용등급 B6등급, 약정기간 1년, 대출한도 10억원, 신용 취급 기준\n* 가산금리는 신용등급, 약정기간, 대출한도, 담보 등에 따라 차등 적용됩니다.\n※ 상기금리는 기준일 현재의 적용금리로 기준금리 변동시 금리가 변경될 수 있습니다.\n※ 기준금리는 건별 실행된 대출 만기의 기간에 따라 FTP 1개월물, 2개월물 등 해당 기간별 금리가 적용됩니다.\n※ 연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용\n※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n※ 개별대출의 이자는 대출취급시 일괄선취함을 원칙으로 합니다. (무보증 전자채권담보대출 기준임)","이자계산방법 등":"이자계산방법\n1년을 365일(윤년은 366일)로 보고 1일 단위로 계산하여 적용\n원금 또는 이자의 상환시기 및 방법\n전자채권의 만기일에 구매기업의 결제대금으로 상환\n원금 또는 이자상환 관련 제한\n해당사항 없음","수수료":"수수료항목 부담주체 수수료(원) 비고\n발행수수료 구매기업 500원 채권발행 시 결제계좌 출금납부\n보관수수료 판매기업 500원 채권만기일 또는 대출취급 시 차감납부\n전자채권 담보대출\n창구취급 수수료 판매기업 10,000원 창구에서 대출 취급 시 현금 또는 계좌출금 납부","대출관련비용":"인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50%씩 부담합니다.\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","계약기간 등":"계약기간\n1년 단위\n계약해지 또는 갱신의 방법\n서면에 의한 방법\n지연배상금 부과 등 처리방법\n대출금의 만기일 미상환 시, 상환하여야 할 금액에 대하여 그 다음 날부터 지연배상금 부과","최저거래금액\n또는\n최고거래한도":"전자채권 금액 범위 내 최저 1만원 이상 취급 가능","담보 또는\n보증의 필요여부":"예금, 보증서, 부동산 등 하나은행의 여신업무내규에서 인정하는 담보 또는 신용 취급 가능","대출거래\n제한사항 등":"대출거래 제한사항\nB2B상거래정보조회(국세청 전자세금계산서 진위확인 등) 절차에 따라 세금계산서 합계 금액 초과하거나 국세청 전자세금계산서 진위확인이 되지 않는 경우에는 대출거래 제한됨\n대출신청 자격요건\n사업자등록증을 교부 받은 기업으로서, 연체, 신용도 판단정보 등재 등 대출 취급에 결격사유가 없는 기업","차주에게\n발생할 수 있는\n불이익 및\n그 조건":"신용정보관리규약 상 신용거래정보 중 연체정보 등의 등록","부가혜택이\n주어지는\n조건 및 내용":"해당사항 없음","기타 주요\n계약 내용":"해당사항 없음","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로\n철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나\n불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년\n(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"무보증전자채권을 담보로 대출받은 경우 구매기업이 채권대금을 입금하지 않으면 채무자가 대출금을 상환하여야 하며, 상환이 지연될 경우 연체에 따른 불이익등을 부담하여야 합니다.\n건별 대출금의 만기연장은 불가 합니다.\n금융상품에 관한 계약을 체결하기 전에 금융상품 설명서 및 약관을 읽어 보시기 바랍니다.\n본 홍보물은 법령 및 내부통제기준에 따른 절차를 거쳐 제공됩니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","상품내용\n변경에 관한\n사항":"상품내용 변경에 관한 사항\n(2023.06.28 변경)\n<약정이율>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 24%입니다.\n(단, 2021.07.07 이후 법정최고금리는 연 20%임)\n변경 후 : ※ 대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정 최고 금리는 연 20% 입니다.\n<금리인하요구권>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.\n변경 후 :\n기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리입니다.\n금리인하 요구권 대상 (아래 조건 모두 충족)\n① 기업 신용평가 등에 따라 금리가 차등 적용되는 기업대출\n② 회사채 등급상승, 재무상태 개선(감사보고서 등), 특허취득, 담보 제공 등으로 인해 신용등급에 변동이 있는 경우\n신청방법: 해당 사유를 증빙하는 서류를 지참하시고 대출계좌를 관리하는 해당 영업점을 방문하시면 됩니다.\n신청일로부터 10영업일 이내에 금리인하 수용 여부 및 그 사유를 통보해 드립니다. [통보방법: 전화, SMS, 이메일 등]\n은행의 신용평가 결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n변경 후 :\n대출계약 철회권 행사가능 상품입니다.\n대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약 철회권 남용 시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존 가입 고객에 대한 적용 여부 :\n- 2021.03.25 이후 신규 가입 고객에 한해 적용\n변경 전 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하여 계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결 일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n변경 후 :\n위법계약해지권 행사가능 상품입니다.\n은행이 금융소비자보호에 관한 법률에서 정하는 적합성원칙, 적정성 원칙 및 설명의무를 위반하였거나 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법위반사실을 안 날’부터 1년(계약체결일로부터 5년이내의 범위에 있어야 합니다) 이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n<유의사항>\n기존 가입 고객에 대한 적용 여부 : 적용\n변경 전 : 신설\n변경 후 :\n금융소비자는 해당 상품 또는 서비스에 대하여 설명 받을 권리가 있습니다.\n대출계약철회권, 금리인하요구권 및 위법계약해지권 등 자세한 사항은 상품설명서 및 하나은행 홈페이지(www.kebhana.com)를 참조하시거나, 하나은행 고객센터(1599-2222) 또는 영업점으로 문의하시기 바랍니다.\n손님의 신용도에 따라 대출한도와 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (290, 5, '지급보증', '{"대출대상":"채무에 대한 당행의 지급보증을 거래처(보증처)에게 제공하고자 하는 기업","대출한도":"보증채무의 원금, 보증기간 중 이자 및 지연배상금의 합계액 범위 내","대출기간":"3년 이내","보증료":"2021.05.11 기준 기본금리\n지급보증 최저금리(A1) 1.960%\n취급가능등급 최고금리(B6) 6.480%\n대출금액 1억, 대출기간 1년, 전액 신용, 계약이행보증 개별거래를 가정하여 산정한 것으로 실제 대출 취급 시 대출 대상 기업의 신용등급, 담보비율 등의 요소에 따라 달라질 수 있습니다\n기본금리는 신용등급, 대출기간, 담보, 대출금액 등에 따라 차등적용됩니다.\n연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용됩니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 20%입니다.","보증해지":"당행으로 지급보증서 원본 제출","보증료계산 방법":"원화 : 보증금액에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n외화 : 보증금액에 연이율과 대출일수를 곱한 후 이를 평년, 윤년에 불구하고 360일로 나누어 산출한 외화로 징수하되 해당\n통화의 보조통화단위 미만을 절사\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매3개월 등) 단위로 선취 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출(만기일시 상환) 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출 계약해지 : 고객이 대출약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문 연장 등의 방법을 통해 신청 가능합니다.","특별배상금":"보증 기일에 보증이 해지되지 않은 경우에는 보증 기일로부터 보증 해지 전일(또는 보증채무 이행청구 전일, 대지급 발생 전일)까지 약정보증요율에 의한 특별배상금이 발생할 수 있습니다\n※ 특별배상금 = 보증금액 X 약정보증요율 X 해당일수 / 365 (윤년인 경우 366, 외화지급보증인 경우 360)\n[해당일수 = 보증기일 다음날 ~ 보증 해지 전일]","발급수수료":"대내지급보증 : 25,000원 (전자지급보증은 면제)\n대외지급보증 : 30,000원","금리인하요구권":"재무상태개선, 신용등급 상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금리인하요구권 등 상품관련 자세한 사항은 상품설명서를 참조하시거나 하나은행 고객센터(1599-1111) 또는 영업점으로 문의하시기 바랍니다.\n이 금융상품을 가입(계약)하시기 전에 ‘금융상품 설명서‘ 및 ‘약관’을 반드시 읽어보시기 바랍니다.\n이 금융상품을 가입(계약)하시는 경우 금융소비자보호법 제19조 제1항에 따라 상품에 관한 중요한 사항을 설명 받으실 수 있습니다.","상품내용 변경에 관한 사항":"2021.03.25\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경 전 : 금리인하요구권대상여부 : 본 상품은 금리인하요구권의 신청이 가능합니다\n변경 후 : 금리인하요구권\n재무상태개선, 신용도상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경 전 : 신설\n변경 후 :\n대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 미적용\n변경 전 : 신설\n변경 후 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (291, 5, '하나기업 종합통장 대출', '{"대출대상":"개인사업자 및 법인","대출금리":"2021.05.11 기준 기본금리 기준금리 가산금리\n하나기업 종합통장 대출 최저금리(A1) 4.580% 1.400% 3.180%\n취급가능등급 최고금리(B6) 12.290% 1.400% 10.890%\n대출금액 1억, 대출기간 1년, 1년 고정금리 기준, 전액 신용, 만기일시상환을 가정하여 산정한 것으로, 실제 대출 취급 시 대출 대상 기업의 신용등급, 담보비율 등의 요소에 따라 달라질 수 있습니다\n기본금리 = 기준금리 + 가산금리\n기준금리는 CD 3개월물, 금융채 6개월, 금융채 1년, 기간별 고정금리 중 선택 가능합니다\n가산금리는 신용등급, 대출기간, 담보, 대출금액 등에 따라 차등적용됩니다.\n연체이자율은 [대출이자율+연체가산이율]로 적용 / 연체가산이율은 연 3%, 연체이자율은 최고 연 15% 적용됩니다.\n대부업 등의 등록 및 금융이용자 보호에 관한 법률에 따른 법정최고금리는 연 20%입니다.","대출한도":"동일한 개인/법인당 30억원 이내(단, 한도소진율에 따라 50억원 이내)","대출기간":"여신기간은 1년 이내로 함\n(다만, 당행 예, 적부금 및 수익권을 담보로 하는 여신 제외)\n※ 1년단위로 기한연장 가능","이자계산 방법":"매일의 마감잔액에 대하여 이자를 계산.\n다만, 일중 최고잔액(-)이 마감잔액(-)보다 큰 경우에는 아래의 적수에 대하여 이자 계산\n※ 적수 = 마감잔액 + (최고잔액 - 개시잔액 또는 마감잔액 중 큰 금액)","원금 또는\n이자의\n상환시기 및\n방법":"원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n이자는 매월 셋째주 첫 영업일 전일 또는 고객이 지정한 매월 이자납입일 전일까지의 기간에 대하여 계산하여\n다음 영업일자에 기본계좌인 예금잔액에서 인출하거나 대출원금에 원가","이용시간":"이자조회 및 납입/원금상환 및 한도해지/기간연장 : (평일, 토요일, 휴일) 08:30 ~ 23:30","계약해지 또는\n갱신방법":"한도대출 계약해지 : 고객이 대출약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문 등의 방법을 통해 신청 가능합니다.","이자결산일":"매월 셋째주 첫 영업일 전일 또는 고객이 지정한 매월 이자 납입일 전일까지의 기간에 대하여 결산하여 다음 영업일자에 기본 계좌인 예금잔액에서 인출하거나 대출원금에 원가","대출관련비용":"한도대출의 경우 아래의 ①, ②의 수수료 중 택1\n한도약정수수료 = 한도약정액 x 한도약정수수료율 x 약정기간 ÷ 365일(윤년 366일, 외화대출 360일)\n※ 한도약정일 징구, 당행 내부등급 A6이상 0.1% / A7 ~ B2이상 0.2% / B3이하 및 기타 0.3%\n약정한도미사용수수료 = (약정한도평잔 – 운용기간 중 여신평잔) x 한도미사용수수료율 x 운용기간 ÷ 365일(윤년은 366일, 외화대출 360일)\n※ 결산일 익일 및 약정해지일, 당행 내부등급 A6이상 0.2% / A7 ~ B2이상 0.4% / B3이하 및 기타 0.5%\n수입인지(대출금액 5천만원 초과시 인지세의 50% 고객부담)\n근저당권 설정시 국민주택채권매입비용\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","금리인하요구권":"재무상태개선, 신용등급 상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.","대출계약철회권":"대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우 대출만기 도래 시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 연체이자가 부과되며 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n금리인하요구권 등 상품관련 자세한 사항은 상품설명서를 참조하시거나 하나은행 고객센터(1599-1111) 또는 영업점으로 문의하시기 바랍니다.\n이 금융상품을 가입(계약)하시기 전에 ‘금융상품 설명서‘ 및 ‘약관’을 반드시 읽어보시기 바랍니다.\n이 금융상품을 가입(계약)하시는 경우 금융소비자보호법 제19조 제1항에 따라 상품에 관한 중요한 사항을 설명 받으실 수 있습니다.","상품내용\n변경에 관한\n사항":"2021.03.25\n<금리인하요구권>\n기존가입고객 적용여부 : 적용\n변경 전 : 금리인하요구권대상여부 : 본 상품은 금리인하요구권의 신청이 가능합니다\n변경 후 : 금리인하요구권\n재무상태개선, 신용도상승 등에 따라 본인의 신용상태가 개선되었다고 판단되는 경우, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않음)에 금리인하 요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 심사결과에 따라 금리인하가 수용되지 않을 수 있습니다.\n<대출계약철회권>\n기존가입고객 적용여부 : 적용\n변경 전 : 신설\n변경 후 :\n대출 실행일(대출금 수령일)로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.\n<위법계약해지권>\n기존가입고객 적용여부 : 미적용\n변경 전 : 신설\n변경 후 :\n은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 혹은 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날’부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.\n대출한도 변경\n2015.09.01\n변경 전 : 동일한 개인/법인당 10억원 이내\n변경 후 : 동일한 개인/법인당 30억원 이내","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (292, 5, '한국은행 금융중개지원대출관련 신성장·일자리지원 프로그램', '{"신성장·\n일자리지원\n대출이란?":"「한국은행 금융중개지원대출 관련 신성장·일자리지원대출 프로그램」 운용절차 및 세칙에 근거하여 중소기업(개인사업자 포함)에 지원하는 대출 상품임","대출대상":"창업 후 7년 이내의 중소기업인 창업기업 (기술형창업, 일반창업), 소재·부품의 제조 및 기술개발을 주된 사업으로 영위하는 전문 소재·부품·장비기업과 창업 후 업력에 제한을 받지 않은 일자리 창출 중소기업","기술형창업\n기업":"「중소기업창업 지원법」에 따른 창업 후 7년 이내 중소기업 + 아래 1~6 조건 중 하나\n1. 특허권 및 실용신안권 보유기업\n2. 정부 및 정부 공인기관 인증기술 보유 기업\n3. R&D기반 창업기업\n4. 기술신용평가(TCB)기반 창업기업\n5. 연구개발 우수기업\n6. 신성장 동력 영위기업","일반창업\n기업":"「중소기업창업 지원법」에 따른 창업 후 7년 이내 중소기업\n지원대상 제외업종: 금융업, 보험업, 부동산업, 임대업, 숙박 및 음식점업, 무도장운영업, 골프장 및 스키장운영업, 사행시설\n관리 및 운영업, 변호사업, 변리사업, 공인회계사업, 세무사업, 병원, 의원, 기타 개인서비스업 등 한국은행 통화정책국장이\n정하는 업종","일자리창출\n기업":"1. 「조세특례제한법」 제 26조에 따른 고용창출투자에 대한 세액공제를 받은 기업\n2. 「조세특례제한법」 제 29조의5에 따른 청년고용 증대에 대한 세액공제를 받은 기업\n3. 「조세특례제한법」 제 29조의7에 따른 고용증대에 대한 세액공제를 받은 기업\n4. 「조세특례제한법」 제 30조의2에 따른 정규직 근로자로의 전환에 대한 세액공제를 받은 기업\n5. 「조세특례제한법」 제 30조의4에 따른 중소기업 사회보험료 세액공제를 받은 기업","자금용도":"운전자금","대출한도":"최대 100억원 범위내","상환방식":"만기일시상환방식\n분할상환방식","이자계산 방법":"대출금에 연이율과 대출일수를 곱한 후 이를 365일(윤년인 경우 366일)로 나누어 산출하되 원단위 미만은 절사\n원리금균등분할상환대출의 월별이자 계산은 대출원금에 연이율을 곱한 다음 12로 나누어 계산\n일수계산은 여신당일로부터 기일 또는 상환일(일부상환 및 분할상환 포함) 전일까지로 한다.(한편넣기)\n위 내용에도 불구하고 다음의 여신은 여신당일부터 기일 또는 상환일까지로 한다.\n1. 대출 당일에 회수되는 대출금 / 2. 대외기관으로부터 자금을 차입하는 대출금으로서 이자를 상환일까지 지급하는 대출금 /\n3. 연체기간이 1일인 연체대출채권 및 지급보증대지급금 / 4. 대여유가증권\n원금 균등분할상환대출 : 대출금액 x 대출이자율 x 이자일수 ÷ 365(윤년은 366일)\n원리금 균등분할상환대출 : 대출금액 × 대출이자율 ÷ 12","원금 또는\n이자의\n상환시기 및\n방법":"건별대출(만기일시상환) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n한도대출(마이너스통장) : 원금은 만기에 일시상환, 이자는 일정주기(매1개월 등) 단위로 납부\n분할상환대출 : 분할상환원금 및 이자를 일정주기(매1개월 등) 단위로 납부\n대출실행 응당일/별도지정일에 대출금 입금계좌/자동등록계좌에서 자동이체 처리\n고객님께서 선택하시는 대출방식 및 상품별로 원금/이자의 상환시기 및 방법이 일부 상이할 수 있으므로, 대출취급시\n작성하시는 대출거래약정서의 상환방법 및 이자지급방법 내용을 참조하여 주시기 바랍니다.","이용시간":"고객센터의 인터넷뱅킹 이용시간 안내를 참조하시기 바랍니다. 바로가기 >","계약해지 또는\n갱신방법":"건별대출 계약해지 : 고객이 대출금 전액을 상환한 때\n한도대출(마이너스통장방식) 계약해지 : 고객이 통장대출 약정에 대한 해지의사를 표시한 때\n계약갱신 : 대출금은 약정기일(만기일)에 상환이 원칙이나, 당행에서 정한 일정요건을 갖춘 경우 기간연장 가능\n※ 대출기간 만료시 은행이 정한 기준에 따라 기간연장 가능여부 심사후 승인을 득한 경우 계약갱신 가능하며, 고객님의 신용도,\n대출상품 등에 따라 영업점 방문/고객센터 연장/전자금융채널(인터넷뱅킹·스마트폰뱅킹) 등의 방법을 통해 연장 가능합니다.","대출금리":"변동금리 운전대 연 2.47-8.37%\n[기준금리 1.72%(기준금리, 91일물 CD유통수익률) + 2.06~7.96%(가산금리)) – 차입효과 1.31% * 이자 산정방법 : 최저이율(기준금리+가산금리-우대금리) / 최고이율(기준금리+가산금리)\n* 당행 내부신용(CRS) (최고)A1등급, (최저)B6등급 / 대출금액 100백만원 / 대출기간 1년 / 한국은행 자금 차입이율 0.50%, 차입비율 70%(운전/신용)기준\n* 가산금리는 신용등급, 대출기간, 대출금액, 대출과목, 상환방식, 담보종류, 한국은행 차입비율에 따라 차등 적용되며, 당행 기업여신 금리가이드라인에 따라 변경될 수 있습니다.\n* 연체대출금리는 적용금리+3.0%과 연 15.0%중 낮은 금리를 적용합니다.\n* 이자의 부과시기 : 후취방식 징구를 원칙으로 1개월 이내의 기간 단위로 징수합니다.\n* 기준금리 안내( 바로가기) : 상품공시실 → 대출상품 → 금리안내 → 대출(가계대출기준금리) → 91일물 CD유통수익률","대출기간":"운전자금: 1년 이내","대출관련비용":"대출금액 5천만원 초과시 수입인지 비용 발생\n인지세\n인지세법에 의해 대출약정 체결시 납부하는 세금으로 대출금액에 따라 세액이 차등 적용되며, 은행과 고객이 각 50% 씩 부담합니다.\n※ 대출관련 인지세\n대출금액 인지세액\n5천만원 이하 비과세\n5천만원 초과 ~ 1억원 이하 7만원\n1억원 초과 ~ 10억원 이하 15만원\n10억원 초과 35만원","금리인하요구권":"기업신용평가 등에 따라 고객의 신용상태가 호전되었다고 인정되는 경우 대출금리 변경을 요구할 수 있는 권리이며, 증빙자료를 첨부한 금리인하신청서를 은행에 제출하여 금리인하를 요구할 수 있습니다.\n금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다.)에 금리 인하요구 수용 여부를 통지해 드립니다.\n신용상태가 금리에 영향을 미치지 않는 상품은 금리인하요구 대상에서 제외되며, 은행의 신용평가 결과에 따라 금리인하가 되지 않을 수도 있습니다.","대출계약철회권":"대출금 수령일로부터 14일(기간의 말일이 휴일인 경우 다음 영업일)까지 은행에 서면, 전화, 컴퓨터 통신으로 철회의사를 표시하고 원금, 이자 및 부대비용을 전액 반환한 경우 대출계약을 철회할 수 있습니다.\n대출계약철회권 남용시 불이익: 당행에서 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출〮만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.","위법계약해지권":"은행이 『금융소비자 보호에 관한 법률』에서 정하는 적합성원칙, 적정성원칙 및 설명의무를 위반하였거나, 불공정영업행위 또는 부당권유행위를 하여 대출계약을 체결한 경우, ‘법 위반사실을 안 날＇부터 1년(계약체결일로부터 5년 이내의 범위에 있어야 합니다.)이내에 해당 계약의 해지를 요구할 수 있습니다.\n은행은 해지를 요구받은 날부터 10일 이내에 고객에게 수락여부를 통지하며, 거절할 때에는 그 사유를 함께 통지합니다.","유의사항":"손님의 신용도에 따라 대출한도, 대출금리가 차등 적용되며 대출취급이 제한될 수 있습니다.\n대출금리는 기준금리 변동 시 변경될 수 있습니다.\n대출거래약정서에 별도로 정한 바가 없을 경우, 대출만기 도래시 채무자의 신청 및 은행 내규 등에 따라 만기연장 여부가 결정되며, 만기연장 조치 없이 대출금액을 상환하지 않을 경우에는 은행이 채권보전을 위한 절차에 착수하게 되므로 채무자의 재산/신용상의 불이익(압류, 경매 등)이 발생할 수 있으니 주의하시기 바랍니다.\n대출원리금 납입이 지연되거나 대출만기도래시, 대출금액을 상환하지 않을 경우 재산 및 신용상 불이익이 발생할 수 있으니 주의하시기 바랍니다.\n금융상품에 관한 계약을 체결하기전에 금융상품설명서 및 약관을 읽어보시기 바랍니다.\n상환능력에 비해 대출금이 과도할 경우 귀하의 신용점수가 하락할 수 있습니다.\n신용등급 또는 개인신용평점의 하락으로 금융거래 관련 불이익을 받을 수 있습니다.\n원리금 연체 시 계약만료 기한 전에 모든 원리금을 변제해야 할 의무가 발생될 수 있습니다.\n자세한 사항은 상품설명서를 참조하시거나,영업점으로 문의하시기 바랍니다.","상품내용 변경에 관한 사항":"상품내용 변경에 관한 사항\n(2022.04.26 변경)\n<대출대상>\n기존가입고객 적용여부 : 적용\n소재·부품· 장비기업 지원종료\n<자금용도>\n시설자금 지원종료","금리표":[],"상품설명서_경로":""}');
INSERT INTO BANK_TELLER_PRODUCT (PRODUCT_ID, CATEGORY_ID, PRODUCT_NAME, ATTRIBUTES) VALUES (293, 5, 'e커머스 정산채권 팩토링', '{"가입대상":"다음의 요건을 모두 충족한 판매업체\n\n1. 은행과 협약된 e커머스 마켓(중심업체) 거래 또는 거래 예정인 판매업체\n2. 사업자등록증 보유 개인사업자 또는 법인사업자\n3. 중심업체가 은행에 팩토링 대상업체로 전자적 방식으로 통지한 판매업체\n4. 중심업체로부터 정산대금 받는 계좌를 팩토링 대금 입금계좌와 동일하게 지정","약정한도":"판매업체별 약정한도 부여 없이, 은행과 협약된 중심업체 총 한도 내에서 팩토링 이용\n※ 단, 모든 판매업체의 팩토링 채권잔액의 합이 중심업체 총한도 초과시 이용불가","실행":"중심업체가 은행에 전자적 방식으로 양도 가능한 것으로 통지한 정산채권 범위 내에서 판매업체 팩토링 실행","금리":"중심업체의 신용도에 따라 산정된 할인율(금리) 적용","이자납입방법":"팩토링 실행시 정산채권 만기일까지 할인료(이자) 선취","채권결제 및\n상환청구권":"중심업체가 정산채권 만기에 상환(결제)하며, 은행은 팩토링 실행 이후 판매업체에 대하여 그 상환을 청구할 권리가 없음","약정기간 및\n약정해지":"약정 후 별도 약정만기를 정하지 않으며, 약정해지 사유 중 하나에 해당시 약정해지\n\n[약정해지 사유]\n1. 판매업체가 은행에 약정해지 신청하는 경우\n2. 중심업체와 판매업체간 마겟이용계약이 해지되는 경우\n3. 판매업체가 팩토링 실행한 마지막 날로부터 2년간 신규 실행을 하지 않는 경우\n4. 중심업체와 은행간 팩토링 관련 협약이 해지된 경우","유의사항":"e커머스 정산채권 팩토링 실행 후 은행에 매각한 정산채권의 중도환매는 불가능하며, 중심업체에 의해서만 상환가능","금리표":[],"상품설명서_경로":""}');